UPDATE creature_ai_texts SET content_default='Also da waren wir - ich und mein Battailon, gerade bei einer Routine Erkundung den Knochenweg runter. Wir wollten den Aufklärer hoch schicken um sich umzusehen falls sich irgendetwas gefährliches zusammenbraut.' WHERE entry=-293;
