UPDATE creature_ai_texts SET content_default='Wenn mein Bauch nicht damit drohen würde, sich von meinem Körper zu lösen um mich alleine zu lassen mit diesem Müll den wir den Gefangenen geben, wäre ich beleidigt von diesem... diesem Essen.' WHERE entry=-295;
