UPDATE creature_ai_texts SET content_default='Nun, da wir das geklärt haben, willkommen auf der Insel Quel\'Danas. Der Sonnenbrunnen ist auf dem südlichen Plateau und Prinz Kael\'thas hat sich auf der Terasse der Magister im Osten verkrochen.' WHERE entry=-385;
