UPDATE creature_ai_texts SET content_default='Die derzeitige Situation sieht wie folgt aus: Wir halten, was wir können, während diejenigen mit mehr Erfahrung und besserer Ausrüstung gegen Prinz Kael\'thas vorgehen.' WHERE entry=-390;
