UPDATE creature_ai_texts SET content_default='Leute, ich versichere euch, sollte er erfolgreich sein, wird diese Welt ein weitaus schlimmeres Schicksal erleiden, als die Scherbenwelt es je musste! Also lasst uns sichergehen, dass das nicht geschieht.' WHERE entry=-392;
UPDATE creature_ai_texts SET content_default='Krieger werden abgestellt, um Schlüsselpositionen zu halten, während ihr Bogenschützen zum Fernhalten der gegnerischen Flieger abkommandiert werdet.' WHERE entry=-393;
