UPDATE npc_text SET Text0_1='Seid gegrüßt, $N' WHERE id=1;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N', Text0_1='Seid gegrüßt, $N' WHERE id=68;
UPDATE npc_text SET Text0_0='He, $GBürger:Bürgerin ! Ihr seht recht kräftig aus. Wir Wachen sind hier eher spärlich gesät. Daher könnten ich Eure Hilfe gut gebrauchen...' WHERE id=197;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N', Text0_1='Seid gegrüßt, $N' WHERE id=384;
UPDATE npc_text SET Text0_0='Viel zu tun, viel zu tun! Der Verband hat uns Fristen gesetzt, die wir besser nicht überschreiten sollten!' WHERE id=518;
UPDATE npc_text SET Text0_0='Direktor Riddlevox hat mir die Aufsicht über das Entwicklungsprogramm für Schredder übertragen, das der Verband durchführt. Ich bin erst wenige Monate dabei und sitze schon in der Tinte! Ich könnte Eure Hilfe brauchen, $N!' WHERE id=519;
UPDATE npc_text SET Text0_0='Seht Ihr, der neue Schredder der Venture Company ist besser als mein letzter, von den austauschbaren Greifern bis hin zu den Steuerungshebeln! Mein Boss hat mir einen ziemlich knappen Ablieferungstermin aufgebrummt, aber meinen Jungs und mir ist einfach nichts eingefallen.$B$BNichts hat funktioniert, also haben wir uns entschlossen, einen ihrer Prototypen "auszuborgen". Ich habe Kurbelwizz ins Schlickmoor geschickt, um einen zu stehlen, aber er ist noch immer nicht zurück!' WHERE id=520;
UPDATE npc_text SET Text0_0='Ihr habt Geistheuler getötet. Ihr seid ein toller Hecht!' WHERE id=521;
UPDATE npc_text SET Text0_0='Kommt näher, mein Kind. Zu meiner Zeit konnte ich das flatternde Herz meiner Beute hören, aber heute müsst Ihr laut sprechen, damit Eure Worte gehört werden.' WHERE id=522;
UPDATE npc_text SET Text0_0='Viele, viele Jahreszeiten lang zog ich durch diese Ebenen. Und ich habe gewaltige Wildtiere gejagt. Und nie wurde ich übertroffen... bis ich Geistheuler sah.$B$BVor langer Zeit war der enorme Wolf ein Verbündeter der Tauren und kämpfte tapfer gegen die Brennende Legion. Aber er wurde von einem Dämon schlimm verwundet und der Riss in seinem Fleisch ist nie verheilt. Schließlich... trieb er Geistheuler in den Wahnsinn.$B$BJetzt durchstreift er gequält die Ebenen und sucht nach dem Ende seiner Pein. Zu meiner Schande fehlte mir die Kraft, ihn zu töten, als wir kämpften...' WHERE id=523;
UPDATE npc_text SET Text0_0='Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?', Text0_1='Ich grüße Euch, Magier. Darf ich Euch die Welt der Magie noch ein wenig näher bringen?' WHERE id=538;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', Text0_1='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE id=539;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.', Text0_1='Seid gegrüßt. Ich bin Magielehrer und Ihr seid Magier.' WHERE id=558;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', Text0_1='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE id=559;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.', Text0_1='Seid gegrüßt. Ich bin Gnomenmagielehrer und Ihr seid Magier.' WHERE id=560;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...', Text0_1='Seid gegrüßt, $C. Ihr erbittet einen Rat? Freundet Euch mit einem Magier an! Wir haben da einen Trick oder zwei auf Lager, ja das haben wir...' WHERE id=561;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.', Text0_1='Seid gegrüßt. Ich bin Untoten-Magielehrer und Ihr seid Magier.' WHERE id=562;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.', Text0_1='Seid gegrüßt, $C. Mein Rat an Euch ist Folgender: geht vorsichtig mit der Magie um, während Ihr durch die Welt reist, denn sie wird diejenigen zerstören, die ihr nicht gewachsen sind.' WHERE id=563;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?', Text0_1='Seid gegrüßt, $C. Seid Ihr hier, um die verborgenen Künste zu erlernen?' WHERE id=564;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...', Text0_1='Seid gegrüßt, $C. Ich bemitleide Eure Art, da Euch die Geheimnisse der wahren Magie verwehrt sind...' WHERE id=565;
UPDATE npc_text SET Text0_1='Für Euch ist die Zeit noch nicht reif. Ich werde Euch bei der Reise zurück in die Welt der Lebenden behilflich sein... gegen Bezahlung.' WHERE id=580;
UPDATE npc_text SET Text0_0='Mit Zustimmung von Fürstin Sylvanas steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.', Text0_1='Mit Zustimmung von Fürstin Sylvanas steigt die Anzahl der Todespirscher an. Mehr Klingen, die unsere Feinde treffen, wenn sie es am wenigsten erwarten.' WHERE id=581;
UPDATE npc_text SET Text0_0='Was wollt Ihr, $C? Ich bin spät dran und mein Arbeitgeber sieht Unpünktlichkeit nicht gern.' WHERE id=623;
UPDATE npc_text SET Text0_0='Ja bin ich. Was gibts zu tun?' WHERE id=624;
UPDATE npc_text SET Text0_0='Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.', Text0_1='Ungeachtet der Ängstlichkeit des Kriegshäuptlings wird der Klan der Zerschmetterten Hand der Horde zu ihrem früheren Ruhm verhelfen.' WHERE id=638;
UPDATE npc_text SET Text0_0='Warum besucht ein junger $C wie Ihr einen alten Bauern wie mich?' WHERE id=678;
UPDATE npc_text SET Text0_0='Wenn Therzok Euch geschickt hat, dann sagt ihm: Nein, ich werde nicht zu ihm zurückkehren, egal wie oft er noch fragt.' WHERE id=679;
UPDATE npc_text SET Text0_0='Naralex schläft wieder!$B$BVor langer Zeit reiste Naralex mit dem hehren Ziel zu dieser Höhle, in den Smaragdgrünen Traum einzutreten und aus dem Brachland wieder einen üppigen Wald werden zu lassen. Seine Konzentration nahm ab und seine Gedanken wurden von Visionen von Schlangen besudelt.$B$BJetzt sickern reptilienartige Bestien aus seinen Träumen in die Realität. Er muss aus seinem Alptraum erweckt werden, ansonsten wäre er für immer verloren und etwas sehr Böses wäre entfesselt.$B$BErst, wenn seine verderbten Diener, die Giftzahnlords, tot sind, kann ich das Erweckungsritual durchführen.' WHERE id=698;
UPDATE npc_text SET Text0_0='Die Giftzahnlords sind tot! Aber noch ist nicht alle Hoffnung verloren!$B$BDas Erweckungsritual ist jedoch sehr kompliziert. Es kann nicht allein vollzogen werden.$B$Blch werde mich um die notwendigen Vorbereitungen kümmern, aber Ihr müsst mich beschützen. Die beschworenen Nachtmahre aus Naralex\' Reise durch den Smaragdgrünen Traum haben sich als äußerst gefährlich erwiesen. Ohne Eure Hilfe ist diese Mission zum Scheitern verurteilt.$B$BWenn Ihr bereit seid, mich zu verteidigen, gebt Bescheid und ich werde Euch den Weg zu Naralex\' Kammer weisen.' WHERE id=699;
UPDATE npc_text SET Text0_0='Die Maschine richtet sich auf Euch aus.$B$BAuf ihrer Rückseite ist ein leerer, seltsam deformierter Schlitz mit einer Inschrift: "Eigntuhm vom Käpt\'n Gelkak Gyromast"' WHERE id=718;
UPDATE npc_text SET Text0_0='He! Seid Ihr auf einem Botengang für Tirth? An Eurer Stelle würde ich mich möglichst fern von ihm halten! Seine Gedanken sind zwar vom vielen Trinken benebelt, aber seine Magie ist so stark wie immer!' WHERE id=720;
UPDATE npc_text SET Text0_0='Sicher werde ich Euch den Satz sagen. Solange ich nicht in Tirths Nähe muss!' WHERE id=738;
UPDATE npc_text SET Text0_0='Gelkaks Schlüssel gleitet in den leeren Schlitz.' WHERE id=758;
UPDATE npc_text SET Text0_0='Macht Ihr Euch etwa über mich lustig, während ich an dieses Eisen gefesselt bin und mich nicht verteidigen kann?' WHERE id=759;
UPDATE npc_text SET Text0_0='Einer der Menschen hat den Schlüssel zu diesem Höllenapparat. Ich meine, jemand hätte gesagt, er hieße Eston.' WHERE id=760;
UPDATE npc_text SET Text0_0='Verschwindet von hier, $C, oder sie werden auch Euch in Ketten legen.' WHERE id=761;
UPDATE npc_text SET Text0_0='Marlgen... Einer der Kerkermeister. Er hat den Schlüssel zu meinen Fesseln.' WHERE id=762;
UPDATE npc_text SET Text0_0='Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', Text0_1='Die Bank von Sturmwind befindet sich im Handelsdistrikt nordwestlich der Stadttore. Hier, ich zeige es Euch auf Eurer Karte.', Text1_0='Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei und genehmigt Euch einen Schluck.', Text1_1='Das Zählhaus Sturmwind findet Ihr in der Nähe der vorderen Tore im Handelsdistrikt von Sturmwind. Und wenn Ihr keine Lust mehr habt, Euer Geld zu zählen, dann schaut mal in der Güldenen Rose vorbei und genehmigt Euch einen Schluck.' WHERE id=764;
UPDATE npc_text SET Text0_0='Ich wünschte, der Schnee würde einfach wegschmelzen. Er macht meine Robe immer nass und hinterlässt nichts als Dreck!' WHERE id=766;
UPDATE npc_text SET Text0_0='Blöder Schnee... He, Ihr seid doch $N, nicht wahr? Ich habe gerade eine Nachricht erhalten, in der nach Euch gefragt wurde.' WHERE id=767;
UPDATE npc_text SET Text0_0='Ihr wurdet von Lago Finsterzang beschworen, dem Meister aller Hexenmeister von Eisenschmiede. Ihr tätet gut daran, Euch unverzüglich bei ihm zu melden. Ihr findet ihn in der Stadt im Distrikt des düsteren Viertels.' WHERE id=768;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $R. Mein Name ist Tharnariun Baumwahrer.' WHERE id=769;
UPDATE npc_text SET Text0_0='Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', Text0_1='Wenn Ihr mich fragt, sind die Goblins ein todsicherer Tipp. Wir haben das Werkzeug und das Talent!', Text1_0='Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', Text1_1='Hätten die nicht dieses verfluchte Gnomenglück, würden sie niemals ein Rennen gewinnen!', Text2_0='An Eurer Stelle würde ich einen Goblinrennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', Text2_1='An Eurer Stelle würde ich einen Goblinrennwettschein kaufen. Das andere Team anzufeuern ist reine Zeitverschwendung.', Text3_0='Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', Text3_1='Ich habe mein Geld auf die Goblins gesetzt! Und wenn Ihr einen Funken Verstand habt, tut Ihr es mir gleich!', Text4_0='Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.', Text4_1='Ich hörte, sie wollen den Wagen des Gnoms im nächsten Rennen hochgehen lassen! Das mag stimmen oder auch nicht, aber auf jeden Fall würde ich mich von Ihrer Box fern halten.' WHERE id=778;
UPDATE npc_text SET Text0_0='Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', Text0_1='Oh, Ihr meint Johnson? Ich hörte, er würde sich verstecken.', Text1_0='Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', Text1_1='Ha! Tirth sucht ihn schon seit Tagen, aber er will nicht gefunden werden. Er ist so ein Angsthase!', Text2_0='Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkreiter auf dem Turm fragt, sagt er es Euch vielleicht...', Text2_1='Ihr sucht nach Eisenherz? Tja, das habt Ihr nicht von mir, aber... wenn Ihr Kronkreiter auf dem Turm fragt, sagt er es Euch vielleicht...' WHERE id=779;
UPDATE npc_text SET Text0_0='Hätten die nicht dieses unglaubliche Goblinglück, würden die in jedem Rennen einen Unfall bauen!', Text0_1='Hätten die nicht dieses unglaubliche Goblinglück, würden die in jedem Rennen einen Unfall bauen!', Text1_0='In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', Text1_1='In diesen Rennen können wir endlich mal zeigen, wie genial wir sind!', Text2_0='Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', Text2_1='Ich habe im Matrix-Regulometer unseres Wagens ein neues Gerät eingebaut. Damit sollte sich die Beschleunigung um satte fünfzehn Prozent erhöhen lassen!', Text3_0='Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblinschrotthaufen!', Text3_1='Mein Sohn könnte einen schnelleren Wagen bauen als diesen klobigen Goblinschrotthaufen!', Text4_0='Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!', Text4_1='Mein Gizmotik-Almanakotron sagt, dass wir gegen die Goblins noch mal 20 Sekunden Vorsprung rausholen können!' WHERE id=780;
UPDATE npc_text SET Text0_0='Endlich! Endlich bin ich aus dieser Zelle befreit!$B$BDie Kirin Tor schickten mich von Dalaran aus, um Arugals Fortschritte mit den Untoten hier im Silberwald zu untersuchen. Wir wussten nicht, dass der Hexer seinen Verstand verloren hatte.$B$BIch muss über diesen Wahnsinn sofort Bericht erstatten! Aber bevor ich gehe, erlaubt mir, das magische Schloss an der Hoftür zu öffnen.$B$BIhr habt Rotkralle besiegt, vielleicht werdet Ihr auch überleben, was jenseits von hier liegt...' WHERE id=798;
UPDATE npc_text SET Text0_0='Endlich! Jemand, der mich aus dieser Zelle befreit!$B$BHochexekutor Hadrec schickte uns aus, Informationen über die Burg zu sammeln, sodass ein Plan entwickelt werden konnte, Arugal ein für alle Mal aus dem Weg zu räumen.$B$BAber der alte Hexer hat viele Asse im Ärmel und wir wurden von einem magischen Zauberschutz entdeckt. Ich wurde in den Kerker geworfen. Vincent hatte nicht so viel Glück.$B$BIch muss sofort zurück zu Hadrec zurückkehren und ihm Bericht erstatten. Aber erst werde ich Euch das Schloss der Tür zum Hof öffnen. Vielleicht wollt Ihr Euer Glück gegen die Feinde versuchen, die dahinter lauern.' WHERE id=799;
UPDATE npc_text SET Text0_0='Einst folgte ich der Denkart der Schattenhammerkultisten. Ich war besessen davon, die Alten Götter wiederzubeleben, und so drang ich tief in diesen Unterschlupf vor, um mir die Macht der verfluchten Bestie Aku\'mai zu Eigen zu machen.$B$BNachdem ich zahllose Unschuldige hatte sterben sehen, kam ich wieder zu Verstand.$B$BIch war gefangen, doch nun bin ich frei. Allerdings bin ich so weit fort von meiner Heimat Khaz Modan!$B$BDaher hört mein Angebot: Ich biete Euch eine Passage in ein ganz besonderes Land als Gegenleistung für den Tod von Aku\'mai. Wenn Ihr es wünscht, bringe ich Euch nach Darnassus.' WHERE id=818;
UPDATE npc_text SET Text0_0='Willkommen in meinem Gasthaus, $G müder Reisender:müde Reisende;. Was kann ich für Euch tun?', Text0_1='Willkommen in meinem Gasthaus, $G müder Reisender:müde Reisende;. Was kann ich für Euch tun?' WHERE id=820;
UPDATE npc_text SET Text0_0='Ruht Eure müden Knochen für einen Zauber aus.', Text0_1='Ruht Eure müden Knochen für einen Zauber aus.' WHERE id=821;
UPDATE npc_text SET Text0_0='Wie der Wind in den Ebenen seid Ihr hier immer willkommen.', Text0_1='Wie der Wind in den Ebenen seid Ihr hier immer willkommen.' WHERE id=822;
UPDATE npc_text SET Text0_0='Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.', Text0_1='Füllt Euren Krug und zieht einen Stuhl heran. Wir haben Geschichten zu erzählen und Humpen zu leeren.' WHERE id=823;
UPDATE npc_text SET Text0_0='Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.', Text0_1='Euer Körper sieht geschwächt und Euer Geist sieht müde aus. Ruht Euch unter unserem Dach aus und erlaubt es Elune, Eure Träume zu führen.' WHERE id=824;
UPDATE npc_text SET Text0_0='Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.', Text0_1='Kommt herein, kommt nur herein. Setzt Euch nur nicht zu nahe an den Ofen. Er ist heute zwar noch nicht explodiert, aber nur um sicherzugehen solltet Ihr Euch woanders hinsetzen.' WHERE id=825;
UPDATE npc_text SET Text0_1='Hallo, $R, es ist mir ein Vergnügen, Eure Bekanntschaft zu machen.' WHERE id=838;
UPDATE npc_text SET Text0_1='Tja, tja... schaut nur, wer da hereinschneit... und wie kann ich Euch helfen, $C?' WHERE id=878;
UPDATE npc_text SET Text0_0='Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', Text0_1='Ihr findet den Greifenmeister am nordöstlichen Wall, der das Tal der Helden überblickt.', Text1_0='Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', Text1_1='Ahhh, Ihr sucht nach Dungar Trinkfest. Er besitzt einige der schnellsten Greife diesseits von Khaz Modan! Ihr findet ihn auf den Wällen des Handelsdistrikts.', Text2_0='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', Text2_1='Ihr wollt einen Greifenritt unternehmen, was? Dungar hält seine Vögel oben auf dem Wall des Handelsdistrikts. Denkt aber immer daran, Euch festzuhalten!', Text3_0='Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.$B$BIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.', Text3_1='Greife, ja? Hab mich nie wirklich um die Biester gekümmert, aber jedem das seine.$B$BIhr findet Dungar Trinkfest auf dem Wall des Handelsdistrikts.' WHERE id=879;
UPDATE npc_text SET Text0_0='Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', Text0_1='Ihr findet Aldwin Laughlin im Besucherzentrum des Handelsdistrikts von Sturmwind.', Text1_0='Wollt \'ne Gilde aufmachen, was? Dann viel Glück!$B$BSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', Text1_1='Wollt \'ne Gilde aufmachen, was? Dann viel Glück!$B$BSprecht mit Aldwin Laughlin im Besucherzentrum von Sturmwind. Wenn Ihr durch das Tal der Helden hineinkommt, befindet es sich gleich am Anfang des Handwerkdistrikts.', Text2_0='Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.', Text2_1='Ahhh, dann sucht Ihr also nach dem Besucherzentrum von Sturmwind. Es ist das erste Gebäude auf der linken Seite, wenn Ihr durch die vorderen Tore vom Tal der Helden aus kommt.' WHERE id=882;
UPDATE npc_text SET Text0_0='Einen Lehrer für welche Klasse sucht Ihr?', Text0_1='Einen Lehrer für welche Klasse sucht Ihr?' WHERE id=898;
UPDATE npc_text SET Text0_0='Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', Text0_1='Die Magier versammeln sich im Magiersanktum im Magierviertel. Dort gibt es einen recht eindrucksvollen Turm. Der lässt unsere Kasernen wie Arbeiterhütten aussehen.', Text1_0='Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', Text1_1='Oh, Ihr sucht also nach arkaner Erleuchtung? Dann gibt es für Euch keinen besseren Ort als das Magiersanktum im Magierviertel.', Text2_0='Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.', Text2_1='Hört sich an, als wolltet Ihr mit Maginor Dumas sprechen! Ihr findet ihn normalerweise im Magiersanktum im Magierviertel. Natürlich weiß man nie, wann oder wohin sich diese Magier gerade teleportieren.' WHERE id=899;
UPDATE npc_text SET Text0_0='Dann wollt Ihr mit Osborne reden. In Sturmwind gibt es keinen besseren Lehrer in den Künsten der Verstohlenheit als ihn. Ihr findet ihn in der Altstadt.', Text0_1='Dann wollt Ihr mit Osborne reden. In Sturmwind gibt es keinen besseren Lehrer in den Künsten der Verstohlenheit als ihn. Ihr findet ihn in der Altstadt.', Text1_0='Pah, Schurken haben nichts anderes getan, als unsere Arbeit hier in Sturmwind zu erschweren. Ich wünschte, ich könnte sowohl Osborne als auch Mathias aus der Altstadt direkt zum Verlies zerren.', Text1_1='Pah, Schurken haben nichts anderes getan, als unsere Arbeit hier in Sturmwind zu erschweren. Ich wünschte, ich könnte sowohl Osborne als auch Mathias aus der Altstadt direkt zum Verlies zerren.', Text2_0='Wenn Ihr Schurken zu finden wünscht, solltet Ihr in die Altstadt gehen. Sie befindet sich in der nordöstlichsten Ecke von Sturmwind.', Text2_1='Wenn Ihr Schurken zu finden wünscht, solltet Ihr in die Altstadt gehen. Sie befindet sich in der nordöstlichsten Ecke von Sturmwind.' WHERE id=900;
UPDATE npc_text SET Text0_0='Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag\' Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', Text0_1='Krieger sind normalerweise in den Kasernen der Altstadt oder in der Taverne Zum pfeifenden Schwein zu finden. Ich sag\' Euch aber, dass die Taverne wohl der bessere Ort zum Suchen ist.', Text1_0='Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', Text1_1='Ihr sucht nach Ausbildung, ja? Ich könnte Euch besser als jeder andere in Sturmwind unterrichten, aber die Wache verbietet es. Ich denke, Ihr müsst Euch mit einem dieser Abgehalfterten zufrieden geben, die sich in der Altstadt bei der Kaserne oder in der Taverne Zum pfeifenden Schwein herumtreiben.', Text2_0='Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.', Text2_1='Schaut in der Kaserne der Altstadt oder der Taverne Zum pfeifenden Schwein nach. Wenn Ihr in die Taverne geht, müsst Ihr ein paar der vorzüglichen Biersorten kosten. Ich sag Euch, Zwergenbier schmeckt schal dagegen.' WHERE id=901;
UPDATE npc_text SET Text0_0='Tja, Ihr könnt Theridan und Maldryn beim Mondbrunnen im Park finden. Geht einfach in den Westen von Sturmwind, nördlich vom Magierviertel, aber westlich vom Kathedralenplatz.', Text0_1='Tja, Ihr könnt Theridan und Maldryn beim Mondbrunnen im Park finden. Geht einfach in den Westen von Sturmwind, nördlich vom Magierviertel, aber westlich vom Kathedralenplatz.', Text1_0='Wenn Ihr nach Druidenlehrern sucht, dann ist der Park der richtige Ort für Euch. Theridan und Maldryn sind üblicherweise beim Mondbrunnen im Zentrum des Distrikts zu finden.', Text1_1='Wenn Ihr nach Druidenlehrern sucht, dann ist der Park der richtige Ort für Euch. Theridan und Maldryn sind üblicherweise beim Mondbrunnen im Zentrum des Distrikts zu finden.', Text2_0='Sind wir ein Kind der Natur? Dann müsst Ihr nicht weiter suchen als bis zum Mondbrunnen im Park.', Text2_1='Sind wir ein Kind der Natur? Dann müsst Ihr nicht weiter suchen als bis zum Mondbrunnen im Park.' WHERE id=902;
UPDATE npc_text SET Text0_0='Die Kathedrale des Lichts am Kathedralenplatz ist die Heimat der Priester von Sturmwind. Dort werdet Ihr die Hohepriesterin Laurena finden.', Text0_1='Die Kathedrale des Lichts am Kathedralenplatz ist die Heimat der Priester von Sturmwind. Dort werdet Ihr die Hohepriesterin Laurena finden.', Text1_0='Laurena ist die Hohepriesterin von Sturmwind. Ihr findet sie in der Kathedrale des Lichts am Kathedralenplatz.', Text1_1='Laurena ist die Hohepriesterin von Sturmwind. Ihr findet sie in der Kathedrale des Lichts am Kathedralenplatz.', Text2_0='Ich würde vorschlagen, dass Ihr in der Kathedrale des Lichts am Kathedralenplatz nachschaut.', Text2_1='Ich würde vorschlagen, dass Ihr in der Kathedrale des Lichts am Kathedralenplatz nachschaut.' WHERE id=903;
UPDATE npc_text SET Text0_0='Wenn Ihr ein mächtiger Paladin werden wollt, dann gibt es keinen besseren Lehrer als Lord Grayson Schattenbruch. Ihr findet ihn in der Kathedrale des Lichts am Kathedralenplatz.', Text0_1='Wenn Ihr ein mächtiger Paladin werden wollt, dann gibt es keinen besseren Lehrer als Lord Grayson Schattenbruch. Ihr findet ihn in der Kathedrale des Lichts am Kathedralenplatz.' WHERE id=904;
UPDATE npc_text SET Text0_0='Thorfin Steinschild hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', Text0_1='Thorfin Steinschild hat den weiten Weg von Eisenschmiede hierher zu uns gemacht, um sein Geschick in der Jagdkunst mit uns zu teilen. Wenn es Euch nichts ausmacht, die rauchgeschwängerte Luft des Zwergendistriktes einzuatmen, dann findet Ihr ihn in seinem Haus im Nordwesten des Distriktes.', Text1_0='Ein Jäger, sagt Ihr? Tja, dann müsst Ihr mit Thorfin Steinschild drüben im Zwergendistrikt reden.$B$BAch, und richtet ihm das hier aus: Wenn ich noch einmal in einen der Haufen trete, die sein Tier hinterlassen hat, dann wird er demnächst ganz andere Jäger ausbilden, während er im Verlies hinter Gittern sitzt!', Text1_1='Ein Jäger, sagt Ihr? Tja, dann müsst Ihr mit Thorfin Steinschild drüben im Zwergendistrikt reden.$B$BAch, und richtet ihm das hier aus: Wenn ich noch einmal in einen der Haufen trete, die sein Tier hinterlassen hat, dann wird er demnächst ganz andere Jäger ausbilden, während er im Verlies hinter Gittern sitzt!' WHERE id=905;
UPDATE npc_text SET Text0_0='Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. $B$BObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', Text0_1='Was? Ich weiß nichts über Hexenmeisterlehrer in Sturmwind. $B$BObwohl... beim Geschlachteten Lamm drüben im Magierviertel gab es Sichtungen dämonischer Aktivitäten. Ihr solltet vielleicht dort einmal nachschauen.', Text1_0='Hexenmeister, hm? Also, Ihr habt das nicht von mir, aber ich hab Gerüchte gehört, dass sich diese Typen im Magierviertel in der Taverne \'Zum geschlachteten Lamm\' treffen.', Text1_1='Hexenmeister, hm? Also, Ihr habt das nicht von mir, aber ich hab Gerüchte gehört, dass sich diese Typen im Magierviertel in der Taverne \'Zum geschlachteten Lamm\' treffen.' WHERE id=906;
UPDATE npc_text SET Text0_0='Was führt Euch in diesen Teil der Welt, $N?', Text0_1='Was führt Euch in diesen Teil der Welt, $N?' WHERE id=907;
UPDATE npc_text SET Text0_0='Jeden Tag wird er fetter und korrupter. Man kann den Gestank, den sein Körper absondert, echt schon riechen. Er is\' dreckig un\' damit mein ich nicht nur, dass er sich waschen müsste.$B$BAber genug Plapperei über Hulfdan. Wo kommt Ihr her und wie kann ich Euch helfen, $C?' WHERE id=908;
UPDATE npc_text SET Text0_0='Einen Lehrer für welchen Beruf sucht Ihr?', Text0_1='Einen Lehrer für welchen Beruf sucht Ihr?' WHERE id=918;
UPDATE npc_text SET Text0_0='Wenn Ihr Ausbildung in Alchemie benötigt, gibt es in Sturmwind nur einen Ort dafür... den Alchemiebedarf. Ihr findet ihn im Magierviertel am äußeren Ladenring.', Text0_1='Wenn Ihr Ausbildung in Alchemie benötigt, gibt es in Sturmwind nur einen Ort dafür... den Alchemiebedarf. Ihr findet ihn im Magierviertel am äußeren Ladenring.', Text1_0='Versteckt im Magierviertel liegt ein kleiner Laden namens \'Alchemiebedarf\'. Auf Eurem Weg durch das Magierviertel werdet Ihr jede Menge seltsame Läden sehen, aber wenn Ihr am äußeren Rand entlanggeht, dann könnt Ihr ihn nicht verfehlen.', Text1_1='Versteckt im Magierviertel liegt ein kleiner Laden namens \'Alchemiebedarf\'. Auf Eurem Weg durch das Magierviertel werdet Ihr jede Menge seltsame Läden sehen, aber wenn Ihr am äußeren Rand entlanggeht, dann könnt Ihr ihn nicht verfehlen.' WHERE id=919;
UPDATE npc_text SET Text0_0='Der Zwerg, mit dem Ihr reden solltet, heißt Therum Tiefenschmied. Wenn Ihr mich fragt, ist er einer der besten Schmiede, die es hier gibt. Ihr findet ihn im Herzen des Zwergendistrikts.', Text0_1='Der Zwerg, mit dem Ihr reden solltet, heißt Therum Tiefenschmied. Wenn Ihr mich fragt, ist er einer der besten Schmiede, die es hier gibt. Ihr findet ihn im Herzen des Zwergendistrikts.', Text1_0='Wollt Ihr etwa behaupten, Ihr könntet den Rauch der Zwergenschmieden von hier aus nicht riechen? Wie auch immer, Ihr müsst Therum Tiefenschmied aufsuchen, wenn Ihr die Schmiedekunst erlernen wollt. Er ist drüben im Zwergendistrikt, nördlich vom Kathedralenplatz.', Text1_1='Wollt Ihr etwa behaupten, Ihr könntet den Rauch der Zwergenschmieden von hier aus nicht riechen? Wie auch immer, Ihr müsst Therum Tiefenschmied aufsuchen, wenn Ihr die Schmiedekunst erlernen wollt. Er ist drüben im Zwergendistrikt, nördlich vom Kathedralenplatz.' WHERE id=920;
UPDATE npc_text SET Text0_0='Wenn Ihr ein phantastischer Koch werden wollt, geht zur Taverne Zum pfeifenden Schwein und sprecht mit Stephen Ryback. Der Mann kann wie kein anderer eine ganze Ladung Rippchen zubereiten.$B$BHimmel... jetzt habt Ihr mir Appetit auf seine berühmten Rippchen gemacht. Aber rechnet nicht damit, dass er Euch das Rezept dafür verrät!', Text0_1='Wenn Ihr ein phantastischer Koch werden wollt, geht zur Taverne Zum pfeifenden Schwein und sprecht mit Stephen Ryback. Der Mann kann wie kein anderer eine ganze Ladung Rippchen zubereiten.$B$BHimmel... jetzt habt Ihr mir Appetit auf seine berühmten Rippchen gemacht. Aber rechnet nicht damit, dass er Euch das Rezept dafür verrät!', Text1_0='In der Taverne Zum pfeifenden Schwein in der Altstadt arbeitet ein genialer Koch. Ich habe mal gesehen, wie er das Messer schwingt, wenn er kocht, und - unter uns gesagt - ich glaube nicht, dass er sein Leben lang ein Koch war. Versteht mich nicht falsch - wenn Ihr immer noch lernen möchtet, ist er der beste Lehrer weit und breit.', Text1_1='In der Taverne Zum pfeifenden Schwein in der Altstadt arbeitet ein genialer Koch. Ich habe mal gesehen, wie er das Messer schwingt, wenn er kocht, und - unter uns gesagt - ich glaube nicht, dass er sein Leben lang ein Koch war. Versteht mich nicht falsch - wenn Ihr immer noch lernen möchtet, ist er der beste Lehrer weit und breit.' WHERE id=921;
UPDATE npc_text SET Text0_0='Der in Sturmwind wohnende Ingenieurskunstlehrer ist ein geschickter Gnom namens Liliam Spindelfunks. Er ist im hinteren Teil des Zwergendistrikts zu finden.', Text0_1='Der in Sturmwind wohnende Ingenieurskunstlehrer ist ein geschickter Gnom namens Liliam Spindelfunks. Er ist im hinteren Teil des Zwergendistrikts zu finden.', Text1_0='He, da fällt mir ein: Ich muss meine Raketenstiefel noch reparieren lassen. Kleiner Tipp am Rande, $Gmein Freund:meine Liebe;: Raketenstiefel und Greife passen nicht zusammen. $B$BAch ja, Ihr braucht noch eine Wegbeschreibung... geht einfach rüber in den Zwergendistrikt - Lilliam Spindelfunks findet Ihr in dessen hinterem Teil.', Text1_1='He, da fällt mir ein: Ich muss meine Raketenstiefel noch reparieren lassen. Kleiner Tipp am Rande, $Gmein Freund:meine Liebe;: Raketenstiefel und Greife passen nicht zusammen. $B$BAch ja, Ihr braucht noch eine Wegbeschreibung... geht einfach rüber in den Zwergendistrikt - Lilliam Spindelfunks findet Ihr in dessen hinterem Teil.' WHERE id=922;
UPDATE npc_text SET Text0_0='Die Kathedrale des Lichts ist Sturmwinds Zentrum aller Heilpraktiken. Ihr findet dort auch Shaina Fuller, von der Ihr diese Kunst erlernen könnt.', Text0_1='Die Kathedrale des Lichts ist Sturmwinds Zentrum aller Heilpraktiken. Ihr findet dort auch Shaina Fuller, von der Ihr diese Kunst erlernen könnt.', Text1_0='Die Erste Hilfe erlernt Ihr am besten in der Kathedrale des Lichts, unter den wachsamen Augen von Shaina Fuller. $B$BUm dorthin zu gelangen, müsst Ihr entweder nach den gelben Dächern Ausschau halten oder nach der Turmspitze der Kathedrale selbst, denn die kann man von fast überall in der Stadt aus sehen.', Text1_1='Die Erste Hilfe erlernt Ihr am besten in der Kathedrale des Lichts, unter den wachsamen Augen von Shaina Fuller. $B$BUm dorthin zu gelangen, müsst Ihr entweder nach den gelben Dächern Ausschau halten oder nach der Turmspitze der Kathedrale selbst, denn die kann man von fast überall in der Stadt aus sehen.' WHERE id=923;
UPDATE npc_text SET Text0_0='Ihr könnt Tannysa im Magierviertel vor dem Alchemiebedarf finden.', Text0_1='Ihr könnt Tannysa im Magierviertel vor dem Alchemiebedarf finden.', Text1_0='Es geht doch nichts darüber, mit der Natur Zwiesprache zu halten... das habe ich mir zumindest von Nachtelfen sagen lassen. Ich selber hab\'s ja nicht so damit, mit Blumen und Bäumen zu reden, aber ich bin mir sicher, Tannysa kann Euch helfen. Sie ist drüben im Magierviertel, vor dem \'Alchemiebedarf\'.', Text1_1='Es geht doch nichts darüber, mit der Natur Zwiesprache zu halten... das habe ich mir zumindest von Nachtelfen sagen lassen. Ich selber hab\'s ja nicht so damit, mit Blumen und Bäumen zu reden, aber ich bin mir sicher, Tannysa kann Euch helfen. Sie ist drüben im Magierviertel, vor dem \'Alchemiebedarf\'.' WHERE id=924;
UPDATE npc_text SET Text0_0='Wollt die Lederverarbeitung lernen, ja? Simon Gerber sollte es Euch beibringen können. Ihr findet ihn im Schützenden Balg in der Altstadt.', Text0_1='Wollt die Lederverarbeitung lernen, ja? Simon Gerber sollte es Euch beibringen können. Ihr findet ihn im Schützenden Balg in der Altstadt.', Text1_0='Ihr interessiert Euch für Leder, was? Klingt, als solltet Ihr mal mit Simon Gerber drüben in der Altstadt reden. Er führt dort einen Laden namens \'Der schützende Balg\'.', Text1_1='Ihr interessiert Euch für Leder, was? Klingt, als solltet Ihr mal mit Simon Gerber drüben in der Altstadt reden. Er führt dort einen Laden namens \'Der schützende Balg\'.' WHERE id=925;
UPDATE npc_text SET Text0_0='Gelman Steinhand ist derjenige, mit dem Ihr reden solltet. Ich glaube, dass er drüben im Zwergendistrikt ein Haus hat, aber kann mich nicht genau erinnern. Ich würde jedenfalls dort nachschauen.', Text0_1='Gelman Steinhand ist derjenige, mit dem Ihr reden solltet. Ich glaube, dass er drüben im Zwergendistrikt ein Haus hat, aber kann mich nicht genau erinnern. Ich würde jedenfalls dort nachschauen.', Text1_0='Typisch Zwerge: Schicken extra einen Minenspezialisten aus Eisenschmiede, um uns beizubringen, wie man Bergbau betreibt. Wie schwer kann es schon sein, eine Hacke in die Hand zu nehmen und auf ein paar Felsen einzuhauen? Na, jedenfalls ist er hier, also solltet Ihr Euch wohl auch mit ihm unterhalten. Er heißt Gelman und wohnt drüben im Zwergendistrikt.', Text1_1='Typisch Zwerge: Schicken extra einen Minenspezialisten aus Eisenschmiede, um uns beizubringen, wie man Bergbau betreibt. Wie schwer kann es schon sein, eine Hacke in die Hand zu nehmen und auf ein paar Felsen einzuhauen? Na, jedenfalls ist er hier, also solltet Ihr Euch wohl auch mit ihm unterhalten. Er heißt Gelman und wohnt drüben im Zwergendistrikt.' WHERE id=927;
UPDATE npc_text SET Text0_0='Wenn Ihr die Kürschnerei lernen wollt, solltet Ihr rüber in die Altstadt gehen und im Schützenden Balg reinschauen.', Text0_1='Wenn Ihr die Kürschnerei lernen wollt, solltet Ihr rüber in die Altstadt gehen und im Schützenden Balg reinschauen.', Text1_0='Wisst Ihr, ich könnte einen Orc aufspießen oder einen Troll in Einzelteile zerlegen; ich könnte sogar meine Anfangsbuchstaben in einen Oger ritzen, aber beim Gedanken daran, ein Tier zu häuten, wird mir schlecht. Komisch, was? $B$BNa was soll\'s, wenn Euch das nichts ausmacht, dann sucht Maris Granger in \'Der schützende Balg\' in der Altstadt auf.', Text1_1='Wisst Ihr, ich könnte einen Orc aufspießen oder einen Troll in Einzelteile zerlegen; ich könnte sogar meine Anfangsbuchstaben in einen Oger ritzen, aber beim Gedanken daran, ein Tier zu häuten, wird mir schlecht. Komisch, was? $B$BNa was soll\'s, wenn Euch das nichts ausmacht, dann sucht Maris Granger in \'Der schützende Balg\' in der Altstadt auf.' WHERE id=928;
UPDATE npc_text SET Text0_0='Duncans Textilien beschäftigt einen der besten Schneider Sturmwinds namens Georgio Bolero. Der Laden befindet sich drüben im Magierviertel.', Text0_1='Duncans Textilien beschäftigt einen der besten Schneider Sturmwinds namens Georgio Bolero. Der Laden befindet sich drüben im Magierviertel.', Text1_0='Ein Schneider, sagt Ihr? Na, wenn Ihr Euch lange genug hier herumtreibt, dann werdet Ihr schon früh genug von Georgio Bolero hören. Er bestimmt jetzt schon seit ein paar Monaten die Mode hier. Wenn Ihr von ihm lernen möchtet, dann findet Ihr ihn drüben im Magierviertel am äußeren Rand.', Text1_1='Ein Schneider, sagt Ihr? Na, wenn Ihr Euch lange genug hier herumtreibt, dann werdet Ihr schon früh genug von Georgio Bolero hören. Er bestimmt jetzt schon seit ein paar Monaten die Mode hier. Wenn Ihr von ihm lernen möchtet, dann findet Ihr ihn drüben im Magierviertel am äußeren Rand.' WHERE id=929;
UPDATE npc_text SET Text0_0='Eisenherz will nicht, dass Tirth erfährt, dass er noch immer in der schimmernden Ebene ist, deswegen hat er sich in ein Huhn verwandelt.$B$BWenn Ihr Eisenherz findet, dann solltet Ihr ihn herbeiwinken. Vielleicht kommt er dann aus seinem Versteck.' WHERE id=930;
UPDATE npc_text SET Text0_0='Ich hoffe, Ihr seid für die Arbeit bereit.' WHERE id=931;
UPDATE npc_text SET Text0_0='Willkommen bei den Rennen! Genießt Euren Aufenthalt und verwettet nicht Euer ganzes Geld auf einmal!', Text0_1='Willkommen bei den Rennen! Genießt Euren Aufenthalt und verwettet nicht Euer ganzes Geld auf einmal!', Text1_0='Seid gegrüßt, $C! Willkommen bei den Rennen der schimmernden Ebene!', Text1_1='Seid gegrüßt, $C! Willkommen bei den Rennen der schimmernden Ebene!', Text2_0='Wer hat den schnellsten Wagen? Wer weiß?!?', Text2_1='Wer hat den schnellsten Wagen? Wer weiß?!?', Text3_0='Der Goblinrennwagen verträgt zwar eine Menge, aber die Gnome haben in ihren Wagen ein oder zwei Tricks eingebaut...', Text3_1='Der Goblinrennwagen verträgt zwar eine Menge, aber die Gnome haben in ihren Wagen ein oder zwei Tricks eingebaut...' WHERE id=932;
UPDATE npc_text SET Text0_0='Wofür braucht Ihr Anweisungen?', Text0_1='Wofür braucht Ihr Anweisungen?' WHERE id=933;
UPDATE npc_text SET Text0_0='Wie eine nasse Katze, die man aus dem Regen holt, kratzt und klammert Ihr, und dann macht Ihr es noch mal... Ich komme wieder...$B$BOh, ich habe Euch nicht hereinkommen sehen, $N! Was kann ich für Euch tun?' WHERE id=934;
UPDATE npc_text SET Text0_0='Wenn wir diese neuen Ländereien zu den unseren machen wollen, müssen wir Orte haben, an denen wir unsere vom Kämpfen geschwächten Körper ausruhen können. Legt Eure Axt ans Feuer und lasst uns Geschichten Eurer ruhmreichen Schlachten hören.', Text0_1='Wenn wir diese neuen Ländereien zu den unseren machen wollen, müssen wir Orte haben, an denen wir unsere vom Kämpfen geschwächten Körper ausruhen können. Legt Eure Axt ans Feuer und lasst uns Geschichten Eurer ruhmreichen Schlachten hören.' WHERE id=938;
UPDATE npc_text SET Text0_0='Es gibt nichts Vergleichbares, als einen Tag lang am Kristallsee zu angeln. Arnold Leland ist der Mann, der mir das beibrachte. Üblicherweise ist er an den Docks des Kanaldistrikts zu finden, wo er den lieben langen Tag angelt.$B$BOh, und glaubt keine der Geschichten, die er Euch über Monster im Kanal erzählen wird.', Text0_1='Es gibt nichts Vergleichbares, als einen Tag lang am Kristallsee zu angeln. Arnold Leland ist der Mann, der mir das beibrachte. Üblicherweise ist er an den Docks des Kanaldistrikts zu finden, wo er den lieben langen Tag angelt.$B$BOh, und glaubt keine der Geschichten, die er Euch über Monster im Kanal erzählen wird.', Text1_0='Leland, dieser Penner... Der macht doch den ganzen Tag lang nichts anderes, als vor dem Handelsdistrikt am Kai zu sitzen und zu angeln. Ich will doch schwer hoffen, dass er gut darin ist, so viel Zeit, wie er beim Angeln verbringt! Warum sprecht Ihr nicht mit ihm, wenn Ihr etwas lernen wollt?', Text1_1='Leland, dieser Penner... Der macht doch den ganzen Tag lang nichts anderes, als vor dem Handelsdistrikt am Kai zu sitzen und zu angeln. Ich will doch schwer hoffen, dass er gut darin ist, so viel Zeit, wie er beim Angeln verbringt! Warum sprecht Ihr nicht mit ihm, wenn Ihr etwas lernen wollt?' WHERE id=940;
UPDATE npc_text SET Text0_0='Lucan Cordell besitzt einen kleinen Laden am Kanal beim Verlies. Ich bin sicher, dass er Euch die Verzauberkunst beibringen kann.', Text0_1='Lucan Cordell besitzt einen kleinen Laden am Kanal beim Verlies. Ich bin sicher, dass er Euch die Verzauberkunst beibringen kann.', Text1_0='Ah, Ihr sucht Lucan Cordell. Er führt einen Laden außerhalb des Magieviertels, auf dem Weg zum Verlies.', Text1_1='Ah, Ihr sucht Lucan Cordell. Er führt einen Laden außerhalb des Magieviertels, auf dem Weg zum Verlies.' WHERE id=941;
UPDATE npc_text SET Text0_0='Ich reise allein, um meine Ehre zu verteidigen und dieses Land von unseren Feinden zu befreien.$B$BWas kann ich für Euch tun, $N?' WHERE id=942;
UPDATE npc_text SET Text0_0='Den Orden der Zerschmetterten Hand gab es schon vor Thrall und es wird ihn auch nach Thrall noch geben.' WHERE id=1038;
UPDATE npc_text SET Text0_0='Zando\'zan von der Zerschmetterten Hand. Wie kann Zando\'zan Euch helfen? Gar nicht! Und er wird es auch nicht!' WHERE id=1039;
UPDATE npc_text SET Text0_0='Seid Ihr zur Ausbildung hier? Oder seid Ihr nur hier, um die Zeit eines Kriegers zu verschwenden?', Text0_1='Seid Ihr zur Ausbildung hier? Oder seid Ihr nur hier, um die Zeit eines Kriegers zu verschwenden?' WHERE id=1040;
UPDATE npc_text SET Text0_0='Die Maschine springt bei Eurer Berührung an.' WHERE id=1041;
UPDATE npc_text SET Text0_0='Er bringt Euch Met$BEr bringt Euch Bier$BVon Ohr zu Ohr grinst er hier$BVon Jahr zu Jahr half er bei mir$BWir nennen ihn lächelnder Jim.' WHERE id=1058;
UPDATE npc_text SET Text0_0='Das ist eine exotische Pflanzenart, die man nirgendwo sonst in Teldrassil findet.' WHERE id=1059;
UPDATE npc_text SET Text0_0='Diese Pflanze hebt sich von allen gewöhnlichen Pflanzen Teldrassils ab und verbreitet einen seltsamen, lilafarbenen Schein.' WHERE id=1060;
UPDATE npc_text SET Text0_0='Willkommen. Meine Aufgabe besteht darin, Euch Zugang und, falls erlaubt, auch Einblick in das Werk der Schöpfer zu gewähren, die für die Welt Azeroth die Irdenen geschaffen haben. Ich kann Euch diese Dinge nun anvertrauen, wenn Ihr bereit seid.' WHERE id=1079;
UPDATE npc_text SET Text0_0='Die Irdenen sind ein von den Schöpfern künstlich geschaffenes Volk. Sie fungierten in der Welt Azeroth ursprünglich einmal als eines der Stammvölker. Die Irdenen dienten den Schöpfern als Helfer bei der Formung der Tiefen dieser Welt.$B$BSie stellen eine Abwandlung der Standardmatrix für unterirdische Lebewesen dar, wie sie die Schöpfer auf anderen Welten benutzt haben. Bei der Konstruktion dieses Prototyps entstanden diverse Anomalien, die allerdings erst später bemerkt wurden.' WHERE id=1080;
UPDATE npc_text SET Text0_0='Es handelt sich um eine der Synthesematrizen, die die Schöpfer beim Anlegen einer Welt verwenden. Jede Synthesematrix dient dabei einem Ziel der Schöpfer. Für Azeroth wurde die Matrix für unterirdische Lebewesen so modifiziert, dass ein ausdauerndes Wesen mit einer Vorliebe für die Formung tiefer Regionen entstand: Die Irdenen sind das Ergebnis dieser Modifkation.$B$BNachdem die Irdenen den Dienst für die Schöpfer aufgenommen hatten, kam es zu unvorhergesehenen Problemen, wenn die Synthesematrix unter hohen Stressdruck geriet.' WHERE id=1081;
UPDATE npc_text SET Text0_0='Um diese Frage korrekt zu beantworten, müsst Ihr noch einiges zum Hintergrund der Irdenen wissen.$B$BBerechnung läuft.$B$BDie Anomalien der Irdenen erklären sich aus dem Zweck, zu dem sie geschaffen wurden. Die Irdenen sind ein Versuch der Schöpfer, die Standardmatrix für unterirdische Lebewesen überlebensfähiger zu machen. Zu diesem Zweck wurde besagter Matrix ureigenstes widerstandsfähiges Material aus der Welt hinzugefügt.' WHERE id=1082;
UPDATE npc_text SET Text0_0='Es ist das Grundmaterial für die Schaffung ganzer Völker. Für die Schaffung der Irdenen wurde statt der üblichen Biomasse ein Querschnitt der Erdoberfläche Azeroths verwendet.$B$BEine Analyse der Zusammensetzung der Welt hatte die Schöpfer zu der Theorie geführt, dass sie daraus ein intensiviertes Lebewesen erschaffen könnten, das die Widerstandsfähigkeit der Essenz dieser Welt hat. Dies erreichten sie durch eine Mischung des Gesteinsmaterials von Azeroth als Grundlage.' WHERE id=1083;
UPDATE npc_text SET Text0_0='Vereinfacht ausgedrückt trifft dies zu.$B$BRein äußerlich betrachtet gleichen die Irdenen klein gewachsenen Humanoiden, aber sie bestehen aus den diversen Komponenten des Gesteinsmaterials Azeroths. Dadurch wurden Kraft und Ausdauer maximiert, ohne dass dies zu Lasten ihrer kognitiven Fähigkeiten ging. Haut und Muskulatur sind praktisch nicht zu beschädigen, und mit nur sehr schwachen Modifikationen zeigen die Irdenen erstaunliche Widerstandsfähigkeit gegen Magie.' WHERE id=1084;
UPDATE npc_text SET Text0_0='Hierzu muss man unbedingt wissen, dass die Schöpfer ein Volk schaffen wollten, das lange nach ihrer Abreise noch integraler Bestandteil des Entwicklungszyklus\' der Welt sein würde. Dieses Volk sollte die langfristigen Entwicklungsziele für Azeroth erfüllen. $B$BDer Name "die Irdenen" schien den Schöpfern angesichts der Zusammensetzung des neuen Volks im Übrigen nur passend.' WHERE id=1085;
UPDATE npc_text SET Text0_0='Das einzige Problem bei den Irdenen besteht in ihrer tendenziellen Instabilität unter hohem Stressdruck.$B$BAddendum: Die neue Matrix war speziell für die Formung tiefer Erdschichten geschaffen worden und erfüllte folglich niemals Anforderungen zur Duplizierung einer Hochstressdruckumgebung.$B$BDoch diese Destabilisierung wurde erst lange nach Beginn des ersten Dienstleistungszyklus\' für die Schöpfer festgestellt. Die Schöpfer nahmen nachträgliche Änderungen vor, statt noch einmal von vorn zu beginnen.' WHERE id=1086;
UPDATE npc_text SET Text0_0='[Nach der Frage folgt eine merkliche Pause.]$B$BDie gewünschte Auskunft ist momentan in diesem Datenspeicher nicht verfügbar. Dies bedaure ich zutiefst.$B$BBitte richtet die Anfrage erneut an mich, wenn der Informationsdatenspeicher aktualisiert worden ist. Oder fragt die gewünschte Auskunft von einem anderen Datenspeicher in Azeroth ab.' WHERE id=1087;
UPDATE npc_text SET Text0_0='Es gibt zwei mögliche Ergebnisse, wenn sich die Synthesematrix der Irdenen destabilisiert. Beginne mit optischer Hilfsdarstellung eins.$B$BDie erste Matrixvariante ist ein degeneratives Syntheseprodukt, für gewöhnlich als "Trogg" bezeichnet. Diese Variante ist ähnlich stark und ausdauernd wie die Irdenen, die kognitiven Fähigkeiten sind jedoch fast vollständig ausgelöscht. Triebfedern eines Troggs sind einzig seine starke Willenskraft und Schlauheit.' WHERE id=1088;
UPDATE npc_text SET Text0_0='Zu meinem Bedauern bin ich nicht in der Lage, Euren Zeitbezug mit den hier aufgezeichneten Daten abzugleichen.$B$BDer Trogg enthält verschiedenes Steinmaterial von Azeroth, aber die fehlenden kognitiven Fähigkeiten machen diese Abweichung für die Schöpfer inakzeptabel.$B$BEin Trogg vermehrt sich so wie eine Standardbiomasse. In der Regel frisst er Steine und alles andere, auch andere Troggs. Kannibalismus ist für die Schöpfer jedoch inakzeptabel.' WHERE id=1089;
UPDATE npc_text SET Text0_0='Beginne mit optischer Hilfsdarstellung zwei.$B$BDie zweite resultierende Abweichung von der Matrix für die Irdenen weist im Vergleich zur Standardmatrix der unterirdischen Lebewesen viele erwünschte Elemente auf. In der Regel wird diese Form als "Zwerg" bezeichnet.$B$BDiese Form weist Stärke und Ausdauer der Irdenen auf. In einigen Fällen übersteigen die kognitiven Fähigkeiten der Zwerge sogar die der eigentlichen Matrix. Allerdings enthalten die Zwerge nicht mehr das ursprüngliche Steinmaterial aus dem Inneren Azeroths.' WHERE id=1090;
UPDATE npc_text SET Text0_0='Die hier gespeicherten Daten korrelieren nicht mit Eurer klugen Anmerkung.$B$BZwerge weisen nach wie vor eine Vorliebe für die steinerne Zusammensetzung Azeroths auf, doch aufgrund der unter hohem Druck auftretenden Anomalien in der Synthesematrix für die Irdenen sind Zwerge nicht mehr als Standardbiomasse.$B$BFolglich weisen Zwerge auch nicht die Degenerierungen der Schöpfungsmatrix der Irdenen auf, wie Ihr mit Eurer Frage vielleicht andeuten wolltet.' WHERE id=1091;
UPDATE npc_text SET Text0_0='Zu meinem Bedauern bin ich nicht in der Lage, Euren Zeitbezug mit den hier aufgezeichneten Daten abzugleichen.$B$BZwar weisen Zwerge größtenteils die geistige Verbundenheit der Irdenen mit dem Steinmaterial aus den Tiefen Azeroths auf, aber ihre Rückentwicklung in lebende Biomasse stellt die Hauptdegeneration der Matrix dar. Zwerge essen und vermehren sich auf für Standardbiomasse typische Weise.$B$BDie Schöpfer sehen die Zwerge als akzeptable Variante an, die einer eigenen Schöpfungsgeschichte würdig ist.' WHERE id=1092;
UPDATE npc_text SET Text0_0='Die Schöpfer sind... die Schöpfer.$B$BSie sind die Wesen, die diese Welt angelegt und geformt haben. Sie sind der Grund für das Dasein dieser und vieler anderer Welten. Sie sind der Quell, aus dem Azeroth entspringt.' WHERE id=1093;
UPDATE npc_text SET Text0_0='Die Schöpfer sind sich der Fülle der in diesem Speicher enthaltenen Angaben bewusst. Folglich wird Euch eine tragbare Ausgabe der hier gespeicherten Daten zur Verfügung gestellt, sobald Ihr Euch mit den Scheiben von Norgannon vertraut gemacht habt.$B$BBitte betrachtet Euren oberflächlichen Wissensdurst hiermit als gestillt. Greift bei Bedarf auf die Scheiben von Norgannon zu, um mit der Synthese Eurer Scheiben zu beginnen.' WHERE id=1094;
UPDATE npc_text SET Text0_0='Schmied wollt Ihr also werden? Dann schnappt Euch mal Hammer und Schürze, $GBursche:Mädchen;, und lasst mich sehen, was Ihr draufhabt.' WHERE id=1118;
UPDATE npc_text SET Text0_0='Die Zerschmetterte Hand beherrscht diesen Landstrich. Wir sind der Schleier, der über Kalimdor liegt: Wir sind Beschützer, Ordnungsmacht, Richter, Geschworene und Henker in einem. Wir sehen alles und wir wissen alles.$B$BIhr habt Euch gut weiterentwickelt, seit Ihr der Hand dient, $N. Es wird Zeit für Euren ersten echten Einsatz.$B$BSeid Ihr bereit für die nächste Mission?' WHERE id=1124;
UPDATE npc_text SET Text0_0='Willkommen in der großen Schmiede, $GJunge:Mädchen;. Draußen mag es kalt sein, aber wir hier drinnen haben\'s immer gemütlich warm. Schnappt Euch einen Hammer, bearbeitet den Amboss und zeigt mir, ob Ihr ein Schmied der großen Schmiede sein könnt!' WHERE id=1127;
UPDATE npc_text SET Text0_0='Nette Form, $N. Ich wette um einen fetten Krug in Bruuks Ecke, dass die Funktion auch nichts zu wünschen übrig lässt. Wisst Ihr was? Ihr solltet vielleicht ma\' mit Myolor Zornesbeben reden. Der wird\'s Euch schon zeigen.' WHERE id=1128;
UPDATE npc_text SET Text0_0='Klar kann ich Euch sagen, wo Myolor is\'. Gleich da drüben in der Nähe vom Amboss der großen Schmiede, da macht er seine Arbeit. Hört ihm gut zu, $GBursche:Mädchen;, Myolor ist der Beste von allen.' WHERE id=1129;
UPDATE npc_text SET Text0_0='Meinen Segen habt Ihr, $N. Ihr könnt bessere Klingen schmieden als alle, die ich in den letzten Jahren ausgebildet habe. Wenn Ihr aber mal \'ne echte Waffe schmieden wollt, dann haltet Euch an Myolor Zornesbeben. Das is\'n Zwerg, der Euch auf\'n richtigen Weg bringt.' WHERE id=1130;
UPDATE npc_text SET Text0_0='Aha, Ihr wollt also zu Myolor, was? Der is\' grad da drüben... hinter Euch! Nee, vielleicht auch vor Euch! Beim groß\'n Amboss halt.' WHERE id=1131;
UPDATE npc_text SET Text0_0='An diesem Turm ist was faul. Ich bin krank geworden und die anderen Arbeiter von der Venture Company, die in der Nähe des Turms zu tun hatten, auch.$B$BErst letzte Woche sind zwei unserer Patrolleure gestorben, nur um kurz darauf als hirnlose Drohnen zurückzukommen!$B$BDie Lebenskraft entweicht meinem Körper. Ich merke, wie ich die Kontrolle verliere. Instinkte übermannen mich... Üble Instinkte... *Fitzel ohrfeigt sich selbst* Noch nicht! Hört zu... Ich bin in einer hohen Position. Dank dieser Position verfüge ich über geheime Kenntnisse.' WHERE id=1132;
UPDATE npc_text SET Text0_0='Seid Ihr komplett meschugge? Das ist keine Waffe, das ist ein Frühstücksmesser. Ich gebe mich nicht mit Anfängern ab.' WHERE id=1133;
UPDATE npc_text SET Text0_0='Dafür habt Ihr mich gestört? Da seid Ihr ja besser dran, wenn Ihr Euch einen toten Hasen auf den Rücken schnallt und damit durch eine Trollhöhle rennt!' WHERE id=1135;
UPDATE npc_text SET Text0_0='$N, richtig? Das ist mächtig gute Arbeit, die Ihr da abgeliefert habt. Ich nehme mich Eurer an und zeig Euch, wie Ihr \'ne Rüstung macht, die König Magni höchstpersönlich gut zu Gesicht stehen würde.' WHERE id=1136;
UPDATE npc_text SET Text0_0='Vor etwa fünf Monaten hat die Abteilung der Venture Company im Schlingendorntal von einem zwielichtigen Händler namens Crank Zischelbub in Beutebucht ein spezielles Gift erworben. Und Zischelbub wiederum hatte es wohl von Zanzil gestohlen.$B$BJa, von Zanzil dem Ausgestoßenen - jenem Troll, den sogar sein eigener Stamm wegen seiner \'Experimente\' in Nekromantie verstoßen hat.$B$BDann begannen sie natürlich, das Gift aneinander auszuprobieren. Das Ergebnis war eine Katastrophe.' WHERE id=1138;
UPDATE npc_text SET Text0_0='Der oberste Großknecht Puzik Gallywix griff schließlich ein und nahm das restliche Gift mit in seinen Turm hier im Schlickmoor. Er wollte Nachforschungen über die instabile Beschaffenheit des Toxins anstellen. Schließlich kam man überein, das Geheimnis des Gifts mit der Bruderschaft der Defias zu teilen und dafür eine Menge stabilen, verwendbaren Gifts zu verlangen.$B$BKlaven Jagdruf von der Bruderschaft der Defias wurde damit betraut, die chemische Zusammensetzung von Zanzils Mixtur zu verändern.' WHERE id=1158;
UPDATE npc_text SET Text0_0='Machen wir einen großen Sprung in die Gegenwart: Die so erhaltene Formel funktioniert und ermöglicht die Schaffung einer besser kontrollierbaren Drohne. Der Nachteil ist allerdings, dass das inaktive Präparat extrem flüchtig ist.$B$BGallywix, jeder im Turm und jeder außerhalb des Turms ist von dem Gift befallen. Es schwächt uns körperlich und geistig. Unsere inneren Organe sind schon ganz weich und extrem empfindlich. Das ist Eure Chance, $N. Ich werde nicht mehr lange durchhalten und schon bald einer von denen sein.' WHERE id=1159;
UPDATE npc_text SET Text0_0='Die Verteidigungsanlagen des Turms sind im Augenblick so sehr geschwächt wie nie zuvor. Die Patrolleure, Wachposten, Ausgucke und sogar Gallywix selbst sind von dem Gift befallen und Euren speziellen Fähigkeiten und Angriffen gegenüber äußerst verwundbar.$B$BIch will Euch in den Turm eindringen sehen, und wenn Ihr wieder rauskommt, will ich, dass Ihr vom Blut unserer Feinde bedeckt seid, den Kopf des obersten Großknechts Puzik Gallwix in Euren Händen haltet und das restliche Gift dabeihabt. Bringt alles zu Shenthul. Er wird wissen, was zu tun ist... hoffe ich.' WHERE id=1160;
UPDATE npc_text SET Text0_0='Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.', Text0_1='Ich hoffe, Ihr habt einen guten Grund für Eure Störung, ich war gerade mitten bei der Arbeit.' WHERE id=1202;
UPDATE npc_text SET Text0_0='Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.', Text0_1='Ein Kräuterkundiger, so, so. Ich bezweifle, dass Ihr Euch mit meinen Fertigkeiten messen könnt, aber vielleicht kann ich Euch ja das eine oder andere beibringen.' WHERE id=1203;
UPDATE npc_text SET Text0_0='Willkommen in Sturmwind. Hat lange gedauert, die Stadt wieder aufzubauen, aber das war es wert. Seid Ihr nur zu Besuch oder geschäftlich hier?', Text0_1='Willkommen in Sturmwind. Hat lange gedauert, die Stadt wieder aufzubauen, aber das war es wert. Seid Ihr nur zu Besuch oder geschäftlich hier?' WHERE id=1206;
UPDATE npc_text SET Text0_0='Bei mir schauen nicht oft andere Köche wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.', Text0_1='Bei mir schauen nicht oft andere Köche wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.' WHERE id=1207;
UPDATE npc_text SET Text0_0='Sehe ich vielleicht aus, als hätte ich die Zeit, jeden Touristen zu verhätscheln, der seinen Kopf durch diese Tür steckt? Sagt mir, was Ihr wollt, und fasst Euch kurz!', Text0_1='Sehe ich vielleicht aus, als hätte ich die Zeit, jeden Touristen zu verhätscheln, der seinen Kopf durch diese Tür steckt? Sagt mir, was Ihr wollt, und fasst Euch kurz!' WHERE id=1208;
UPDATE npc_text SET Text0_0='Bei mir schauen nicht oft andere Kürschner wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.', Text0_1='Bei mir schauen nicht oft andere Kürschner wegen einer Ausbildung vorbei, aber ich kann Euch wohl das eine oder andere beibringen, wenn Ihr bereit seid.' WHERE id=1210;
UPDATE npc_text SET Text0_0='Die großen Kriege mögen ja für den Augenblick vorbei sein, aber wir dürfen dennoch nicht nachlassen!', Text0_1='Die großen Kriege mögen ja für den Augenblick vorbei sein, aber wir dürfen dennoch nicht nachlassen!' WHERE id=1215;
UPDATE npc_text SET Text0_0='Sturmwind ist die Säule der Menschheit, aufrecht gehalten von Willenskraft und der Schneide einer stabilen Klinge.', Text0_1='Sturmwind ist die Säule der Menschheit, aufrecht gehalten von Willenskraft und der Schneide einer stabilen Klinge.' WHERE id=1216;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Ihr sucht Anleitung in der Kriegskunst?', Text0_1='Seid gegrüßt. Sucht Ihr Anleitung in der Kriegskunst?' WHERE id=1217;
UPDATE npc_text SET Text0_0='Ganz gleich wie viel man zu wissen glaubt, man lernt nie aus.', Text0_1='Ganz gleich wie viel man zu wissen glaubt, man lernt nie aus.' WHERE id=1218;
UPDATE npc_text SET Text0_0='Oh, ein Arzt, wie ich einer bin! Es wäre mir eine Freude, Euch alles zu lehren, was Euch auf Euren Reisen nützlich sein kann.', Text0_1='Oh, ein Arzt, wie ich einer bin! Es wäre mir eine Freude, Euch alles zu lehren, was Euch auf Euren Reisen nützlich sein kann.' WHERE id=1220;
UPDATE npc_text SET Text0_0='Ja, $Gmein Sohn:meine Tochter;? Was kann ich für Euch tun?', Text0_1='Ja, $Gmein Sohn:meine Tochter;? Was kann ich für Euch tun?' WHERE id=1221;
UPDATE npc_text SET Text0_0='Ich kam von Eisenschmiede hierher, um beim Wiederaufbau zu helfen. Wenn ich Euch genug von meinen Fertigkeiten lehre, kann ich diese Aufgabe vielleicht in Eure Hände legen und nach Hause zurückkehren.', Text0_1='Ich kam von Eisenschmiede hierher, um beim Wiederaufbau zu helfen. Wenn ich Euch genug von meinen Fertigkeiten lehre, kann ich diese Aufgabe vielleicht in Eure Hände legen und nach Hause zurückkehren.' WHERE id=1225;
UPDATE npc_text SET Text0_0='Was ist? Ihr stört, und dafür solltet Ihr einen verflucht guten Grund haben!', Text0_1='Was ist? Ihr stört, und dafür solltet Ihr einen verflucht guten Grund haben!' WHERE id=1226;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N.' WHERE id=1228;
UPDATE npc_text SET Text0_0='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text0_1='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text1_0='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', Text1_1='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE id=1231;
UPDATE npc_text SET Text0_0='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text0_1='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text1_0='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', Text1_1='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE id=1232;
UPDATE npc_text SET Text0_0='Geschaffen von den besten Handwerkern in ganz Teldrassil und als Zeichen der Festigkeit der Allianz zwischen dem Volk des großen Baums und den Menschen dieses Landes durch Boten von Kalimdor nach Azeroth gebracht. Wenn Ihr etwas braucht, müsst Ihr nur fragen.', Text0_1='Geschaffen von den besten Handwerkern in ganz Teldrassil und als Zeichen der Festigkeit der Allianz zwischen dem Volk des großen Baums und den Menschen dieses Landes durch Boten von Kalimdor nach Azeroth gebracht. Wenn Ihr etwas braucht, müsst Ihr nur fragen.' WHERE id=1233;
UPDATE npc_text SET Text0_0='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text0_1='Seit dem Fall von Dalaran und der Zerstörung von Quel\'Thalas sind solche Waren schwer zu bekommen. Aber Lucan Cordell und einige andere unserer Handwerker haben es geschafft, für unsere Bestellungen erstklassige Handelsware zu besorgen.', Text1_0='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.', Text1_1='Ich verkaufe nur die beste arkane Ausrüstung, gemacht von den Handwerkern und Magiern hier in Sturmwind.' WHERE id=1234;
UPDATE npc_text SET Text0_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text0_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text2_0='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', Text2_1='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE id=1235;
UPDATE npc_text SET Text0_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text0_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text2_0='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', Text2_1='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE id=1236;
UPDATE npc_text SET Text0_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text0_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_0='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text1_1='Die besten Preise in ganz Sturmwind, was Besseres werdet Ihr nicht finden. Womit kann ich Euch denn nun dienen?', Text2_0='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?', Text2_1='Seht Ihr Euch nur um oder kann ich Euch irgendwie helfen?' WHERE id=1237;
UPDATE npc_text SET Text0_0='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.', Text0_1='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.' WHERE id=1238;
UPDATE npc_text SET Text0_0='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.', Text0_1='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.' WHERE id=1239;
UPDATE npc_text SET Text0_0='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.', Text0_1='Ich führe nur die beste Ausrüstung, geprüft und echt. Die Preise stehen fest. Wenn Ihr also feilschen wollt, sucht Euch einen Goblin.' WHERE id=1240;
UPDATE npc_text SET Text0_0='Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.', Text0_1='Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.' WHERE id=1241;
UPDATE npc_text SET Text0_0='Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.', Text0_1='Möge das Licht Euch den Weg weisen und Euch auf Euren Reisen beschützen. Wenn Ihr Hilfe braucht, stehe ich Euch zur Verfügung.' WHERE id=1242;
UPDATE npc_text SET Text0_0='Lasst Euch von den Menschen auf dem Basar nicht über den Tisch ziehen, $GBursche:Mädel;. Mit der von den Gnomen gebauten Untergrundbahn ist es leichter, als Ihr vielleicht glaubt, Waren von Eisenschmiede hierher zu schaffen. Wenn sie Euch was von Importzöllen erzählen wollen, sagt Ihr einfach, Ihr kauft Eure Sachen bei mir.', Text0_1='Lasst Euch von den Menschen auf dem Basar nicht über den Tisch ziehen, $GBursche:Mädel;. Mit der von den Gnomen gebauten Untergrundbahn ist es leichter, als Ihr vielleicht glaubt, Waren von Eisenschmiede hierher zu schaffen. Wenn sie Euch was von Importzöllen erzählen wollen, sagt Ihr einfach, Ihr kauft Eure Sachen bei mir.' WHERE id=1243;
UPDATE npc_text SET Text0_0='Wenn Ihr Lasche A in Schlitz B steckt, dann macht\'s natürlich BUMM! Lasst mich Euch mal zeigen, was ich habe. Danach könnt Ihr Lilliam fragen, wie man es zusammensetzt, ohne dass es Euch um die Ohren fliegt.', Text0_1='Wenn Ihr Lasche A in Schlitz B steckt, dann macht\'s natürlich BUMM! Lasst mich Euch mal zeigen, was ich habe. Danach könnt Ihr Lilliam fragen, wie man es zusammensetzt, ohne dass es Euch um die Ohren fliegt.' WHERE id=1249;
UPDATE npc_text SET Text0_0='Hier wurde noch nie etwas gestohlen. Noch nie in der ganzen Geschichte von... der ganzen Geschichte!', Text0_1='Hier wurde noch nie etwas gestohlen. Noch nie in der ganzen Geschichte von... der ganzen Geschichte!', Text1_0='Willkommen bei der Bank von Sturmwind. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?', Text1_1='Willkommen bei der Bank von Sturmwind. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?' WHERE id=1250;
UPDATE npc_text SET Text0_0='Willkommen im Blauen Eremiten, wo die Magiebegabten Dampf ablassen. Was natürlich nicht wörtlich zu nehmen ist. Womit kann ich Euch dienen, $Gwerter Herr:holde Dame;?', Text0_1='Willkommen im Blauen Eremiten, wo die Magiebegabten Dampf ablassen. Was natürlich nicht wörtlich zu nehmen ist. Womit kann ich Euch dienen, $Gwerter Herr:holde Dame;?' WHERE id=1252;
UPDATE npc_text SET Text0_0='Hört auf meinen Rat und haltet Euch von den Schatten fern.', Text0_1='Hört auf meinen Rat und haltet Euch von den Schatten fern.' WHERE id=1253;
UPDATE npc_text SET Text0_0='Willkommen im Pfeifenden Schwein! Holt Euch was zu trinken und setzt Euch. Je mehr, desto lustiger!', Text0_1='Willkommen im Pfeifenden Schwein! Holt Euch was zu trinken und setzt Euch. Je mehr, desto lustiger!' WHERE id=1254;
UPDATE npc_text SET Text0_0='Wir sind die einzigen Lieferanten von gesunden Blumen in ganz Sturmwind. Wir haben wunderschöne Blumen für jeden Anlass.', Text0_1='Wir sind die einzigen Lieferanten von gesunden Blumen in ganz Sturmwind. Wir haben wunderschöne Blumen für jeden Anlass.' WHERE id=1255;
UPDATE npc_text SET Text0_0='Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', Text0_1='Wohin des Weges, $GBursche:Mädel;? Wohin Ihr auch wollt, für ein paar Münzen bringen Euch meine Greife in Windeseile hin.', Text1_0='Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.', Text1_1='Diese großen Wildtiere kennen Wege, die man zu Fuß nie und nimmer finden würde. Sie bringen Euch blitzschnell überallhin und zeigen Euch dabei womöglich noch etwas Neues.' WHERE id=1256;
UPDATE npc_text SET Text0_0='Sprecht leise und vorsichtig. Ein falsches Wort kann Euch hierzulande den Kopf kosten. Nun denn, was ist Euer Begehr?', Text0_1='Sprecht leise und vorsichtig. Ein falsches Wort kann Euch hierzulande den Kopf kosten. Nun denn, was ist Euer Begehr?' WHERE id=1257;
UPDATE npc_text SET Text0_0='Sprecht leise und vorsichtig. Ein falsches Wort kann Euch hierzulande den Kopf kosten. Nun denn, was ist Euer Begehr?', Text0_1='Sprecht leise und vorsichtig. Ein falsches Wort kann Euch hierzulande den Kopf kosten. Nun denn, was ist Euer Begehr?' WHERE id=1258;
UPDATE npc_text SET Text0_0='Willkommen bei der Gildenregistrierung von Sturmwind. Wie kann ich Euch heute helfen?', Text0_1='Willkommen bei der Gildenregistrierung von Sturmwind. Wie kann ich Euch heute helfen?' WHERE id=1259;
UPDATE npc_text SET Text0_0='Willkommen bei der Gildenregistrierung von Sturmwind. Wie kann ich Euch heute helfen?', Text0_1='Willkommen bei der Gildenregistrierung von Sturmwind. Wie kann ich Euch heute helfen?' WHERE id=1260;
UPDATE npc_text SET Text0_0='Ich hoffe, Ihr habt nicht den ganzen Weg zur großen Schmiede auf Euch genommen, bloß weil\'s hier so schön warm ist. Sprecht, $GBursche:Mädel;, und sagt mir, was ich für Euch tun kann!' WHERE id=1261;
UPDATE npc_text SET Text0_0='Ihr seht mir wahrlich wie ein Schmied aus. Ruß unter den Nägeln, Stahl in den Knochen. Aye, in Euren Adern fließt sehr wohl das Blut eines Schmieds. Was bedeutet, Ihr wollt von mir ein oder zwei Dinge lernen. Nun, $GBursche:Mädel;, schnappt Euch einen Hammer. Und dann lasst uns mal sehen, was ich Euch noch so alles beibringen kann.' WHERE id=1262;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=1272;
UPDATE npc_text SET Text0_0='Der Stier, den Ihr sucht, heißt Krumn. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung von Donnerfels.', Text0_1='Der Stier, den Ihr sucht, heißt Krumn. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung von Donnerfels.', Text1_0='Eine Gemeinschaft gleichdenkender Geister kann eine mächtige Truppe sein. Krumn hilft Euch, das zu erreichen. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung von Donnerfels neben den Fahrstühlen.', Text1_1='Eine Gemeinschaft gleichdenkender Geister kann eine mächtige Truppe sein. Krumn hilft Euch, das zu erreichen. Ihr findet ihn auf der unteren Anhöhe beim Informationszentrum der Stadtverwaltung von Donnerfels neben den Fahrstühlen.' WHERE id=1291;
UPDATE npc_text SET Text0_0='Die Bank von Donnerfels findet Ihr auf der unteren Anhöhe.', Text0_1='Die Bank von Donnerfels findet Ihr auf der unteren Anhöhe.', Text1_0='Eure weltlichen Besitztümer könnt Ihr in der Bank von Donnerfels auf der unteren Anhöhe abgeben.', Text1_1='Eure weltlichen Besitztümer könnt Ihr in der Bank von Donnerfels auf der unteren Anhöhe abgeben.' WHERE id=1292;
UPDATE npc_text SET Text0_0='Der Windreiterhorst befindet sich ganz oben auf der Totemtreppe in der Stadtmitte.', Text0_1='Der Windreiterhorst befindet sich ganz oben auf der Totemtreppe in der Stadtmitte.', Text1_0='Ah, mit den großen Vögeln des Himmels zu fliegen. Sucht Tal auf dem Windreiterhorst auf. Er wird Euch Eurem Ziel näher bringen.', Text1_1='Ah, mit den großen Vögeln des Himmels zu fliegen. Sucht Tal auf dem Windreiterhorst auf. Er wird Euch Eurem Ziel näher bringen.' WHERE id=1293;
UPDATE npc_text SET Text0_0='Sucht Turak Runentotem in der Halle der Ältesten auf der Anhöhe der Ältesten auf.', Text0_1='Sucht Turak Runentotem in der Halle der Ältesten auf der Anhöhe der Ältesten auf.', Text1_0='Seid mir gegrüßt. Turak Runentotem und die anderen Druiden erwarten Euch in der Halle der Ältesten auf der Anhöhe der Ältesten.', Text1_1='Seid mir gegrüßt. Turak Runentotem und die anderen Druiden erwarten Euch in der Halle der Ältesten auf der Anhöhe der Ältesten.' WHERE id=1294;
UPDATE npc_text SET Text0_0='Ah, dann müsst Ihr es in der Jägerhalle versuchen. Auf der Anhöhe der Jäger findet Ihr mehrere so große Jäger wie Holt Donnerhorn.', Text0_1='Ah, dann müsst Ihr es in der Jägerhalle versuchen. Auf der Anhöhe der Jäger findet Ihr mehrere so große Jäger wie Holt Donnerhorn.', Text1_0='Ein Freund der Tiere ist ein Freund von mir. Ihr sucht die Jägerhalle. Sie befindet sich auf der Anhöhe der Jäger.', Text1_1='Ein Freund der Tiere ist ein Freund von mir. Ihr sucht die Jägerhalle. Sie befindet sich auf der Anhöhe der Jäger.' WHERE id=1295;
UPDATE npc_text SET Text0_0='Ein Anhänger des Arkanen seid Ihr? Hmpf! Nun gut - Ihr findet die Leute bei den Teichen der Visionen unterhalb der Anhöhe der Geister.', Text0_1='Ein Anhänger des Arkanen seid Ihr? Hmpf! Nun gut - Ihr findet die Leute bei den Teichen der Visionen unterhalb der Anhöhe der Geister.', Text1_0='Sehr traurig, wenn man bedenkt, was über diese armen untoten Seelen gekommen ist. Ihr findet Erzmagier Shymm beim Meditieren bei den Teichen der Visionen unterhalb der Anhöhe der Geister.', Text1_1='Sehr traurig, wenn man bedenkt, was über diese armen untoten Seelen gekommen ist. Ihr findet Erzmagier Shymm beim Meditieren bei den Teichen der Visionen unterhalb der Anhöhe der Geister.' WHERE id=1296;
UPDATE npc_text SET Text0_0='Vater Cobb ist Euer Mann. Ihr findet ihn unterhalb der Anhöhe der Geister in der Höhle bei den Teichen der Visionen.', Text0_1='Vater Cobb ist Euer Mann. Ihr findet ihn unterhalb der Anhöhe der Geister in der Höhle bei den Teichen der Visionen.', Text1_0='Ungeachtet ihrer Tragödie sind die Untoten den weiten Weg gekommen, uns zu unterstützen. Ich hoffe nur, wir können ihnen gleichermaßen hilfreich sein. Sucht Vater Cobb bei den Teichen der Visionen unterhalb der Anhöhe der Geister auf.', Text1_1='Ungeachtet ihrer Tragödie sind die Untoten den weiten Weg gekommen, uns zu unterstützen. Ich hoffe nur, wir können ihnen gleichermaßen hilfreich sein. Sucht Vater Cobb bei den Teichen der Visionen unterhalb der Anhöhe der Geister auf.' WHERE id=1297;
UPDATE npc_text SET Text0_0='Die Halle der Geister ist das Zuhause unserer Schamanen. Ihr findet sie auf der Anhöhe der Geister. Mögen Eure Vorfahren über Euch wachen.', Text0_1='Die Halle der Geister ist das Zuhause unserer Schamanen. Ihr findet sie auf der Anhöhe der Geister. Mögen Eure Vorfahren über Euch wachen.', Text1_0='Die Erdenmutter ruft und Ihr antwortet. Ihr findet die Halle der Geister auf der Anhöhe der Geister.', Text1_1='Die Erdenmutter ruft und Ihr antwortet. Ihr findet die Halle der Geister auf der Anhöhe der Geister.' WHERE id=1298;
UPDATE npc_text SET Text0_0='Ihr wollt eines Tages zu den Unsrigen gehören? Dann haltet Euch an Sark Rachtotem. Er ist in der Jägerhalle.', Text0_1='Ihr wollt eines Tages zu den Unsrigen gehören? Dann haltet Euch an Sark Rachtotem. Er ist in der Jägerhalle.', Text1_0='Starker Körper und starker Geist. Eine bessere Kombination gibt es nicht! Sucht Euer Schicksal bei Sark Rachtotem in der Jägerhalle.', Text1_1='Starker Körper und starker Geist. Eine bessere Kombination gibt es nicht! Sucht Euer Schicksal bei Sark Rachtotem in der Jägerhalle.' WHERE id=1299;
UPDATE npc_text SET Text0_0='Welchen Ausbilder sucht Ihr?', Text0_1='Welchen Ausbilder sucht Ihr?' WHERE id=1300;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=1301;
UPDATE npc_text SET Text0_0='Das ist faszinierend. Ich habe gehört, wie sich Orcs beschwert haben, vom Geruch der Menschen werde ihnen übel, und ich habe gehört, wie Menschen behaupteten, der Gestank von Orcs sei fürchterlich - und doch riechen für mich alle Völker dieser Länder gleich: Sie alle stinken fürchterlich nach Gier.$B$BDas glaubt Ihr nicht? Dann betrachtet Ihr die Dinge, die Ihr in Eurem Leben tut, nicht nur als Ergebnis der Gier, die jede Eurer Entscheidungen diktiert?$B$BSo sei es. Da es die Gier war, die mich aus der Heimat vertrieb, soll es auch die Gier sein, die mich zurückbringt.' WHERE id=1302;
UPDATE npc_text SET Text0_0='Dann geht hinüber zu \'Benas Alchemie\' auf der mittleren Anhöhe und sprecht mit Bena persönlich. Sie kann Euch in der Kunst des Tränkebrauens unterweisen.', Text0_1='Dann geht hinüber zu \'Benas Alchemie\' auf der mittleren Anhöhe und sprecht mit Bena persönlich. Sie kann Euch in der Kunst des Tränkebrauens unterweisen.', Text1_0='Die Kräuter der Natur sind mächtige Verbündete, wenn man sie respektvoll kombiniert. Ihr müsst mit Bena in ihrem Alchemieladen auf der mittleren Anhöhe sprechen, wenn Ihr Eure Ausbildung weiterführen möchtet.', Text1_1='Die Kräuter der Natur sind mächtige Verbündete, wenn man sie respektvoll kombiniert. Ihr müsst mit Bena in ihrem Alchemieladen auf der mittleren Anhöhe sprechen, wenn Ihr Eure Ausbildung weiterführen möchtet.' WHERE id=1332;
UPDATE npc_text SET Text0_0='Ihr wollt den Umgang mit Hammer und Amboss erlernen? Karn Steinhuf kann Euch das beibringen. Er hat eine Schmiede auf der unteren Anhöhe am Tümpel.', Text0_1='Ihr wollt den Umgang mit Hammer und Amboss erlernen? Karn Steinhuf kann Euch das beibringen. Er hat eine Schmiede auf der unteren Anhöhe am Tümpel.', Text1_0='Wie alles andere, so sollte auch Metall im Einklang mit der Natur verwendet werden. Karn Steinhuf hat eine Schmiede am Tümpel auf der unteren Anhöhe. Sucht seine Weisheit.', Text1_1='Wie alles andere, so sollte auch Metall im Einklang mit der Natur verwendet werden. Karn Steinhuf hat eine Schmiede am Tümpel auf der unteren Anhöhe. Sucht seine Weisheit.' WHERE id=1333;
UPDATE npc_text SET Text0_0='In Donnerfels gibt es keine bessere Köchin als Aska Nebelläufer. Mmmmm, ich kann bis hierher riechen, was sie kocht. Geht zu ihrer Küche auf der oberen Anhöhe.$B$BVerflixt... jetzt habe ich Hunger.', Text0_1='In Donnerfels gibt es keine bessere Köchin als Aska Nebelläufer. Mmmmm, ich kann bis hierher riechen, was sie kocht. Geht zu ihrer Küche auf der oberen Anhöhe.$B$BVerflixt... jetzt habe ich Hunger.', Text1_0='Ihr ehrt den Geist der Tiere, die Ihr tötet, indem Ihr ihre Körper nicht verrotten lasst. Aska Nebelläufer zeigt Euch, wie. Sie hat eine Küche auf der oberen Anhöhe.', Text1_1='Ihr ehrt den Geist der Tiere, die Ihr tötet, indem Ihr ihre Körper nicht verrotten lasst. Aska Nebelläufer zeigt Euch, wie. Sie hat eine Küche auf der oberen Anhöhe.' WHERE id=1334;
UPDATE npc_text SET Text0_0='Dann schaut bei Morgenwandlers Verzauberungen auf der mittleren Anhöhe vorbei. Teg wird Euch mit Freude auf jede erdenkliche Weise behilflich sein.', Text0_1='Dann schaut bei Morgenwandlers Verzauberungen auf der mittleren Anhöhe vorbei. Teg wird Euch mit Freude auf jede erdenkliche Weise behilflich sein.', Text1_0='Der Beruf des Verzauberers spiegelt den Zyklus von Zerstörung und Schöpfung wider, der das Herz der Natur ausmacht. Teg bei \'Morgenwandler-Verzauberer\' auf der mittleren Anhöhe zeigt Euch, wie.', Text1_1='Der Beruf des Verzauberers spiegelt den Zyklus von Zerstörung und Schöpfung wider, der das Herz der Natur ausmacht. Teg bei \'Morgenwandlers Verzauberungen\' auf der mittleren Anhöhe zeigt Euch, wie.' WHERE id=1335;
UPDATE npc_text SET Text0_0='Erste Hilfe ist immer eine praktische Fertigkeit. Hat mir im Kampf schon oft die Haut gerettet. Besucht die \'Spirituelle Heilung\' auf der Anhöhe der Geister. Pand Steinbinder kann Euch alles lehren, was Ihr wissen müsst, damit auch Ihr Eure Haut retten könnt.', Text0_1='Erste Hilfe ist immer eine praktische Fertigkeit. Hat mir im Kampf schon oft die Haut gerettet. Besucht die \'Spirituelle Heilung\' auf der Anhöhe der Geister. Pand Steinbinder kann Euch alles lehren, was Ihr wissen müsst, damit auch Ihr Eure Haut retten könnt.', Text1_0='Das Leben ist ein Kampf und das Sprichwort \'Was uns nicht umbringt, macht uns nur stärker\' trifft nicht immer zu. Manchmal heilen Wunden langsam, darum haben wir Verbände. Pand Steinbinder auf der Anhöhe der Geister kann Euch die Kunst der \'Ersten Hilfe\' lehren.', Text1_1='Das Leben ist ein Kampf und das Sprichwort \'Was uns nicht umbringt, macht uns nur stärker\' trifft nicht immer zu. Manchmal heilen Wunden langsam, darum haben wir Verbände. Pand Steinbinder auf der Anhöhe der Geister kann Euch die Kunst der \'Ersten Hilfe\' lehren.' WHERE id=1336;
UPDATE npc_text SET Text0_0='Kann Euch nicht verdenken, dass Ihr zu angeln angefangen habt. Mulgore hat einige der besten Angelstellen im ganzen südlichen Kalimdor und \'Gipfelbergs Köder und Ausrüstung\' auf der oberen Anhöhe hat alles, was Ihr braucht.$B$BSagt Kah Nebelläufer, ich hätte Euch geschickt!', Text0_1='Kann Euch nicht verdenken, dass Ihr zu angeln angefangen habt. Mulgore hat einige der besten Angelstellen im ganzen südlichen Kalimdor und \'Gipfelbergs Köder und Ausrüstung\' auf der oberen Anhöhe hat alles, was Ihr braucht.$B$BSagt Kah Nebelläufer, ich hätte Euch geschickt!', Text1_0='Kah Nebelläufer von \'Gipfelbergs Köder und Ausrüstung\' auf der oberen Anhöhe zeigt Euch den Weg zu innerem Frieden und Gelassenheit, während Ihr Eure nächste Mahlzeit angelt.', Text1_1='Kah Nebelläufer von \'Gipfelbergs Köder und Ausrüstung\' auf der oberen Anhöhe zeigt Euch den Weg zu innerem Frieden und Gelassenheit, während Ihr Eure nächste Mahlzeit angelt.' WHERE id=1337;
UPDATE npc_text SET Text0_0='In dem Fall ist Komin Winterhuf bei \'Ganzheitliche Kräuterkunde\' Euer Mann. Ihr findet ihn auf der mittleren Anhöhe.', Text0_1='In dem Fall ist Komin Winterhuf bei \'Ganzheitliche Kräuterkunde\' Euer Mann. Ihr findet ihn auf der mittleren Anhöhe.', Text1_0='Die Natur spendet alles, was wir brauchen. Sprecht mit Komin Winterhuf bei \'Ganzheitliche Kräuterkunde\' auf der mittleren Anhöhe.', Text1_1='Die Natur spendet alles, was wir brauchen. Sprecht mit Komin Winterhuf bei \'Ganzheitliche Kräuterkunde\' auf der mittleren Anhöhe.' WHERE id=1338;
UPDATE npc_text SET Text0_0='Sucht Una in der \'Rüstungsschmiede von Donnerfels\' auf. Ihr findet sie auf der mittleren Anhöhe.', Text0_1='Sucht Una in der \'Rüstungsschmiede von Donnerfels\' auf. Ihr findet sie auf der mittleren Anhöhe.', Text1_0='Die Vorstellung, dass die Haut Eures Feindes Euch Schutz bietet, hat etwas Symmetrisches. Wenn Ihr Eure Lederverarbeitungsfertigkeit verbessern möchtet, sucht Una in der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe auf.', Text1_1='Die Vorstellung, dass die Haut Eures Feindes Euch Schutz bietet, hat etwas Symmetrisches. Wenn Ihr Eure Lederverarbeitungsfertigkeit verbessern möchtet, sucht Una in der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe auf.' WHERE id=1339;
UPDATE npc_text SET Text0_0='Brek Steinhuf findet Ihr bei \'Steinhufgeologie\' auf der unteren Anhöhe. Er kann Euch zeigen, wie man Erz und Edelsteine abbaut.', Text0_1='Brek Steinhuf findet Ihr bei \'Steinhufgeologie\' auf der unteren Anhöhe. Er kann Euch zeigen, wie man Erz und Edelsteine abbaut.', Text1_0='Die Erdenmutter birgt viele Geheimnisse und Brek Steinhufkann Euch helfen, einige zu enträtseln. Ihr findet ihn auf der unteren Anhöhe bei \'Steinhufgeologie\'.', Text1_1='Die Erdenmutter birgt viele Geheimnisse und Brek Steinhufkann Euch helfen, einige zu enträtseln. Ihr findet ihn auf der unteren Anhöhe bei \'Steinhufgeologie\'.' WHERE id=1340;
UPDATE npc_text SET Text0_0='Tepa ist bekannt für ihre Stoffarbeiten. Man findet sie in der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.', Text0_1='Tepa ist bekannt für ihre Stoffarbeiten. Man findet sie in der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.', Text1_0='Dem Stamm Kleidung zu geben, ist so wichtig wie jede andere Arbeit auch. Ihr solltet in der \'Rüstungsschmiede von Donnerfels\' nach Tepa suchen. Sie ist ein Naturtalent mit der Nähnadel.', Text1_1='Dem Stamm Kleidung zu geben, ist so wichtig wie jede andere Arbeit auch. Ihr solltet in der \'Rüstungsschmiede von Donnerfels\' nach Tepa suchen. Sie ist ein Naturtalent mit der Nähnadel.' WHERE id=1341;
UPDATE npc_text SET Text0_0='Mooranta kann Euch die Kunst der Kürschnerei beibringen. Ihr findet sie vor der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.', Text0_1='Mooranta kann Euch die Kunst der Kürschnerei beibringen. Ihr findet sie vor der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.', Text1_0='Jeder verwendete Krümel verringert die Verschwendung und bringt uns der Erdenmutter näher. Mooranta kann Euch den Weg weisen. Ihr findet sie vor der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.', Text1_1='Jeder verwendete Krümel verringert die Verschwendung und bringt uns der Erdenmutter näher. Mooranta kann Euch den Weg weisen. Ihr findet sie vor der \'Rüstungsschmiede von Donnerfels\' auf der mittleren Anhöhe.' WHERE id=1343;
UPDATE npc_text SET Text0_0='Die Wildtiere in den verwüsteten Landen weisen übernatürliche Attribute auf, die man bislang in der Natur nicht kannte.$B$BWir vermuten, dass die bei der Öffnung des Dunklen Portals freigesetzte Energie aus den Kreaturen das gemacht hat, was sie heute sind.$B$BDie Wildtiere sind fürchterlich, aber sie können uns sicher bei unseren Studien auch nützlich sein.$B$BVielleicht wollt Ihr uns ja beim Studium dieser Wildtiere helfen, $N...' WHERE id=1351;
UPDATE npc_text SET Text0_1='Während sich mein Bruder Drazial auf die körperlichen Merkmale der Wildtiere in den verwüsteten Landen konzentriert, widme ich mich mehr den spirituellen Aspekten.$B$BDenn es stimmt, dass die Wildtiere extrem ungewöhnliche Kraft, Beweglichkeit und Ausdauer an den Tag legen, aber einige sind zudem sehr intelligent und haben große Regenerationsfähigkeiten.$B$BVielleicht wollt Ihr mir ja helfen, meine Forschung voranzutreiben, $R...' WHERE id=1371;
UPDATE npc_text SET Text0_0='Ich weiß, es ist schwer vorstellbar, dass sich so viel Elend in einem so kleinen Landstrich abspielen kann, und doch herrschen jenseits des Sumpfs Chaos und Zerstörung im Land.$B$BPasst auf Euch auf. Die verwüsteten Lande sind die letzte Ruhestätte weit größerer Wesen, als Ihr es seid.' WHERE id=1391;
UPDATE npc_text SET Text0_0='Wir wurden \'Thralls Zugriff\' genannt. Zwanzig von Thralls erfahrensten und ehrenvollsten Soldaten.$B$BIch hatte das Regiment angewiesen, hier an der Grenze zu den verwüsteten Landen einen Stützpunkt zu errichten, während wir auf Befehle aus Orgrimmar warteten. Wochen vergingen ohne Kunde vom Kriegshäuptling und die Männer wurden unruhig.$B$BIch wertete das als Zeichen, $N. Ich war jung und ehrgeizig und fürchtete mich vor nichts. Das Bataillon setzte sich unter meinem Kommando Richtung verwüstete Lande in Marsch.$B$BDiese Entscheidung habe ich später schwer bereut.' WHERE id=1392;
UPDATE npc_text SET Text0_0='Bedauern. Das ist alles, was mir noch geblieben ist. Eine einzige Fehlentscheidung hatte gleich mehrere schreckliche Konsequenzen. Hätte ich doch nur auf meine Befehle gewartet. Vielleicht hätte darin etwas über die Schrecken gestanden, die uns in jenem verfluchten Land erwarteten! Vielleicht hätten sie uns zu einer anderen Abteilung beordert... Letztendlich war es meine Schuld. Es war alles meine Schuld.' WHERE id=1411;
UPDATE npc_text SET Text0_0='Würdet Ihr glauben, dass ich versagt habe... in meiner dunkelsten Stunde und auf dem Höhepunkt meines Lebens?$B$BAls ich vor der Übermacht der gegnerischen Soldaten floh, konnte ich nur die Schreie meiner Leute und das Klopfen meines eigenen Herzens hören.$B$BKurz darauf kehrte ich in die verwüsteten Lande zurück in der Hoffnung, meine Männer retten und meine Ehre wiederherstellen zu können. Aber ich wurde gefangen und totgeschlagen.$B$BEs war schrecklich, was ich während meiner Befragung und der anschließenden Folter herausfand.' WHERE id=1451;
UPDATE npc_text SET Text0_0='Meine Entführer waren unglaublich brutal, $N. Sie folterten abwechselnd Leutnant Kirith und mich, der andere musste immer zusehen. Eines besonders brutalen Tages nutzte Kirith eine Pause und erzählte mir, was mit meinen Männern geschehen war und wie er sie von ihren Meistern befreien wollte.$B$BMeine Männer hatten ein Schicksal schlimmer als der Tod erlitten - schlimmer auch als die Schmerzen, die Kirith und ich erdulden mussten.' WHERE id=1452;
UPDATE npc_text SET Text0_0='Wir beide können uns wohl kaum vorstellen, wie schlimm es ist, wenn einem die Seele herausgerissen wird. Man ist nicht tot, aber lebendig ist man erst recht nicht. Stattdessen wandelt man umher als Diener jener, die einen an diese Welt gebunden haben.$B$BLord Razelikh, der dämonische Oberanführer dieser Lande, hat sie alle versklavt. Neun beanspruchte er für sich selbst. Er platzierte Steine der Bindung in der Nähe des Dunklen Portals. Die anderen neun wurden unter seinen Untergebenen aufgeteilt.' WHERE id=1453;
UPDATE npc_text SET Text0_0='Dem Erzmagier Allistarj von den Schattenanbetern wurden drei zugesprochen. Lady Sevine, Hexenmeisterin der Schattenanbeter, wurden ebenfalls drei zugesprochen. Die restlichen drei bekam Grol der Zerstörer, Lord der Schreckensfelsoger der verwüsteten Lande.$B$BDenn wisset, $N, ohne Seele kann der Körper nicht sterben. Jetzt sind meine Männer - oder das, was von ihnen übrig ist - unbesiegbare Kampfmaschinen. Zum Glück können sie durch die Steine der Bindung nicht weit reisen.' WHERE id=1454;
UPDATE npc_text SET Text0_0='Wenn Ihr das Regiment befreien wollt, müsst ihr die Steine der Bindung zerstören! Wie ich bereits sagte: Kirith hatte einen Plan.$B$BJeder der Diener hat eine Schwachstelle. Schädigt ihre Körper in der Nähe der jeweiligen Steine, bis sie kurz vor der Auflösung stehen. An diesem Punkt, und nur an diesem Punkt, sind die Steine angreifbar. Ihr müsst Euch indes beeilen, die Steine zu zerstören, denn wenn sich die Versklavten erst einmal erholt haben, sind sie wieder unverwundbar.' WHERE id=1455;
UPDATE npc_text SET Text0_0='Ich wünschte, es wäre so, $N, aber Ihr braucht Hilfe, wenn Ihr Razelikh und seine Untergebenen besiegen wollt.$B$BStellt eine schlagkräftige Truppe aus Abenteurern und potenziellen Helden zusammen und befreit meine Männer von ihrer immer währenden Qual! Achtzehn Steine müsst Ihr zerschmettern.$B$BKämpft hart und sterbt ehrenvoll, $N.' WHERE id=1456;
UPDATE npc_text SET Text0_0='Ihr braucht also Alchemiegrundbedarfswaren? Oder was wollt Ihr von mir?' WHERE id=1471;
UPDATE npc_text SET Text0_0='Das Leben in der Wüste ist hart! Wenn ich da draußen in der Sonne stehe, werde ich immer so durstig.' WHERE id=1493;
UPDATE npc_text SET Text0_0='Hilfe!$B$BWenn Ihr mich hier rausholt, kann ich die Tür aufsprengen, die zum Häuptling von Zul\'Farrak führt!' WHERE id=1511;
UPDATE npc_text SET Text0_0='Wir müssen noch mehr Trolle töten! Tötet genug und ich sprenge die Tür zum Trollhäuptling auf!' WHERE id=1513;
UPDATE npc_text SET Text0_0='Danke, $N. Wäret Ihr nicht aufgetaucht, hätte das wohl unser Ende bedeutet! Und jetzt steht zwischen uns und dem Trollhäuptling nur noch diese Tür...' WHERE id=1514;
UPDATE npc_text SET Text0_0='Hilfe! Meine Gruppe und ich wurden von Trollen gefangen und sie wollen uns opfern!$B$BHelft uns hier raus und ich gebe Euch alles, was Ihr wollt!' WHERE id=1515;
UPDATE npc_text SET Text0_0='Wir haben keine Zeit zum Reden! Wir müssen uns um die Trolle kümmern!' WHERE id=1516;
UPDATE npc_text SET Text0_0='Habt vielen Dank, $N. Ohne Euch hätten wir es nicht geschafft. Oh, und falls ich vorhin etwas von einer Gegenleistung für unsere Rettung gesagt habe... vergesst es!' WHERE id=1517;
UPDATE npc_text SET Text0_1='Hallo, Ihr da! Ich habe einen Vorschlag für ein abenteuerlustiges Wesen wie Euch, falls Ihr Euch die Sache zutraut!$B$BZusammen mit meiner Erfindung, dem Super-Ei-o-Maten, versuche ich, den Nachtelfdruiden bei der Kampagne zur Rettung der Hippogryphe zu helfen. Die Vögel leiden unter den üblen Auswirkungen der Oger der Gordunni in den Ruinen von Feralas. Es werden immer weniger.$B$BDarum sammle ich viele verschiedene Eier.' WHERE id=1519;
UPDATE npc_text SET Text0_1='Westlich von hier liegt Feralas. Die dort lebenden Hippogryphe waren lange Zeit Gefährten der Nachtelfen. Jetzt sind sie in Gefahr.$B$BIhr müsst mir ein Ei aus den Ruinen von Isildien im Süden von Feralas holen. Der Super-Ei-o-Mat kann jedes Ei analysieren und erkennen, ob es ausgebrütet werden kann. Dann schicke ich die Eier nach Darnassus, wo sie vor der dunklen Magie der Oger der Gordunni sicher sind und ausgebrütet werden können.$B$BIhr müsst indes wissen, $N, dass einige der Eier schlecht sind und nie ausgebrütet werden können.' WHERE id=1521;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N.' WHERE id=1569;
UPDATE npc_text SET Text0_0='Hallo, $N! Was ich nicht im Laden habe, kann ich Euch wahrscheinlich besorgen!' WHERE id=1571;
UPDATE npc_text SET Text0_0='Man trifft nicht viele Schmiede so weit von Eisenschmiede entfernt. Was kann ich für Euch tun?' WHERE id=1597;
UPDATE npc_text SET Text0_0='Ohne eine Decke aus stabilem Granit über mir fühle ich mich einfach nicht wohl. All diese Sterne da oben, da läuft\'s einem ja kalt den Rücken runter.' WHERE id=1598;
UPDATE npc_text SET Text0_0='Seid willkommen in meiner Schmiede. Lasst mich Euch ein Geheimnis anvertrauen. Ihr könnt niemandem vertrauen. Keinem Mann, keiner Frau, keiner Bestie. Vertrauen könnt Ihr nur hierauf: Stahl.' WHERE id=1605;
UPDATE npc_text SET Text0_0='Was führt Euch in meine Schmiede? Eine Ausbildung? Oder ist es das Geheimnis des Stahls? Die Rätsel der Schmiede? Sprecht!' WHERE id=1606;
UPDATE npc_text SET Text0_0='Ihr seht mir aus wie $Gein Mann:eine Frau; auf der Suche nach Wissen. Vielleicht sucht Ihr sogar die Lehre von Schmiede und Hammer. Vielleicht kann ich Euch auf diesem Weg geleiten. Aber erst müsst Ihr die Geister akzeptiert und ihre Rufe gehört haben.' WHERE id=1615;
UPDATE npc_text SET Text0_0='Wie ist die Schneide Eures Stahls? Scharf genug, Eurem Feind den Bauch aufzuschlitzen? Lasst mich Euch zeigen, wie man sie noch schärfer macht.' WHERE id=1618;
UPDATE npc_text SET Text0_0='Ihr wollt Schmied werden? Ihr wollt Klingen schmieden, mit denen Ihr Euren Gegner aufschlitzen könnt? Und Hämmer, mit denen Ihr ihm den Schädel einschlagen könnt? Und ob ich Euch helfen kann. Nur zu gern, wenn damit auch noch Blut vergossen werden soll.' WHERE id=1619;
UPDATE npc_text SET Text0_0='Begebt Euch in die Orcstadt Orgrimmar. Dort findet Ihr ihn im Tal der Ehre in einem Laden namens \'Legendäre Waffen\'.', Text0_1='Begebt Euch in die Orcstadt Orgrimmar. Dort findet Ihr ihn im Tal der Ehre in einem Laden namens \'Legendäre Waffen\'.' WHERE id=1620;
UPDATE npc_text SET Text0_0='Das sind gute Klingen, die Ihr da macht, $N. Sie zerteilen Stein und sind danach doch noch scharf genug, eine Kehle aufzuschlitzen. Und doch habe ich schon bessere gesehen, von einem Orc namens Kelgruk Blutaxt nämlich. Geht und lernt, was immer er Euch beibringen kann.', Text0_1='Das sind gute Klingen, die Ihr da macht, $N. Sie zerteilen Stein und sind danach doch noch scharf genug, eine Kehle aufzuschlitzen. Und doch habe ich schon bessere gesehen, von einem Orc namens Kelgruk Blutaxt nämlich. Geht und lernt, was immer er Euch beibringen kann.' WHERE id=1621;
UPDATE npc_text SET Text0_0='$N! Wie ist es Euch ergangen, $GBursche:Mädchen;? Zeigt mir, was Ihr gelernt habt, seit wir uns das letzte Mal sahen, dann wissen wir, ob ich Euch etwas Neues beibringen kann.' WHERE id=1622;
UPDATE npc_text SET Text0_0='Guter Stahl, $N. Schnappt Euch einen Hammer und kommt her. Ich zeige Euch, wie man eine legendäre Waffe schmiedet.' WHERE id=1634;
UPDATE npc_text SET Text0_0='Was ist? Seht Ihr nicht, dass ich beschäftigt bin?' WHERE id=1635;
UPDATE npc_text SET Text0_0='Ihr wollt noch mehr lernen, $N? Dann kommt her und zeigt mir, dass Ihr bereit dazu seid.' WHERE id=1637;
UPDATE npc_text SET Text0_1='Ich bilde keine Schmiede ohne Fertigkeit aus. Kommt wieder, wenn Ihr etwas vorzuweisen habt.' WHERE id=1640;
UPDATE npc_text SET Text0_1='Ich sehe Potenzial in Euch, $N. Dann schnappt Euch mal einen Hammer und ein paar Stücke Alteisen, Ihr braucht jede Menge Übung.' WHERE id=1641;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-A, einem Sicherheitsterminal der Stufe Alpha.$B$BFür den Zugriff auf die Terminalfunktionen wird eine weiße Lochkarte benötigt.$B$B01000100 01101111 01110111 01101110 00100000 01110111 01101001 01110100 01101000 00100000 01110100 01101000 01100101 00100000 01101000 01101111 01110010 01100100 01100101 00100001' WHERE id=1643;
UPDATE npc_text SET Text0_0='Einführen der weißen Lochkarte erfolgreich...$B$BEs erfolgt Ausgabe einer Einheit der Sicherheitsstufe Beta.$B$B01010000 01101100 01100001 01111001 00100000 01110100 01101000 01100101 00100000 01010111 01100001 01110010 01100011 01100001 01110010 01100110 01110100 00100000 01110010 01101111 01101100 01100101 01110000 01101100 01100001 01111001 01101001 01101110 01100111 00100000 01100111 01100001 01101101 01100101' WHERE id=1644;
UPDATE npc_text SET Text0_1='Die Gordunni sind ein großer Ogerstamm, der beträchtliche Teile der Wälder in Feralas kontrolliert. Ihre dunkle Magie sickert in die Elemente um sie herum ein und schädigt sowohl die Umgebung als auch alles Leben darin.$B$BSolltet Ihr Euch nach Feralas begeben wollen, hütet Euch vor diesen Ogern. Sie sind hinterhältig und einige können mächtige dunkle Magie wirken.' WHERE id=1646;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-B, einem Sicherheitsterminal der Stufe Beta.$B$BFür den Zugriff auf die Terminalfunktionen wird eine gelbe Lochkarte benötigt.$B$B01000111 01101110 01101111 01101101 01100101 01110010 01100011 01111001 00100001' WHERE id=1647;
UPDATE npc_text SET Text0_0='Einführen der gelben Lochkarte erfolgreich... Es erfolgt Ausgabe einer Einheit der Sicherheitsstufe Chi.$B$B01000100 01100101 01110010 00100000 01000111 01101110 01101111 01101101 01100101 01101110 01101011 11110110 01101110 01101001 01100111 00100000 01110100 01110010 11100100 01100111 01110100 00100000 01001110 01100001 01100011 01101000 01110100 01100101 01101100 01100110 01100101 01101110 01110101 01101110 01110100 01100101 01110010 01110111 11100100 01110011 01100011 01101000 01100101' WHERE id=1648;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-C, einem Sicherheitsterminal der Stufe Chi.$B$BFür den Zugriff auf die Terminalfunktionen wird eine blaue Lochkarte benötigt.$B$B01010010 01100101 01100011 01101001 01110000 01100101 00111010 00100000 01001101 01100101 01100011 01101000 01100001 01101110 01101001 01100011 01100001 01101100 00100000 01000011 01101000 01101001 01100011 01101011 01100101 01101110 00100000 01010011 01101111 01110101 01110000' WHERE id=1649;
UPDATE npc_text SET Text0_0='Einführen der blauen Lochkarte erfolgreich... Es erfolgt Ausgabe einer Einheit der Sicherheitsstufe Delta.$B$B01010000 01101100 01100001 01111001 00100000 01001101 01101111 01110010 01100101 00100000 01010111 01101111 01010111' WHERE id=1650;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-D, einem Sicherheitsterminal der Stufe Delta.$B$BFür den Zugriff auf die Terminalfunktionen wird eine rote Lochkarte benötigt.$B$B01010011 01101000 01101111 01110010 01110100 00100000 01100001 01101110 01100100 00100000 01110011 01110111 01100101 01100101 01110100' WHERE id=1651;
UPDATE npc_text SET Text0_0='Einführen der roten Lochkarte erfolgreich... Es erfolgt Ausgabe einer Datenkarte der Sicherheitsstufe Delta.$B$B00111100 01101001 01101110 01110011 01100101 01110010 01110100 00100000 01100110 01110101 01101110 01101110 01111001 00100000 01101101 01100101 01110011 01110011 01100001 01100111 01100101 00100000 01101000 01100101 01110010 01100101 00111110' WHERE id=1652;
UPDATE npc_text SET Text0_0='Eraka no Kimbul$BGott der Tiger, Lord der Bestien, König der Katzen, Verhängnis der Beutetiere$B$BUeetay no Mueh\'zala$BGott des Todes, Vater des Schlafes, Sohn der Zeit, Freund der Nacht.' WHERE id=1653;
UPDATE npc_text SET Text0_0='Elortha no Shadra$B$BGöttin der Spinnen, Mutter des Giftes, Seidentänzerin, Geliebte des Todes.' WHERE id=1654;
UPDATE npc_text SET Text0_0='Der Technologie-Wahrheitsdetektor des Matrix-Prägographen hat Euer Können in Sachen Ingenieurskunst bestätigt. Wählt nun unter einer der folgenden Optionen:$B$B01000111 01101111 00100000 01100111 01101111 00100000 01100111 01101111 00100001 00100001' WHERE id=1655;
UPDATE npc_text SET Text0_0='Zugriff auf Daten gewährt. Einen schönen Tag noch!$B$B01001110 01100001 01110100 01100001 01101100 01101001 01100101 00100000 01010000 01101111 01110010 01110100 01101101 01100001 01101110 00100000 01010010 01101111 01100011 01101011 01110011' WHERE id=1657;
UPDATE npc_text SET Text0_1='Gebt mir einen Moment, $N. Kindal ist unten im Grimmtotemlager und versucht die Feendrachen zu befreien. Wir hatten großes Glück, dass zufällig Hilfe kam. Wer weiß, was die Tauren ihnen sonst angetan hätten.' WHERE id=1673;
UPDATE npc_text SET Text0_0='Willkommen. Ich bin der Wächter des Eingangs. Wenn sich in Eurem Scheibensatz nicht die richtigen Platten von Uldum befinden, werde ich Euch den Zugang zum Areal von Uldum nicht gewähren.' WHERE id=1674;
UPDATE npc_text SET Text0_0='Die Platten von Uldum sind von den Schöpfern hergestellte Scheiben, die den Zugang zum Areal von Uldum ermöglichen. Auf diesen Scheiben sind spezielle Daten zu den Aktivitäten der Schöpfer in dieser Gegend gespeichert.$B$BVom Aussehen und vom Umfang her sind sie fast identisch mit dem Scheibensatz, den Ihr bereits besitzt. Die Zeichen darauf haben jedoch unmittelbar mit Informationen über Uldum zu tun und nicht mit dem Uldaman-Komplex.' WHERE id=1675;
UPDATE npc_text SET Text0_0='Ich kontrolliere den Zugang zum Uldum-Komplex für die Schöpfer. Ich gewähre den Zugang zum Areal nur, wenn der Bittsteller die Scheiben in der richtigen Reihenfolge einlegt.$B$BEuer Satz Scheiben enthalt momentan nicht die Platten von Uldum, die Hauptvoraussetzung für einen Zugang sind. Zugang wird erst bei vorhandenen Platten von Uldum gewährt.' WHERE id=1676;
UPDATE npc_text SET Text0_0='Die Schöpfer haben mehrere Sätze der Platten von Uldum zur ihrem speziellen Zweck geschaffen. Die Schöpfer kontrollieren die Verteilung der besagten Scheiben für alle Bereiche der Welt. So ist denn auch ein erneuter Besuch Azeroths geplant. In Zukunft könnten daher neue Scheiben in Umlauf kommen.$B$BDie vorhandenen Scheiben wurden nur an jene Gruppen ausgegeben, die gewisse Sicherheitsanforderungen erfüllten.' WHERE id=1677;
UPDATE npc_text SET Text0_0='Die Schöpfer nutzen Besuche, wenn sie die Kontrolle über von ihnen erschaffene Welten zurückerlangen wollen. Dies geschieht, wenn externe oder interne Kräfte die Matrixdynamik der jeweiligen Welt durcheinander bringen. Man sollte diese Maßnahmen der Schöpfer nicht auf die leichte Schulter nehmen.$B$BWeitere Informationen zum geplanten Besuch dieser Welt entnehmt Ihr bitte dem entsprechenden Datenspeicher, der alle Pläne der Schöpfer hinsichtlich eines Besuchs enthält.' WHERE id=1678;
UPDATE npc_text SET Text0_0='Uldum ist ein Forschungsgebiet der Schöpfer. Dort versuchen sie, die Biosphäre von Azeroth zu verbessern. Genauere Angaben zum Wirken der Schöpfer in Uldum sind Personen vorbehalten, die in ihrem Satz Scheiben die Platten von Uldum haben.$B$BInnerhalb des Uldum-Komplexes gibt es Datenspeicher, die Informationen zu bestimmten Experimenten sowie Datenzusammenstellungen enthalten. Weitere Informationen enthaltet Ihr dort.' WHERE id=1679;
UPDATE npc_text SET Text0_1='Dem Licht sei Dank, dass Ihr hier seid! Im Augenblick würde ich auch Hilfe von einem zweiköpfigen Oger annehmen.$B$BDie Stadt ist von Troggs überrannt worden! Versuche, sie zu vernichten, endeten mit einer Massenverseuchung. Aber noch gibt es Hoffnung.$B$BUnsere nach Eisenschmiede evakuierten Wissenschaftler arbeiten an einem Heilmittel für die durch die Ablagerung ausgelöste Erkrankung. Wenn Ihr mir helft, die Invasion der Troggs aufzuhalten, können wir in der Zwischenzeit vielleicht die Stadt zurückerobern.' WHERE id=1693;
UPDATE npc_text SET Text0_0='Gebt mir ein paar Minuten, $N. Eben gab es hier einige Aufregung und ich will rasch schauen, ob alles wieder ruhig ist. Ich setze mich bald mit Euch in Verbindung.' WHERE id=1713;
UPDATE npc_text SET Text0_0='Willkommen bei \'Blaumond Dies-und-Das\', $C. Lasst es mich wissen, wenn Ihr Fragen habt.' WHERE id=1734;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-A, einem Sicherheitsterminal der Stufe Alpha.$B$BWeiße Lochkarte erfasst. Zugriff gewährt.$B$B01010100 01100101 01101100 01101100 00100000 01111001 01101111 01110101 01110010 00100000 01100110 01110010 01101001 01100101 01101110 01100100 01110011 00100000 01110100 01101111 00100000 01110000 01101100 01100001 01111001 00100000 01010111 01101111 01010111' WHERE id=1753;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-B, einem Sicherheitsterminal der Stufe Beta.$B$BGelbe Lochkarte erfasst. Zugriff gewährt.$B$B01001011 01101001 01110011 01110011 00100000 01101101 01100101 00101100 00100000 01001001 00100111 01101101 00100000 01100111 01101110 01101111 01101101 01101001 01110011 01101000 00100001' WHERE id=1754;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-C, einem Sicherheitsterminal der Stufe Chi.$B$BBlaue Lochkarte erfasst. Zugriff gewährt.$B$B01000111 01101110 01101111 01101101 01100101 01110010 01100101 01100111 01100001 01101110 00100000 01100110 01101111 01110010 01100101 01110110 01100101 01110010 00100001' WHERE id=1755;
UPDATE npc_text SET Text0_0='Brzzzzt!$B$BWillkomen beim Matrix-Prägographen 3005-D, einem Sicherheitsterminal der Stufe Delta.$B$BRote Lochkarte erfasst. Zugriff gewährt.$B$B01001000 01101111 01110111 00100000 01100001 01101101 00100000 01001001 00100000 01100100 01110010 01101001 01110110 01101001 01101110 01100111 00111111' WHERE id=1756;
UPDATE npc_text SET Text0_0='Den Geistern, oder dem "Muisek", der Kreaturen, gegen die wir kämpfen, wohnt unglaubliche Macht inne.$B$BSeht in Euch selbst hinein und bündelt die Macht Eures eigenen Geistes, dann werdet Ihr mächtiger sein, als Ihr es Euch je erträumt habt.' WHERE id=1757;
UPDATE npc_text SET Text0_0='Sul\'thraze... als ich diesen Namen zum ersten Mal vernahm, war ich wohl in Eurem Alter. Es bedeutete mir damals nichts, so wie es Euch heute nichts bedeutet. Erst als eine Trollkriegsmeute aus Zul\'Farrak das Land verwüstete, verstand ich... und bekam Angst.$B$BViele werden Euch erzählen, dass die Spaltklinge des Leidens nicht existiert. Dass sie ein Mythos sei, ein Märchen, mit dem man unartige Kinder ängstigt. Aber glaubt mir, $R, es gibt sie.$B$B<Trenton fährt sich geistesabwesend mit der Hand über die gezackte Narbe in seinem Gesicht.>' WHERE id=1758;
UPDATE npc_text SET Text0_0='In zwei Teile gespalten, um eine Einheit zu bilden. Die Zwillingsklingen sind an Zul\'Farrak gebunden.$B$BSul\'thraze wird wiedergeboren, wenn die Klingen gefunden sind.' WHERE id=1759;
UPDATE npc_text SET Text0_0='Die Wachen von Theramore sind die besten Soldaten der Allianz in Kalimdor. Auch fern unserer Heimat ist es unsere Pflicht, die Gesetze der Allianz zu wahren.' WHERE id=1793;
UPDATE npc_text SET Text0_0='Wir wissen nicht viel über den Brand im Gasthaus Zur süßen Ruh, nur dass James Hyals Frau und Kind den Flammen nicht entkommen konnten.$B$BJames konnten wir retten, aber seither ist er ein anderer Mensch.$B$BWir haben viele Hinweise, aber keine heiße Spur. Unsere Hoffnung ist, dass wir durch die Wächter und die Mithilfe der Bevölkerung von Theramore dieses schreckliche Verbrechen aufklären können.$B$BFalls Ihr bei den Ermittlungen helfen wollt, findet Ihr die Überreste des Gasthauses an der Grenze zum Brachland.' WHERE id=1794;
UPDATE npc_text SET Text0_0='Könnt Ihr sie sehen, sterbliches Wesen? Nein... Hätte mich auch gewundert. Sehend, ohne sehen zu können - das ist Eure Gabe und Euer Fluch.' WHERE id=1796;
UPDATE npc_text SET Text0_0='Im Krieg war Razelikh der Entweiher einer der gefürchtetsten Offiziere von Lord Kazzak. Als die Legion besiegt und Kazzak zum Rückzug in die faulende Narbe gezwungen worden war, bekam Razelikh von seinem Meister den Auftrag, die verwüsteten Lande zu sichern.$B$BWährend die Allianz in unbedeutende Scharmützel mit der Horde über Gebiete außerhalb der verwüsteten Lande verstrickt war, vereinigte Razelikh die verschiedenen Sekten des Schattenrats sowie die grobschlächtigen Schreckensfelsoger des Landes.' WHERE id=1813;
UPDATE npc_text SET Text0_0='Tragischerweise war es Thralls Weisheit, die letztlich den Bund zwischen dem Dämonenlord und seinen Dienern besiegelte. Trebors Regiment fungierte dabei als Katalysator für die Einheit zwischen Dämonenlord, Schattenanbeter-Sekten und Ogern. Die Aussicht auf nahezu unbegrenzte Macht und die Gewährung von unsterblichen Schocktruppen für jeden Anführer besiegelten den Handel mit Blut.' WHERE id=1814;
UPDATE npc_text SET Text0_0='Was Sevine, Allistarj und Grol nicht erkannten, war, dass sie ebenso an das Land gebunden sein würden wie ihre Diener... Denn, seht Ihr, der Preis für ihre Unsterblichkeit war Gefangenschaft und ewige Dienerschaft unter dem Befehl von Razelikh. Seine grausame Schlauheit hatte Razelikh einmal mehr als Sieger hervorgehen lassen.' WHERE id=1815;
UPDATE npc_text SET Text0_0='Ihre Hirne sind nun besudelt, $R. Ja, sogar noch mehr als zuvor. Sie leben nur, um Razelikh zu dienen und ihn zu beschützen. Die Fragmente des Amuletts, die sie bei sich tragen, sind der Schlüssel zur Beschwörung des Dämonenlords. Was Ihr indes nicht zu verstehen scheint, ist, dass man sie nicht auf konventionelle Weise verletzen kann.' WHERE id=1816;
UPDATE npc_text SET Text0_0='Selbst falls Ihr es schafft, den Bann zu erzeugen, mit dem Ihr den dämonischen Zauberschutz durchbrechen könnt, unter dem die drei stehen... Was würdet Ihr dann tun? Dem Entweiher in seinem eigenen Unterschlupf gegenübertreten?$B$B<Loramus lacht.>' WHERE id=1817;
UPDATE npc_text SET Text0_0='Die Menschen hatten einen großen Teil der Düstermarschen an sich gerissen, aber der Steinbrecherklan konnte sie mit unserer Hilfe zurückdrängen.' WHERE id=1818;
UPDATE npc_text SET Text0_0='Die Menschen bauten ein Gasthaus an der Grenze des Brachlandes. Gasthaus Zur süßen Ruh, so nannten sie es. Vor Wochen wurde es niedergebrannt.$B$BDer Kriegshäuptling sagte uns, wir sollten die Menschen in Ruhe lassen, solange sie unsere Grenzen nicht überqueren, aber offenbar handelte jemand seinem Wunsch zuwider.$B$BEr hat mir den Auftrag gegeben, der Sache auf den Grund zu gehen. Wenn Ihr mir helfen wollt, geht zu den Überresten des Gasthauses Zur süßen Ruh und bringt alle Hinweise, die Ihr finden könnt.' WHERE id=1819;
UPDATE npc_text SET Text0_0='Wenn Reethe etwas mit dem Brand im Gasthaus Zur süßen Ruh zu tun hat, müsst Ihr ihn finden und Antworten von ihm verlangen.$B$BIch habe Ogron losgeschickt, um ihn aufzuspüren. Er brach nach Südosten in Richtung der Hauptstraße durch die Marschen auf. Trefft Euch mit Ogron und geht dann Reethe suchen.' WHERE id=1820;
UPDATE npc_text SET Text0_1='Ich will das gar nicht hören, $N. Ihr werdet unter gar keinen Umständen in die sengende Schlucht gelassen. Unmöglich. Das wird nicht passieren. Wie oft soll ich Euch noch sagen, dass ich Euch nicht einlassen kann? Seid Ihr taub? Seid Ihr ein verkleideter Orc? N-E-I-N. NEIN!$B$BWenn ich Euch reinlasse, muss ich alle reinlassen: erst Eure besten Freunde, dann entfernte Verwandte, dann deren Freunde und so weiter. Als das zuletzt geschah... na, darüber sollten wir gnädig schweigen!' WHERE id=1833;
UPDATE npc_text SET Text0_1='Seid Ihr toll? Wisst Ihr, was für Bestien diesen Ort bevölkern? Schlimmstenfalls könnte passieren, dass ich Eure Überreste mit einer Schaufel aufsammeln muss. Wir haben schon manch guten Zwerg an diese Ungeheuer verloren.$B$BDie Antwort lautet nein. Wenn Ihr unbedingt in die sengende Schlucht wollt, müsst Ihr es anders anstellen.' WHERE id=1834;
UPDATE npc_text SET Text0_1='Ich sollte das wahrscheinlich tunlichst für mich behalten, aber wenn Ihr es schafft, an all den Tieren und Bestien des Ödlands vorbeizukommen, dann erreicht Ihr vielleicht auch die sengende Schlucht.$B$BJa, das Ödland. Es gibt eine Art Schleichweg, den die Orcs bei Kargath angelegt haben. Das soll Euch nur zeigen, mit was für Dummköpfen wir es hier zu tun haben! Eine Hintertür zur Hölle? Was haben die sich bloß dabei gedacht?' WHERE id=1835;
UPDATE npc_text SET Text0_1='Unverwundbar?' WHERE id=1836;
UPDATE npc_text SET Text0_1='Ihr seid ein Dummkopf und solltet auf mich hören. Niemand ist unverwundbar. Ihr tätet gut daran, das zu beherzigen.' WHERE id=1837;
UPDATE npc_text SET Text0_1='Ich sag Euch was: Da Ihr Euch selbst als Helden zu betrachten scheint, bahnt Euch einen Weg durch das Ödland zur sengenden Schlucht und bringt mir Beweise Eurer Heldentaten. Dann sehe ich mal, ob ich Euch einen Schlüssel besorgen kann.$B$BGanz im Südosten der sengenden Schlucht lebt eine Frau, die fast so hart ist wie ich. Man nennt sie Margol die Wütende. Vielleicht solltet Ihr dort mal nachforschen.' WHERE id=1838;
UPDATE npc_text SET Text0_0='Was Ihr in einem Gasthaus tun könnt?$B$BNun, in einem Gasthaus genießt Ihr einen sehr komfortablen Aufenthalt. Deshalb fühlt Ihr Euch auch schneller wieder "gut erholt" als draußen in der Wildnis. Und wenn Ihr gut erholt seid, lernt Ihr mehr aus Euren Erfahrungen.$B$BAußerdem könnt Ihr beim Gastwirt einen Ruhestein erwerben, mit dem Ihr dann später schneller wieder zum Gasthaus zurückgelangt.', Text0_1='Was Ihr in einem Gasthaus tun könnt?$B$BNun, in einem Gasthaus genießt Ihr einen sehr komfortablen Aufenthalt. Deshalb fühlt Ihr Euch auch schneller wieder "gut erholt" als draußen in der Wildnis. Und wenn Ihr gut erholt seid, lernt Ihr mehr aus Euren Erfahrungen.$B$BAußerdem könnt Ihr beim Gastwirt einen Ruhestein erwerben, mit dem Ihr dann später schneller wieder zum Gasthaus zurückgelangt.' WHERE id=1853;
UPDATE npc_text SET Text0_0='Wir nennen dieses Land zwar Brachland, doch wer näher hinsieht, der bemerkt, dass der Ort voller Leben steckt.' WHERE id=1875;
UPDATE npc_text SET Text0_0='Ich verkaufe die beste Stoff- und Lederkleidung diesseits des Tales!' WHERE id=1914;
UPDATE npc_text SET Text0_1='Ihr seid weiter gekommen als viele andere, $N. Wenn Ihr die Elemente um Euch herum respektiert, dann wird es Euch weit besser ergehen als den meisten anderen.' WHERE id=1916;
UPDATE npc_text SET Text0_0='Der Altar scheint Euch anzuziehen, so, als sei er lebendig.' WHERE id=1918;
UPDATE npc_text SET Text0_0='Lobet Elune! Es ist schon viele Jahre her, dass ich Besucher hatte.' WHERE id=1920;
UPDATE npc_text SET Text0_0='Als junger Nachtelf in Dunkelküste kreuzten sich meine Wege einst mit einem widerlichen Pack, das sich der Schattenhammerklan nannte. Mit einigen von ihnen freundete ich mich an und zu meiner Schande muss ich gestehen, dass ich meinen Glauben an Elune leugnete und die Parolen des Schattenhammerklans begeistert annahm.$B$BErst eine Vision von Elune ließ mich aus diesem bösen Traum erwachen, in dem ich als Anhänger der alten Götter gefangen war.$B$BVoller Reue begann ich mit der Arbeit an dem Gebet für Elune.' WHERE id=1921;
UPDATE npc_text SET Text0_0='Ich ätzte das Zeugnis meiner Reue, meiner Scham, meines Bedauerns und meiner Trauer in eine Schrifttafel aus reinstem Gold. Leider fand der Schattenhammerklann die Tafel und brandmarkte mich als Ketzer. Ich wurde hingerichtet, ehe ich mein Gebet an Elune fertig stellen konnte. Nun bin ich als Geist an diese Welt gefesselt und kann nicht hinüber ins Nether; gefangen in einem Käfig als Warnung an all jene, die den Glauben des Schattenhammerklans verraten.' WHERE id=1922;
UPDATE npc_text SET Text0_0='Der alte Altar von Hakkar steht stumm am Eingang des Raums.$B$BAls Ihr zu den Altären darüber hinaufschaut, bemerkt Ihr große, schlangenähnliche Statuen, die darauf ruhen. Die Statuen sehen genau wie die auf dem Altar vor Euch aus. Es muss einen Zusammenhang zwischen ihnen geben...', Text0_1='Der alte Altar von Hakkar steht stumm am Eingang des Raums.$B$BAls Ihr zu den Altären darüber hinaufschaut, bemerkt Ihr große, schlangenähnliche Statuen, die darauf ruhen. Die Statuen sehen genau wie die auf dem Altar vor Euch aus. Es muss einen Zusammenhang zwischen ihnen geben...' WHERE id=1923;
UPDATE npc_text SET Text0_0='In dieser Gegend leben viele Kreaturen, die als Verbündete der Nachtelfen gelten. Die Elfen hatten immer schon Zugang zu der Macht in diesen Kreaturen. Es ist an der Zeit, dass auch der Horde diese Genugtuung zuteil wird. Ich habe eine Möglichkeit gefunden, wie wir diese Macht fassen und nutzen können.$B$BZweifelt nicht an meinen Worten, $N. Helft mir und Ihr werdet sehen, dass ich die Wahrheit sage.' WHERE id=1924;
UPDATE npc_text SET Text0_0='Wenn Schneidern Euer Metier ist, dann hat Vizzklick wahrscheinlich, was Ihr braucht. Seht Euch nur um, $N, und sagt mir, wenn Ihr etwas zu kaufen wünscht.' WHERE id=1933;
UPDATE npc_text SET Text0_0='Packt Eure Sachen, $N! Ich habe soeben von Nilith Lokrav in der sengenden Schlucht gehört, dass der Untermarkt mitten im Kessel aktiv werden will. Sucht Nilith Lokrav in der sengenden Schlucht auf und besorgt Euch die nötigen Informationen!' WHERE id=1934;
UPDATE npc_text SET Text0_0='Ein helles Licht erscheint auf der anderen Seite des Raums und verschwindet wieder. Dann seht Ihr noch ein Licht aufleuchten... und noch eines.$B$BSie scheinen Euch ein Muster zu zeigen.', Text0_1='Ein helles Licht erscheint auf der anderen Seite des Raums und verschwindet wieder. Dann seht Ihr noch ein Licht aufleuchten... und noch eines.$B$BSie scheinen Euch ein Muster zu zeigen.' WHERE id=1935;
UPDATE npc_text SET Text0_0='Die Rache wird mein sein.' WHERE id=1953;
UPDATE npc_text SET Text0_0='Mein Vater wurde von dem Abschaum im Kessel niedergemetzelt. Mein Flehen um Beistand stieß beim Magistrat von Sturmwind auf taube Ohren. Nein, sie wollen nicht einmal zugeben, dass es diesen Teil der Welt überhaupt gibt, geschweige denn ihr tolles Militär hinschicken, um dort für Recht und Ordnung zu sorgen!$B$BIch will Rache, $R, ganz einfach. Gibt es einen besseren Beweggrund?' WHERE id=1954;
UPDATE npc_text SET Text0_0='Wie Ihr seht, bin ich nur ein einzelner Mann und mein Knappe ist ein einfältiger Blödian. Gegen die Macht der Zwerge von Dunkeleisen und der Bestien, die sich in Ragnaros\' eisernem Griff befinden, hätte ich sicherlich keine Chance. Aber mit einem so mutigen und ehrgeizigen Abenteurer, wie Ihr einer seid, könnten wir der Bewohner des Kessels Herr werden. Wenn Ihr mir helft, werde ich Euch reich machen - reicher, als Ihr Euch das je hättet träumen lassen!' WHERE id=1955;
UPDATE npc_text SET Text0_0='Ich heiße Euch herzlich willkommen, $R. Ihr habt die normalen Pfade weit hinter Euch gelassen, nur um mit jemandem zu sprechen, der in einer Höhle haust. Ich vermute, Euer unerwarteter Besuch hier hat einen Grund. Ja?' WHERE id=1973;
UPDATE npc_text SET Text0_0='Seid gegrüßet $Gwerter Herr:holde Dame;. Ich bin Meister Xylems Lehrling Nyrill. Sollte Euch etwas bedrücken, so habt keine Scheu, es meinem Meister zu sagen.$B$BIch stehe Euch zu Diensten, wenn Ihr bereit seid, wieder zum Fuß des Berges zurückzukehren.' WHERE id=1994;
UPDATE npc_text SET Text0_0='Ich weiß, wer Ihr seid, $N. Das Schicksal und Kräfte, stärker noch als das Schicksal, haben Euch zu mir geführt. Dies hat einen Grund, wenn er mir auch nicht gefallen will. Ihr werdet in Zukunft viele Entscheidungen fällen und ich hoffe, Ihr seid weise genug, das Richtige zu tun.$B$BGlaubt mir, $R, wenn ich Euch versichere: Es ist möglich, die richtige Wahl zu treffen.' WHERE id=1995;
UPDATE npc_text SET Text0_0='Ihr seid ziemlich neugierig, aber eine solche Neugier deutet auch immer auf einen Mangel an Verständnis hin. Ich sage dies, weil die von Euch gestellte Frage sinnlos wäre, hätte Euer Hiersein einen Sinn. $B$BDamit wünsche ich Euch einen guten Tag.' WHERE id=1996;
UPDATE npc_text SET Text0_0='Ihr wisst, dass ich Eranikus kenne, sterbliches Wesen. Ich versuche nicht, Euch etwas vorzumachen, und rate Euch, es mir gleichzutun.$B$BIhr seid hier, weil Ihr wisst, dass Euch das, was Ihr im Versunkenen Tempel von Atal\'al in Euren Besitz gebracht habt, zu einem bestimmten Zweck zu mir geführt hat. Dieser Zweck ist, meinen Gefährten... meinen Bruder zur Ruhe zu betten. Er ist in einem Alptraum innerhalb eines Traums gefangen. Und allein kann er sich nicht befreien.' WHERE id=1997;
UPDATE npc_text SET Text0_0='Der Gott, den die Atal\'ai hervorbrachten ist dafür verantwortlich. Wir haben die körperliche Manifestation des Gottes vernichtet und seinen Tempel versenkt, aber der Gott war viel stärker, als wir es erwartet hatten. Die Saat der Verderbnis wurzelt tief und befällt alles, was ihr in die Quere kommt.$B$BEranikus wurde damit betraut, den Versunkenen Tempel zu bewachen und aufzupassen, dass weder die einfachen Atal\'ai noch ihr Gott wiederkehren. Er war ein unschuldiges Opfer der Verderbtheit und wir unterschätzten ihre Wirkung.' WHERE id=1998;
UPDATE npc_text SET Text0_0='Dass Ihr hier seid, ist ein erster Schritt... eine richtige Entscheidung, wenn Ihr so wollt. Ihr helft damit Yseras Drachenschwarm.$B$BMöglicherweise seid Ihr aus purer Gier hier oder weil Ihr Euch moralisch verpflichtet fühlt. Keiner der beiden Gründe ist für sich allein wichtig. Aber beide sind in einer Hinsicht bedeutsam: Eranikus\' Pein hat mich aus meinem Traum geschreckt und meine Aufgabe ist es nun, seine Qual zu beenden. Ich bitte Euch hiermit, mir dabei zu helfen.' WHERE id=1999;
UPDATE npc_text SET Text0_0='Interessant, interessant... Ich weiß von einer Druidin, die in den Hügeln über den Höhlen des Wehklagens lebt, $N. Sie kann Euch vielleicht mehr über diesen Splitter sagen.$B$BUnd jetzt muss ich zurück an die Arbeit!' WHERE id=2013;
UPDATE npc_text SET Text0_0='Die Tage, die wir hier in Azshara verbracht haben, gehören sicher nicht zu unseren besten und glorreichsten, aber wir sind noch jung und noch fließt Blut in unseren Adern. Hier werden wir den Sieg erringen. Es ist nur eine Frage der Zeit.' WHERE id=2033;
UPDATE npc_text SET Text0_0='Belgrom hat uns aus Orgrimmar hergeschickt, um ihm in dieser wenig klugen Allianz beizustehen, aber wenn sich sein Wagnis auszahlt, ist es für die ganze Horde von großem Vorteil.$B$BZu viele Orcs mussten schon ihr Leben lassen, weil er den Weibern imponieren wollte, aber er ist trotzdem noch immer unser Befehlshaber. Die Ehre gebietet uns, das Beste aus der Situation zu machen.' WHERE id=2034;
UPDATE npc_text SET Text0_1='Ich bin nicht der Ehre wegen hier, $N Ich bin wegen des Hochmuts eines einzigen Orcs hier.$B$BHätte ich Belgroms Angebote nicht ausgeschlagen, so würde ich mich jetzt sicherlich in Orgrimmar um die wirklich wichtigen Dinge kümmern.$B$BSein Verlangen nach Frauen ist auch den Kriegerinnen von Durotar nicht unbekannt, aber er spielt trotzdem weiter den Traumprinzen.$B$BEr kann allerdings von Glück sagen, dass er sein Verlangen nach mir nicht noch weiter verfolgt hat, sonst würden ihm heute nicht nur die Finger seiner Hände fehlen.' WHERE id=2035;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $N. Wie ich sehe, seid Ihr aus Orgrimmar hergekommen, um uns zu helfen. Das freut mich. Ich will diesen verfluchten Wald so schnell wie möglich hinter mir lassen. Da sitze ich doch viel lieber in Orgrimmar und vertreibe mir die Zeit bei einem guten Bier.$B$BObwohl ich es gar nicht erwarten kann zu hören, wie Euch Belgrom überredet hat, hierher zu kommen.' WHERE id=2036;
UPDATE npc_text SET Text0_0='Was führt Euch zum Zirkel des Cenarius, $N?' WHERE id=2037;
UPDATE npc_text SET Text0_0='Ich weiß, ich weiß, ich befinde mich hier am Ende der Welt!$B$BEigentlich stamme ich ja aus Ratschet, aber nachdem ich hier mit meinem Zeppelin abgestürzt war, dachte ich, ich könnte auch gleich mit den Forschungen beginnen, deretwegen ich hergekommen bin.' WHERE id=2038;
UPDATE npc_text SET Text0_0='Dieser Geist schwebt über seinem Leichnam. Seine geisterhaften Augen starren durch Euch hindurch in weite Ferne...' WHERE id=2039;
UPDATE npc_text SET Text0_1='Das Land Feralas hat viel durchgemacht, $R. Erde, Bäume, Lebewesen - sie alle verändern sich und was Feralas ist und bleibt.$B$BDie Zwillingskolosse über uns sind die einzige Konstante, bleiben unverändert inmitten all der Geschehnisse...' WHERE id=2053;
UPDATE npc_text SET Text0_0='Ich biete den Bedürftigen meine Schwingen für einen sicheren Abstieg an.' WHERE id=2054;
UPDATE npc_text SET Text0_0='Tja, jetzt, da Ihr es erwähnt... ja, gestern Nachmittag ist mir wirklich was Komisches aufgefallen.$B$BIch rannte gerade an ein paar Trollruinen südöstlich von hier vorbei... Genau genommen hüpfte ich dran vorbei, wenn Ihr\'s wissen wollt... Der Sand ist so unglaublich heiß!$B$BJedenfalls hoppelte ich an den Ruinen vorbei, da seh ich doch, dass einer sein Lager aufgeschlagen hat. Ist ja schon ein komischer Platz für ein Lager, wenn Ihr mich fragt... Ich hätte mich ja noch ein bisschen genauer umgesehen, aber da hat mich so ein blöder Riesengeier weggejagt.' WHERE id=2055;
UPDATE npc_text SET Text0_0='Danke, dass Ihr mich gerettet habt!$B$BIch bin ein Koch und Alchemist aus Sturmwind. Für Rezepte meines Onkel Angus war ich auf der Suche nach Kräutern, die es nur hier gibt, als mich diese Stacheleber gefangen nahmen. Wenn Ihr mich nicht gefunden hättet, wäre ich sicher im Kochtopf gelandet!' WHERE id=2113;
UPDATE npc_text SET Text0_0='Hm. Ja, ich habe da tatsächlich ein Rezept, das etwas für Euch sein könnte. Ich will es Euch lehren!' WHERE id=2114;
UPDATE npc_text SET Text0_0='Nun ja, ich kenne da tatsächlich ein seltenes alchimistisches Rezept. Ich will es Euch lehren!' WHERE id=2115;
UPDATE npc_text SET Text0_0='Seid gegrüßt! Falls es Euer Begehr ist, auf Baupläne der Gobliningenieurskunst zuzugreifen, haltet jetzt bitte Eure Mitgliedskarte der Gobliningenieure bereit.' WHERE id=2133;
UPDATE npc_text SET Text0_0='Seid gegrüßt! Falls es Euer Begehr ist, auf Baupläne der Gnomeningenieurskunst zuzugreifen, haltet jetzt bitte Eure Mitgliedskarte der Gnomeningenieure bereit.' WHERE id=2134;
UPDATE npc_text SET Text0_0='Guten Tag auch, $GBursche:Mädchen;, mein Name ist Glotz und diese schier unglaublichen Gnomengeräte in Eurem Besitz gehen vielleicht auf mein Konto! Wenn sie in die Luft geflogen sind, waren es nicht meine. Wenn sie Euch reich gemacht haben, können wir hingegen gern über Tantiemen reden!$B$BOh, und falls Ihr aus Gnomeregan kommt, dann könnt Ihr Euch ebensogut zwei Neunziggradwinkel schnappen, sie aneinanderkleben und den Heimweg antreten. Ich habe Euch nichts zu sagen.' WHERE id=2135;
UPDATE npc_text SET Text0_0='Na, wenn das nicht $N ist! Ich freue mich, Euch zu sehen. Ich kann Euch gar nicht sagen, wie dankbar ich Euch für all die Hilfe bei meinen Erfindungen bin!$B$BWas kann ich heute für Euch tun?' WHERE id=2136;
UPDATE npc_text SET Text0_0='Ich bin Tüftlermeister Oberfunks, Chefarchitekt für moderne gnomische Ingenieursleitsätze in der heutigen Welt. Meine Aufgabe ist es, dafür zu sorgen, dass die Kraft der Erfindung auch weiterhin von unseren Bogenlichtschraubenschlüsseln ausgeht!$B$BUnd falls Ihr zufällig gerade ein gutes Mittel zur Strahlungseliminierung wisst... würden wir im Moment zufälligerweise sehr daran interessiert sein...' WHERE id=2137;
UPDATE npc_text SET Text0_0='Lasst mich Euch ein bisschen Nachhilfe in Mathematik geben: "Goblin" plus "Ingenieurskunst" gleich FACHWISSEN, Baby. Jeder, der etwas anderes behauptet, hat keine Ahnung.' WHERE id=2138;
UPDATE npc_text SET Text0_0='Durch meine Studien ist mir jetzt klar, dass ihn die Niederlage des Avatars des Gottes Hakkar geschwächt hat. Doch der Großteil seiner Kraft ist noch da und wartet am anderen Ende des Nether.$B$BIhr habt seinen Avatar bereits einmal besiegt, $N. Wenn Euch das noch einmal gelänge, könntet Ihr den Gott vielleicht so weit schwächen, dass wir ihm eines Tages in seiner wahren Gestalt gegenübertreten können.$B$BIch habe einen Zauber vorbereitet, mit dem Ihr abermals einen Avatar Hakkars im Sanktum des gefallenen Gottes beschwören könnt. Wollt Ihr ihm nochmals gegenübertreten?' WHERE id=2139;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Zwar haben Zeit und Trunksucht meine Sinne getrübt, doch sehe ich mit dem inneren Auge noch immer gut.' WHERE id=2140;
UPDATE npc_text SET Text0_0='Hallo, $N. Ich bilde hier die jungen Hippogryphe aus und versorge sie. Seht Euch ruhig um.' WHERE id=2153;
UPDATE npc_text SET Text0_0='Druiden spüren die Not jeglicher Lebewesen dieser Welt und reagieren sehr sensibel darauf. Als klar wurde, dass diese Vögel in Gefahr sind, haben wir uns überlegt, wie wir sie vor der Verderbnis von Feralas retten könnten.$B$BEine Gnomin namens Curgle Kurbelhüpf hat sich der Sache angenommen. Sie sammelt die Eier ein und bringt sie hierher, wo ich mich dann um sie kümmere.$B$BUnterstützung für unsere Sache können wir immer gebrauchen, $N. Vielleicht solltet Ihr Euch mal nach Gadgetzan begeben und mit Curgle reden.' WHERE id=2154;
UPDATE npc_text SET Text0_0='Habt Dank, $Gkleiner:kleine:r; $R!' WHERE id=2155;
UPDATE npc_text SET Text0_0='Eure Kochkünste lassen doch noch zu sehr zu wünschen übrig. Wenn Ihr etwas mehr Übung habt, kann ich Euch vielleicht lehren, was ich weiß.' WHERE id=2173;
UPDATE npc_text SET Text0_1='Das Arkane verdirbt nur die Schwachen. Selbst wenn Ihr dem Pfad des Hexenmeisters nicht folgt, solltet Ihr das niemals vergessen.' WHERE id=2175;
UPDATE npc_text SET Text0_0='Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.', Text0_1='Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.' WHERE id=2193;
UPDATE npc_text SET Text0_0='Ich bin für immer an diese verfluchte Welt gebunden. Was wünscht Ihr, Sterblicher?' WHERE id=2213;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $N. Seid Ihr gekommen, um die Geschichte von Eisenschmiede kennen zu lernen?' WHERE id=2234;
UPDATE npc_text SET Text0_1='Gebt mir Bescheid, wenn Ihr bereit seid, die Geschichte anzuhören.' WHERE id=2235;
UPDATE npc_text SET Text0_1='Vor Jahrhunderten ragte die große Stadt Thaurissan stolz über einem fruchtbaren Land empor, das heute die verwüstete brennende Steppe ist.$B$BThaurissan war die Heimat der Nation der Dunkeleisenzwerge und zu jener Zeit Zentrum eines Bürgerkriegs, der unter dem Zwergenvolk tobte.$B$BSeht, dieser Teil der Welt war einstmals ein Teil des Rotkammgebirges - bis zu dem Tag, als Thaurissan von einer gewaltigen Explosion vernichtet wurde. Die Stadt ward dem Erdboden gleich, das Umland eine Aschenwüste.' WHERE id=2236;
UPDATE npc_text SET Text0_1='Das wissen wir nicht.$B$BDie überlebenden Dunkeleisenzwerge zogen in die Berge und begannen mit dem Wiederaufbau ihrer Stadt.$B$BAus der Asche von Thaurissan entstanden dann die Schwarzfelsspitze und die neue Dunkeleisennation. In dem Gebiet der sengenden Schlucht, das der Kessel genannt wird, wurden weitere Befestigungen errichtet.$B$BGerüchte über etwas uraltes Böses, das das Königreich der Dunkeleisenzwerge beherrschte, machten die Runde.' WHERE id=2237;
UPDATE npc_text SET Text0_1='In letzter Zeit sah man die Dunkeleisenzwerge wieder regelmäßiger an der Oberfläche. Im Ödland wurden Festungen erbaut. Auf der Suche nach der alten Lehre schickte man gewaltige Erforschungstrupps in die Tiefen von Uldaman. Sie sind sogar bis zu unserer Heimat vorgestoßen und dort gingen sie beim Loch gegen unseren Damm vor.' WHERE id=2238;
UPDATE npc_text SET Text0_1='Wir brauchen Abenteurer zu unserer Unterstützung, $N. Werdet Ihr helfen?' WHERE id=2239;
UPDATE npc_text SET Text0_0='Äh, hallo! Falls Ihr hier seid, um ein Honorar einzutreiben und/oder wegen einer Vorladung, dann müsst Ihr mich verwechseln. Tut mir schrecklich leid!' WHERE id=2253;
UPDATE npc_text SET Text0_1='Ich glaube, meine Halskette ist in den See gefallen. Mein Papa hat sie mir geschenkt. Jetzt wird er bestimmt schrecklich wütend.' WHERE id=2273;
UPDATE npc_text SET Text0_1='Danke, $Gwerter Herr:werte Dame;, dass Ihr meine Halskette gefunden habt! Ihr seid \'ne Wucht!' WHERE id=2274;
UPDATE npc_text SET Text0_0='Außer gegen die Grunzer da draußen kämpften meine Brüder und ich in der Schlacht von Hyjal. Alle, die die Bank bestehlen wollen, bekommen es zuerst mit uns zu tun.', Text0_1='Außer gegen die Grunzer da draußen kämpften meine Brüder und ich in der Schlacht von Hyjal. Alle, die die Bank bestehlen wollen, bekommen es zuerst mit uns zu tun.', Text1_0='Ich garantiere mit meinem Blut für die Sicherheit dieser Bank, genügt Euch das?', Text1_1='Ich garantiere mit meinem Blut für die Sicherheit dieser Bank, genügt Euch das?', Text2_0='Würdet Ihr Eure Waren lieber einem Goblin anvertrauen?', Text2_1='Würdet Ihr Eure Waren lieber einem Goblin anvertrauen?' WHERE id=2275;
UPDATE npc_text SET Text0_0='Hallo, $Gwerter Herr:werte Dame;, hättet Ihr wohl ein bisschen Zeit und könntet uns helfen? Bitte?' WHERE id=2276;
UPDATE npc_text SET Text0_0='Nochmals vielen Dank, dass Ihr Hilary geholfen habt, $Gwerter Herr:werte Dame;! Wenn ich erwachsen bin, will ich auch so heldenhaft sein!' WHERE id=2277;
UPDATE npc_text SET Text0_0='Nach so vielen Jahren des Kampfs und der Sklaverei ist es schön, wieder ein Zuhause zu haben.', Text0_1='Nach so vielen Jahren des Kampfs und der Sklaverei ist es schön, wieder ein Zuhause zu haben.', Text1_0='Sagt mir, was Ihr wollt, und fasst Euch kurz.', Text1_1='Sagt mir, was Ihr wollt, und fasst Euch kurz.' WHERE id=2278;
UPDATE npc_text SET Text0_0='Ich lebe nur, um dem Meister zu dienen.' WHERE id=2279;
UPDATE npc_text SET Text0_0='Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?', Text0_1='Selbst im Frieden herrscht noch Krieg. Viele Klans kämpfen nach wie vor unter dem Banner des Kriegshäuptlings. Seid Ihr hier, um Euren Klan bei jenen einzureihen, die für Orgrimmar kämpfen?' WHERE id=2280;
UPDATE npc_text SET Text0_0='Hallo, $N! Ich bin Meisteringenieur Scooty.' WHERE id=2282;
UPDATE npc_text SET Text0_0='Die Erde birgt viele Geheimnisse, $C; Eure Pflicht ist es, dafür zu sorgen, dass jene, die zum Wohle aller geheim bleiben müssen, es auch bleiben.' WHERE id=2284;
UPDATE npc_text SET Text0_0='Wenn Ihr hier seid, um meine Zeit zu verschwenden, dann verschwendet Ihr die Zeit des gesamten Zirkel des Cenarius. Lasst uns um Euretwillen hoffen, dass Ihr nicht töricht genug seid, das zu tun.' WHERE id=2285;
UPDATE npc_text SET Text0_0='Ich kann die Führung von Sturmwind nicht verstehen. Sie schickt den Großteil unserer Armee weg... wo unsere Heimat so sehr bedrängt wird!$B$BVon Westfall kommen Diebe, aus Ost und West nähern sich Gnolle - es ist ein Wunder, dass Sturmwind selbst nicht belagert wird.' WHERE id=2286;
UPDATE npc_text SET Text0_0='Hallo, $C. Ich hoffe, Ihr seid gekommen, um unsere Truppen zu verstärken. Bestien und Diebe dringen in unser geliebtes Elwynn ein und wir haben kaum ausreichend Männer, um die Straßen zu sichern!$B$BUnd falls Ihr es noch nicht getan habt, lest die Information über Hogger. Das ist eine Bestie in jenem Walde dort, derer man sich annehmen muss.' WHERE id=2287;
UPDATE npc_text SET Text0_0='Hallo, $N. Willkommen in Thelsamar. Versäumt während Eures Aufenthalts nicht, unsere örtliche Brauerei zu besuchen!' WHERE id=2288;
UPDATE npc_text SET Text0_0='Willkommen in Thelsamar, Eurer zweiten Heimat am Loch Modan.$B$BThelsamar ist ein hübsches Fleckchen, hat aber allen, die dem Ruf der Pflicht folgen, mehr zu bieten als nur Bier und Pökelfleisch! Wenn Ihr bereit seid, im Namen der Allianz Gefahren zu trotzen, dann lest den Aushang der Zweigstelle Thelsamar der Forscherliga.' WHERE id=2289;
UPDATE npc_text SET Text0_0='Verschwörungen allerorten, sage ich. Vielen fehlt das visionäre Denken. Sie begreifen nicht, was ich mache, und würden mich am liebsten in Vergessenheit sehen!$B$BTretet nicht in den Schmutz.' WHERE id=2313;
UPDATE npc_text SET Text0_0='Was ich mache? Seht Ihr das nicht? Ich bin ein Träumer, $Gwerter Herr:werte Dame;, und ich träume von einer besseren Zukunft für uns alle. Ich bin eindeutig ein Visionär.$B$BEs gibt da freilich auch jene, die uns in Banalität zu halten trachten. Ich sagte dem Erzdruiden Hirschhaupt, dass ich ein Träumer sei, worauf er antwortete: "Die Welt braucht auch Straßenfeger."$B$BBis zu der Zeit, da uns die Fesseln des Banalen abgestreift werden, helfe ich dem Zirkel des Cenarius diese Dreckballen herumzuschleppen.' WHERE id=2314;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $R. Ich bin Franclorn Schmiedevater. Seid Ihr hier, um etwas über die Spitze und die Dunkeleisenzwerge zu lernen?' WHERE id=2315;
UPDATE npc_text SET Text0_0='Ich hatte keine Wahl, das dachte ich jedenfalls. Ich würde die Zerstörung von Thaurissan ertragen und an der Seite meines Königs und seines Volkes stehen.$B$BUnter der Leitung von Ragnaros ließ König Dagran Thaurissan das Herz des Berges herausreißen und errichtete die Schwarzfelsspitze. Wir arbeiteten unermüdlich, um eine Zwergenstadt zu bauen, die mächtiger als jede Stadt oder Festung zuvor sein sollte... oder danach.' WHERE id=2316;
UPDATE npc_text SET Text0_0='Ich musste mit ansehen wie Ragnaros\' Verderbnis die Herzen und Gedanken der Dunkeleisennation vergiftete. Das einst ehrwürdige Volk, dem Wahnsinn verfallen... dem Bösen.$B$BAls der Bau der Spitze abgeschlossen war, schwand mein Wunsch, bei meinem Volk zu leben. Ich verschied als Held der neuen Dunkeleisennation. Dieses Grabmal wurde mir zu Ehren hier errichtet - am Eingang zu der Stadt, die ich geplant und erbaut hatte.' WHERE id=2317;
UPDATE npc_text SET Text0_0='Eisenhölle, mein gewaltiger Hammer, wurde dem neuen Chefarchitekten weitergegeben, Fineous Dunkelader: Ein Mann mit fragwürdigen Motiven und wenig Talent.$B$BUnd hier kommt Ihr ins Spiel, $N. Ich will, dass er aufgehalten wird. Er ist so unfähig, dass er die wahre Macht von Eisenhölle nicht begreift; sollte ihm jedoch jemand nachfolgen, der fähig ist, das ganze Potenzial von Eisenhölle zu nutzen, könnten die Verheerungen, die dessen Schöpfungen über die Welt bringen würden, katastrophal sein.' WHERE id=2318;
UPDATE npc_text SET Text0_0='Ja, aber Ihr werdet feststellen, dass es sehr viel lohnender ist, wenn Ihr auch einige Fertigkeiten in der Kräuterkunde habt! Ihr werdet bestimmte Samen hegen und kultivieren - und nicht blindwütig eine Axt schwingen oder hektisch nach vorüberhuschenden Schatten gestikulieren müssen. Mit dem Immergrünbeutel, den Ihr erhaltet, kann zwar selbst der dümmste Einfaltspinsel etwas wachsen lassen, aber alle, die über ein bisschen Wissen in der Kräuterkunde verfügen, werden eine reichlichere Ernte erzielen.' WHERE id=2320;
UPDATE npc_text SET Text0_0='Wisset, $GPrinz:Prinzessin;... mir ist nicht nach Kommentaren, die Ihr witzig oder komisch findet. Ich bin hier, um darauf zu achten, dass die Forschungsarbeit des Erzdruiden wie geplant vorankommt. Ich werde alle aussondern, die nicht das Zeug haben, um meinem geliebten Zirkel des Cenarius zu dienen.' WHERE id=2333;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $C. Dieser demütige Diener des Zirkels des Cenarius wünscht Euch und den Euren Segen und Überfluss.' WHERE id=2353;
UPDATE npc_text SET Text0_0='Seid lieber vorsichtig, $C, denn ich habe einen Verwandlungszauber, auf dem Euer Name steht...' WHERE id=2354;
UPDATE npc_text SET Text0_0='Ich liebe Kuchen.', Text0_1='Ich liebe Kuchen.', Text1_0='Nana sagte immer, es gäbe nichts Schöneres auf der Welt als ein warmes Stück frisch gebackenen Kirschkuchen.', Text1_1='Nana sagte immer, es gäbe nichts Schöneres auf der Welt als ein warmes Stück frisch gebackenen Kirschkuchen.', Text2_0='Dieser Kuchen ist echt lecker, möchtet Ihr ein Stück?', Text2_1='Dieser Kuchen ist echt lecker, möchtet Ihr ein Stück?', Text3_0='Kein guter Tag heute? Kostet etwas Kirschkuchen.', Text3_1='Kein guter Tag heute? Kostet etwas Kirschkuchen.', Text4_0='Dieser Kirschkuchen schmeckt so lecker, er könnte einen ausgewachsenen Gnom zum Weinen bringen.', Text4_1='Dieser Kirschkuchen schmeckt so lecker, er könnte einen ausgewachsenen Gnom zum Weinen bringen.', Text5_0='Kostet von diesem Kirschkuchen, er wird ein zehn Meilen breites Lächeln auf Euer Gesicht zaubern.', Text5_1='Kostet von diesem Kirschkuchen, er wird ein zehn Meilen breites Lächeln auf Euer Gesicht zaubern.', Text6_0='Frischer Kirschkuchen zu verkaufen. Schon allein sein Anblick wird Euch Tränen der Begeisterung in die Augen treiben.', Text6_1='Frischer Kirschkuchen zu verkaufen. Schon allein sein Anblick wird Euch Tränen der Begeisterung in die Augen treiben.', Text7_0='Kirschkuchen schmeckt von allen Sachen in Azeroth einfach am besten.', Text7_1='Kirschkuchen schmeckt von allen Sachen in Azeroth einfach am besten.' WHERE id=2355;
UPDATE npc_text SET Text0_0='Ach, wenn ich doch nur einen Ast hätte, um ihn vor mich zu halten. Dann würden die Wachen mich nicht sehen.' WHERE id=2393;
UPDATE npc_text SET Text0_0='Ihr müsst Geduld mit mir haben, $N. Mein Spionagebot ist da drin und arbeitet an einer heiklen Sache. Ich bin sofort bei Euch.$B$BOh, und tut mir einen Gefallen, lasst Euch nicht von Lescovar sehen... wir wollen ihn in einen Hinterhalt locken.' WHERE id=2394;
UPDATE npc_text SET Text0_0='Harte Zeiten, eh, $N? Wo man geht und steht, braucht jemand Hilfe, und meine Frau und ich machen da keine Ausnahme.' WHERE id=2414;
UPDATE npc_text SET Text0_0='Hallo, Ihr da! Man nennt mich Gregan. Was kann ich für Euch tun?' WHERE id=2433;
UPDATE npc_text SET Text0_0='Klar doch, $N. Ihr seht vertrauenswürdig aus... Vertrauenswürdig! Das sehe ich auf eine Meile Entfernung...$B$BWie auch immer, zurück zum Thema. Man braucht eine Evowurzelpflanze, um das Videre-Elixier herzustellen. Alle anderen Zutaten sind echt trivial, aber die Evowurzel ist nicht so leicht zu beschaffen.' WHERE id=2434;
UPDATE npc_text SET Text0_0='Alles ist verloren...' WHERE id=2474;
UPDATE npc_text SET Text0_0='Dann hört gut zu, $R.$B$BVor mehreren Monaten schickte der Magistrat von Seenhain Botschafter nach Eisenschmiede und bat den König um Unterstützung gegen die Schwarzfelsinvasoren, die aus dem Rotkammgebirge und der brennenden Steppe kamen.$B$BGegen meinen Rat bot Prinzessin Moira ihre Hilfe an. Mit wurden zwanzig Elitegardisten zugeteilt, um die Prinzessin auf der gefährlichen Reise vom Loch nach Seenhain zu beschützen.' WHERE id=2477;
UPDATE npc_text SET Text0_0='<Kharan räuspert sich.>$B$BWir durchquerten den Tunnel der sengenden Schlucht und gelangten in das verheerte Wüstenland, das die Schlucht bildet. Zurückblickend muss ich agen, es war verdächtig ruhig. Die einzigen Gefahren unterwegs stellte die Tierwelt der Region das: Kreaturen, mit denen meine Männer mühelos fertig wurden.$B$BWir gelangten fast ungehindert zum Schwarzfels und bahnten uns ohne jegliches Problem einen Weg durch das steinerne Bauwerk.' WHERE id=2478;
UPDATE npc_text SET Text0_0='Auf dem Marsch durch die brennende Steppe gerieten wir in einen Hinterhalt, es müssen Hunderte gewesen sein... Dunkeleisenzwerge! Sie kamen aus den Felswänden, aus den Steinen der Steppe; aus jeder erdenklichen Spalte, Nische und Öffnung. Sie überranten uns und überwältigten die Wachen und die wenigen tapferer Helden, die uns freiwillig begleitet hatten.$B$BEines stand fest: Es war ein minutiös geplanter Hinterhalt. Es war als hätten sie jede unserer Bewegungen vorausgeahnt, bevor wir sie machten...' WHERE id=2479;
UPDATE npc_text SET Text0_0='Wie wäre das sonst möglich gewesen? Jamadn muss die Dunkeleisenzwerge informiert haben.$B$B<Kharan schüttelt die Faust unter Eurer Nase.>$B$BHelft mir. Wenn ich jemals hier rauskomme, werde ich den Veräter den Rest meines Lebens jagen.' WHERE id=2480;
UPDATE npc_text SET Text0_0='Wo war ich doch gleich? Ach ja, wir wurden also gefangen genommen. Meine Elitegardisten wurden auf der Stalle hingerichtet. Mich verschonten sie... und die Prinzessin brachten sie mit Hilfe eines Widders fort. Ich wurde erst in die Zellen der Schlackengrube und von dort in dieses Gefängnis gebracht.$B$BUch nehme an, ich bin nur noch am leben, weil sie ein Lösegeld für mein jämmerliches Leben erpressen möchsten. Oder aus Grausamkeit, damit ich bis in alle Ewigkeit über die Geschehnisse nachdenken kann. Bedauern ist der grausamste aller Kerkermeister, $R.' WHERE id=2481;
UPDATE npc_text SET Text0_0='Jetzt weilt die an der Seite von Imperator Thaurissan. Er hat sie mit einem mächtigen, die Gedanken verändernden Zauber belegt... und ich sitze vollkommen hilflos hier.' WHERE id=2482;
UPDATE npc_text SET Text0_0='Nur ein Narr würde wagen, mich in meinem Unterschlupf zu stören.' WHERE id=2493;
UPDATE npc_text SET Text0_0='<Cyrus\' Augen leuchten.>$B$BVerschlinge ich Euch mit Haut und Haar oder lasse ich Euch leben?$B$BVielleicht könnt Ihr ja noch nützlich sein...$B$BFordert meinen Diener heraus, $R. Überlebt und bringt mir einen Beweis Eures Sieges.' WHERE id=2494;
UPDATE npc_text SET Text0_0='Grummel, grummel...' WHERE id=2496;
UPDATE npc_text SET Text0_0='Sucht nach Yelmaks \'Alchemie und Tränke\'. Ihr findet es mitten in der Gasse, westlich des gewaltigen Baumes.', Text0_1='Sucht nach Yelmaks \'Alchemie und Tränke\'. Ihr findet es mitten in der Gasse, westlich des gewaltigen Baumes.', Text1_0='Tränke und Öle, darüber lehrt Yelmak Euch etwas, wenn Ihr würdig seid. Sucht sein Geschäft auf, es befindet sich in der Mitte der Gasse.', Text1_1='Tränke und Öle, darüber lehrt Yelmak Euch etwas, wenn Ihr würdig seid. Sucht sein Geschäft auf, es befindet sich in der Mitte der Gasse.' WHERE id=2497;
UPDATE npc_text SET Text0_0='Sprecht mit Saru Stahlzorn beim Brennenden Amboss. Geht zum Tal der Ehre. Es befindet sich am Ende des Pfades rechts vom \'Bergbauunternehmen Rotschlucht\'.', Text0_1='Sprecht mit Saru Stahlzorn beim Brennenden Amboss. Geht zum Tal der Ehre. Es befindet sich am Ende des Pfades rechts vom \'Bergbauunternehmen Rotschlucht\'.', Text1_0='Vielleicht wird Saru Stahlzorn Euch lehren, wie Ihr eines Tages eine Axt herstellen könnt, die so gut wie meine ist. Ihr findet ihn beim Brennenden Amboss im Tal der Ehre, den Pfad hinab rechts hinter \'Bergbauunternehmen Rotschlucht\'.', Text1_1='Vielleicht wird Saru Stahlzorn Euch lehren, wie Ihr eines Tages eine Axt herstellen könnt, die so gut wie meine ist. Ihr findet ihn beim Brennenden Amboss im Tal der Ehre, den Pfad hinab rechts hinter \'Bergbauunternehmen Rotschlucht\'.' WHERE id=2499;
UPDATE npc_text SET Text0_0='Ihr sucht Zamja in \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Geht die linke Treppe hinauf, wenn Ihr die Gasse vom Tal der Stärke her betretet.', Text0_1='Ihr sucht Zamja in \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Geht die linke Treppe hinauf, wenn Ihr die Gasse vom Tal der Stärke her betretet.', Text1_0='Ihr müsst bei Zamja lernen. Geht zu \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Nehmt die Treppe links, wenn Ihr vom Tal der Stärke her hereinkommt.', Text1_1='Ihr müsst bei Zamja lernen. Geht zu \'Borstans Feuergrube\' auf der oberen Etage der Gasse. Nehmt die Treppe links, wenn Ihr vom Tal der Stärke her hereinkommt.' WHERE id=2500;
UPDATE npc_text SET Text0_0='Ihr müsst zu \'Godans Runenarbeiten\' gehen. Sucht am westlichen Ende der Gasse danach, gleich neben dem Tal der Weisheit.', Text0_1='Ihr müsst zu \'Godans Runenarbeiten\' gehen. Sucht am westlichen Ende der Gasse danach, gleich neben dem Tal der Weisheit.', Text1_0='Könnt Ihr meine Axt verzaubern? Ich will einen dauerhaften Zauber. Na gut, dann vielleicht später. Sucht nach \'Godans Runenarbeiten\' am westlichen Ende der Gasse nahe dem Tal der Weisheit.', Text1_1='Könnt Ihr meine Axt verzaubern? Ich will einen dauerhaften Zauber. Na gut, dann vielleicht später. Sucht nach \'Godans Runenarbeiten\' am westlichen Ende der Gasse nahe dem Tal der Weisheit.' WHERE id=2501;
UPDATE npc_text SET Text0_0='Arnok macht gute Verbände. Ihr findet ihn in der Hütte \'Der Stärkste überlebt\' am Ufer des Sees neben der Geisterlodge im Tal der Geister. Das ist westlich über dem Tal der Stärke.', Text0_1='Arnok macht gute Verbände. Ihr findet ihn in der Hütte \'Der Stärkste überlebt\' am Ufer des Sees neben der Geisterlodge im Tal der Geister. Das ist westlich über dem Tal der Stärke.', Text1_0='Ich schätze, jemand muss uns Krieger nach den Kämpfen verbinden. Sucht Arnok in der \'Der Stärkste überlebt\'-Hütte neben der Geisterlodge im Tal der Geister auf. Haltet westlich über dem Tal der Stärke nach einem See Ausschau. Da müsste er zu finden sein.', Text1_1='Ich schätze, jemand muss uns Krieger nach den Kämpfen verbinden. Sucht Arnok in der \'Der Stärkste überlebt\'-Hütte neben der Geisterlodge im Tal der Geister auf. Haltet westlich über dem Tal der Stärke nach einem See Ausschau. Da müsste er zu finden sein.' WHERE id=2502;
UPDATE npc_text SET Text0_0='Dieser Lumak... ständig angelt er, statt zu kämpfen. Ihr findet ihn auf seinem Dock am See im Tal der Ehre. Und grüßt Shankys von mir, wenn Ihr schon mal da seid.', Text0_1='Dieser Lumak... ständig angelt er, statt zu kämpfen. Ihr findet ihn auf seinem Dock am See im Tal der Ehre. Und grüßt Shankys von mir, wenn Ihr schon mal da seid.', Text1_0='Angeln ist ein Sport für die Alten, die nicht im Kampf gefallen sind! Sucht Lumak am See im Tal der Ehre auf.', Text1_1='Angeln ist ein Sport für die Alten, die nicht im Kampf gefallen sind! Sucht Lumak am See im Tal der Ehre auf.' WHERE id=2503;
UPDATE npc_text SET Text0_0='Kräuter... Ihr müsst mit Jandi in ihrem Arboretum in der Gasse sprechen. Hinter \'Asorans Markt\' im Tal der Weisheit gibt es eine Holztreppe. Geht zur oberen Etage der Gasse hinauf, da findet Ihr sie.', Text0_1='Kräuter... Ihr müsst mit Jandi in ihrem Arboretum in der Gasse sprechen. Hinter \'Asorans Markt\' im Tal der Weisheit gibt es eine Holztreppe. Geht zur oberen Etage der Gasse hinauf, da findet Ihr sie.', Text1_0='Ja, Mann, da müsst Ihr zu Jandis Arboretum. Hab ich das gerade gesagt? Glok-Tar, ich muss müde sein! Folgt der Holztreppe hinter \'Asorans Markt\' im Tal der Weisheit in die Gasse hinauf. Ihr findet Jandi auf der oberen Etage.', Text1_1='Ja, Mann, da müsst Ihr zu Jandis Arboretum. Hab ich das gerade gesagt? Glok-Tar, ich muss müde sein! Folgt der Holztreppe hinter \'Asorans Markt\' im Tal der Weisheit in die Gasse hinauf. Ihr findet Jandi auf der oberen Etage.' WHERE id=2504;
UPDATE npc_text SET Text0_0='Sucht die \'Kodolederer\' in der Gasse auf. Fragt nach Karolek.', Text0_1='Sucht die \'Kodolederer\' in der Gasse auf. Fragt nach Karolek.', Text1_0='Da müsst Ihr wohl zum \'Kodolederer\'. Dein findet Ihr in der Mitte der Gasse.', Text1_1='Da müsst Ihr wohl zum \'Kodolederer\'. Den findet Ihr in der Mitte der Gasse.' WHERE id=2513;
UPDATE npc_text SET Text0_0='Sucht das \'Bergbauunternehmen Rotschlucht\' nordöstlich des Sees im Tal der Ehre auf. Sprecht dort mit Makaru.', Text0_1='Sucht das \'Bergbauunternehmen Rotschlucht\' nordöstlich des Sees im Tal der Ehre auf. Sprecht dort mit Makaru.', Text1_0='Makaru ist Tag und Nacht beim \'Bergbauunternehmen Rotschlucht\' im Tal der Ehre zu finden, direkt am See.', Text1_1='Makaru ist Tag und Nacht beim \'Bergbauunternehmen Rotschlucht\' im Tal der Ehre zu finden, direkt am See.' WHERE id=2515;
UPDATE npc_text SET Text0_0='Thuwd zeigt Euch, wie Ihr Eure getöteten Tiere häutet. Ihr findet ihn beim \'Kodolederer\' in der Gasse.', Text0_1='Thuwd zeigt Euch, wie Ihr Eure getöteten Tiere häutet. Ihr findet ihn beim \'Kodolederer\' in der Gasse.', Text1_0='Es ist gut, dass Ihr die getöteten Tiere ehrt, indem Ihr ihre Kadaver sinnvoll verwendet. Sucht Thuwd im \'Kodolederer\' in der Mitte der Gasse auf.', Text1_1='Es ist gut, dass Ihr die getöteten Tiere ehrt, indem Ihr ihre Kadaver sinnvoll verwendet. Sucht Thuwd im \'Kodolederer\' in der Mitte der Gasse auf.' WHERE id=2516;
UPDATE npc_text SET Text0_0='Magars Tuchwaren\' liegt in der Gasse. Ihr findet das südlich des gewaltigen Baums neben \'Kodolederer\'.', Text0_1='Magars Tuchwaren\' liegt in der Gasse. Ihr findet das südlich des gewaltigen Baums neben \'Kodolederer\'.', Text1_0='Kleidung!? Die nützt Euch nichts gegen guten Stahl! Pah, wenn Ihr wirklich das Schneiderhandwerk erlernen möchtet, sucht nach \'Magars Tuchwaren\' in der Gasse.', Text1_1='Kleidung!? Die nützt Euch nichts gegen guten Stahl! Pah, wenn Ihr wirklich das Schneiderhandwerk erlernen möchtet, sucht nach \'Magars Tuchwaren\' in der Gasse.' WHERE id=2518;
UPDATE npc_text SET Text0_0='Wir haben unsere Verpflichtungen, $C. Verpflichtungen gegenüber der Horde.' WHERE id=2533;
UPDATE npc_text SET Text0_0='Ich glaube, ich höre ihn kommen! Schaut nach Westen!' WHERE id=2534;
UPDATE npc_text SET Text0_0='Die Bank von Orgrimmar liegt im Tal der Stärke. Sucht nördlich des Haupteingangs zur Stadt danach.', Text0_1='Die Bank von Orgrimmar liegt im Tal der Stärke. Sucht nördlich des Haupteingangs zur Stadt danach.', Text1_0='Geht zum Tal der Stärke und sucht die Bank von Orgrimmar südlich des Himmelsturms auf.', Text1_1='Geht zum Tal der Stärke und sucht die Bank von Orgrimmar südlich des Himmelsturms auf.' WHERE id=2554;
UPDATE npc_text SET Text0_0='Sucht nach dem Windreitermeister auf der Spitze des Himmelsturms im Tal der Stärke. Doras wird dafür sorgen, dass Ihr den Weg zu dem Kampf findet, den Ihr sucht.', Text0_1='Sucht nach dem Windreitermeister auf der Spitze des Himmelsturms im Tal der Stärke. Doras wird dafür sorgen, dass Ihr den Weg zu dem Kampf findet, den Ihr sucht.', Text1_0='Was dachtet Ihr denn, wo der Windreitermeister sein würde? Geht zur Spitze des Himmelsturms im Tal der Stärke!', Text1_1='Was dachtet Ihr denn, wo der Windreitermeister sein würde? Geht zur Spitze des Himmelsturms im Tal der Stärke!' WHERE id=2555;
UPDATE npc_text SET Text0_0='Ihr sucht Urtrun Klanrufer in der Botschaft der Horde. Die findet Ihr an der Westseite des Tals der Stärke.', Text0_1='Ihr sucht Urtrun Klanrufer in der Botschaft der Horde. Die findet Ihr an der Westseite des Tals der Stärke.', Text1_0='Die Botschaft der Horde liegt im Tal der Stärke auf der Westseite.', Text1_1='Die Botschaft der Horde liegt im Tal der Stärke auf der Westseite.' WHERE id=2556;
UPDATE npc_text SET Text0_0='Das ist das zweite Gebäude rechts im Tal der Stärke, nördlich des Haupteingangs der Stadt. Sprecht mit Gastwirtin Gryshka.', Text0_1='Das ist das zweite Gebäude rechts im Tal der Stärke, nördlich des Haupteingangs der Stadt. Sprecht mit Gastwirtin Gryshka.', Text1_0='Sucht nach der Gastwirtin Gryshka im Tal der Stärke. Ihr Gasthaus liegt auf der Ostseite, nördlich des Hauptstadttores.', Text1_1='Sucht nach der Gastwirtin Gryshka im Tal der Stärke. Ihr Gasthaus liegt auf der Ostseite, nördlich des Hauptstadttores.' WHERE id=2557;
UPDATE npc_text SET Text0_0='So etwas findet Ihr vor der Bank von Orgrimmar, inmitten des Tals der Stärke.', Text0_1='So etwas findet Ihr vor der Bank von Orgrimmar, inmitten des Tals der Stärke.', Text1_0='Ihr könnt Eure Liebesbriefe vor der Bank in der Mitte des Tals der Stärke abholen.', Text1_1='Ihr könnt Eure Liebesbriefe vor der Bank in der Mitte des Tals der Stärke abholen.' WHERE id=2558;
UPDATE npc_text SET Text0_0='Geht zur Jägerhalle im Tal der Ehre, westlich des Kolosseums. Sprecht dort mit Ormak Grimmschlag.', Text0_1='Geht zur Jägerhalle im Tal der Ehre, westlich des Kolosseums. Sprecht dort mit Ormak Grimmschlag.', Text1_0='Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.', Text1_1='Geht schnurstracks zum nordwestlichen Teil des Tals der Ehre. Dort findet Ihr die Jägerhalle.' WHERE id=2559;
UPDATE npc_text SET Text0_0='Sprecht mit Pephredo in der Dunkeldornenlodge im Tal der Geister, westlich über dem Tal der Stärke.', Text0_1='Sprecht mit Pephredo in der Dunkeldornenlodge im Tal der Geister, westlich über dem Tal der Stärke.', Text1_0='Was, Ihr könnt nicht dorthin teleportieren? Geht zur Dunkeldornenlodge im Tal der Geister am See, westlich und über dem Tal der Stärke.', Text1_1='Was, Ihr könnt nicht dorthin teleportieren? Geht zur Dunkeldornenlodge im Tal der Geister am See, westlich und über dem Tal der Stärke.' WHERE id=2560;
UPDATE npc_text SET Text0_0='Zayus und seine Priester sind in der Geisterlodge, und die befindet sich im Tal der Geister. Sucht am See westlich und über dem Tal der Stärke danach.', Text0_1='Zayus und seine Priester sind in der Geisterlodge, und die befindet sich im Tal der Geister. Sucht am See westlich und über dem Tal der Stärke danach.', Text1_0='Heilung ist für jene, die keinen Schmerz ertragen können. Aber wenn es denn unbedingt sein muss, dann geht zur Geisterlodge am See im Tal der Geister, über dem Tal der Stärke im Westen.', Text1_1='Heilung ist für jene, die keinen Schmerz ertragen können. Aber wenn es denn unbedingt sein muss, dann geht zur Geisterlodge am See im Tal der Geister, über dem Tal der Stärke im Westen.' WHERE id=2561;
UPDATE npc_text SET Text0_0='Ihr sucht Kardris Traumsucher. Sie und die anderen Schamanen sind im Eingangsraum von Thralls Festung im Tal der Weisheit zu finden.', Text0_1='Ihr sucht Kardris Traumsucher. Sie und die anderen Schamanen sind im Eingangsraum von Thralls Festung im Tal der Weisheit zu finden.', Text1_0='Eure Suche ist fast abgeschlossen. Sucht Eure Wahrheit im Eingangsraum von Thralls Festung im Tal der Weisheit.', Text1_1='Eure Suche ist fast abgeschlossen. Sucht Eure Wahrheit im Eingangsraum von Thralls Festung im Tal der Weisheit.' WHERE id=2562;
UPDATE npc_text SET Text0_0='Ihr sucht nach Shenthul von der Bruderschaft der Schattenläufer, hm? Sucht in der Gasse gegenüber von \'Droffers und Sohn Bergungen\' nach einem Tunnel, der in die Kluft der Schatten hinabführt.', Text0_1='Ihr sucht nach Shenthul von der Bruderschaft der Schattenläufer, hm? Sucht in der Gasse gegenüber von \'Droffers und Sohn Bergungen\' nach einem Tunnel, der in die Kluft der Schatten hinabführt.', Text1_0='Lasst Euch nicht dabei erwischen, wie Ihr etwas stehlt! Sucht nach Shentul und seiner Bruderschaft der Schattenläuferin der Kluft der Schatten. Ihr findet den Tunnel, der dort hinabführt, im Süden des Tals der Weisheit.', Text1_1='Lasst Euch nicht dabei erwischen, wie Ihr etwas stehlt! Sucht nach Shentul und seiner Bruderschaft der Schattenläuferin der Kluft der Schatten. Ihr findet den Tunnel, der dort hinabführt, im Süden des Tals der Weisheit.' WHERE id=2563;
UPDATE npc_text SET Text0_0='Ihr sucht Grol\'dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', Text0_1='Ihr sucht Grol\'dar und die Dunkelfeuerenklave? Sucht in der unterirdischen Höhle namens Kluft der Schatten danach. Es führen zwei Tunnel in diese Höhle hinab, einer südlich des Tals der Weisheit, einer von der Gasse.', Text1_0='Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Dunkelfeuerenklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.', Text1_1='Haben wir uns nicht gerade erst von der Brennenden Legion befreit, und schon lasst Ihr Euch wieder mit Dämonen ein? Die Dunkelfeuerenklave ist in der unterirdischen Höhle namens Kluft der Schatten zu finden. Von der Gasse aus führt ein Tunnel hinunter.' WHERE id=2564;
UPDATE npc_text SET Text0_0='Sprecht mit Grezz Zornfaust in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!', Text0_1='Sprecht mit Grezz Zornfaust in der Halle der Kriegerhelden. Sie befindet sich im Tal der Ehre. Legt ein gutes Wort für mich ein!', Text1_0='Sucht die Halle der Kriegerhelden im Tal der Ehre auf und sprecht mit Grezz Zornfaust. Lasst die Leute wissen, dass ich mich nach meiner Schicht hier bei ihnen melde.', Text1_1='Sucht die Halle der Kriegerhelden im Tal der Ehre auf und sprecht mit Grezz Zornfaust. Lasst die Leute wissen, dass ich mich nach meiner Schicht hier bei ihnen melde.' WHERE id=2565;
UPDATE npc_text SET Text0_0='Nur sehr wenige suchen nach mir... Was kann ich für Euch tun?' WHERE id=2568;
UPDATE npc_text SET Text0_0='Hi, hi... Ihr seid recht neugierig, was, $N? Also, das müsst Ihr tun:$B$BNördlich von hier, in den alten Ruinen, lebt ein höchst übellauniger Gnoll namens Miblon Fletschmaul. Er bewacht die einzige Evowurzelpflanze, die ich kenne, und das tut er Tag und Nacht! Er scheint niemals zu schlafen!$B$BAllerdings könnt Ihr eines tun: ihn bestechen. Ich kann Euch Köder verkaufen, die er lecker finden wird - legt sie einfach in seiner Nähe hin, das lenkt ihn lange genug ab, dass Ihr Euch die Evowurzel schnappen könnt. Viel Glück!' WHERE id=2570;
UPDATE npc_text SET Text0_0='Ja, aber Ihr werdet feststellen, dass es sehr viel lohnender ist, wenn Ihr auch einige Fertigkeiten in der Kräuterkunde habt! Damit könnt Ihr besondere Samenkörner anbauen und aufziehen. Mit dem Immergrünbeutel, den Ihr erhaltet, kann zwar selbst ein absoluter Neuling etwas wachsen lassen, aber alle, die über ein bisschen Wissen in der Kräuterkunde verfügen, werden eine reichlichere Ernte erzielen.' WHERE id=2574;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Was kann dieser bescheidene Diener der Mondfederfeste heute für Euch tun?' WHERE id=2575;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=2593;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=2594;
UPDATE npc_text SET Text0_1='Der Tod unseres Fleisches bedeutete die Geburt unseres Geistes und unserer heiligen Aufgabe.' WHERE id=2595;
UPDATE npc_text SET Text0_0='Unser Anführer Un\'rel hat die Herausforderung gehört.' WHERE id=2596;
UPDATE npc_text SET Text0_0='Unser Anführer Un\'rel hat die Herausforderung gehört.' WHERE id=2597;
UPDATE npc_text SET Text0_0='Die Berührung des Steins ist mir für immer verwehrt. Nie wieder werde ich die Pracht geschmiedeten Eisens spüren...' WHERE id=2598;
UPDATE npc_text SET Text0_0='Welchen Ausbilder sucht Ihr?', Text0_1='Welchen Ausbilder sucht Ihr?' WHERE id=2599;
UPDATE npc_text SET Text0_1='Ihr dürft nicht vorbei. Ihr seid weder von unserer Art noch habt Ihr eine Herausforderung ausgesprochen.' WHERE id=2600;
UPDATE npc_text SET Text0_0='Unser Schicksal ist das Verhängnis aller, die dem großen Feuer trotzen.' WHERE id=2601;
UPDATE npc_text SET Text0_0='Ihr möchtet das alte Handwerk erlernen? Ihr wollt dunkles Eisen verhütten?$B$BStimmt mich milde, $R. Zeigt mir ein Opfer und ich denke darüber nach!' WHERE id=2604;
UPDATE npc_text SET Text0_0='Euer Wille ist stark, Eure Absicht eindeutig.$B$BVielleicht seid Ihr würdig...' WHERE id=2605;
UPDATE npc_text SET Text0_0='Ihr habt mir Euer Begehren gezeigt und mit kostbarem Stein bezahlt. Ich werde Euch unterrichten...' WHERE id=2606;
UPDATE npc_text SET Text0_0='Ich... Ich weiß nicht so genau, wie ich hierher gekommen bin...$B$BIch erinnere mich schwach an eine Reise... ein Floß...$B$B' WHERE id=2633;
UPDATE npc_text SET Text0_0='Bezahlt mich und ich werde reden...$B$BOh, Ihr seid es, $N! Entschuldigt bitte...', Text1_0='Kommt Euch mein Name bekannt vor? Vielleicht kannten wir einander vor langer Zeit...$B$BIch glaube, ich wurde nach jemand Wichtigem benannt...', Text2_0='Hallo, Ihr da! Schön, dass Ihr zurückgekommen seid, $N. Sagt, habt Ihr die erste Aufgabe abgeschlossen, die ich Euch gestellt habe? Wenn ja, habe ich möglicherweise eine zweite für Euch...', Text3_0='Als ich nach meiner Bruchlandung hier durch den Krater lief, fiel mir etwas ein, das man mir einst erzählt hatte... "Geh hinauf, den Berg hinauf", sagte die Stimme in meinem Kopf.$B$BIch hörte auf sie und so kam ich hierher, $N!', Text4_0='Vor langer Zeit traf ich auf meinen Reisen einen Mann, der zu mir sagte: "Meistere seine Benutzung, dann kannst du dies haben."$B$BWas, meint Ihr, soll das heißen? Ich wusste es damals nicht und weiß es auch heute nicht...', Text5_0='Es ist für alle ein Rätsel... mich eingeschlossen!$B$BSchön, dass Ihr mich gefunden habt - zum Glück verstecke ich mich nicht im Gebüsch oder unter einem Stein, was?' WHERE id=2634;
UPDATE npc_text SET Text0_0='Danke, $N! Ihr seid der Held des Kraters von Un\'Goro!' WHERE id=2635;
UPDATE npc_text SET Text0_0='Ragnaros wird unangefochten herrschen!', Text0_1='Ragnaros wird unangefochten herrschen!', Text1_0='Trinkt einen Schluck, $C. Er könnte Eure letzter sein!', Text1_1='Trinkt einen Schluck, $C. Er könnte Eure letzter sein!', Text2_0='Ohne den \'Grimmigen Säufer\' würde ich in meiner Freizeit Großdrachenwelpen die Flügel ausreißen.', Text2_1='Ohne den \'Grimmigen Säufer\' würde ich in meiner Freizeit Großdrachenwelpen die Flügel ausreißen.', Text3_0='Ohne den Grimmigen Säufer würde ich in meiner Freizeit vermutlich Großdrachenwelpen die Flügel ausreißen.', Text3_1='Ohne den Grimmigen Säufer würde ich in meiner Freizeit vermutlich Großdrachenwelpen die Flügel ausreißen.', Text4_0='Die Wasserelementare führen Krieg gegen die Feuerelementare, und damit also auch in gewisser Weise gegen uns. Hydraxis, einer ihrer Anführer, rekrutiert Agenten für seine Sache...', Text4_1='Die Wasserelementare führen Krieg gegen die Feuerelementare, und damit also auch in gewisser Weise gegen uns. Hydraxis, einer ihrer Anführer, rekrutiert Agenten für seine Sache...', Text5_0='Ich hörte, dass Hydraxis, ein Adliger unter den Wasserelementaren, Krieg gegen die Diener des Feuers führt. Hoffen wir, dass er ihn verliert!', Text5_1='Ich hörte, dass Hydraxis, ein Adliger unter den Wasserelementaren, Krieg gegen die Diener des Feuers führt. Hoffen wir, dass er ihn verliert!', Text6_0='Der Fürst der Wasserlords, Hydraxis, versteckt sich in Azshara, auf einer Insel in der Bucht der Stürme. Was für eine Bedrohung kann er von dort aus wohl zusammenziehen?', Text6_1='Der Fürst der Wasserlords, Hydraxis, versteckt sich in Azshara, auf einer Insel in der Bucht der Stürme. Was für eine Bedrohung kann er von dort aus wohl zusammenziehen?', Text7_0='Falls Hydraxis in diesem Krieg noch weiter rekrutiert, könnte sich das Machtverhältnis ändern...', Text7_1='Falls Hydraxis in diesem Krieg noch weiter rekrutiert, könnte sich das Machtverhältnis ändern...' WHERE id=2636;
UPDATE npc_text SET Text0_0='Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', Text0_1='Es geht nur um Waffen und Flammen, bis jemand verliert und stirbt.', Text1_0='Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', Text1_1='Mein Vater sagte mir, dass solche Tage kommen würden, da ich fremden Leuten begegnen würde und mich hüten müsste, sie zu töten.', Text2_0='Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', Text2_1='Habt Ihr schon das Donnerbräu Lagerbier versucht? Das ist echt guter Stoff!', Text3_0='In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!', Text3_1='In einer Stunde habe ich Dienst, aber bis dahin... hoch die Tassen!' WHERE id=2637;
UPDATE npc_text SET Text0_0='Willkommen im Camp Mojache, oh $C. Wie kann ich Euch heute dienen?' WHERE id=2638;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C... Wenn Ihr nach Lederwaren und Lederverarbeitungsbedarf sucht, seid Ihr zum richtigen halb pensionierten Paladin gekommen.', Text1_0='Ich schwöre... falls Quint noch einmal versuchen sollte, einen Angelwurm auf mir zu platzieren, dann werde ich seinen Baum mit meinem nicht ganz so rostigen Schwert fällen.' WHERE id=2639;
UPDATE npc_text SET Text0_0='Ihr seht aus, als würdet Ihr eine Überfahrt über das große Meer brauchen. Tja, da seid Ihr hier genau richtig, dies ist die Anlegestelle der LAUNISCHEN MINNA, des besten Passagierschiffs in diesen Gewässern. Wenn es einläuft, dann geht einfach an Bord, und im Handumdrehen befindet Ihr Euch an den Sandstränden des Schlingendorntals.', Text0_1='Ihr seht aus, als würdet Ihr eine Überfahrt über das große Meer brauchen. Tja, da seid Ihr hier genau richtig, dies ist die Anlegestelle der LAUNISCHEN MINNA, des besten Passagierschiffs in diesen Gewässern. Wenn es einläuft, dann geht einfach an Bord, und im Handumdrehen befindet Ihr Euch an den Sandstränden des Schlingendorntals.' WHERE id=2640;
UPDATE npc_text SET Text0_0='Ich muss sagen, in Beutebucht gibt es die beste Muschelsuppe der ganzen südlichen Meere. Oh, Ihr interessiert Euch nicht für Cuisine, eh? Dann lasst mich Euch als Ersten an der Anlegestelle der LAUNISCHEN MINNA begrüßen, des besten Passagierschiffs, das zwischen den östlichen Königreichen und Kalimdor verkehrt. Es sollte bald für eine weitere Reise nach Ratschet hier einlaufen, also macht es Euch bis zu seiner Ankunft bequem.', Text0_1='Ich muss sagen, in Beutebucht gibt es die beste Muschelsuppe der ganzen südlichen Meere. Oh, Ihr interessiert Euch nicht für Cuisine, eh? Dann lasst mich Euch als Ersten an der Anlegestelle der LAUNISCHEN MINNA begrüßen, des besten Passagierschiffs, das zwischen den östlichen Königreichen und Kalimdor verkehrt. Es sollte bald für eine weitere Reise nach Ratxchet hier einlaufen, also macht es Euch bis zu seiner Ankunft bequem.' WHERE id=2641;
UPDATE npc_text SET Text0_0='Natürlich besteht keine Gefahr, dass alles Feuer fängt und explodiert wie eine riesige Heliumbombe. Dieses Schmuckstück reist schneller und sicherer als jedes Boot nach Unterstadt und die Aussicht ist wahrhaft atemberaubend. Und da wir gerade vom Atmen sprechen: Rauchen ist an Bord des Zeppelins nicht gestattet, und während der Reise dürfen auch keine Feuerzauber gewirkt werden.', Text0_1='Natürlich besteht keine Gefahr, dass alles Feuer fängt und explodiert wie eine riesige Heliumbombe. Dieses Schmuckstück reist schneller und sicherer als jedes Boot nach Unterstadt und die Aussicht ist wahrhaft atemberaubend. Und da wir gerade vom Atmen sprechen: Rauchen ist an Bord des Zeppelins nicht gestattet.' WHERE id=2642;
UPDATE npc_text SET Text0_0='Hallo! Ihr seid genau die Person, nach der ich gesucht habe. Setzt Euch und hört zu, denn ich habe einen Plan, der uns beide reich machen wird!' WHERE id=2643;
UPDATE npc_text SET Text0_0='Mein Bruder sagte, wir könnten viel Geld mit dieser Handelsroute machen, aber ich wusste nicht, dass ich auf dieser Seite leben würde. Wenn Ihr mit dem Zeppelin wieder hier vorbeikommt, könntet Ihr mir da wohl etwas zu essen mitbringen? In Orgrimmar verkaufen sie das beste Fleisch, das man sich nur vorstellen kann, und ich könnte wirklich etwas vertragen, das nicht schon Wochen, bevor es sich zu bewegen aufhörte, gestorben ist.', Text0_1='Mein Bruder sagte, wir könnten viel Geld mit dieser Handelsroute machen, aber ich wusste nicht, dass ich auf dieser Seite leben würde. Wenn Ihr mit dem Zeppelin wieder hier vorbeikommt, könntet Ihr mir da wohl etwas zu essen mitbringen? In Orgrimmar verkaufen sie das beste Fleisch, das man sich nur vorstellen kann, und ich könnte wirklich etwas vertragen, das nicht schon Wochen, bevor es sich zu bewegen aufhörte, gestorben ist.' WHERE id=2644;
UPDATE npc_text SET Text0_0='Sucht nach \'Noggs Maschinenladen\' im Tal der Ehre nordöstlich von \'Bergbauunternehmen Rotschlucht\'.', Text0_1='Sucht nach \'Noggs Maschinenladen\' im Tal der Ehre nordöstlich von \'Bergbauunternehmen Rotschlucht\'.', Text1_0='Ihr findet diesen Goblin und seinen Spielzeugladen im Tal der Ehre.', Text1_1='Ihr findet diesen Goblin und seinen Spielzeugladen im Tal der Ehre.' WHERE id=2653;
UPDATE npc_text SET Text0_0='Hallo, $N. Wenn Ihr das eine oder andere über das Kochen lernen möchtet, schaut einfach bei mir rein.' WHERE id=2676;
UPDATE npc_text SET Text0_1='<Lady Prestor schaut Euch böse an.>' WHERE id=2693;
UPDATE npc_text SET Text0_1='Ich stand direkt neben Euch, Schwachkopf. Ich weiß, was Ihr sucht.' WHERE id=2694;
UPDATE npc_text SET Text0_1='Ihr wollt unsere globale Verteidigung schwächen, um einem paranoiden Magistrat und Gesindel zu gefallen, die höchstwahrscheinlich an Hitzschlag leiden.' WHERE id=2695;
UPDATE npc_text SET Text0_1='Natürlich nicht, $Glieber Junge:liebes Mädchen;. Ihr Abenteurertypen seid häufig Opfer der Umstände und den politischen Dramen ausgeliefert, die sich zwischen den Bürgern und dem Adel abspielen.$B$BMein Rat für Euch ist daher, sucht etwas anderes, genauer: einen neuen Beruf.$B$BDiese Welt hat bereits genügend Helden.' WHERE id=2696;
UPDATE npc_text SET Text0_0='Was wollt Ihr? *Hick*' WHERE id=2713;
UPDATE npc_text SET Text0_0='Windsor war an dem Tag besonders unausstehlich - und glaubt mir, bei Windsor ist das eine reife Leistung. Er sagte immer wieder, dass \'etwas nicht stimmt\'. Und das war kein Scherz!$B$BWir waren in der Mitte des Schwarzfels, als die elenden Bestien angriffen. Ich spreche natürlich von den Orcs. Passt gefälligst auf, ja? Man konnte nur Grunzen und das Klirren von Stahl hören, als sie auf uns zustürmten.' WHERE id=2714;
UPDATE npc_text SET Text0_0='Ich gegen fünfzig Orcs? Ich bin doch kein Narr, $N. Mein Papa hat immer gesagt: \'Diskreditieren ist die bessere Hälfte eines Kräckers\', oder so ähnlich, und ich wusste, was das bedeutet.' WHERE id=2715;
UPDATE npc_text SET Text0_0='Schon gut, schon gut. Wie auch immer, ich verkrümelte mich in die Schatten.$B$BDas kam nicht besonders gut an bei Windsor, der sowieso schon total zappelig war. Der fängt an, den ollen Eisenfeind zu schwingen und die Orcs wie ein Wahnsinniger anzubrüllen.' WHERE id=2716;
UPDATE npc_text SET Text0_0='Yep. Noch nie von Eisenfeind gehört? Dem legendären Orcschlächterhammer? Jupp, jupp, das war der Hammer des alten Windsor. Er sagte mir, dass Franclorn Schmiedevater diesen Hammer höchstpersönlich für seinen Ururgroßpapa gemacht hat. DER Franclorn Schmiedevater: Der Dunkeleisenzwerg, der verantwortlich ist für Steinwerkarchimetek... Bauzeugs halt. Er sagte auch, der Hammer hätte einen Zwillingsbruder, den Franclorn für sich behielt. Ich glaube, er nannte ihn Eisenhölle oder so.' WHERE id=2717;
UPDATE npc_text SET Text0_0='Wo war ich doch gleich? Ach ja, die Orcs stürmten also auf Windsor los, aber Windsor, der bewegte sich keinen Millimeter. Er stand reglos da, während sie in Zehnergruppen auf ihn los stürmten. Ich konnte nur Eisenfeind funkeln und jede Menge Blut sehen. Das ging Stunden so, möglicherweise Tage. Ich erinnere mich nicht. Jedenfalls war es dann ENDLICH vorbei.' WHERE id=2718;
UPDATE npc_text SET Text0_0='Gestorben? Seid Ihr nicht gescheit, $N? Pardon, $Gwerter Herr:holde Dame; $N! Als hätten fünfzig Orcs Windsor töten können. So sicher, wie Blutwurst aus Thelsamar die größte Köstlichkeit der Welt ist, stand er da: Von Kopf bis Fuß von Orcfetzen bedeckt, in rund achtzehn Schichten ihres Blutes gehüllt, aber eindeutig am Leben... und so was von wütend.' WHERE id=2719;
UPDATE npc_text SET Text0_0='Warum sagt Ihr dauernd, dass er gestorben sei? Wer hat Euch gesagt, dass er starb? Ich jedenfalls nicht.$B$BEr wird vermisst, das ist alles.$B$BSeht Ihr, er geriet offenbar mitten in einen großen Kampf von Orcs gegen Dunkeleisenzwerge. Die Orcs, diese schändlichen, dreckigen kleinen Feiglinge, kamen früher raus und richteten ein paar Fallen und andere diabolische Sachen ein, die Ihr nicht verstehen würdet.' WHERE id=2720;
UPDATE npc_text SET Text0_0='Zwerge werden nicht betrunken, $GJungchen:grünes Gör;. Ich bin nur ein bisschen bedüdelt.$B$BWie auch immer, zurück zu Windsor. Ich glaube, der ist irgendwo in den Schwarzfelstiefen. Für Euch uninformatierte Dummköpfe, dies ist die Stadt der Dunkeleisenzwerge.' WHERE id=2721;
UPDATE npc_text SET Text0_0='Langsam! Dazu kam ich doch gerade!$B$BEr steht also da und trieft von Blut und Gekröse, und wer erscheint da auf der Bildfläche?$B$BDie Dunkeleisenzwerge! Habt Ihr mir denn überhaupt nicht zugehört???$B$BNa ja, die Dunkeleisenzwerge sind etwas geschickter als diese Orcs des Schwarzfelsklans. Sie waren vorbereitet. Mit vorbereitet meine ich, sie waren rund 300 an der Zahl... *Hick* \'tschuljung.' WHERE id=2722;
UPDATE npc_text SET Text0_0='$GJungchen:Mademeusele;, wenn ich es nicht besser wüsste, würde ich denken, Ihr würdet zu dieser... \'speziellen\' Sorte von Leuten gehören. Wir nennen sie Troggs.$B$BWindsor hatte kein Hühnchen mit den Dunkeleisenzwergen zu rupfen, immerhin war der beste Freund seines Ururgroßpapas ein Dunkeleisenzwerg. Aus diesem Grund hat die Dunkeleisenarmee ihn vermutlich auch nicht sofort getötet.' WHERE id=2723;
UPDATE npc_text SET Text0_0='Endlich! Steckt Euch die Finger in die Ohren; Euer Hirn scheint um das Fünffache gewachsen zu sein und ich will nicht, dass es rausquillt.$B$BAlso, die Dunkeleisenzwerge schonten sein Leben und nahmen ihn gefangen. Ihr Anführer, ein selbstgefälliger, aufgeblasener Bursche namens Thauris-Sowieso nahm Eisenfeind an sich. Und da hab ich den ollen Windsor zum letzten Mal gesehen... *Hick* \'tschuljung.' WHERE id=2725;
UPDATE npc_text SET Text0_0='Die Verderbnis des Teufelswaldes hat meinen Geist hier gefangen... Ich finde wohl niemals Ruhe...' WHERE id=2726;
UPDATE npc_text SET Text0_1='Willkommen im Grimmigen Säufer!', Text1_1='Trinkt unbedingt unser Dunkeleisenbier. Ich habe gehört, die Zwerge hier können gar nicht genug davon bekommen.', Text2_1='Mein Meister, Stöpsel Zapfring, ist ein guter Boss. Und er verdient eine Menge Geld. Seine Taschen platzen förmlich!' WHERE id=2727;
UPDATE npc_text SET Text0_1='Gute Idee!' WHERE id=2728;
UPDATE npc_text SET Text0_1='Habt Ihr die wunderbaren Dinge hier im Krater von Un\'Goro gesehen, $N? Einfach erstaunlich. Nicht so erstaunlich wie viele der Dinge, die ich mit meiner Ingenieursfertigkeit herstellen kann, aber da hier alles Natur pur ist... nicht übel.$B$BMein Onkel Wirrbart liebt altes Zeug wie diesen Ort. Ich trete irgendwie in seine Fußstapfen - auch ich möchte neue Sachen von alten Kulturen entdecken!' WHERE id=2734;
UPDATE npc_text SET Text0_1='Hmm, ich habe keine Mithrilgehäuse. Nichts mehr da. Mehr braucht Ihr nicht, um A-Me zu reparieren? Oh, das wäre phantastisch!$B$BMithrilgehäuse sind daheim in der Zivilisation nicht schwer zu finden. Bittet irgendeinen geschickten Ingenieur, Euch eins zu machen. Ich glaube, dazu sind ein paar Barren Mithril erforderlich, aber wenn Ihr mir aushelft, werdet Ihr gewiss gut belohnt!' WHERE id=2735;
UPDATE npc_text SET Text0_0='Das hier ist das beste und modernste Fortbewegungsmittel, das man für Geld überhaupt bauen kann. Was? Die Tiefenbahn? Gnome? Hört mal, $GKumpel:Mädel;, wenn Ihr diesen Stuss glaubt, dann hab\' ich eine Statue unten im Schlingendorntal, die ich Euch unbedingt verkaufen muss. Also, wollt Ihr jetzt nach Grom\'gol laufen oder wollt Ihr stilvoll in diesem wunderbaren Flugapparat reisen? Es liegt bei Euch.' WHERE id=2753;
UPDATE npc_text SET Text0_0='Seht Ihr mich an, $GJüngelchen:Mädchen;?', Text0_1='Seht Ihr mich an, $GJüngelchen:Mädchen;?', Text1_0='Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', Text1_1='Ich nehme einen Becher Dunkeleisenbier. Oh, seid Ihr nicht $Gder Kellner:die Kellnerin;?', Text2_0='Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', Text2_1='Ich glaube, Stöpsel hat eines seiner Fässchen zu voll gemacht. Sieht aus, als würde es gleich explodieren!', Text3_0='Stört mich nicht beim Abfeiern...', Text3_1='Stört mich nicht beim Abfeiern...' WHERE id=2758;
UPDATE npc_text SET Text0_0='Was ist denn Euer Ziel?', Text0_1='Was ist denn Euer Ziel?' WHERE id=2760;
UPDATE npc_text SET Text0_0='Habt Ihr neue Schätze, die Ihr sicher aufbewahren wollt? Dann solltet Ihr sie bei den Zastersteins im \'Tresor\' einlagern. $B$BIhr findet den \'Tresor\' knapp einen Hammerwurf nordöstlich vom Eingang nach Eisenschmiede.', Text0_1='Habt Ihr neue Schätze, die Ihr sicher aufbewahren wollt? Dann solltet Ihr sie bei den Zastersteins im \'Tresor\' einlagern. $B$BIhr findet den \'Tresor\' knapp einen Hammerwurf nordöstlich vom Eingang nach Eisenschmiede.', Text1_0='Die Bank? Oh, Ihr meint den \'Tresor\'! Es gibt keinen sichereren Ort für Wertsachen als den \'Tresor\'. Ihr findet ihn gleich nordöstlich des Eingangs von Eisenschmiede.', Text1_1='Die Bank? Oh, Ihr meint den \'Tresor\'! Es gibt keinen sichereren Ort für Wertsachen als den \'Tresor\'. Ihr findet ihn gleich nordöstlich des Eingangs von Eisenschmiede.' WHERE id=2761;
UPDATE npc_text SET Text0_0='Ihr habt Lust auf einen Greifenritt, ja? Mit diesem Anliegen solltet Ihr zu Gryth Thurden gehen. $B$BIhr findet ihn auf der Ostseite der großen Schmiede in der Mitte von Eisenschmiede.', Text0_1='Ihr habt Lust auf einen Greifenritt, ja? Mit diesem Anliegen solltet Ihr zu Gryth Thurden gehen. $B$BIhr findet ihn auf der Ostseite der großen Schmiede in der Mitte von Eisenschmiede.', Text1_0='Ah, nichts ist mit einem Greifenritt zu vergleichen. Die Nachtelfen können ihre Hippos getrost behalten, mir genügt ein Greif und der Wind in den Haaren! $B$BAber ich will Euch nicht länger aufhalten, geht einfach zu Gryth Thurden auf der Ostseite der großen Schmiede, der bringt Euch in die Lüfte.', Text1_1='Ah, nichts ist mit einem Greifenritt zu vergleichen. Die Nachtelfen können ihre Hippos getrost behalten, mir genügt ein Greif und der Wind in den Haaren! $B$BAber ich will Euch nicht länger aufhalten, geht einfach zu Gryth Thurden auf der Ostseite der großen Schmiede, der bringt Euch in die Lüfte.' WHERE id=2762;
UPDATE npc_text SET Text0_0='Wenn Ihr eine Gilde gründen möchtet, dann müsst Ihr zu Jondor Stahlbraue drüben im Besucherzentrum von Eisenschmiede gehen. Ihr findet ihn südöstlich des Eingangs von Eisenschmiede, wenn Ihr ihn sprechen wollt.', Text0_1='Wenn Ihr eine Gilde gründen möchtet, dann müsst Ihr zu Jondor Stahlbraue drüben im Besucherzentrum von Eisenschmiede gehen. Ihr findet ihn südöstlich des Eingangs von Eisenschmiede, wenn Ihr ihn sprechen wollt.', Text1_0='Na ja, Ihr braucht Papiere, wenn Ihr \'ne Gilde gründen wollt, und Jondor Stahlbraue kann Euch dabei helfen, die ganze Sache in Gang zu bringen. Ihr findet ihn drüben im Besucherzentrum von Eisenschmiede, wenn Ihr bereit seid, loszulegen.', Text1_1='Na ja, Ihr braucht Papiere, wenn Ihr \'ne Gilde gründen wollt, und Jondor Stahlbraue kann Euch dabei helfen, die ganze Sache in Gang zu bringen. Ihr findet ihn drüben im Besucherzentrum von Eisenschmiede, wenn Ihr bereit seid, loszulegen.' WHERE id=2764;
UPDATE npc_text SET Text0_0='Für welche Klasse sucht Ihr denn einen Lehrer?', Text0_1='Für welche Klasse sucht Ihr denn einen Lehrer?' WHERE id=2766;
UPDATE npc_text SET Text0_0='Das Gasthaus? Ah, Ihr wollt ein paar der besten Bierchen wegkippen, die Eisenschmiede zu bieten hat, was? Dann müsst Ihr Euch auf den Weg zur Steinfeuertaverne machen. $B$BIhr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst.', Text0_1='Das Gasthaus? Ah, Ihr wollt ein paar der besten Bierchen wegkippen, die Eisenschmiede zu bieten hat, was? Dann müsst Ihr Euch auf den Weg zur Steinfeuertaverne machen. $B$BIhr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst.', Text1_0='Wenn Ihr ein Gasthaus sucht, dann ist die Steinfeuertaverne die erste Adresse. Ihr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst. $B$BOh, und wenn Ihr dorthin geht, dann sagt ihnen, sie sollen schon mal einen Humpen für mich zapfen.', Text1_1='Wenn Ihr ein Gasthaus sucht, dann ist die Steinfeuertaverne die erste Adresse. Ihr findet sie links vom Eingang von Eisenschmiede, wenn Ihr von draußen kommt, oder rechts, wenn Ihr die Stadt verlasst. $B$BOh, und wenn Ihr dorthin geht, dann sagt ihnen, sie sollen schon mal einen Humpen für mich zapfen.' WHERE id=2768;
UPDATE npc_text SET Text0_0='Ihr findet einen Briefkasten gleich drüben bei der Steinfeuertaverne. Die liegt gleich nördlich des Stadttors von Eisenschmiede.', Text0_1='Ihr findet einen Briefkasten gleich drüben bei der Steinfeuertaverne. Die liegt gleich nördlich des Stadttors von Eisenschmiede.', Text1_0='Pah! Briefkästen... ich krieg die Tage nix als Reklame! $B$BMach das hier größer... mach das hier kleiner... fordere jetzt dein Gratis-Drachentöterschwert an... Ich muss alle zwei Monate meine Postfachnummer ändern, ehrlich! $B$BAlso sagt nicht, ich hätt Euch nicht gewarnt. Wenn Ihr also zum Briefkasten wollt, der ist direkt vor der Steinfeuertaverne.', Text1_1='Pah! Briefkästen... ich krieg die Tage nix als Reklame! $B$BMach das hier größer... mach das hier kleiner... fordere jetzt dein Gratis-Drachentöterschwert an... Ich muss alle zwei Monate meine Postfachnummer ändern, ehrlich! $B$BAlso sagt nicht, ich hätt Euch nicht gewarnt. Wenn Ihr also zum Briefkasten wollt, der ist direkt vor der Steinfeuertaverne.' WHERE id=2769;
UPDATE npc_text SET Text0_0='Gestern hörte ich Regnus Donnerstein in der Halle der Waffen über eine neue Trophäe für seinen Bau prahlen. Wenn Ihr Ausbildung bei einem versierten Jäger sucht, dann ist Regnus Euer Mann.', Text0_1='Gestern hörte ich Regnus Donnerstein in der Halle der Waffen über eine neue Trophäe für seinen Bau prahlen. Wenn Ihr Ausbildung bei einem versierten Jäger sucht, dann ist Regnus Euer Mann.', Text1_0='Habt Ihr das Potenzial, $Gein fähiger Jäger:eine fähige Jägerin; zu werden? Tja, $GJungchen:Mädel;, das wird nur die Zeit zeigen. Haltet Euch einfach ein bisschen zurück und schießt nicht auf Äpfel auf anderer Leute Köpfe, solange Ihr nicht mit Regnus drüben in der Halle der Waffen geredet habt.', Text1_1='Habt Ihr das Potenzial, $Gein fähiger Jäger:eine fähige Jägerin; zu werden? Tja, $GJungchen:Mädel;, das wird nur die Zeit zeigen. Haltet Euch einfach ein bisschen zurück und schießt nicht auf Äpfel auf anderer Leute Köpfe, solange Ihr nicht mit Regnus drüben in der Halle der Waffen geredet habt.' WHERE id=2770;
UPDATE npc_text SET Text0_0='Bink, die kleine Hexenbraut drüben in der Halle der Mysterien, kann Euch wahrscheinlich in allen magischen Belangen weiterhelfen. $B$BDie Halle der Mysterien liegt gleich nördlich des Stadttors von Eisenschmiede.', Text0_1='Bink, die kleine Hexenbraut drüben in der Halle der Mysterien, kann Euch wahrscheinlich in allen magischen Belangen weiterhelfen. $B$BDie Halle der Mysterien liegt gleich nördlich des Stadttors von Eisenschmiede.', Text1_0='Ausbildung in den magischen Künsten könnt Ihr ganz sicher drüben in der Halle der Mysterien nördlich des Stadttors von Eisenschmiede bekommen. Juli Sturmkessel ist dort und hat ständig die Nase in einem Buch.', Text1_1='Ausbildung in den magischen Künsten könnt Ihr ganz sicher drüben in der Halle der Mysterien nördlich des Stadttors von Eisenschmiede bekommen. Juli Sturmkessel ist dort und hat ständig die Nase in einem Buch.' WHERE id=2771;
UPDATE npc_text SET Text0_0='Ah, Braenna Kieselbruch. Sie hat magische Hände. Braenna muss man konsultieren, wenn es um Fragen der Priesterschaft geht.$B$BIhr müsst vom Stadttor von Eisenschmiede aus nach Norden ziehen, zur Halle der Mysterien. Da müsste sie zu finden sein.', Text0_1='Ah, Braenna Kieselbruch. Sie hat magische Hände. Braenna muss man konsultieren, wenn es um Fragen der Priesterschaft geht.$B$BIhr müsst vom Stadttor von Eisenschmiede aus nach Norden ziehen, zur Halle der Mysterien. Da müsste sie zu finden sein.', Text1_0='Ach, es gibt doch nichts Besseres als einen Priester, um Eure Leiden zu heilen. Klar, ein schönes, großes Bier heilt auch \'ne Menge Leiden, aber ich glaube nicht, dass Braenna Kieselbruch einem beibringt, wie man braut. Und deswegen, schätze ich, müsst Ihr Euch wohl mit \'n bisschen Priesterausbildung in der Halle der Mysterien begnügen.', Text1_1='Ach, es gibt doch nichts Besseres als einen Priester, um Eure Leiden zu heilen. Klar, ein schönes, großes Bier heilt auch \'ne Menge Leiden, aber ich glaube nicht, dass Braenna Kieselbruch einem beibringt, wie man braut. Und deswegen, schätze ich, müsst Ihr Euch wohl mit \'n bisschen Priesterausbildung in der Halle der Mysterien begnügen.' WHERE id=2772;
UPDATE npc_text SET Text0_0='Brandur Eisenhammer ist Euer Mann. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', Text0_1='Brandur Eisenhammer ist Euer Mann. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', Text1_0='Ah, der gute alte Brandur Eisenhammer. Er wird Euch zweifellos das Wissen geben können, das Ihr sucht, $GJungchen:Mädel;. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.', Text1_1='Ah, der gute alte Brandur Eisenhammer. Er wird Euch zweifellos das Wissen geben können, das Ihr sucht, $GJungchen:Mädel;. Ihr findet ihn in der Halle der Mysterien, nördlich des Stadttors von Eisenschmiede.' WHERE id=2773;
UPDATE npc_text SET Text0_0='Na, warum bedürft Ihr wohl der Dienste von einem dieser Kerle? Hulfdan Schwarzbart, dieser Schurke drüben im düsteren Viertel, macht uns schon genug Ärger, muss ich da auch noch Euch im Auge behalten?', Text0_1='Na, warum bedürft Ihr wohl der Dienste von einem dieser Kerle? Hulfdan Schwarzbart, dieser Schurke drüben im düsteren Viertel, macht uns schon genug Ärger, muss ich da auch noch Euch im Auge behalten?', Text1_0='Diese ganzen Schurkenkerle hängen im düsteren Viertel nördlich des Stadttors von Eisenschmiede herum. Ich wette, Ihr findet Fenthwick dort, und zweifellos wird er die Münzen in einer weiteren gestohlenen Börse zählen. Eines Tages werde ich ihn erwischen! $B$BBis dahin behalte ich Euch ebenfalls im Auge, $GJungchen:Mädchen;.', Text1_1='Diese ganzen Schurkenkerle hängen im düsteren Viertel nördlich des Stadttors von Eisenschmiede herum. Ich wette, Ihr findet Fenthwick dort, und zweifellos wird er die Münzen in einer weiteren gestohlenen Börse zählen. Eines Tages werde ich ihn erwischen! $B$BBis dahin behalte ich Euch ebenfalls im Auge, $GJungchen:Mädchen;.' WHERE id=2774;
UPDATE npc_text SET Text0_0='Wo sollte man wohl nach einer finsteren und grimmigen Bande suchen, wenn nicht im verrufenen Teil von Eisenschmiede, der so zutreffend düsteres Viertel genannt wird. Distelherz ist ganz sicher dort zu finden und wahrscheinlich beschwört er gerade eine garstige Kreatur, ihm zu dienen.', Text0_1='Wo sollte man wohl nach einer finsteren und grimmigen Bande suchen, wenn nicht im verrufenen Teil von Eisenschmiede, der so zutreffend düsteres Viertel genannt wird. Distelherz ist ganz sicher dort zu finden und wahrscheinlich beschwört er gerade eine garstige Kreatur, ihm zu dienen.', Text1_0='Ein Hexenmeister, hm... Also, wenn Ihr vorhabt, Euch hier in der Gegend mit so was zu beschäftigen, dann solltet Ihr Eure Dämonen besser an der Leine halten, $GJungchen:Mädel;. Dämonen kann ich nicht leiden, aber ich finde es toll, ihnen mit meiner Axt eins überzubraten. Alles klar? $B$BAlso lasst mich jetzt gefälligst in Ruhe und verzieht Euch in das düstere Viertel. Da findet Ihr einen Hexenmeister namens Distelherz.', Text1_1='Ein Hexenmeister, hm... Also, wenn Ihr vorhabt, Euch hier in der Gegend mit so was zu beschäftigen, dann solltet Ihr Eure Dämonen besser an der Leine halten, $GJungchen:Mädel;. Dämonen kann ich nicht leiden, aber ich finde es toll, ihnen mit meiner Axt eins überzubraten. Alles klar? $B$BAlso lasst mich jetzt gefälligst in Ruhe und verzieht Euch in das düstere Viertel. Da findet Ihr einen Hexenmeister namens Distelherz.' WHERE id=2775;
UPDATE npc_text SET Text0_0='Soso! Ihr möchtet also \'ne Ausbildung zum Krieger, wie? Kelstrum Steinbrecher zeigt Euch, wo\'s langgeht! Marschiert einfach rüber zur Halle der Waffen, östlich vom Stadttor von Eisenschmiede.', Text0_1='Soso! Ihr möchtet also \'ne Ausbildung zum Krieger, wie? Kelstrum Steinbrecher zeigt Euch, wo\'s langgeht! Marschiert einfach rüber zur Halle der Waffen, östlich vom Stadttor von Eisenschmiede.', Text1_0='Kelstrum is\' \'n Großmaul und \'n Angeber... und als wenn das nicht schon genug wär, isser auch noch so ziemlich der beste Krieger, dem Ihr je begegnen werdet.$B$BEins sag ich Euch: Wenn Ihr Euch zum Krieger ausbilden lassen wollt, dann is\' er der Richtige für Euch. Geht zur Halle der Waffen, östlich des Stadttors von Eisenschmiede, dort müsste er zu finden sein.', Text1_1='Kelstrum is\' \'n Großmaul und \'n Angeber... und als wenn das nicht schon genug wär, isser auch noch so ziemlich der beste Krieger, dem Ihr je begegnen werdet.$B$BEins sag ich Euch: Wenn Ihr Euch zum Krieger ausbilden lassen wollt, dann is\' er der Richtige für Euch. Geht zur Halle der Waffen, östlich des Stadttors von Eisenschmiede, dort müsste er zu finden sein.' WHERE id=2776;
UPDATE npc_text SET Text0_0='Für welchen Beruf sucht Ihr denn einen Lehrer?', Text0_1='Für welchen Beruf sucht Ihr denn einen Lehrer?' WHERE id=2793;
UPDATE npc_text SET Text0_0='Einen Lehrer für Alchemie braucht Ihr also, ah ja. Tja, da war ich auch gerade. Ihr findet Ms. Brausefitz in der Tüftlerstadt, wo sich die Gnome treffen.', Text0_1='Einen Lehrer für Alchemie braucht Ihr also, ah ja. Tja, da war ich auch gerade. Ihr findet Ms. Brausefitz in der Tüftlerstadt, wo sich die Gnome treffen.', Text1_0='Ach, Ihr sucht also einen Lehrer für Alchemie? Von so einem komm ich gerade her. Ihr findet Ms. Brausefitz in der Tüftlerstadt, wo sich die Gnome treffen. $B$B<seufz> ...warum, oh warum nur habe ich den blauen Trank nicht genommen.', Text1_1='Ach, Ihr sucht also einen Lehrer für Alchemie? Von so einem komm ich gerade her. Ihr findet Ms. Brausefitz in der Tüftlerstadt, wo sich die Gnome treffen. $B$B<seufz> ...warum, oh warum nur habe ich den blauen Trank nicht genommen.' WHERE id=2794;
UPDATE npc_text SET Text0_0='Ihr seid hier genau richtig, $GJungchen:Mädel;! Die Leute kommen aus der ganzen Welt hierher, um sich zum Schmied ausbilden zu lassen. Bengus Tiefenschmied wird zweifellos mitten in unserer schönen Stadt, bei der großen Schmiede selbst, auf seinem Amboss herumhämmern.', Text0_1='Ihr seid hier genau richtig, $GJungchen:Mädel;! Die Leute kommen aus der ganzen Welt hierher, um sich zum Schmied ausbilden zu lassen. Bengus Tiefenschmied wird zweifellos mitten in unserer schönen Stadt, bei der großen Schmiede selbst, auf seinem Amboss herumhämmern.', Text1_0='Fragt Ihr mich echt, wo die Schmiede is\'? Habt Ihr etwa noch nie von der großen Schmiede gehört? Da treffen sich all unsere besten Schmiede und stellen ihre Waffen und Rüstungen her! $B$BDie is\' genau in der Mitte von Eisenschmiede, un\' da findet Ihr einen unserer genialsten Schmiede, Bengus Tiefenschmied.', Text1_1='Fragt Ihr mich echt, wo die Schmiede is\'? Habt Ihr etwa noch nie von der großen Schmiede gehört? Da treffen sich all unsere besten Schmiede und stellen ihre Waffen und Rüstungen her! $B$BDie is\' genau in der Mitte von Eisenschmiede, un\' da findet Ihr einen unserer genialsten Schmiede, Bengus Tiefenschmied.' WHERE id=2795;
UPDATE npc_text SET Text0_0='Ein Kochkunstlehrer soll es sein? Wohlan, begebt Euch \'Zum Bronzekessel\' auf der nordöstlichen Seite der großen Schmiede. Daryl Riknussun bringt Euch bei, wie man ein sagenhaft leckeres Gericht zubereitet, das Eure Lebensgeister ganz bestimmt wieder weckt!', Text0_1='Ein Kochkunstlehrer soll es sein? Wohlan, begebt Euch \'Zum Bronzekessel\' auf der nordöstlichen Seite der großen Schmiede. Daryl Riknussun bringt Euch bei, wie man ein sagenhaft leckeres Gericht zubereitet, das Eure Lebensgeister ganz bestimmt wieder weckt!', Text1_0='Hey! Dieser Darly Riknussun is\' \'n genialer Koch! Der macht \'n Chili, mit dem könnt man Stahl härten! Ich glaub nich\', dass er Euch das Rezept verrät, aber er kann Euch garantiert dabei helfen, $G\'n guter Koch:\'ne gute Köchin; zu werden. Ihr findet ihn im \'Bronzekessel\' auf der nordöstlichen Seite von der großen Schmiede.', Text1_1='Hey! Dieser Darly Riknussun is\' \'n genialer Koch! Der macht \'n Chili, mit dem könnt man Stahl härten! Ich glaub nich\', dass er Euch das Rezept verrät, aber er kann Euch garantiert dabei helfen, $G\'n guter Koch:\'ne gute Köchin; zu werden. Ihr findet ihn im \'Bronzekessel\' auf der nordöstlichen Seite von der großen Schmiede.' WHERE id=2796;
UPDATE npc_text SET Text0_0='Ah, ich glaube, Gimble Distelflaum ist Euer Mann, wenn Ihr den Beruf des Verzauberers erlernen wollt. Ihr findet Gimble in seinem Laden, \'Arkanarien Distelflaum\', direkt beim Greifenmeister an der großen Schmiede.', Text0_1='Ah, ich glaube, Gimble Distelflaum ist Euer Mann, wenn Ihr den Beruf des Verzauberers erlernen wollt. Ihr findet Gimble in seinem Laden, \'Arkanarien Distelflaum\', direkt beim Greifenmeister an der großen Schmiede.', Text1_0='Hey! Ihr solltet aufpassen, was Ihr Euch verzaubern lasst, $GJungchen:Mädel;. Ich hab meinen Becher verzaubern lassen, damit nix verschütt\' geht, und jetzt kann ich nich\' mehr draus trinken! Echt tragisch, das.$B$BVergesst das bloß nich\', wenn Ihr bei Gimble seid. Er is\' in sei\'m Laden direkt beim Greifenmeister an der großen Schmiede.', Text1_1='Hey! Ihr solltet aufpassen, was Ihr Euch verzaubern lasst, $GJungchen:Mädel;. Ich hab meinen Becher verzaubern lassen, damit nix verschütt\' geht, und jetzt kann ich nich\' mehr draus trinken! Echt tragisch, das.$B$BVergesst das bloß nich\', wenn Ihr bei Gimble seid. Er is\' in sei\'m Laden direkt beim Greifenmeister an der großen Schmiede.' WHERE id=2797;
UPDATE npc_text SET Text0_0='Das Tüftlerhandwerk soll es also für Euch sein? Dann solltet Ihr zweifellos Sprungspindel Zischelgang in der Tüftlerstadt aufsuchen. $B$BGeht einfach vom Stadttor von Eisenschmiede aus nach Osten oder folgt dem Knallen der Explosionen.', Text0_1='Das Tüftlerhandwerk soll es also für Euch sein? Dann solltet Ihr zweifellos Sprungspindel Zischelgang in der Tüftlerstadt aufsuchen. $B$BGeht einfach vom Stadttor von Eisenschmiede aus nach Osten oder folgt dem Knallen der Explosionen.', Text1_0='Ach, dann müsst Ihr mit Sprungspindel Zischelgang reden. Jaja, dieser kleine Gnom und seine Gerätschaften... Eins sag ich Euch: Wenn Ihr in seinem Laden in der Tüftlerstadt seid, dann behaltet Eure Finger bei Euch und fasst seine mechanischen Schafe nicht an! $B$BExplodierende Schafe... wer kommt bloß auf solche Ideen...', Text1_1='Ach, dann müsst Ihr mit Sprungspindel Zischelgang reden. Jaja, dieser kleine Gnom und seine Gerätschaften... Eins sag ich Euch: Wenn Ihr in seinem Laden in der Tüftlerstadt seid, dann behaltet Eure Finger bei Euch und fasst seine mechanischen Schafe nicht an! $B$BExplodierende Schafe... wer kommt bloß auf solche Ideen...' WHERE id=2798;
UPDATE npc_text SET Text0_0='Erste Hilfe ist eine wertvolle Fertigkeit. Nissa Feuerstein, unsere hiesige Lehrerin, kann Euch zeigen, wie es gemacht wird. Ihr findet sie auf der Südseite der großen Schmiede beim \'Heiler von Eisenschmiede\'.', Text0_1='Erste Hilfe ist eine wertvolle Fertigkeit. Nissa Feuerstein, unsere hiesige Lehrerin, kann Euch zeigen, wie es gemacht wird. Ihr findet sie auf der Südseite der großen Schmiede beim \'Heiler von Eisenschmiede\'.', Text1_0='Erste Hilfe? Also Ihr seht mir nich\' so aus, als wenn Ihr verletzt wärt. Wenn Ihr wirklich Erste Hilfe lernen wollt, dann is\' Nissa Feuerstein die Richtige für Euch. Besucht se doch mal in ihr\'m Laden, das is\' der \'Heiler von Eisenschmiede\', drüben bei der großen Schmiede.', Text1_1='Erste Hilfe? Also Ihr seht mir nich\' so aus, als wenn Ihr verletzt wärt. Wenn Ihr wirklich Erste Hilfe lernen wollt, dann is\' Nissa Feuerstein die Richtige für Euch. Besucht se doch mal in ihr\'m Laden, das is\' der \'Heiler von Eisenschmiede\', drüben bei der großen Schmiede.' WHERE id=2799;
UPDATE npc_text SET Text0_0='Grimnur Steinmal wird Euch Geschichten über Fische, die ihm entkommen sind, auftischen, dass sich die Balken biegen, aber er ist trotzdem einer der besten Angler in der Gegend. Er hat im düsteren Viertel einen Laden, der \'Reisender Angler\' heißt.', Text0_1='Grimnur Steinmal wird Euch Geschichten über Fische, die ihm entkommen sind, auftischen, dass sich die Balken biegen, aber er ist trotzdem einer der besten Angler in der Gegend. Er hat im düsteren Viertel einen Laden, der \'Reisender Angler\' heißt.', Text1_0='Ich hab mal gehört, wie der alte Grimnur erzählt hat, dass er sich\'n Schwert geangelt hätt\'. Oder war\'s \'n Schwertfisch? Ach verdammt, Grimnur hat so viele Fischgeschichten erzählt, dass ich gar nicht mehr weiß, was jetzt was war. Egal, warum geht Ihr nich\' rüber ins düstere Viertel und lasst Euch von ihm selbst erzählen, was es genau war.', Text1_1='Ich hab mal gehört, wie der alte Grimnur erzählt hat, dass er sich\'n Schwert geangelt hätt\'. Oder war\'s \'n Schwertfisch? Ach verdammt, Grimnur hat so viele Fischgeschichten erzählt, dass ich gar nicht mehr weiß, was jetzt was war. Egal, warum geht Ihr nich\' rüber ins düstere Viertel und lasst Euch von ihm selbst erzählen, was es genau war.' WHERE id=2800;
UPDATE npc_text SET Text0_0='Blumen und Samenkörner und dergleichen? Das wäre Reyna Steinzweigs Fachgebiet. Sie hegt und pflegt ihre Pflanzen beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', Text0_1='Blumen und Samenkörner und dergleichen? Das wäre Reyna Steinzweigs Fachgebiet. Sie hegt und pflegt ihre Pflanzen beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', Text1_0='Oh, Ihr wollt lernen, wie man Gänseblümchen pflückt, ja? Ha! Ich nehm Euch doch nur auf den Arm, $GJüngelchen:Mädel;. $B$BEs ist ein guter Beruf, und Reyna Steinzweig kann Euch alles darüber beibringen. Sie arbeitet beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.', Text1_1='Oh, Ihr wollt lernen, wie man Gänseblümchen pflückt, ja? Ha! Ich nehm Euch doch nur auf den Arm, $GJüngelchen:Mädel;. $B$BEs ist ein guter Beruf, und Reyna Steinzweig kann Euch alles darüber beibringen. Sie arbeitet beim \'Heiler von Eisenschmiede\' auf der Südseite der großen Schmiede.' WHERE id=2801;
UPDATE npc_text SET Text0_0='Fimble Feinspindel hat einen Laden auf der Nordwestseite der großen Schmiede. Ich wette, der könnte Euch zeigen, wie man eine erlesene Rüstung macht.', Text0_1='Fimble Feinspindel hat einen Laden auf der Nordwestseite der großen Schmiede. Ich wette, der könnte Euch zeigen, wie man eine erlesene Rüstung macht.', Text1_0='Seid Ihr sicher, dass Ihr kein Schmied werden wollt? Na, wenn Ihr Euch partout auf die Lederverarbeitung versteift habt, dann solltet Ihr am besten mit Fimble Feinspindel reden, drüben in seinem Laden auf der nordwestlichen Seite der großen Schmiede.', Text1_1='Seid Ihr sicher, dass Ihr kein Schmied werden wollt? Na, wenn Ihr Euch partout auf die Lederverarbeitung versteift habt, dann solltet Ihr am besten mit Fimble Feinspindel reden, drüben in seinem Laden auf der nordwestlichen Seite der großen Schmiede.' WHERE id=2802;
UPDATE npc_text SET Text0_0='Ob wir hier Bergbaulehrer haben? Was denkt Ihr denn, wo Ihr seid!?$B$BIhr findet einen der besten Minenarbeiter in ganz Dun Morogh in der Tiefenbergbaugilde auf der Nordseite der großen Schmiede. Dort wird Euch Geofram Geröllzeh in die wahre Kunst des Bergbaus einweisen.', Text0_1='Ob wir hier Bergbaulehrer haben? Was denkt Ihr denn, wo Ihr seid!?$B$BIhr findet einen der besten Minenarbeiter in ganz Dun Morogh in der Tiefenbergbaugilde auf der Nordseite der großen Schmiede. Dort wird Euch Geofram Geröllzeh in die wahre Kunst des Bergbaus einweisen.', Text1_0='Sieh an, sieh an! Wenn Ihr die Kunst des Bergbaus erlernen wollt, dann seid Ihr hier haargenau richtig, $GJungchen:Mädel;! Geofram Geröllzeh höchstpersönlich hält sich in der Tiefenbergbaugilde auf. Einen besseren Lehrer als ihn werdet Ihr nirgendwo finden.', Text1_1='Sieh an, sieh an! Wenn Ihr die Kunst des Bergbaus erlernen wollt, dann seid Ihr hier haargenau richtig, $GJungchen:Mädel;! Geofram Geröllzeh höchstpersönlich hält sich in der Tiefenbergbaugilde auf. Einen besseren Lehrer als ihn werdet Ihr nirgendwo finden.' WHERE id=2804;
UPDATE npc_text SET Text0_0='Balthus Steinschinder muss man konsultieren, wenn es um die Kürschnerei geht. Wenn man etwas häuten kann, dann zeigt er Euch, wie es geht. Wandert einfach zu \'Lederwaren Feinspindel\' auf der Nordseite der großen Schmiede.', Text0_1='Balthus Steinschinder muss man konsultieren, wenn es um die Kürschnerei geht. Wenn man etwas häuten kann, dann zeigt er Euch, wie es geht. Wandert einfach zu \'Lederwaren Feinspindel\' auf der Nordseite der großen Schmiede.', Text1_0='Kürschnerei? Ihr seht nich\' so aus, als ob Ihr was anderes kürschnern könntet als die Haut von Euren eigenen Knien! Ha! Also warum sucht Ihr nich\' Balthus Steinschinder auf, damit der Euch beibringt, wie man Tiere anständig häutet! Ihr findet ihn bei \'Lederwaren Feinspindel\' auf der Nordseite der großen Schmiede.', Text1_1='Kürschnerei? Ihr seht nich\' so aus, als ob Ihr was anderes kürschnern könntet als die Haut von Euren eigenen Knien! Ha! Also warum sucht Ihr nich\' Balthus Steinschinder auf, damit der Euch beibringt, wie man Tiere anständig häutet! Ihr findet ihn bei \'Lederwaren Feinspindel\' auf der Nordseite der großen Schmiede.' WHERE id=2805;
UPDATE npc_text SET Text0_0='Dieser Jormund Steinbraue hat den Bogen mit der Nadel raus. Wenn Ihr eine Ausbildung braucht, dann findet Ihr ihn in der \'Tuchmacherei Steinbraue\' auf der Nordseite der großen Schmiede.', Text0_1='Dieser Jormund Steinbraue hat den Bogen mit der Nadel raus. Wenn Ihr eine Ausbildung braucht, dann findet Ihr ihn in der \'Tuchmacherei Steinbraue\' auf der Nordseite der großen Schmiede.', Text1_0='Vielleicht wisst Ihr\'s nich\', aber Jormund is\' nich\' nur \'n fähiger Schneider, sondern auch \'n Riesentöter! Er hat mal gehört, wie \'n paar von uns Geschichten erzählten, und da hat er einfach dazwischengebrüllt, dass er mal sieben auf einen Streich getötet hätte.$B$BIhr solltet ihn echt mal in seinem Laden im Norden von der großen Schmiede aufsuchen - vielleicht erzählt er Euch die Geschichte!', Text1_1='Vielleicht wisst Ihr\'s nich\', aber Jormund is\' nich\' nur \'n fähiger Schneider, sondern auch \'n Riesentöter! Er hat mal gehört, wie \'n paar von uns Geschichten erzählten, und da hat er einfach dazwischengebrüllt, dass er mal sieben auf einen Streich getötet hätte.$B$BIhr solltet ihn echt mal in seinem Laden im Norden von der großen Schmiede aufsuchen - vielleicht erzählt er Euch die Geschichte!' WHERE id=2807;
UPDATE npc_text SET Text0_0='Ich habe gelitten, aber jetzt spüre ich, wie es langsam nachlässt. Ich fühle mich... frei.' WHERE id=2809;
UPDATE npc_text SET Text0_0='Die Kristallpylonen von Un\'Goro sind allen ein Rätsel. Die wenigen Einwohner hier sehen sie als Legenden und spekulieren viel über ihren Sinn und Zweck.' WHERE id=2810;
UPDATE npc_text SET Text0_0='Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit blauen, gelben und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer blauer Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus gelben und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.' WHERE id=2811;
UPDATE npc_text SET Text0_0='Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit gelben, roten und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer gelber Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus roten und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.' WHERE id=2812;
UPDATE npc_text SET Text0_0='Nach eingehenderer Untersuchung des Pylons findet Ihr eine kleine Schrifttafel mit roten, blauen und grünen Kristallen, die in einer bestimmten Folge angeordnet zu sein scheinen.$B$BEin großer roter Kreis von Kristallen wird auf beiden Seiten von einem kleineren Kreis aus blauen und einem weiteren aus grünen flankiert.$B$BIhr prägt Euch die Farben der Kristalle sorgfältig ein, da Ihr wisst, J. D. wird größtes Interesse für Euren Fund zeigen.' WHERE id=2813;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Für einige ist Jäger eine Lebensweise, ein erwählter Beruf. Für andere ein Zeitvertreib, eine Mutprobe, ein Initiationsritual, je nach Stamm.$B$BFür mich sind Volk und Beruf bedeutungslos - die Prüfungen von Geist und Verstand sind es, die ich respektiere. Wenn sich jemand gegen die Naturgewalten stellt und all seine Fertigkeiten der Prüfung unterzieht, ist das für mich ein Zeichen von Ehre.$B$BWenn Ihr Euch prüfen und meinen Respekt erlangen wollt, sprecht weiter mit mir.' WHERE id=2816;
UPDATE npc_text SET Text0_1='Ihr habt wahrscheinlich nicht erwartet, mich in dieser Höhle zu finden, was?$B$BMein Name ist J. D. und ich kann Euch wirklich nicht sagen, wie ich hierher gekommen bin, aber eines weiß ich, diese Höhle ist jetzt mein Zuhause!$B$BIch habe mein Leben lang die seltsamen Kristalle studiert, die man überall in dem Krater finden kann.' WHERE id=2817;
UPDATE npc_text SET Text0_0='Brennende Steppe - Schrifttafel der Sieben' WHERE id=2819;
UPDATE npc_text SET Text0_0='Die Symbole auf dem Täfelchen werden sorgfältig abgeschrieben...' WHERE id=2820;
UPDATE npc_text SET Text0_0='Ihr habt den Weg der Ehre eingeschlagen, $N. Meine Vorfahren respektieren Eure Bereitwilligkeit, aber mein Wohlwollen erlangt Ihr erst, wenn Ihr meine Aufgaben erledigt habt.$B$BIhr wisst, was Ihr tun müsst, wenn Ihr Euch als Jäger im Geiste beweisen wollt.' WHERE id=2821;
UPDATE npc_text SET Text0_0='Ich grüße Euch aufs Herzlichste, $C, genau wie die Zerschmetterte Hand.$B$BSchaut nicht so überrascht drein, $N. Schatten gibt es selbst auf den höchsten Zinnen von Orgrimmar - dort muss sich etwas verbergen. Die Zerschmetterte Hand übernimmt diese Rolle bereitwillig. Thrall stimmt trotz seines gütigen Herzens darin zu, dass die Hand notwendig ist, um die Sicherheit von Orcs und Trollen zu gewährleisten.$B$BAber Ihr seid nicht hier, um über mein Geschäft zu sprechen, sondern wegen etwas anderem.' WHERE id=2822;
UPDATE npc_text SET Text0_0='Der Krater von Un\'Goro liegt weit im Süden von Kalimdor. Jenseits von Tausend Nadeln in Tanaris. Sucht nach den großen schwebenden Steinen... sie kennzeichnen den Eingang zum Krater. Aber seid vorsichtig... es ist kein leichter Weg. Die Raptoren sind nicht weit vom Eingang entfernt.' WHERE id=2823;
UPDATE npc_text SET Text0_1='Dank Euch können wir jetzt die Geheimnisse der Kristalle und der geheimnisvollen Pylonen entschlüsseln, $N. Wenn Ihr irgendwann mehr Informationen benötigt, kommt zurück und besucht mich wieder!' WHERE id=2833;
UPDATE npc_text SET Text0_1='Der östliche Pylon akzeptiert blaue, gelbe und grüne Kristalle.$B$BEr fokussiert auf die Energie, die mit den blauen Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer verleiht der Willenskraft einen Bonus, einer erzeugt einen Schaden zufügenden Schild.$B$B\'Kristallkraft\' erfordert blaue und grüne Kristalle. Das verleiht Euch eine Erhöhung der Willenskraft.$B$B\'Kristallspitze\' erfordert blaue und gelbe Kristalle. Damit wird ein Schild erzeugt, der Angreifern Schaden zufügt.' WHERE id=2834;
UPDATE npc_text SET Text0_0='Die Ravasaurier hier in Un\'Goro scheinen mit den Raptoren des Brachlands verwandt zu sein - hochintelligent, gefräßig und mit einem gewissen Verhaltenskodex. Der größte von allen, denen ich begegnet bin, ist Lar\'korwi. Er ist eine der gefährlichsten Bestien, die ich je gesehen habe. Auf Taurisch heißt Lar\'korwi so viel wie "Scharfe Klaue", aber das wird ihm nicht gerecht.$B$BUnterschätzt Lar\'korwi nicht, wenn Ihr je vorhaben solltet, ihn zu jagen. Aber wenn Ihr das beabsichtigt, unterstütze ich Euch natürlich in jeder erdenklichen Weise.' WHERE id=2835;
UPDATE npc_text SET Text0_1='Der nördliche Pylon akzeptiert gelbe, rote und grüne Kristalle.$B$BDer Fokus ist die Energie, die mit den gelben Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer erzeugt Schaden und einer negiert Schaden. $B$B\'Kristallsprengladung\' erfordert rote und gelbe Kristalle. Damit wird eine gewaltige Explosion erzeugt, die Eure Feinde lähmt.$B$B\'Kristallflicker\' erfordert gelbe und grüne Kristalle. Das verleiht Euch eine heilende Aura.' WHERE id=2836;
UPDATE npc_text SET Text0_1='Der westliche Pylon akzeptiert rote, grüne und blaue Kristalle.$B$BDer Pylon fokussiert auf die Energie, die mit den roten Kristallen als Hauptenergiequelle erzeugt werden kann. Es gibt zwei mögliche Effekte: Einer erhöht Euren Schutz vor Schaden, einer schwächt die Verteidigung des Gegners.$B$B\'Kristallbarriere\' erfordert rote und grüne Kristalle. Das verleiht Euch eine Erhöhung der Rüstung.$B$B\'Kristallschwächer\' erfordert blaue und rote Kristalle. Damit kann die Rüstung eines bestimmten Ziels verringert werden.' WHERE id=2837;
UPDATE npc_text SET Text0_1='Klar doch, $N. Hier ist noch so \'n Teil für Euch.' WHERE id=2838;
UPDATE npc_text SET Text0_1='Dieser einst herrliche Wald leidet unter einem schrecklichen Fluch! Kräfte der Verderbnis lassen das Leben hier zu einem Schatten seiner einstigen Pracht verkommen. Wenn Ihr kein Agent dieser Verderbnis seid, könnt Ihr mir vielleicht helfen, den Teufelswald wieder für das Leben zurückzuerobern!' WHERE id=2842;
UPDATE npc_text SET Text0_1='"Taurahe?"$B$BDie Taurendruidin sieht Euch erwartungsvoll an, aber Euch ist klar, dass Ihr verbal nicht mit ihr kommunizieren könnt. Unbeeindruckt zeigt sie auf sich selbst und lächelt. Sie zeigt auf Euch und lächelt abermals. Schließlich weist sie mehrmals den Weg nach Süden entlang.$B$BVielleicht will sie damit sagen, dass weiter unten an der Straße ein Druide ist, mit dem Ihr sprechen könnt?' WHERE id=2843;
UPDATE npc_text SET Text0_1='Dieser einst herrliche Wald leidet unter einem schrecklichen Fluch! Kräfte der Verderbnis lassen das Leben hier zu einem Schatten seiner einstigen Pracht verkommen. Wenn Ihr kein Agent dieser Verderbnis seid, könnt Ihr mir vielleicht helfen, den Teufelswald wieder für das Leben zurückzuerobern!' WHERE id=2844;
UPDATE npc_text SET Text0_1='"Darnassae?"$B$BDie Nachtelf-Druidin sieht Euch erwartungsvoll an, aber Euch ist klar, dass Ihr verbal nicht mit ihr kommunizieren könnt. Unbeeindruckt zeigt sie auf sich selbst und lächelt. Sie zeigt auf Euch und lächelt abermals. Schließlich weist sie mehrmals den Weg nach Norden entlang.$B$BVielleicht will sie damit sagen, dass weiter oben an der Straße ein Druide ist, mit dem Ihr sprechen könnt?' WHERE id=2845;
UPDATE npc_text SET Text0_0='HILFE!!!' WHERE id=2846;
UPDATE npc_text SET Text0_1='Es gibt Pflanzen im Teufelswald, die vor der Verderbnis gerettet werden können, wenn man sie mit Cenarischer Pflanzensalbe einreibt. Ich weiß, wie man die Salbe aus verschiedenen Arten von Dingen herstellt; je nach Euren Fertigkeitswerten habt Ihr verschiedene Möglichkeiten, um mir zu helfen.$B$BAußerdem braucht Ihr ein cenarisches Leuchtfeuer, um hier zu arbeiten; das ist ein Mechanismus, mit dem Ihr die Besudelung und Verderbnis in der Natur erkennen könnt, die man mit bloßem Auge nicht sehen kann.' WHERE id=2848;
UPDATE npc_text SET Text0_1='Es gibt Pflanzen im Teufelswald, die vor der Verderbnis gerettet werden können, wenn man sie mit Cenarischer Pflanzensalbe einreibt. Ich weiß, wie man die Salbe aus verschiedenen Arten von Dingen herstellt; je nach Euren Fertigkeitswerten habt Ihr verschiedene Möglichkeiten, um mir zu helfen.$B$BAußerdem braucht Ihr ein cenarisches Leuchtfeuer, um hier zu arbeiten; das ist ein Mechanismus, mit dem Ihr die Besudelung und Verderbnis in der Natur erkennen könnt, die man mit bloßem Auge nicht sehen kann.' WHERE id=2849;
UPDATE npc_text SET Text0_0='Die Überreste sind mit Fliegen und Maden übersät.' WHERE id=2850;
UPDATE npc_text SET Text0_0='Vor vielen Jahren errichteten die Nachtelfen, die dieses Land bewohnten, diesen Schrein.$B$BWelche Geheimnisse mag er bergen?' WHERE id=2873;
UPDATE npc_text SET Text0_0='Die in den Pylonen von Un\'Goro verborgenen Geheimnisse sind jetzt Euer. Ihr könnt die Pylonen jederzeit besuchen, ihre Macht nutzen und nützliche Rohstoffe bekommen.' WHERE id=2933;
UPDATE npc_text SET Text0_0='Wie ein Fels in der Brandung sticht diese Liedblumenpflanze aus der Verderbnis hervor, die den ganzen Teufelswald durchzieht. Ihre Blüten verströmen einen starken Duft, der Euren Geist belebt und Eure Entschlossenheit stärkt.' WHERE id=2953;
UPDATE npc_text SET Text0_0='Willkommen in Tanaris, $N. Wie kann ich Euch heute dienlich sein? Ihr seht mir nicht wie ein Schmied aus, aber das Aussehen kann täuschen.' WHERE id=2954;
UPDATE npc_text SET Text0_0='Wie ein Leuchtfeuer in der Dunkelheit sticht diese Windblütenpflanze aus der Verderbtheit hervor, die den ganzen Teufelswald durchzieht. Pralle, saftige Beeren hängen von der Pflanze herab sie haben die richtige Reife, um gepflückt zu werden.' WHERE id=2955;
UPDATE npc_text SET Text0_0='Diese gesunde Peitscherwurzelpflanze ist ein krasser Gegensatz zu der kränklichen Besudelung der Verderbnis, die dan übrigen Teufelswald beherrscht. Aus zarten und gesunden Wurzeln sprießen köstliche Knollen, die neben der Pflanze am Boden liegen."' WHERE id=2956;
UPDATE npc_text SET Text0_0='Diese vor Leben sprühende Nachtdrachenpflanze steht in krassem Gegensatz zu der dämonischen Besudelung, die den ganzen Teufelswald umfangen hält. Die Pflanze biegt sich unter der Last rarer und seltener Früchte, bekannt als \'Nachtdrachenodem\'.' WHERE id=2957;
UPDATE npc_text SET Text0_0='Konversation liegt mir nicht.' WHERE id=2973;
UPDATE npc_text SET Text0_0='Sie sind verloren, $R. Von den Elfenmeistern geschriebene Buchbände. Sie wurden in aller Welt verstreut, als die Geißel über Quel\'Thalas kam.$B$BJetzt suchen wir nach diesen Buchbänden. Suchen sie, um ihre Geheimnisse zu bergen - und verlorene Macht zurückzugewinnen.$B$BIch suche die zehn, die Meister Kariel Winthalus schrieb. Solltet Ihr einen Buchband finden, so lasst mich seine magischen Eigenschaften absorbieren, und für Euch werde ich ein Gemisch der Macht anfertigen... natürlich hat alles seinen Preis. Ihr werdet meinen Preis recht hoch finden, aber bezahlen werdet Ihr ihn.' WHERE id=2993;
UPDATE npc_text SET Text0_0='Die Gier führt Euch zu mir zurück.$B $BEin weiser Draenei sagte mir einst, dass sie - die Gier - allen Völkern dieser Welt gemeinsam sei. Ich muss ihm Recht geben.$B $BHabt Ihr die in dem Buchband aufgelisteten Komponenten gesammelt?' WHERE id=2998;
UPDATE npc_text SET Text0_0='Möge das Licht Euch heute beschützen.', Text0_1='Möge das Licht Euch heute beschützen.' WHERE id=2999;
UPDATE npc_text SET Text0_0='Was Ihr wollen von Schleifer?' WHERE id=3001;
UPDATE npc_text SET Text0_0='Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber des Tresors.', Text0_1='Wo das Auktionshaus ist? Das liegt direkt beim Stadttor von Eisenschmiede, wenn Ihr unsere schöne Stadt betretet. Ihr findet es gleich gegenüber des Tresors.' WHERE id=3014;
UPDATE npc_text SET Text0_0='Zu welchem der vielen wundersamen Orte in Darnassus darf ich Euch geleiten?', Text0_1='Zu welchem der vielen wundersamen Orte in Darnassus darf ich Euch geleiten?' WHERE id=3016;
UPDATE npc_text SET Text0_0='Euren weltlichen Besitz könnt Ihr in der Bank von Darnassus einlagern. Sie liegt im Zentrum unserer wunderbaren Stadt.', Text0_1='Euren weltlichen Besitz könnt Ihr in der Bank von Darnassus einlagern. Sie liegt im Zentrum unserer wunderbaren Stadt.', Text1_0='Ihr seid wohl ziemlich materialistisch eingestellt, was? Tja, wenn Euch Eure Wertgegenstände so viel wert sind, dann wollt Ihr sie vermutlich in der Bank von Darnassus im Stadtzentrum deponieren.', Text1_1='Ihr seid wohl ziemlich materialistisch eingestellt, was? Tja, wenn Euch Eure Wertgegenstände so viel wert sind, dann wollt Ihr sie vermutlich in der Bank von Darnassus im Stadtzentrum deponieren.' WHERE id=3017;
UPDATE npc_text SET Text0_0='Vesprystus\' Hippogryphen bringen Euch geschwind an Euer Ziel. Ihr findet ihn in Rut\'theran. Das Dorf erreicht Ihr durch das Portal im westlichen Teil von Darnassus.', Text0_1='Vesprystus\' Hippogryphen bringen Euch geschwind an Euer Ziel. Ihr findet ihn in Rut\'theran. Das Dorf erreicht Ihr durch das Portal im westlichen Teil von Darnassus.', Text1_0='Vesprystus ist der Richtige für Euch. Er hütet die Hippogryphen, die von Teldrassils Basis kommen und dorthin zurückkehren. Ihr findet ihn, wenn Ihr im westlichen Teil von Darnassus durch das Tor geht, das nach Rut\'theran führt.', Text1_1='Vesprystus ist der Richtige für Euch. Er hütet die Hippogryphen, die von Teldrassils Basis kommen und dorthin zurückkehren. Ihr findet ihn, wenn Ihr im westlichen Teil von Darnassus durch das Tor geht, das nach Rut\'theran führt.' WHERE id=3018;
UPDATE npc_text SET Text0_0='Wenn Ihr eine Gemeinschaft gründen möchtet, dann müsst Ihr zu Gildenmeisterin Lysheana gehen. Ihr findet sie auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.', Text0_1='Wenn Ihr eine Gemeinschaft gründen möchtet, dann müsst Ihr zu Gildenmeisterin Lysheana gehen. Ihr findet sie auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.', Text1_0='Lysheana kann Euch die notwendigen Papiere besorgen. Sie wohnt auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus. Möge Eure Gemeinschaft den Nachtelfen Ruhm bringen!', Text1_1='Lysheana kann Euch die notwendigen Papiere besorgen. Sie wohnt auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus. Möge Eure Gemeinschaft den Nachtelfen Ruhm bringen!' WHERE id=3019;
UPDATE npc_text SET Text0_0='Das kühle Gras von Teldrassil ist so weich wie jedes Bett, doch Saeliennes Betten im Gasthaus der Terrasse der Handwerker genügen auch den luxuriösesten Ansprüchen.', Text0_1='Das kühle Gras von Teldrassil ist so weich wie jedes Bett, doch Saeliennes Betten im Gasthaus der Terrasse der Handwerker genügen auch den luxuriösesten Ansprüchen.', Text1_0='Ist die Sonne schon aufgegangen? Die Nacht hat einfach nicht genug Stunden! Na, wie dem auch sei, Saelienne wird sich gut um Euch kümmern. Ihr findet sie drüben auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus. Dort betreut sie ihre Gäste.', Text1_1='Ist die Sonne schon aufgegangen? Die Nacht hat einfach nicht genug Stunden! Na, wie dem auch sei, Saelienne wird sich gut um Euch kümmern. Ihr findet sie drüben auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus. Dort betreut sie ihre Gäste.' WHERE id=3020;
UPDATE npc_text SET Text0_0='Es gibt einen Briefkasten vor der Bank, die Ihr im Herzen von Darnassus findet.', Text0_1='Es gibt einen Briefkasten vor der Bank, die Ihr im Herzen von Darnassus findet.', Text1_0='Ein Briefkasten? Wollt Ihr einen Brief versenden oder erwartet Ihr vielleicht einen? Ich hoffe es sind gute Nachrichten! Ihr findet einen Briefkasten direkt vor der Bank im Herzen von Darnassus.', Text1_1='Ein Briefkasten? Wollt Ihr einen Brief versenden oder erwartet Ihr vielleicht einen? Ich hoffe es sind gute Nachrichten! Ihr findet einen Briefkasten direkt vor der Bank im Herzen von Darnassus.' WHERE id=3021;
UPDATE npc_text SET Text0_0='Hier in Darnassus findet Ihr versierte Lehrer, die ihre Fertigkeiten in ihrer gewählten Klasse durch jahrelange Ausbildung und Hingabe vervollkommnet haben. Ich führe Euch zu einem, der Euer Mentor werden kann, Ihr müsst nur den Weg nennen, den Ihr einschlagen möchtet.', Text0_1='Hier in Darnassus findet Ihr versierte Lehrer, die ihre Fertigkeiten in ihrer gewählten Klasse durch jahrelange Ausbildung und Hingabe vervollkommnet haben. Ich führe Euch zu einem, der Euer Mentor werden kann, Ihr müsst nur den Weg nennen, den Ihr einschlagen möchtet.' WHERE id=3022;
UPDATE npc_text SET Text0_0='Ihr könnt Euch glücklich schätzen, dass Ihr nach Darnassus gekommen seid, wo sich unter den Ausbildern so tüchtige Jägerinnen wie Jocaste befinden. Ich rate Euch, sie in der Enklave des Cenarius zu besuchen und als Lehrmeisterin zu gewinnen.', Text0_1='Ihr könnt Euch glücklich schätzen, dass Ihr nach Darnassus gekommen seid, wo sich unter den Ausbildern so tüchtige Jägerinnen wie Jocaste befinden. Ich rate Euch, sie in der Enklave des Cenarius zu besuchen und als Lehrmeisterin zu gewinnen.', Text1_0='Jocaste war eine der besten Jägerinnen ihrer Zeit. Heutzutage findet man sie drüben in der Enklave des Cenarius, wo sie denjenigen Unterricht erteilt, die gerade erst den Weg des Jägers eingeschlagen haben.', Text1_1='Jocaste war eine der besten Jägerinnen ihrer Zeit. Heutzutage findet man sie drüben in der Enklave des Cenarius, wo sie denjenigen Unterricht erteilt, die gerade erst den Weg des Jägers eingeschlagen haben.' WHERE id=3023;
UPDATE npc_text SET Text0_0='Die Druiden lassen niemals in ihrer Wachsamkeit oder in ihrem Schutz der Natur nach. Angeführt werden sie vom Erzdruiden Fandral Hirschhaupt, der in der Enklave des Cenarius lebt. Ihr findet ihn und die anderen Druidenlehrer im nördlichen Teil von Darnassus.', Text0_1='Die Druiden lassen niemals in ihrer Wachsamkeit oder in ihrem Schutz der Natur nach. Angeführt werden sie vom Erzdruiden Fandral Hirschhaupt, der in der Enklave des Cenarius lebt. Ihr findet ihn und die anderen Druidenlehrer im nördlichen Teil von Darnassus.', Text1_0='In schlimmen Zeiten kann ein Druide ein teurer Freund für seine Umgebung sein. Das wird Euch jeder Druide in der Enklave des Cenarius bestätigen.', Text1_1='In schlimmen Zeiten kann ein Druide ein teurer Freund für seine Umgebung sein. Das wird Euch jeder Druide in der Enklave des Cenarius bestätigen.' WHERE id=3024;
UPDATE npc_text SET Text0_0='Der Tempel des Mondes ist die Heimat der Priester von Elune. Dort findet Ihr Jandria und Lariia, die Euch den weiteren Weg zur Priesterschaft weisen.', Text0_1='Der Tempel des Mondes ist die Heimat der Priester von Elune. Dort findet Ihr Jandria und Lariia, die Euch den weiteren Weg zur Priesterschaft weisen.', Text1_0='Die Macht von Elune beflügelt Euch! Die Macht von Elune beflügelt Euch! $B$BGeht jetzt! Sucht Jandria in der zentralen Halle im Tempel des Mondes im südlichen Viertel von Darnassus auf!', Text1_1='Die Macht von Elune beflügelt Euch! Die Macht von Elune beflügelt Euch! $B$BGeht jetzt! Sucht Jandria in der zentralen Halle im Tempel des Mondes im südlichen Viertel von Darnassus auf!' WHERE id=3025;
UPDATE npc_text SET Text0_0='Ihr müsst Syurna aufsuchen, wenn Ihr den Weg des Schurken beschreiten wollt. Sie haust in der Enklave des Cenarius, den gewundenen Weg am westlichsten Baum hinab.', Text0_1='Ihr müsst Syurna aufsuchen, wenn Ihr den Weg des Schurken beschreiten wollt. Sie haust in der Enklave des Cenarius, den gewundenen Weg am westlichsten Baum hinab.', Text1_0='Natürlich wollen manche den Weg des Schurken einschlagen, doch nur die Nachtelfen sind die wahren Geschöpfe der Nacht. Wenn Ihr den Weg des Schattens wählen wollt, dann sucht Syurna in der Enklave des Cenarius auf.', Text1_1='Natürlich wollen manche den Weg des Schurken einschlagen, doch nur die Nachtelfen sind die wahren Geschöpfe der Nacht. Wenn Ihr den Weg des Schattens wählen wollt, dann sucht Syurna in der Enklave des Cenarius auf.' WHERE id=3026;
UPDATE npc_text SET Text0_0='Ihr findet Sildinair bei den Wachen auf der Terrasse der Krieger, wo sie kühnen Blickes nach Feinden Ausschau hält, die unsere schöne Stadt betreten möchten.', Text0_1='Ihr findet Sildinair bei den Wachen auf der Terrasse der Krieger, wo sie kühnen Blickes nach Feinden Ausschau hält, die unsere schöne Stadt betreten möchten.', Text1_0='Sildinair hat viele der Schildwachen ausgebildet, die unsere geliebte Stadt bewachen. Ich bin sicher, sie ist in der Lage, aus Euresgleichen $Geinen Krieger:eine Kriegerin; zu machen. Ihr findet sie draußen auf der Terrasse der Krieger, in der Nähe des Eingangs zu unserer Stadt.', Text1_1='Sildinair hat viele der Schildwachen ausgebildet, die unsere geliebte Stadt bewachen. Ich bin sicher, sie ist in der Lage, aus Euresgleichen $Geinen Krieger:eine Kriegerin; zu machen. Ihr findet sie draußen auf der Terrasse der Krieger, in der Nähe des Eingangs zu unserer Stadt.' WHERE id=3033;
UPDATE npc_text SET Text0_0='Hier unter den Handwerkern von Darnassus findet Ihr das gesammelte Wissen von Jahrtausenden zum Wohle Eures Handwerks. Sagt mir jetzt, welches Handwerk scheint Euch am faszinierendsten zu sein?', Text0_1='Hier unter den Handwerkern von Darnassus findet Ihr das gesammelte Wissen von Jahrtausenden zum Wohle Eures Handwerks. Sagt mir jetzt, welches Handwerk scheint Euch am faszinierendsten zu sein?' WHERE id=3034;
UPDATE npc_text SET Text0_0='Wenn Ihr diesen Beruf wirklich erlernen möchtet, dann möchte ich Euch an Ainethil verweisen, eine kunstfertige Alchemistin auf der Terrasse der Handwerker im nördlichen Darnassus.', Text0_1='Wenn Ihr diesen Beruf wirklich erlernen möchtet, dann möchte ich Euch an Ainethil verweisen, eine kunstfertige Alchemistin auf der Terrasse der Handwerker im nördlichen Darnassus.', Text1_0='Ein Alchemist? Gar kein Problem, $C. Mit viel Schweiß und noch mehr Mühe ist Ainethil zur fähigsten Alchemistin auf der Terrasse der Handwerker geworden. An Eurer Stelle würde ich mich schleunigst dorthin begeben. Schleunigst! Und jetzt fort mit Euch!', Text1_1='Ein Alchemist? Gar kein Problem, $C. Mit viel Schweiß und noch mehr Mühe ist Ainethil zur fähigsten Alchemistin auf der Terrasse der Handwerker geworden. An Eurer Stelle würde ich mich schleunigst dorthin begeben. Schleunigst! Und jetzt fort mit Euch!' WHERE id=3035;
UPDATE npc_text SET Text0_0='Alegorn ist ein Meisterkoch, der Jahrhunderte Zeit hatte, seine Kochkunst zu vervollkommnen. Sollte er Euch unterrichten, könnt Ihr Euch als gesegnet betrachten. Falls Ihr Euch für würdig haltet, findet Ihr ihn auf der Terrasse der Handwerker.', Text0_1='Alegorn ist ein Meisterkoch, der Jahrhunderte Zeit hatte, seine Kochkunst zu vervollkommnen. Sollte er Euch unterrichten, könnt Ihr Euch als gesegnet betrachten. Falls Ihr Euch für würdig haltet, findet Ihr ihn auf der Terrasse der Handwerker.', Text1_0='Für die Gerichte, die Alegorn zubereitet, könnte man sterben. Er ist auf der Terrasse der Handwerker ständig damit beschäftigt, neue Köstlichkeiten zuzubereiten. Probiert unbedingt mal sein Rezept für cenarisches Grün... falls noch welches da ist. $B$BWenn Ihr mich jetzt entschuldigen wollt, ich muss mich ein bisschen um das Rehwild kümmern.', Text1_1='Für die Gerichte, die Alegorn zubereitet, könnte man sterben. Er ist auf der Terrasse der Handwerker ständig damit beschäftigt, neue Köstlichkeiten zuzubereiten. Probiert unbedingt mal sein Rezept für cenarisches Grün... falls noch welches da ist. $B$BWenn Ihr mich jetzt entschuldigen wollt, ich muss mich ein bisschen um das Rehwild kümmern.' WHERE id=3036;
UPDATE npc_text SET Text0_0='Wenn kein Heiler zur Wundbehandlung zur Hand ist, kann erste Hilfe Leben retten. Ich würde Dannelor auf der Terrasse der Handwerker bitten, mich in dieser Fertigkeit zu unterweisen.', Text0_1='Wenn kein Heiler zur Wundbehandlung zur Hand ist, kann erste Hilfe Leben retten. Ich würde Dannelor auf der Terrasse der Handwerker bitten, mich in dieser Fertigkeit zu unterweisen.' WHERE id=3037;
UPDATE npc_text SET Text0_0='Eine Zeit raubende, aber lohnende Fertigkeit. Sprecht mit Astaia auf der Terrasse der Händler, vielleicht lernt Ihr dort etwas Nützliches.', Text0_1='Eine Zeit raubende, aber lohnende Fertigkeit. Sprecht mit Astaia auf der Terrasse der Händler, vielleicht lernt Ihr dort etwas Nützliches.', Text1_0='In all den Jahren, in denen sie schon angelt, hat Astaia ein paar lustige Sachen an die Oberfläche geholt. Also, warum geht Ihr nicht rüber zur Terrasse der Händler und lasst Euch von ihr ein paar dieser Kuriositäten zeigen?', Text1_1='In all den Jahren, in denen sie schon angelt, hat Astaia ein paar lustige Sachen an die Oberfläche geholt. Also, warum geht Ihr nicht rüber zur Terrasse der Händler und lasst Euch von ihr ein paar dieser Kuriositäten zeigen?' WHERE id=3038;
UPDATE npc_text SET Text0_0='Firodren Mondrufer kennt und sammelt Kräuter, das ist sein Spezialgebiet. Sucht ihn in den Tempelgärten im südwestlichen Teil von Darnassus.', Text0_1='Firodren Mondrufer kennt und sammelt Kräuter, das ist sein Spezialgebiet. Sucht ihn in den Tempelgärten im südwestlichen Teil von Darnassus.', Text1_0='Wir sind also ein angehender Kräuterkundiger, ja? Dann solltet Ihr Firodren Mondrufer drüben in den Tempelgärten besuchen. Er kann Euch die Feinheiten dieses Handwerks beibringen.', Text1_1='Wir sind also ein angehender Kräuterkundiger, ja? Dann solltet Ihr Firodren Mondrufer drüben in den Tempelgärten besuchen. Er kann Euch die Feinheiten dieses Handwerks beibringen.' WHERE id=3039;
UPDATE npc_text SET Text0_0='Die Fertigkeit, mit der Telonis Rüstungen herstellt, ist unvergleichlich. Ihr solltet dankbar sein, dass er nicht mehr für das Wissen berechnet, das er anderen weitergibt. Ihr findet ihn auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.', Text0_1='Die Fertigkeit, mit der Telonis Rüstungen herstellt, ist unvergleichlich. Ihr solltet dankbar sein, dass er nicht mehr für das Wissen berechnet, das er anderen weitergibt. Ihr findet ihn auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.', Text1_0='Wir pflanzen Gemüse. Hasen fressen unser Gemüse. Der Wolf verschlingt den Hasen. Wir töten den Wolf. Wir nehmen Fleisch und Fell des Wolfskadavers. Es ist der magische Kreislauf des Lebens oder wie immer man dazu sagen will. $B$BGeht jetzt zu Telonis in der Enklave des Cenarius und lasst mich wieder meine Pflicht tun.', Text1_1='Wir pflanzen Gemüse. Hasen fressen unser Gemüse. Der Wolf verschlingt den Hasen. Wir töten den Wolf. Wir nehmen Fleisch und Fell des Wolfskadavers. Es ist der magische Kreislauf des Lebens oder wie immer man dazu sagen will. $B$BGeht jetzt zu Telonis in der Enklave des Cenarius und lasst mich wieder meine Pflicht tun.' WHERE id=3040;
UPDATE npc_text SET Text0_0='Durch ihren Tod geben uns die Tiere der Welt Leben und Schutz. Wenn Ihr glaubt, dass Ihr genug Respekt für ihr Opfer empfindet, könnte die Kürschnerei etwas für Euch sein. Sprecht mit Eladriel auf der Terrasse der Handwerker im nördlichen Darnassus, wenn Ihr mehr erfahren möchtet.', Text0_1='Durch ihren Tod geben uns die Tiere der Welt Leben und Schutz. Wenn Ihr glaubt, dass Ihr genug Respekt für ihr Opfer empfindet, könnte die Kürschnerei etwas für Euch sein. Sprecht mit Eladriel auf der Terrasse der Handwerker im nördlichen Darnassus, wenn Ihr mehr erfahren möchtet.', Text1_0='Es ist ein Grundpfeiler unserer Kultur, dass wir nichts verkommen lassen. Wenn wir eine Kreatur töten müssen, dann nehmen wir alles, was nur irgend möglich ist, damit das Opfer nicht umsonst war. $B$BWenn Ihr die Kunst erlernen wollt, wie man eine erschlagene Bestie häutet, dann sucht Eladriel auf der Terrasse der Handwerker auf.', Text1_1='Es ist ein Grundpfeiler unserer Kultur, dass wir nichts verkommen lassen. Wenn wir eine Kreatur töten müssen, dann nehmen wir alles, was nur irgend möglich ist, damit das Opfer nicht umsonst war. $B$BWenn Ihr die Kunst erlernen wollt, wie man eine erschlagene Bestie häutet, dann sucht Eladriel auf der Terrasse der Handwerker auf.' WHERE id=3042;
UPDATE npc_text SET Text0_0='Ihr müsst auf jeden Fall zu Me\'Lynn, wenn Ihr die Schneiderei erlernen möchtet. Sie residiert auf der Terrasse der Handwerker im nordöstlichen Darnassus.', Text0_1='Ihr müsst auf jeden Fall zu Me\'Lynn, wenn Ihr die Schneiderei erlernen möchtet. Sie residiert auf der Terrasse der Handwerker im nordöstlichen Darnassus.', Text1_0='Wenn man sich die Kleider ansieht, die Me\'Lynn schneidert, dann könnte man glauben, Elune selbst hätte ihre Nadel gesegnet. Und falls Ihr für Eure Nadel nicht auch mit einem solchen Segen aufwarten könnt, solltet Ihr sie unbedingt aufsuchen und Euch anhören, was sie zu sagen hat. Ihr findet sie auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.', Text1_1='Wenn man sich die Kleider ansieht, die Me\'Lynn schneidert, dann könnte man glauben, Elune selbst hätte ihre Nadel gesegnet. Und falls Ihr für Eure Nadel nicht auch mit einem solchen Segen aufwarten könnt, solltet Ihr sie unbedingt aufsuchen und Euch anhören, was sie zu sagen hat. Ihr findet sie auf der Terrasse der Handwerker im nordöstlichen Teil von Darnassus.' WHERE id=3044;
UPDATE npc_text SET Text0_1='Ich habe vier verschiedene Pflanzen gesehen, die von der Verderbnis befallen sind: Liedblume, Windblüte, Peitscherwurzel und die Nachtdrachenpflanze. Liedblume und Windblüte sind verbreitete Pflanzen im Teufelswald, die Peitscherwurzel und die Nachtdrachenpflanze findet man nicht so häufig. Alle sind wichtige Bestandteile des Teufelswald-Ökosystems.' WHERE id=3046;
UPDATE npc_text SET Text0_1='Ich habe vier verschiedene Pflanzen gesehen, die von der Verderbnis befallen sind: Liedblume, Windblüte, Peitscherwurzel und die Nachtdrachenpflanze. Liedblume und Windblüte sind verbreitete Pflanzen im Teufelswald, die Peitscherwurzel und die Nachtdrachenpflanze findet man nicht so häufig. Alle sind wichtige Bestandteile vom Teufelswald-Ökosystem.' WHERE id=3047;
UPDATE npc_text SET Text0_0='Dies war der Schauplatz eines großen Kampfes. Vieler großer Kämpfe.' WHERE id=3048;
UPDATE npc_text SET Text0_0='Sprecht mit Regthar. Er ist weise und kann Euch helfen zu finden, was Ihr sucht.' WHERE id=3049;
UPDATE npc_text SET Text0_0='Hallo?' WHERE id=3055;
UPDATE npc_text SET Text0_0='Ja, kein Problem, $N! Tut mir leid, ich dachte, Ihr wärt schon damit fertig.' WHERE id=3056;
UPDATE npc_text SET Text0_0='Was zieht Ihr vor, Schusswaffen oder Bogen?' WHERE id=3061;
UPDATE npc_text SET Text0_0='Na gut *hick*... Ihr kommt mir so vertraut vor *hick*. Hab ich Euch schon erzählt, wie ich und Windsor mal dreis... äh... taus... \'ne Million Orcs verjagt haben?$B$BMacht es Euch gemütlich, die ist richtig gut!' WHERE id=3074;
UPDATE npc_text SET Text0_0='Das Auktionshaus Orgrimmar liegt im Tal der Stärke. Sucht auf der Ostseite danach, neben \'Schießeisenimporte\'.', Text0_1='Das Auktionshaus Orgrimmar liegt im Tal der Stärke. Sucht auf der Ostseite danach, neben \'Schießeisenimporte\'.', Text1_0='Eure Stimme ist heiser vom Brüllen, was? Ihr findet das Auktionshaus Orgrimmar im Tal der Stärke.', Text1_1='Eure Stimme ist heiser vom Brüllen, was? Ihr findet das Auktionshaus Orgrimmar im Tal der Stärke.' WHERE id=3075;
UPDATE npc_text SET Text0_0='Willkommen bei der Bank von Darnassus. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?', Text0_1='Willkommen bei der Bank von Darnassus. Wir bieten Bankkonten und Schließfächer für wertvolle Gegenstände. Habt Ihr bereits ein Konto bei uns, $Gwerter Herr:gnädige Frau;?' WHERE id=3076;
UPDATE npc_text SET Text0_0='Erstaunlich, was? All diese Artefakte und Überreste.$B$BIch bin nicht sicher, was mich mehr beeindruckt... ihre Größe oder die dazugehörigen Geschichten. Es gibt so viel zu studieren und zu lernen. Und die Verbindungen, die man machen kann...$B$BWusstet Ihr, dass Gerüchten zufolge viele Völker vom selben Ursprung sind wie die Nachtelfen?' WHERE id=3077;
UPDATE npc_text SET Text0_1='Hallo, mein Name ist Petra. Das da rechts von mir ist mein Kodo Dadanga. Wir gehören zu \'Marschalls Expeditionen\', und zwar seit Jahren, schon seit Dadanga ein Baby war.$B$BFalls Ihr brennende Fackeln oder andere offene Flammen bei Euch habt, seid so nett und haltet sie bitte fern. Dadanga mag keinen Rauch.' WHERE id=3093;
UPDATE npc_text SET Text0_0='Bei allem, was Euch lieb und teuer ist, sagt mir bitte, dass Ihr uns aus diesem verlassenen Land wegbringen wollt. Ich bin zwar mit \'Marschalls Expeditionen\' hier, aber ich würde Euch jeden Goldzahn in meinem Mund geben, um in Euren Rucksack klettern und diesen Ort verlassen zu dürfen.' WHERE id=3094;
UPDATE npc_text SET Text0_1='Oh, hallo! Ich bin Hol\'anyee, Chefwissenschaftlerin und Biologin bei \'Marschalls Expeditionen\'. Mein Bruder und ich reisen mit unseren Gefährten durch Azeroth und erforschen die majestätische, ungezähmte Wildnis. Es ist aufregend... aber, ähm... Ich glaube, diesmal ist es ein bisschen zu aufregend für uns!' WHERE id=3095;
UPDATE npc_text SET Text0_0='Hallo $GFremder:Fremde;, willkommen in der ungezähmten, majestätischen Wildnis des Kraters von Un\'Goro!$B$BIch heiße Williden Marschall. Meine Schwester, unsere Gefährten und ich sind \'Marschalls Expeditionen\' - Wissenschaftler und Erforscher der unbekannten Wildnis Azeroths.$B$BWir haben hier ein paar Probleme in Un\'Goro und bräuchten Hilfe, falls Ihr ein bisschen Zeit erübrigen könntet!' WHERE id=3096;
UPDATE npc_text SET Text0_0='Seht Euch diese Proben an! Was bedeutet das? Was bedeutet das? Ich brauche Zeit, um mir all diese Informationen anzusehen. Und ich brauche mehr Informationen. Informationen, die ich nicht bekommen kann... verdammt! Nachdenken, Laris, nachdenken!$B$BWas soll jetzt geschehen? Was?!$B$BEs muss eine Lösung geben!' WHERE id=3098;
UPDATE npc_text SET Text0_0='Ganz sicher nicht in den östlichen Königreichen, das steht fest.$B$BEs müsste irgendwo in Kalimdor sein, aber weit genug entfernt von der Horde und anderen Völkern. An einem von der Teilung unbeeinflussten Ort - das bedeutet, auch nicht in der Nähe der Nachtelfenzivilisation.$B$BUnd es müsste ein geschützter Ort sein... ein Tal oder Krater. Ich weiß es nicht. Ich mutmaße jetzt nur. Tut mir leid, dass ich Euch nicht weiterhelfen konnte.' WHERE id=3102;
UPDATE npc_text SET Text0_0='Ich habe keine Ahnung, warum Ihr diese sonnigen Strände und das kristallblaue Wasser verlassen wollt. Hier ist es wie im Paradies. Aber sagt das nicht meinem Bruder Zapetta, der ist auch so schon verbittert genug, weil er den ganzen Tag in Tirisfal ausharren muss. Dieser Zeppelin landet in Orgrimmar, daher müsst Ihr Euch sein Gewinsel vermutlich nicht anhören. Aber falls Ihr doch bald mal nach Tirisfal kommt, denkt an meine Warnung und gebt ihm keinen Grund, loszumaulen.', Text0_1='Ich habe keine Ahnung, warum Ihr diese sonnigen Strände und das kristallblaue Wasser verlassen wollt. Hier ist es wie im Paradies. Aber sagt das nicht meinem Bruder Zapetta, der ist auch so schon verbittert genug, weil er den ganzen Tag in Tirisfal ausharren muss. Dieser Zeppelin landet in Orgrimmar, daher müsst Ihr Euch sein Gewinsel vermutlich nicht anhören. Aber falls Ihr doch bald mal nach Tirisfal kommt, denkt an meine Warnung und gebt ihm keinen Grund, loszumaulen.' WHERE id=3133;
UPDATE npc_text SET Text0_0='Sucht in dem Gebäude südlich der Bank von Donnerfels auf der unteren Anhöhe nach Gastwirtin Pala.', Text0_1='Sucht in dem Gebäude südlich der Bank von Donnerfels auf der unteren Anhöhe nach Gastwirtin Pala.', Text1_0='Gastwirtin Pala hilft Euch, Euren Körper und Geist auszuruhen. Ihr findet sie in dem Gebäude südlich der Bank auf der unteren Anhöhe.', Text1_1='Gastwirtin Pala hilft Euch, Euren Körper und Geist auszuruhen. Ihr findet sie in dem Gebäude südlich der Bank auf der unteren Anhöhe.' WHERE id=3153;
UPDATE npc_text SET Text0_0='Ihr findet einen Briefkasten vor der Donnerfels-Bank auf der unteren Anhöhe.', Text0_1='Ihr findet einen Briefkasten vor der Donnerfels-Bank auf der unteren Anhöhe.', Text1_0='Ich glaube, vor kurzem wurde eine neue Ladung Post gebracht. Ihr findet den Briefkasten vor der Donnerfels-Bank auf der unteren Anhöhe.', Text1_1='Ich glaube, vor kurzem wurde eine neue Ladung Post gebracht. Ihr findet den Briefkasten vor der Donnerfels-Bank auf der unteren Anhöhe.' WHERE id=3154;
UPDATE npc_text SET Text0_0='Sucht im unteren Bereich von Donnerfels, in der Nähe der Bank. Von da ist es nicht weit.', Text0_1='Sucht im unteren Bereich von Donnerfels, in der Nähe der Bank. Von da ist es nicht weit.', Text1_0='Das Auktionshaus befindet sich auf der mittleren Spitze von Donnerfels. Schaut Euch in der Nähe des Windreiterturms um. Möge Eure Vorfahren über Euch wachen.', Text1_1='Das Auktionshaus befindet sich auf der mittleren Spitze von Donnerfels. Schaut Euch in der Nähe des Windreiterturms um. Möge Eure Vorfahren über Euch wachen.' WHERE id=3155;
UPDATE npc_text SET Text0_0='Es mag ein Schock für Euch sein, aber die Geschäfte liefen in letzter Zeit nicht besonders gut...' WHERE id=3156;
UPDATE npc_text SET Text0_1='Ich heiße Euch willkommen in Theramore, $N.$B$BIch entschuldige mich, dass ich so kurz angebunden bin und keine Zeit zum Plaudern habe, aber meine Stadt hat nun mal Vorrang. Zu vieles bedroht unseren Lebensstil und den Zusammenhalt der Allianz. Ich muss stets nach Kräften Ausschau halten, die sich unserer Kontrolle entziehen, damit mein Volk weiterhin in Sicherheit und Zufriedenheit leben kann.$B$BVielleicht haben wir später Zeit für einen Plausch.' WHERE id=3157;
UPDATE npc_text SET Text0_1='Hendel ist jetzt dank Euch in unserem Gewahrsam, $N.$B$BDas Verhör zieht sich hin, aber ich versichere Euch, er wird uns alles sagen, was er weiß.$B$BHabt bitte ein wenig Geduld. Ich melde mich umgehend bei Euch, wenn wir Eurer Unterstützung bedürfen.$B$BSeht Euch bis dahin in Theramore um. Als Freund der Allianz dürft Ihr Euch in unserer schönen Stadt aufhalten, solange Ihr es wollt.' WHERE id=3158;
UPDATE npc_text SET Text0_0='Vor dem Haupttor der Stadt steht ein Wegweiser. Er weist Euch den Weg zum westlichen Zeppelinturm.', Text0_1='Vor dem Haupttor der Stadt steht ein Wegweiser. Er weist Euch den Weg zum westlichen Zeppelinturm.', Text1_0='Sucht im Südosten vor der Stadt nach dem Zeppelinlandeturm.' WHERE id=3173;
UPDATE npc_text SET Text0_0='Mein Bruder und ich haben uns Sylvanas verschrieben. Sie war unser Licht in finstersten Zeiten. Wir opfern uns Tag für Tag, damit unsere Art das ihr zugedachte Schicksal überwinden kann.$B$BWir werden nicht schwach bleiben. Und wir werden uns nehmen, was uns gehört. Den Schmerz, der uns zugefügt wurde, werden wir zehnfach vergelten. Und wir werden es mit einem Lächeln tun.' WHERE id=3193;
UPDATE npc_text SET Text0_0='Lady Sylvanas hat die Untersuchung einiger Naturschauspiele auf Azeroth angeordnet. Sie glaubt, dass man aus der unbekannten Vergangenheit etwas Großes lernen kann.$B$BKeine Ahnung, woher sie so etwas weiß, aber ich vertraue ihr blind und werde mir alle Mühe geben.$B$BMomentan untersuche ich gerade Brühschlammer, Brühschleimer und dergleichen. Die Kreaturen scheinen alle gleicher Herkunft, aber stark von ihrer Umwelt geprägt zu sein.$B$BIch könnte Eure Hilfe brauchen, um Proben dieser Kreaturen zu sammeln.' WHERE id=3194;
UPDATE npc_text SET Text0_0='<Volcor schaut Euch nicht an, als Ihr auf ihn zugeht. Er achtet anscheinend nur darauf, dass seine Verbände nicht abfallen. Dann sieht er überrascht zu Euch auf.>$B$BAch, entschuldigt, $C. Der Zeitpunkt ist gerade ungünstig. Ich hoffe, Ihr versteht.' WHERE id=3213;
UPDATE npc_text SET Text0_0='Wie ich es sehe, $N, müssen wir zwei Möglichkeiten durchdenken, wie wir diese Höhle verlassen können.$B$BWir können davonschleichen - ich nehme den Mantel, den Ihr mir gabt, und Ihr seid auf Euch allein gestellt. Oder, was angesichts meiner Wunden schwerer sein dürfte, wir kämpfen uns Rücken an Rücken hinaus.$B$BIhr dürft entscheiden, Ihr kennt Eure Kräfte besser als ich.' WHERE id=3214;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $GJungchen:Mädel;. Wenn Ihr die besten Schnäppchen außerhalb von Eisenschmiede sucht, dann seid Ihr hier richtig.' WHERE id=3218;
UPDATE npc_text SET Text0_1='Sind Euch die beschmutzten Wasserelementare in unserem See aufgefallen, $N? Sie sind ein jämmerlicher Anblick, aber ich weiß nicht, woher sie kommen.$B$BMan hat mir schon von Euch erzählt. Vielleicht könnt Ihr mir ja helfen, dieses Problem zu beseitigen.$B$BAls Erstes müssen wir herausbekommen, wer diese Elementare kontrolliert.' WHERE id=3233;
UPDATE npc_text SET Text0_0='Hallöchen! Ich bin Gubber Blump.' WHERE id=3234;
UPDATE npc_text SET Text0_0='... gebacken, gebraten oder frittiert...' WHERE id=3253;
UPDATE npc_text SET Text0_0='Die Flüssigkeiten und Mixturen blubbern und brodeln, als Ihr näher kommt. Neben der Ausrüstung liegt eine große Zange, die aussieht, als könne man damit Brühschlammerproben hineinwerfen.$B$BEs gibt Schutzkleidung, aber die sieht staubig und unbenutzt aus.$B$BIrgendwie findet Ihr, es wäre klüger, nichts anzufassen.' WHERE id=3273;
UPDATE npc_text SET Text0_0='Hallo, $N. Willkommen in der Zuflucht.' WHERE id=3293;
UPDATE npc_text SET Text0_1='Hallo, $N!$B$BIch muss an meinen Geräten arbeiten, aber irgendwann... werde ich jemanden brauchen, der mir Dracheneier beschafft!' WHERE id=3295;
UPDATE npc_text SET Text0_1='Die brennende Steppe ist ein gefährlicher Ort, aber Gelehrte wie ich würden so ziemlich alles tun, um unser Wissen zu vergrößern.' WHERE id=3296;
UPDATE npc_text SET Text0_1='Kar Aman, $N. Ihr scheint $Gein starker:eine starke:c; $C zu sein - passt in dieser Gegend lieber ein bisschen auf und benehmt Euch. Euch unbekannte Dinge zu berühren oder Respektlosigkeit gegenüber den Ältesten sind Anlass für Bestrafung.$B$BIhr mögt mich oder meine Gefährten vielleicht nicht respektieren, aber Ihr werdet lernen, unsere Macht zu respektieren, wenn Ihr töricht genug seid.$B$BDoch genug geplaudert, kann ich etwas für Euch tun?' WHERE id=3298;
UPDATE npc_text SET Text0_1='Ah, ein Hexenmeisterkollege hat sich zu einem gewissen Grad als würdig erwiesen.$B$BEs freut mich, Eure Bekanntschaft zu machen. Mein Name ist Menara Leerbringer... und das nicht ohne Grund.$B$BIhr scheint mächtig und gebildet genug zu sein, dass andere Werkzeuge... mächtigere Werkzeuge... willkommen wären und nicht töricht weggegeben würden. Wenn das stimmt, sprecht weiter mit mir. Wenn nicht, dann kennt Ihr ja den Weg zu den Docks zurück und übers Meer.' WHERE id=3299;
UPDATE npc_text SET Text0_0='Bringt es nicht näher, sterbliches Wesen. Ich spüre das Böse in diesem Siegel schon aus dieser Entfernung.$B$BGebt mir einen Moment Zeit, um den Gegenstand zu inspizieren - haltet ihn gut fest.' WHERE id=3311;
UPDATE npc_text SET Text0_0='$N, ich kam hierher, um herauszufinden, wie bedrohlich die Auswirkungen von Teufelsmoos in Dunkelküste sind. Die Verderbnis von Teldrassil und den umliegenden Ländern erfüllt die Druiden des Zirkels des Cenarius und alle Nachtelfen mit Besorgnis.$B$BMein Ziel ist, herauszufinden, warum es dazu gekommen ist, und dem ein Ende zu bereiten.' WHERE id=3334;
UPDATE npc_text SET Text0_0='Begabte Verzauberer wird man immer brauchen, denn eine Klinge kann nie zu scharf, eine Rüstung nie zu robust sein. So ein Verzauberer ist Taladan. Er kann Euch zeigen, wie man richtig Verzauberungen wirkt, um Waffen und Rüstungen zu verbessern. Ihr findet ihn auf der Terrasse der Handwerker.', Text0_1='Begabte Verzauberer wird man immer brauchen, denn eine Klinge kann nie zu scharf, eine Rüstung nie zu robust sein. So ein Verzauberer ist Taladan. Er kann Euch zeigen, wie man richtig Verzauberungen wirkt, um Waffen und Rüstungen zu verbessern. Ihr findet ihn auf der Terrasse der Handwerker.', Text1_0='Ich bin geschmeichelt, aber nicht interessiert... Was denn? Ihr wollt wissen, wo man die Verzauberkunst erlernen kann? Oh, wie peinlich! Also, Ihr solltet... Ihr könnt sicher... zu, hm... Taladan! Ja, geht zu Taladan auf der Terrasse der Handwerker!', Text1_1='Ich bin geschmeichelt, aber nicht interessiert... Was denn? Ihr wollt wissen, wo man die Verzauberkunst erlernen kann? Oh, wie peinlich! Also, Ihr solltet... Ihr könnt sicher... zu, hm... Taladan! Ja, geht zu Taladan auf der Terrasse der Handwerker!' WHERE id=3337;
UPDATE npc_text SET Text0_0='Willkommen in meinem Laden, $C. Was können die Gebrüder Gutstich für Euch tun?' WHERE id=3357;
UPDATE npc_text SET Text0_0='Ah, einer meiner Lieblingskunden. Ihr seht aus wie ein mächtiger Hexenmeister, $N. Es ist mir eine Ehre. Wenn ich etwas für Euch tun kann, so sagt es bitte.$B$BMein Bruder und ich glauben, dass Euresgleichen uns in die nächste große Ära führen werden. Einer mit dieser Macht... dieser Kontrolle... es ist lediglich eine Frage der Zeit, bis sich sogar die Legion Euch unterwirft.$B$BIch bin auch nur ein ganz klein bisschen neidisch.' WHERE id=3358;
UPDATE npc_text SET Text0_1='Ah, $R... Ich weiß viel über die Welt und wie man Bezauberungen von großer Macht herstellt.' WHERE id=3375;
UPDATE npc_text SET Text0_1='Mau\'ari weiß, warum Ihr sie aufsucht...$B$BBringt mir das E\'ko, das Ihr in der Welt so findet, un\' ich verwandel\'s für Euch.' WHERE id=3377;
UPDATE npc_text SET Text0_0='Ja, ja, ich weiß, dass Dadanga keinen Rauch mag. Seinem Geruch nach zu urteilen, mag er offenbar auch keine Bäder.' WHERE id=3380;
UPDATE npc_text SET Text0_1='Der Behälter von Mau\'ari birgt große Geheimnisse... Die Splitter, aus denen er besteht, reflektieren. Mit der glänzenden Oberfläche kann man Macht von anderen Dingen absaugen und dann auf sich selbst reflektieren.$B$BWenn Ihr den Glücksbringer in Eurem Inventar behaltet, könnt Ihr E\'ko sammeln, die spirituelle Energie der Tiere, gegen die Ihr kämpft. Wenn Ihr E\'ko gefunden habt, bringt\'s zu Mau\'ari.' WHERE id=3382;
UPDATE npc_text SET Text0_1='E\'ko is\' die Macht, die jede Kreatur haben tut. Der Glücksbringer, den ich Euch gemacht hab, kann\'s als feste Materialisierung einfangen.$B$BIch kenn einen Zauberspruch, der kann das E\'ko, das Ihr findet, in Juju verwandeln.$B$BIhr könnt den Behälter von Mau\'ari nehmen und damit die Macht des Juju auf Euch selbst reflektieren.$B$BLos, versucht\'s doch mal. Bringt das E\'ko zu Mau\'ari, das wird lustig.' WHERE id=3383;
UPDATE npc_text SET Text0_0='Euch einen guten Tag, $GJungchen:Mädel;! Kann ich zu Diensten sein?' WHERE id=3395;
UPDATE npc_text SET Text0_0='Zur Ausbildung hier, was? Hab selbst erst damit angefangen, aber ich lehre Euch mit Freuden, was ich schon weiß.' WHERE id=3396;
UPDATE npc_text SET Text0_0='Eh? Ich hab keine Zeit für Schwätzchen, mein Paps sagt, ich soll die Bestellung fürn fertig machen.' WHERE id=3398;
UPDATE npc_text SET Text0_0='Ihr zeigt ein kleines bisschen Geschick. Genug, um ein paar Dinge zu erlernen, denke ich. Mal sehen, was ich Euch alles beibringen kann, ja?' WHERE id=3399;
UPDATE npc_text SET Text0_0='Ihr wisst mehr als ich, das steht mal fest. Ich sag Euch was, sprecht mit meinem Paps. Ihr werdet Euch beweisen müssen, aber er is\' echt \'n guter Lehrmeister. Sein Name ist Rotgath. Ihr findet ihn bei den Ambossen, wo er Stahl für den alten Bengus schmiedet.' WHERE id=3400;
UPDATE npc_text SET Text0_0='Tja, Eure Fertigkeit ist größer als meine. Warum sucht Ihr nicht meinen alten Freund Rotgath Steinbart auf? Er blieb in Eisenschmiede, als ich mich Jainas Armee anschloss. Gewiss kann er Euch etwas beibringen oder er kennt jemanden, der es kann. Wenn er noch hier ist, dann ganz sicher in der großen Schmiede.' WHERE id=3401;
UPDATE npc_text SET Text0_1='Die Kreaturen in Winterquell wären für\'n Anfang nich\' schlecht.$B$BAber Ihr könnt auch an anneren Plätzen nachseh\'n.' WHERE id=3402;
UPDATE npc_text SET Text0_0='Natürlich kann ich Euch bei der Ausbildung helfen. Ich bin zwar selbst noch Geselle, aber ein paar Sachen kann ich Euch sicher zeigen.' WHERE id=3403;
UPDATE npc_text SET Text0_0='Es tut mir leid, ich bin gerade mit Hufeisen für Verner Gutmark in Seenhain beschäftigt.' WHERE id=3405;
UPDATE npc_text SET Text0_0='Seh ich aus, als hätte ich Zeit zum Quatschen?' WHERE id=3406;
UPDATE npc_text SET Text0_0='Ihr seid nicht schlecht, aber ich werde Euch ein paar Tricks zeigen, die recht praktisch sind.' WHERE id=3407;
UPDATE npc_text SET Text0_0='Ein viel besserer Schmied als ich, das steht mal fest. Aber ich werde leugnen, dass ich das gesagt habe, wenn Ihr es ausplaudert. Ihr solltet mit Therum Tiefenschmied sprechen. Wenn Euch jemand mehr beibringen kann, dann er.' WHERE id=3408;
UPDATE npc_text SET Text0_0='Wenn ich einen Ton über Wasservögel höre, dann esst Ihr aus der Schnabeltasse.' WHERE id=3409;
UPDATE npc_text SET Text0_0='Man wird nicht zum Schmied, nur weil man mit dem Hammer auf einen Amboss hauen kann. Gebt mir das mal her, dann bring ich Euch ein paar Sachen bei, ehe Ihr uns alle blamiert.' WHERE id=3410;
UPDATE npc_text SET Text0_0='Murr nennt mich Einfaltspinsel, weil ich zu langsam bin. Aber ich will nur alles richtig machen und das kostet Zeit.' WHERE id=3412;
UPDATE npc_text SET Text0_0='Ich kann Euch zeigen, was Ihr wissen wollt, aber Ihr müsst Geduld haben. Lasst Euch Zeit mit der Stahlbearbeitung und spürt, wie er unter Eurem Hammer Form annimmt. Wenn Ihr eins mit dem Stahl werdet, dann versteht Ihr es.' WHERE id=3413;
UPDATE npc_text SET Text0_0='Ihr lernt schnell, das ist nicht übel. Erledigt die Arbeit aber richtig, ganz gleich, wie lange es dauert. Und jetzt muss ich Euch wohl zu Murr schicken, damit Ihr mehr lernt. Lernt, was er lehren kann, aber hört nicht auf seine wütenden Worte. Wenn Sarus Töchter ihn auslachen, verliert er immer die Beherrschung.' WHERE id=3414;
UPDATE npc_text SET Text0_0='Ich horche und horche, aber ich kann diese Geister, von denen Karn spricht, nicht hören.' WHERE id=3415;
UPDATE npc_text SET Text0_0='Offenbar sprechen die Geister zu Euch lauter als zu mir. Ich höre ihren Ruf immer noch nicht, doch Ihr scheint die Lektionen zu begreifen. Sprecht mit Karn, ihn wird es freuen, dass jemand sie so gut hören kann wie er.' WHERE id=3417;
UPDATE npc_text SET Text0_1='Eure Unterbrechung kostet mich wertvolle Zeit, hoffentlich ist die Sache wichtig.' WHERE id=3418;
UPDATE npc_text SET Text0_0='Hoffentlich schmiede ich eines Tages Waffen und Rüstungen aus den Knochen meiner Feinde.' WHERE id=3421;
UPDATE npc_text SET Text0_0='Ihr habt viel erlernt, mehr als ich lehren kann. Geht zu James Van Brunt, er wird Euch lehren, was ich Euch nicht beibringen kann.' WHERE id=3423;
UPDATE npc_text SET Text0_1='E\'ko is\' unfokussierte Form von übernatürlicher Macht, Juju is\' konzentrierte Essenz. Mit \'nem starken Zauberspruch kann ich E\'ko in Juju verwandeln. Wenn\'s Juju geworden is\', könnt Ihr den Behälter von Mau\'ari nehmen un\' die Macht auf Euch selbst reflektieren.' WHERE id=3424;
UPDATE npc_text SET Text0_0='Ihr könnt einigermaßen mit Hammer und Amboss umgehen, aber nicht gut genug. Sprecht mit Dane Lindgren, er bereitet Euch darauf vor, das zu erlernen, was ich Euch lehren kann.' WHERE id=3453;
UPDATE npc_text SET Text0_0='Ich sagte, in Euren Adern flösse das Blut eines Schmieds, und ich hatte Recht. Geht zur großen Schmiede in Eisenschmiede und sucht einen Zwergenschmied namens Bengus Tiefenschmied auf. Das ist mein Lehrmeister und einer der größten Schmiede, die ich je gesehen habe.' WHERE id=3454;
UPDATE npc_text SET Text0_0='Ein Schmied hat kein leichtes Leben, aber dafür Stahl in den Knochen und Feuer im Herzen. Mein Paps war Schmied und vor ihm mein Großvater in den Schmieden unter Grim Batol. Jetzt tritt mein Sohn in ihre Fußstapfen und der Junge bereitet mir nur Ehre.' WHERE id=3456;
UPDATE npc_text SET Text0_0='Tut mir Leid, $GBursche:Mädel;, Ihr seid der Herausforderung meines Unterrichts nicht ganz gewachsen. Geht zu meinem Sohn Groum, der freut sich bestimmt über einen neuen Lehrling.' WHERE id=3457;
UPDATE npc_text SET Text0_0='Aye, ich würde Euch gern dies und das über die Schmiedekunst lehren. Dann schnappt Euch mal Hammer und Schürze und lasst uns sehen, was Ihr zustande bringt, ja?' WHERE id=3459;
UPDATE npc_text SET Text0_0='Ihr lernt schnell und ein guter Schmied seid Ihr obendrein. Ich würde Eure Arbeiten jederzeit absegnen. Aber ich spüre, Ihr habt immer noch etwas zu erlernen. Geht zu Bengus Tiefenschmied, er ist ein strenger Zuchtmeister, aber einer der Besten.' WHERE id=3460;
UPDATE npc_text SET Text0_1='Schleicht Euch nicht so aus den Schatten. Macht Euch nächstes Mal gefälligst bemerkbar.', Text1_1='Seit die Schatten den Dämmerwald erobert haben, konnte ich nicht mehr schlafen. Ich werde durch geisterhafte Schreie geweckt, die der Wind mit sich bringt. Es ist fast, als würden Lämmer schreien, und sie hören einfach nicht auf.' WHERE id=3461;
UPDATE npc_text SET Text0_1='Ja, ich habe schon munkeln gehört, dass ich Euch früher oder später zu Gesicht bekommen würde. Aber ich nehme nicht einfach jeden auf, ich muss mich vergewissern, dass Ihr auch das Zeug dazu habt. Seid Ihr willens, meinen kleinen Test zu absolvieren und es herauszufinden?' WHERE id=3463;
UPDATE npc_text SET Text0_1='Eure Arbeit ist gut, aber noch ein wenig ungeschliffen. Aber keine Sorge, Übung macht den Meister. Wie dem auch sei, wir sollten uns vielleicht mal ansehen, woran Ihr gearbeitet habt, ja?' WHERE id=3464;
UPDATE npc_text SET Text0_0='Wenn ich mich Saru beweise, hat er keine andere Wahl, als mich um eine seiner Töchter kämpfen zu lassen. Dann werden wir sehen, wer zuletzt lacht.', Text1_0='Haltet Euch von Sumi und Tumi fern. Sie sind... versprochen.' WHERE id=3466;
UPDATE npc_text SET Text0_0='Wenn Ihr mir noch einmal eine so jämmerliche Arbeit vorlegt, schicke ich Euch in einem kleinen Eisenkasten heim, den ich mit Ziegenurin gehärtet habe. Geht zu diesem Idioten Ug\'thok, er ist besser für Euch geeignet.' WHERE id=3467;
UPDATE npc_text SET Text0_0='Ihr denkt also, Ihr seid gut genug, von mir zu lernen? Dem großen Murr? Ihr seid nicht mehr als ein elender Wurm unter meinem Absatz. Aber wenn ich einen Unterling annehme, werde ich vielleicht interessanter für Sumi und Tumi. Mal sehen, was für kümmerliche Fertigkeiten Ihr habt.' WHERE id=3468;
UPDATE npc_text SET Text0_0='Ihr könnt nichts mehr von mir lernen, verschwindet. Und denkt nicht mal im Traum daran, zu Saru zu gehen. Unwürdig wie Ihr seid, würde er seinen Stahl mit Eurem Blut kühlen.' WHERE id=3470;
UPDATE npc_text SET Text0_0='Die Geister haben Euch zu mir gebracht, daher fühle ich mich von ihnen gesegnet.' WHERE id=3471;
UPDATE npc_text SET Text0_0='Ihr müsst der Führung der Geister etwas mehr vertrauen. Doch Euer Weg führt zu einem anderen, zu Thrag Steinhuf in Donnerfels. Vielleicht könnt Ihr etwas voneinander lernen.' WHERE id=3472;
UPDATE npc_text SET Text0_0='Ihr habt viel gelernt, doch ich spüre, dass die Geister Euch noch mehr lehren können. Ich kann Euch auf den Weg geleiten, aber nicht Eure Schritte lenken. Geht zu Saru Stahlzorn in Orgrimmar, dort beginnt der nächste Abschnitt Eurer Reise.' WHERE id=3478;
UPDATE npc_text SET Text0_0='Sehr feine Ausrüstung stellt Ihr her, wirklich sehr fein. Geht nach Orgrimmar, der Heimat der Orcs, und sucht einen Schmied namens Saru Stahlzorn auf. Eines muss man den Orcs lassen, sie fertigen wunderbare Instrumente des Todes.' WHERE id=3481;
UPDATE npc_text SET Text0_0='Tut mir Leid, $GJungchen:holde Maid;, aber Ihr habt einfach nicht das Grundwissen, um zu erlernen, was ich Euch lehren kann. Doch jammert nicht, geht zu meinem Schüler Rotgath, er kann Euch weiterhelfen.' WHERE id=3482;
UPDATE npc_text SET Text0_0='Ich kann Eure Arbeit gar nicht genug loben, Ihr stellt wahrlich erstaunliche Dinge mit Hammer und Amboss an. Also dann, sagt keinem, dass ich Euch einen Goblin als Lehrmeister empfohlen habe, aber genau das werde ich tun. Brikk Keencraft ist sein Name, und soweit ich weiß, gehört ihm ein Laden unten in Beutebucht.' WHERE id=3484;
UPDATE npc_text SET Text0_0='Das ist kein Spiel zum Herumalbern! Geht und sprecht mit Murr da draußen. Er mag eine Schlange in meinem Unterschlupf sein, aber er kann mit dem Amboss umgehen.' WHERE id=3485;
UPDATE npc_text SET Text0_0='Ich sehe, Ihr wisst mehr vom Geheimnis des Stahls als ich. Das ist gut, denn es soll erforscht werden. Jetzt müsst Ihr zum Dschungel südlich der Länder der Menschen reisen, zur Goblinstadt Beutebucht. Dort findet Ihr einen Goblin namens Brikk Kühnwerk. Ich mag Goblins nicht, aber sein Wissen um das Geheimnis des Stahls ist unübertroffen.' WHERE id=3487;
UPDATE npc_text SET Text0_0='Was ist? Natürlich bin ich ein Schmied. Glaubt Ihr etwa, Zwerge wären die Einzigen, die mit einem Amboss umgehen können? Wenn Ihr das so seht, bitte sehr. Sucht einen Zwerg, der Euch hilft!' WHERE id=3488;
UPDATE npc_text SET Text0_0='Wollt Ihr mich veralbern? Ihr müsst mich veralbern! Oder haltet Ihr mich für einen Clown, der Eurer Belustigung dient? Ist das Eure Meinung? Dass ich ein Clown bin? Aber klar! Ihr haltet mich für einen Clown!' WHERE id=3489;
UPDATE npc_text SET Text0_0='Ihr tut ja so, als wäre das der beste Schrott, den ich je gesehen habe. Klar, er ist ganz gut, aber ich sehe jeden Tag Zeug, das \'ganz gut\' ist. Ich sag Euch was: Findet eine Möglichkeit, mich zu beeindrucken, dann lehre ich Euch Dinge, bei denen jeder Zwerg vor Neid erblassen würde.' WHERE id=3490;
UPDATE npc_text SET Text0_0='Auch wenn ich es nicht gern zugebe, aber der Bär war eine Nummer zu groß für mich. Mannomann, das ist aber auch ein großer Bär!$B$BAlso, wenn Ihr diesen Bären niedermacht, wäre das eine stolze Leistung! Er hat mehr als nur einem paar unserer besten Jäger eine Menge Ärger bereitet.$B$B<Er schlägt Euch auf den Rücken.>$B$BBesiegt den Bären und wir nehmen Euch als reguläres Mitglied in die Weitschreiter-Lodge auf.' WHERE id=3493;
UPDATE npc_text SET Text0_0='Hier in der Jagdhütte der Weltenwanderer schätzen wir Jägerfertigkeiten höher als alles andere. Möchtet Ihr meine Billigung? Die müsst Ihr Euch verdienen.$B$BIhr scheint mit der Herausforderung gewachsen, $GJungchen:Mädel;!' WHERE id=3494;
UPDATE npc_text SET Text0_0='Mit Vergnügen... lasst mich diesen Augenblick nur noch etwas auskosten.' WHERE id=3496;
UPDATE npc_text SET Text0_0='Sie in Magieviertel. Ihr sie suchen in gruseliges Schädelgebäude.', Text0_1='Sie in Magieviertel. Ihr sie suchen in gruseliges Schädelgebäude.', Text1_0='Wir wissen, wo sie sein! Schädelgebäude in Magieviertel an Außenring von Stadt.', Text1_1='Wir wissen, wo sie sein! Schädelgebäude in Magieviertel an Außenring von Stadt.' WHERE id=3513;
UPDATE npc_text SET Text0_0='Bank in Handwerksviertel, Mittelturm. Ihr nicht können verfehlen.', Text0_1='Bank in Handwerksviertel, Mittelturm. Ihr nicht können verfehlen.', Text1_0='Wir ein Auge haben und sehen besser als Ihr! Bank in Zentrum von Unterstadt!', Text1_1='Wir ein Auge haben und sehen besser als Ihr! Bank in Zentrum von Unterstadt!' WHERE id=3514;
UPDATE npc_text SET Text0_0='Michael Garrett auf oberer Ring in Handwerksviertel.', Text0_1='Michael Garrett auf oberer Ring in Handwerksviertel.', Text1_0='Fledermausmann auf obere Etage in Handwerksviertel.', Text1_1='Fledermausmann auf obere Etage in Handwerksviertel.' WHERE id=3515;
UPDATE npc_text SET Text0_0='Jawoll, Ihr suchen Christopher Drakul in Handwerksviertel. Er auf Ostplattform.', Text0_1='Jawoll, Ihr suchen Christopher Drakul in Handwerksviertel. Er auf Ostplattform.', Text1_0='Wir Gilde beitreten? Nein? Gut, Ihr finden Gildenmeister auf Ostplattform in Handwerksviertel.', Text1_1='Wir Gilde beitreten? Nein? Gut, Ihr finden Gildenmeister auf Ostplattform in Handwerksviertel.' WHERE id=3516;
UPDATE npc_text SET Text0_0='Mit Gastwirt Norman auf oberer Ring in Handwerksviertel Ihr müssen reden.', Text0_1='Mit Gastwirt Norman auf oberer Ring in Handwerksviertel Ihr müssen reden.', Text1_0='Kein Ruh für die Bösen, aber wir denken, Ihr in Ordnung. Ihr finden Gastwirt Norman auf oberer Ring von Handwerksviertel.', Text1_1='Kein Ruh für die Bösen, aber wir denken, Ihr in Ordnung. Ihr finden Gastwirt Norman auf oberer Ring von Handwerksviertel.' WHERE id=3517;
UPDATE npc_text SET Text0_0='Briefkasten in Stadtmitte, Handwerksviertel, oberer Ring... neben Gasthaus.', Text0_1='Briefkasten in Stadtmitte, Handwerksviertel, oberer Ring... neben Gasthaus.', Text1_0='Wir rauskriegen müssen, ob unser Gehirn... Gehirn... Gehirnhacker bei Auktion sein verkauft worden. Ihr finden Briefkasten neben Gasthaus auf oberer Ring von Handwerksviertel.', Text1_1='Wir rauskriegen müssen, ob unser Gehirn... Gehirn... Gehirnhacker bei Auktion sein verkauft worden. Ihr finden Briefkasten neben Gasthaus auf oberer Ring von Handwerksviertel.' WHERE id=3518;
UPDATE npc_text SET Text0_0='Wir haben Auktionshaus in inneres Ring.', Text0_1='Wir haben Auktionshaus in inneres Ring.', Text1_0='Innerer Ring. Dorthin gehen!', Text1_1='Innerer Ring. Dorthin gehen!' WHERE id=3519;
UPDATE npc_text SET Text0_0='Klein Goblin oben auf Gebäude nördlich von Ruinens von Lordaeron. Müssen Unterstadt verlassen und dorthin gehen.', Text0_1='Klein Goblin oben auf Gebäude nördlich von Ruinens von Lordaeron. Müssen Unterstadt verlassen und dorthin gehen.', Text1_0='Wir sehen Ballon nördlich von Ruinens oben. Bringen Euch zu Ortens, wo weit weg sein.', Text1_1='Wir sehen Ballon nördlich von Ruinens oben. Bringen Euch zu Ortens, wo weit weg sein.' WHERE id=3520;
UPDATE npc_text SET Text0_0='Priesterlehrer? Ihr zu äußeres Ring von Kriegsviertel gehen und dort suchen.', Text0_1='Priesterlehrer? Ihr zu äußeres Ring von Kriegsviertel gehen und dort suchen.', Text1_0='Oh-oh. Unheilige in Kriegsviertel stehen um Hauptgebäude an Außenring von Stadt.', Text1_1='Oh-oh. Unheilige in Kriegsviertel stehen um Hauptgebäude an Außenring von Stadt.' WHERE id=3521;
UPDATE npc_text SET Text0_1='Willkommen in der Jagdhütte der Weltenwanderer, $N.' WHERE id=3523;
UPDATE npc_text SET Text0_0='Schurken in Gebäude an Außenring von Schurkenviertel.', Text0_1='Schurken in Gebäude an Außenring von Schurkenviertel.', Text1_0='Die mich lernen gut schleichen. Ihr sie finden in Schurkenviertel an Außenring von Unterstadt.', Text1_1='Die mich lernen gut schleichen. Ihr sie finden in Schurkenviertel an Außenring von Unterstadt.' WHERE id=3524;
UPDATE npc_text SET Text0_1='Ihr seid hier immer willkommen, $N. Danke für die Volksbelustigung - und keine Bange wegen Daryl, das war überfällig!' WHERE id=3525;
UPDATE npc_text SET Text0_0='Hexenmeisterlehrer in Magieviertel in Gebäude an Außenring mit Magiers.', Text0_1='Hexenmeisterlehrer in Magieviertel in Gebäude an Außenring mit Magiers.', Text1_0='Wir willen Teufelshund für Haustier. Wir ihn gut füttern und behandeln, bringen bei, Stöckchen holen. Ihr finden Hexenmeisters in Magieviertel an Außenring von Stadt.', Text1_1='Wir willen Teufelshund für Haustier. Wir ihn gut füttern und behandeln, bringen bei, Stöckchen holen. Ihr finden Hexenmeisters in Magieviertel an Außenring von Stadt.' WHERE id=3526;
UPDATE npc_text SET Text0_0='In Gebäude von Kriegsviertel an Außenring von Stadt.', Text0_1='In Gebäude von Kriegsviertel an Außenring von Stadt.', Text1_0='Kriegerlehrers kämpfen gut. Ihr sie finden in Kriegsviertel an Außenring von Unterstadt.', Text1_1='Kriegerlehrers kämpfen gut. Ihr sie finden in Kriegsviertel an Außenring von Unterstadt.' WHERE id=3527;
UPDATE npc_text SET Text0_0='Ihr in Apothekarium nachkucken. Dort Doktor Herbert Halsey und Doktor Marsch praktizieren.', Text0_1='Ihr in Apothekarium nachkucken. Dort Doktor Herbert Halsey und Doktor Marsch praktizieren.', Text1_0='Doktor Herbert Halsey im Apothekarium, wo wir sein geborens. Er Euch können besser wieder zusammenflicken als wir.', Text1_1='Doktor Herbert Halsey im Apothekarium, wo wir sein geborens. Er Euch können besser wieder zusammenflicken als wir.' WHERE id=3528;
UPDATE npc_text SET Text0_0='James Van Brunt in Kriegsviertel an inneres Ring.', Text0_1='James Van Brunt in Kriegsviertel an inneres Ring.', Text1_0='James Van Brunt arbeiten in Kriegsviertel an inneres Ring. Er uns machen gut Kett.', Text1_1='James Van Brunt arbeiten in Kriegsviertel an inneres Ring. Er uns machen gut Kett.' WHERE id=3529;
UPDATE npc_text SET Text0_0='Das Eunice Burch in Handwerksviertel.', Text0_1='Das Eunice Burch in Handwerksviertel.', Text1_0='Eunice Burch verkaufen Ersatzteilens. Ihr sie finden in Handwerksviertel.', Text1_1='Eunice Burch verkaufen Ersatzteilens. Ihr sie finden in Handwerksviertel.' WHERE id=3530;
UPDATE npc_text SET Text0_0='Ihr gehen zu inneres Ring von Apothekarium. Lavinia Raab da sein.', Text0_1='Ihr gehen zu inneres Ring von Apothekarium. Lavinia Raab da sein.', Text1_0='Lady Euch lehren magisch Gegenstände zu machen. Sie in inneres Ring von Apothekarium.', Text1_1='Lady Euch lehren magisch Gegenstände zu machen. Sie in inneres Ring von Apothekarium.' WHERE id=3531;
UPDATE npc_text SET Text0_0='Franklin Lloyd im Schurkenviertel am Außenring von Unterstadt.', Text0_1='Franklin Lloyd im Schurkenviertel am Außenring von Unterstadt.', Text1_0='He, Bombens, wir mögen Bombens. Graham Van Talen Euch zeigen, wie man machen. Er in Außenring von Schurkenviertel.', Text1_1='He, Bombens, wir mögen Bombens. Graham Van Talen Euch zeigen, wie man machen. Er in Außenring von Schurkenviertel.' WHERE id=3532;
UPDATE npc_text SET Text0_0='Wir sie kennen gut. Sie Mary Edras in inneres Ring von Schurkenviertel.', Text0_1='Wir sie kennen gut. Sie Mary Edras in inneres Ring von Schurkenviertel.', Text1_0='Lady uns flicken zusammen und verbinden, wenn wir Aua haben. Ihr sie finden in inneres Ring von Schurkenviertel.', Text1_1='Lady uns flicken zusammen und verbinden, wenn wir Aua haben. Ihr sie finden in inneres Ring von Schurkenviertel.' WHERE id=3533;
UPDATE npc_text SET Text0_0='Ihr Armand Cromwell im Magieviertel nicht übersehen können. Er stehen auf Dock.', Text0_1='Ihr Armand Cromwell im Magieviertel nicht übersehen können. Er stehen auf Dock.', Text1_0='Armand Cromwell immer auf Dock von Magieviertel sein. Fischies in grün Glibber besonders lecker. Ihr kosten.', Text1_1='Armand Cromwell immer auf Dock von Magieviertel sein. Fischies in grün Glibber besonders lecker. Ihr kosten.' WHERE id=3534;
UPDATE npc_text SET Text0_0='Ihr suchen Martha Alliestar. Sie auf inneres Ring von Apothekarium.', Text0_1='Ihr suchen Martha Alliestar. Sie auf inneres Ring von Apothekarium.', Text1_0='Ihr zu inneres Ring von Apothekarium gehen müssen. Dort Martha Alliestar Euch können lehren pflücken Blumens.', Text1_1='Ihr zu inneres Ring von Apothekarium gehen müssen. Dort Martha Alliestar Euch können lehren pflücken Blumens.' WHERE id=3535;
UPDATE npc_text SET Text0_0='Er in inneres Ring von Schurkenviertel.', Text0_1='Er in inneres Ring von Schurkenviertel.', Text1_0='Arthur Moor in inneres Ring von Schurkenviertel tun Lederverarbeitung lehren.', Text1_1='Arthur Moor in inneres Ring von Schurkenviertel tun Lederverarbeitung lehren.' WHERE id=3536;
UPDATE npc_text SET Text0_0='Ihr gehen zu inneres Ring von Kriegsviertel. Dort Brom Killian sein.', Text0_1='Ihr gehen zu inneres Ring von Kriegsviertel. Dort Brom Killian sein.', Text1_0='Brom Killian Euch lehren, glänziges Zeugs für mir ausgraben. Er in inneres Ring von Kriegsviertel.', Text1_1='Brom Killian Euch lehren, glänziges Zeugs für mir ausgraben. Er in inneres Ring von Kriegsviertel.' WHERE id=3537;
UPDATE npc_text SET Text0_0='Killian Hagey lehren Kürschnerei in Schurkenviertel an inneres Ring.', Text0_1='Killian Hagey lehren Kürschnerei in Schurkenviertel an inneres Ring.', Text1_0='Wir glänzend Haken haben. Wir das von Killian Hagey kriegen. Der uns lehren, wie tote Tierens häuten. Ihr haben schön Haut. Ihr ihn finden in inneres Ring von Schurkenviertel.', Text1_1='Wir glänzend Haken haben. Wir das von Killian Hagey kriegen. Der uns lehren, wie tote Tierens häuten. Ihr haben schön Haut. Ihr ihn finden in inneres Ring von Schurkenviertel.' WHERE id=3538;
UPDATE npc_text SET Text0_0='Ihr suchen nach Josef Gregorian. Er in Magieviertel auf inneres Ringseit von grün Kanal.', Text0_1='Ihr suchen nach Josef Gregorian. Er in Magieviertel auf inneres Ringseit von grün Kanal.', Text1_0='Hmmm, das gut Idee. Vielleicht wir hingehen sollten und paar Stichens ersetzen lassen? Er gut nähen. Ihr finden Josef Gregorian in inneres Ring von Magieviertel.', Text1_1='Hmmm, das gut Idee. Vielleicht wir hingehen sollten und paar Stichens ersetzen lassen? Er gut nähen. Ihr finden Josef Gregorian in inneres Ring von Magieviertel.' WHERE id=3539;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=3541;
UPDATE npc_text SET Text0_0='Welchen Lehrer sucht Ihr?', Text0_1='Welchen Lehrer sucht Ihr?' WHERE id=3542;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=3543;
UPDATE npc_text SET Text0_0='Hört die Geschichten eines Ältesten. $N. Ich habe viele zu erzählen...' WHERE id=3545;
UPDATE npc_text SET Text0_1='Willkommen in der Zuflucht, $N. Manche kommen absichtlich hierher, andere finden den Weg durch Zufall... Aber jeder hier hat eine einmalige Geschichte zu erzählen.' WHERE id=3546;
UPDATE npc_text SET Text0_0='Grrr...' WHERE id=3550;
UPDATE npc_text SET Text0_0='Ich kam von Süden zum Wegekreuz und suchte Hilfe, $C, finde aber nur Feiglinge, die sich von mir abwenden - selbst der mächtige Thork beleidigt mich auf diese Weise. Er ist ein großer Anführer, aber dass er mein Flehen nicht erhört, erzürnt mich doch sehr.$B$BEr behauptet, meine Wut mache mich unbedacht, sie beeinträchtige mein Urteilsvermögen, aber ich werde erst ruhen, wenn ich mich gerächt habe!' WHERE id=3553;
UPDATE npc_text SET Text0_0='Ich danke Euch, $N. Ihr habt meine flehentliche Bitte erhört und der Geist meiner verlorenen Frau hat endlich Ruhe gefunden. Das kann ich Euch niemals vergelten.$B$BMein Hass auf die Stacheleber wird nie vergehen, aber vorerst ist er gestillt.$B$BJetzt muss ich mein Leben wieder aufbauen.' WHERE id=3554;
UPDATE npc_text SET Text0_1='Der Leichnam ist der einer Orckriegerin.$B$BEs ist klar, dass sie im Kampf starb, wahrscheinlich gegen die Stacheleber in dieser Gegend.' WHERE id=3557;
UPDATE npc_text SET Text0_1='Ihr dreht den Leichnam um und seht Spuren von Klingen und Zaubern an der verstorbenen Orcfrau - ihre Rüstung ist fast zerstört, das Haar klebt an ihrem Gesicht.$B$BNach Mankriks Beschreibung habt Ihr keinen Zweifel, dass dies seine Frau ist.$B$BDie Wahrheit wird schmerzlich für ihn sein, aber Ihr seid sicher, dass er sie erfahren sollte.' WHERE id=3558;
UPDATE npc_text SET Text0_0='Willkommen am Wegekreuz, $C. Mein Name ist Thork - ich befehlige hier im Namen des Kriegshäuptlings die Verteidigung.$B$BBitte fühlt Euch ganz wie zu Hause. Solange Ihr Euch nur um Euren eigenen Kram kümmert, dürfte es keinen stören, wenn Ihr etwas Platz beansprucht.$B$BUnsere Sicherheit hier wird auf vielerlei Weise bedroht. Wenn Ihr bereits mit dem Land vertraut seid, sprecht mit den anderen hier - vielleicht haben sie Aufgaben für Euch.' WHERE id=3559;
UPDATE npc_text SET Text0_0='<Bluthauer spuckt aus.>$B$BIch bin ein Krieger der Horde, $N. Es macht mich wütend, dass unsere Feinde keinen Tagesritt von Kargath entfernt in einer Festung sitzen und Pläne gegen uns schmieden.$B$BWie sehne ich mich danach, die Bande der Pflicht abzustreifen und alle im Namen des Kriegshäuptlings zu vernichten.$B$BAber wir alle haben nun mal unseren Platz und unsere Aufgaben und müssen unsere Rollen erfüllen.' WHERE id=3560;
UPDATE npc_text SET Text0_0='Wahrhaftig, ich beneide Euch. Im Namen der Horde an vorderster Front zu kämpfen. Ich darf nur befehligen.$B$BNun gut, in meiner Jugend lebte ich meine Träume aus, was meinen Lebensabend jetzt erträglich macht. Heute erlebe ich den Ruhm des Sieges und den bitteren Geschmack der Niederlage nur durch die Soldaten, die ich befehlige.' WHERE id=3561;
UPDATE npc_text SET Text0_0='Ich träume, $N... oh ja, ich träume. In meinen Träumen sterbe ich tausend Mal auf unterschiedliche Weise. Manch einer würde meine Träume Alpträume nennen. ICH NICHT! In Träumen sterbe ich im Kampfgetümmel. Für jeden meiner Tode fallen Zehntausende Feinde der Horde vor mir. Das ist glorreich!' WHERE id=3562;
UPDATE npc_text SET Text0_0='Aye, das alles sehe ich Euch an, $N. Ihr erinnert mich sehr stark an mich, als ich noch ein junger Krieger der Horde war.$B$BZweifellos werdet Ihr es im Leben noch weit bringen... und im Tod noch weiter.$B$B<Bluthauer salutiert.>' WHERE id=3563;
UPDATE npc_text SET Text0_0='Und auch noch bescheiden... Vielleicht ist Eure Zeit tatsächlich gekommen, $N.$B$BSeid Ihr bereit, die Festung der Schwarzfelsorcs zu betreten? Zu verwüsten? Die Fundamente der Bergzitadelle einzureißen?' WHERE id=3564;
UPDATE npc_text SET Text0_0='Ich hatte niemals an Euch gezweifelt, $N.$B$BHier sind Eure Befehle, $N. Ich weiß, Ihr enttäuscht mich nicht.' WHERE id=3565;
UPDATE npc_text SET Text0_0='Ah, ein Besucher! Ihr müsst ein Abenteurer sein, Touristen sehen wir hier nicht oft. Holt Euch einen Stuhl, etwas zu trinken und macht es Euch bequem. Ihr seht aus, als hättet Ihr Fleisch auf den Knochen. Möchtet Ihr an den Käfigturnieren teilnehmen?', Text0_1='Ah, ein Besucher! Ihr müsst ein Abenteurer sein, Touristen sehen wir hier nicht oft. Holt Euch einen Stuhl, etwas zu trinken und macht es Euch bequem. Ihr seht aus, als hättet Ihr Fleisch auf den Knochen. Möchtet Ihr an den Käfigturnieren teilnehmen?' WHERE id=3566;
UPDATE npc_text SET Text0_0='Seid mir gegrüßt, $C.' WHERE id=3573;
UPDATE npc_text SET Text0_0='Rend, Sohn von Schwarzfaust: Ohnmächtiger Häuptling des Schwarzfelsklans. Einen störrischeren Kerl gibt es nicht.$B$BAm Ende des zweiten Krieges suchten Rend und sein Bruder Maim Zuflucht in der Bergfestung der Schwarzfelsspitze. Dort erklärte Rend sich selbst zum Kriegshäuptling des geschwächten Schwarzfelsklans.' WHERE id=3574;
UPDATE npc_text SET Text0_0='Ach, mich müsst Ihr davon nicht überzeugen, der Kriegshäuptling und ich haben viel zusammen erlebt. Euer Eifer wird jedoch zur Kenntnis genommen.$B$BWie gesagt, Rend fand eine neue Heimat für sein Volk und ich gehörte zu denen, die widerwillig folgten.$B$BEin Konflikt führte zu anderen... zahllosen anderen. In den Kämpfen des Schwarzfelsklans mit den Dunkeleisenzwergen um die Schwarzfelsspitze fielen meine beiden Söhne. Da wurde mir klar, dass meine Zeit beim Schwarzfelsklan zu Ende ging.' WHERE id=3575;
UPDATE npc_text SET Text0_0='Ich ging. Ich wollte nicht bei den Schwarzfelsorcs bleiben. Nein, der Tod meiner Kinder sollte nicht umsonst gewesen sein. Ich wollte nicht unter einem unfähigen, machtgierigen Irren wie Rend dienen.$B$BIch fand Zuflucht in den Wäldern von Lordaeron und führte bei Herdweilerc ein Einsiedlerleben.' WHERE id=3576;
UPDATE npc_text SET Text0_0='<Etrigg lacht.>$B$BTja, die Welt war nicht immer so in Unordnung. Es gab eine Zeit, bevor die Geißel das Land verheert hatte. Bevor diese Teile von Lordaeron als Pestländer bezeichnet wurden.$B$BDort rettete mir Thrall das Leben und ich schloss mich der neuen Horde an... doch ich schweife ab. Diese Geschichte soll ein andermal erzählt werden. Ja, ein andermal... Vielleicht...' WHERE id=3577;
UPDATE npc_text SET Text0_0='Ohne jeden Zweifel. Vielleicht könntet Ihr eines Tages einem alten Freund von mir eine Nachricht zukommen lassen. Ich höre, er zieht immer noch durch jene Wälder.$B$B<Etrigg neigt einen Moment den Kopf wie im Gebet.>$B$BAlso, Rend, ja... Rend und sein Bruder Maim beherrschten die Spitze im ständigen Kampf mit den Dunkeleisenzwergen. Sie konnten die Zwerge aus den oberen Stadtteilen verdrängen, aber erst nach Ankunft der Großdrachen und nach Maims Tod.' WHERE id=3578;
UPDATE npc_text SET Text0_0='So ist es. Nefarian, Brut von Todesschwinge, herrscht jetzt über die Spitze. Rend steht unter dem Befehl des Großdrachen.$B$BGelänge es Euch doch nur irgendwie, die Hallen des Aufstiegs zu passieren.$B$B<Etrigg seufzt.>$B$BDoch nur wen der schwarze Klan als würdig erachtet, genießt das Privileg des Aufsteigens.' WHERE id=3579;
UPDATE npc_text SET Text0_0='Vielleicht. Geht jetzt. Sucht Rat beim Kriegshäuptling. Er wird von Eurer Entdeckung erfahren wollen.' WHERE id=3580;
UPDATE npc_text SET Text0_0='Das Tal der Prüfungen macht selbst den Schwächsten zu einem Krieger, der der Horde würdig ist. Wer die Herausforderungen nicht bewältigen kann, bleibt zurück und wird in der sengenden Wüstenhitze geröstet.' WHERE id=3583;
UPDATE npc_text SET Text0_1='Guten Tag auch, $N. Ich bin als Chronormu bekannt, aber Ihr dürft mich Chromie nennen. Derlei Vertrautheit stört mich nicht.$B$BAber woher ich Euren Namen kenne, fragt Ihr? Ich gehöre zum bronzenen Drachenschwarm, und wir sind auf ewig auf den Fluss der Zeit eingestellt. Man könnte sagen, dass wir uns bereits einmal begegnet sind. Und daher werden wir auch wieder zusammentreffen.$B$BIch hoffe, das von mir gewählte Aussehen stört Euch nicht; zwar besteht meine Aufgabe darin, einen traurigen Ort zu bewachen, doch lasse ich mich nicht in das Elend des Ortes hineinziehen.' WHERE id=3584;
UPDATE npc_text SET Text0_1='Abermals hallo, $N. Ich spüre, dass der Zeitstrom von Mächten bedroht bleibt, die wahrhaft stark sind. Der bronzene Drachenschwarm weiß Eure Hilfe daher auch fürderhin zu schätzen.$B$BZwar ist die Vergangenheit festgeschrieben, doch die Zukunft ist in konstantem Wandel. Die Entscheidungen, die Ihr hier und heute trefft, helfen mit, diesen Wandel zu lenken.' WHERE id=3585;
UPDATE npc_text SET Text0_1='Hört auf Eure Ältesten, $N. Wenn Ihr willensstark und entschlossen seid, gibt es keine Macht, die Ihr nicht kontrollieren könnt.' WHERE id=3586;
UPDATE npc_text SET Text0_1='Nicht alle Verlassenen werden von Sylvanas\' Willen angetrieben, $N. Einige von uns treibt wahre Macht an - Macht, die wir für uns selbst erringen.' WHERE id=3587;
UPDATE npc_text SET Text0_1='Es... ist... nicht... die... beste... Zeit, $N. Ich muss mich konzen... trieren.' WHERE id=3589;
UPDATE npc_text SET Text0_1='Euer Auftrag muss sehr wichtig sein, $C, dass er Euch so tief in die Marschen führt...' WHERE id=3590;
UPDATE npc_text SET Text0_1='Die Höllenbestie ist einer der mächtigsten Dämonen, den ein Hexenmeister im Kampf an der Seite haben kann. Die Kreatur ist stark, gut gerüstet und versetzt jedem Gegner verheerende Schläge. Wenn Ihr beschließen würdet, Eurer Waffe den Geist einer Höllenbestie einzuverleiben, hättet Ihr einige von deren Vorteilen - bessere Gesundheit und Regeneration und dergleichen.$B$BFalls Ihr mehr über Teufelshunde wissen wollt, solltet Ihr mit Wytula gegenüber sprechen.' WHERE id=3591;
UPDATE npc_text SET Text0_1='Der Teufelshund ist... ein wahrlich tückisches Geschöpf. Er ist körperlich nicht so stark wie eine Höllenbestie, aber ein weiser Hexenmeister weiß, dass brutale Gewalt nicht immer die Lösung ist - denn sonst hättet Ihr stattdessen den Weg des Kriegers gewählt, nicht wahr?$B$BDer Teufelshund entzieht seinen Gegnern Mana. Damit und mit seiner Schnelligkeit überwindet er Feinde. Seine Kraft kommt aus dem Arkanen und er nutzt die arkane Macht für seine Zwecke.$B$BWenn Ihr mehr über Höllenbestien wissen wollt, solltet Ihr mit Magaz gegenüber sprechen.' WHERE id=3592;
UPDATE npc_text SET Text0_1='Habt Ihr schon mit einem meiner Akolyten in dem Turm gesprochen, $N? Wir können Eure Kugel erst fertig stellen, wenn Ihr eine ihrer Aufgaben erledigt habt. Sprecht jetzt mit ihnen, wenn Ihr bereit seid.' WHERE id=3594;
UPDATE npc_text SET Text0_0='Bitte helft mir, $GBruder:Schwester;...$B$BBald werde ich diese Welt verlassen und dann werde ich nicht mehr leiden so wie jetzt. Doch die Tiere, $GBruder:Schwester;... die Tiere leiden ebenfalls und kennen keine Erlösung. Mit meinem letzten Lebensatem bitte ich Euch um Hilfe.' WHERE id=3633;
UPDATE npc_text SET Text0_0='Ja, ich spreche eine Sprache, die Ihr versteht, $R. Wir sind keine Tiere, auch wenn die meisten Angehörigen Eurer Art uns als solche behandeln. Doch unsere Differenzen verblassen angesichts der Tragödie dieses Landes. Bitte, vergesst die Zwistigkeiten mit meiner Art und erhört die Bitte eines Sterbenden.$B$BIch kam in dieses Land, um zu heilen, was immer mir möglich war, und wenn Ihr mir nicht helft, war alles vergebens. Mit meinem letzten Lebensatem bitte ich Euch um Eure Hilfe.' WHERE id=3634;
UPDATE npc_text SET Text0_0='"..."$B$BDer Körper des Taurendruiden liegt still und reglos da. Offenbar ist er endgültig von dieser Welt in sein gelobtes jenseitiges Land gegangen.' WHERE id=3635;
UPDATE npc_text SET Text0_0='Ich bin Beamter der Stadt und für das Volkszählungsbüro von Unterstadt zuständig. Seit wir von der Geißel befreit wurden, versuchen wir, immer genau zu wissen, wie groß die Bevölkerung unserer Stadt ist. Mit arkanen Mitteln und dank strengster geistiger Disziplin bin ich in der Lage, auf diese Aufzeichnungen zuzugreifen. Wenn Ihr in diesem Zusammenhang mit mir zu sprechen wünscht, so tut dies bitte. Ihr solltet ja immerhin wissen, ob es dies ist, weswegen Ihr hier seid, oder nicht.' WHERE id=3653;
UPDATE npc_text SET Text0_0='Der Kriegshäuptling hat mir aufgetragen, alle möglichen Arten von Waffen und Rüstung zu studieren. Er hat mich hierher, zum Zentrum der Kultur von Tauren und Orcs im Brachland geschickt, damit ich möglichst viele Reisende treffe und möglichst viel über das Brachland und seine Kulturen lerne.$B$BEr glaubt, dass man sogar noch von den erbärmlichsten Kulturen etwas lernen könne - zum Beispiel von den Stachelebern oder den Zentauren.' WHERE id=3654;
UPDATE npc_text SET Text0_0='Ihr da! $R! Kommt her. <schnaubt>$B$BDie Zeit ist knapp und mein Ende <schnaubt> naht. Quetschzahn wird siegen, Ihr werdet schon sehen. <schnaubt>$B$BMeine Gefangennahme kann meinem Volk <schnaubt> immer noch nützen. Der Stamm der Klingenmähnen ist mächtiger als der der Borstennacken!$B$BDie Horde will herausfinden, wer die Angriffe auf ihr Volk verübt. Wenn Ihr <schnaubt> Quetschzahn helft, dann gebe ich Euch Informationen, die keiner außer mir hat. Quetschzahn kann Euch helfen! <schnaubt> Aber Ihr müsst auch Quetschzahn helfen.' WHERE id=3656;
UPDATE npc_text SET Text0_0='Willkommen in Sturmwind! Ich bin ein Beamter der Stadt und helfe den Bürgern durch meine Arbeit im Volkszählungsbüro. Wenn Ihr jemanden Bestimmtes finden müsst, dann kann ich Euch vielleicht helfen. Mit arkanen Mitteln und dank strengster geistiger Disziplin bin ich in der Lage, auf diese Aufzeichnungen zuzugreifen.$B$BWenn Ihr also meine Dienste wünscht, so sprecht!' WHERE id=3657;
UPDATE npc_text SET Text0_0='Mein Hass ist stark <schnaubt>, doch jetzt ist die Zeit gekommen, Euch eine Geschichte zu erzählen, $R.$B$BVor langer Zeit fand zwischen den Göttern eine Schlacht statt. Nachdem er im Krieg der Ahnen besiegt worden war, fiel Agammagan, unser großer Ebergott, auf die Erde nieder. Seine Knochen waren zerrissen und sein Blut regnete auf den Boden herab. <schnaubt>$B$BGroße Dornen rankten sich empor, wo immer sein Blut den Erdboden berührte, und sein Schädel schmückt eine der Höhlen im Süden.' WHERE id=3658;
UPDATE npc_text SET Text0_0='Puh... Ich brauche was zu Rauchen und Streicheleinheiten von einer hübschen Gnomenfrau - und nicht unbedingt in dieser Reihenfolge!$B$BIch hab monatelang im Bauch von diesem Ding festgesessen! Was haben wir überhaupt für einen Tag? Ach, egal. Alles, was zählt, ist, dass ich endlich draußen bin.$B$BJUHUUU!' WHERE id=3659;
UPDATE npc_text SET Text0_0='Wie? Na ja, das ist eine lange Geschichte.' WHERE id=3660;
UPDATE npc_text SET Text0_0='Na denn! Also, ich war unten an den Lavagruben der geschmolzenen Spange und probierte dort meinen Lavaanzug aus. Alles klappte wunderbar. Ich konnte sogar in dem Zeug schwimmen, ohne dass mir irgendwas passierte!$B$BDa bin ich also und denke an nichts Böses, schwimme gerade ein bisschen auf dem Rücken, als plötzlich mitten aus dem Nichts dieses Ding auftaucht und mich einfach verschluckt! Wenn ich nicht meinen Lavaanzug mit seinem eingebauten Futterbehälter gehabt hätte, dann wäre es aus mit mir gewesen.' WHERE id=3661;
UPDATE npc_text SET Text0_0='Hmmm, irgendwie hört Ihr Euch an, als würdet Ihr mir nicht glauben - aber doch, ja, genau so ist es passiert.' WHERE id=3662;
UPDATE npc_text SET Text0_0='Da bin ich gerade mal zwei Minuten aus dem Bauch dieser Bestie raus, und schon will da wer wissen, wie ich aufs Klo gehe??? Hört mal, $GJungchen:Frollein;, dafür würde ich Euch den Hintern versohlen, wenn Ihr nicht $Gderjenige:diejenige; gewesen wärt, $G der:die; mich gerettet hat!$B$BNa, egal! Ich bin Euch jedenfalls dankbar für Eure Hilfe. Vielleicht kann ich mich ja revanchieren.' WHERE id=3663;
UPDATE npc_text SET Text0_0='Na jaaa, vielleicht ist \'revanchieren\' nicht ganz das richtige Wort. Versteht Ihr, als ich im Bauch von dem Viech feststeckte, hab ich mir was überlegt.$B$BDiese Kreatur lebt in glühendem, feurigem Magma, und doch kann das Feuer ihm rein gar nichts anhaben!$B$BWenn ich nur eine makellose Haut von einer dieser Bestien finden könnte, dann könnte ich sie meinem guten Freund Malyfous Düsterhammer in der Ewigen Warte bringen. Wenn überhaupt jemand mit der Haut einer solchen Bestie etwas anfangen kann, dann er.' WHERE id=3664;
UPDATE npc_text SET Text0_0='Also, wie ist es? Wollt Ihr eine Nachricht für mich überbringen?' WHERE id=3665;
UPDATE npc_text SET Text0_0='Wollt Ihr \'ne Kakerlake kaufen?' WHERE id=3666;
UPDATE npc_text SET Text0_1='Die Zeit läuft mir davon... läuft uns allen davon...', Text1_1='Ich muss noch so viel tun... so viel tun, ehe die Seuche kommt...', Text2_1='John, Liebling, wo bist du?', Text3_1='Was ist nur mit uns allen los? Mir ist... kalt...', Text4_1='Pssst... riecht Ihr etwas?' WHERE id=3668;
UPDATE npc_text SET Text0_1='Der Geist sieht Euch mit einem Ausdruck der Erfülltheit und der Bewusstheit an, der zuvor noch nicht bei ihm zu spüren war.$B$B"Ich fühle mich, als sei eine große Last von meiner Seele gewichen. Bald schon werde ich für immer in Frieden ruhen. Das weiß ich nun, und geduldig erwarte ich jenen Moment.$B$BHabt Dank, Fremdling."' WHERE id=3669;
UPDATE npc_text SET Text0_0='Ihr habt <schnaubt> Quetschzahn mehr geholfen, als ich es mir erträumt hätte, $R.$B$BDanke.$B$BSolange ich noch hier gefangen bin, könnt Ihr mir Blutsplitter von den Stachelebern der Borstennacken bringen, dann segne ich Euch mit Agammagans Macht.' WHERE id=3670;
UPDATE npc_text SET Text0_0='Die Thoriumbruderschaft verschwendet ihre Zeit nicht mit Termiten. Hinfort mit Euch!' WHERE id=3673;
UPDATE npc_text SET Text0_0='Für einen Handwerker von meinem Format ist das ein ganz einfacher Vorgang.$B$BDas Schwierigste bei der Herstellung von Gegenständen aus der Haut und den Panzern von Tieren ist die Beschaffung besagter Teile. Wenn Ihr mir also eine makellose Haut dieser Bestie brächtet, dann könnte ich Euch mehrere unterschiedliche, mächtige Gegenstände daraus herstellen.' WHERE id=3674;
UPDATE npc_text SET Text0_0='Ich würde sagen, die Chancen, ein solches Stück zu bekommen, stehen bei eins zu einer Million! Eine solche Haut bekommt man nur einmal im Leben. Selbst die diszipliniertesten Fachleute müssten zweifellos Hunderte von Fehlschlägen hinnehmen, ehe es ihnen gelänge, die Bestie so zu häuten, dass sie ein makelloses Exemplar erhielten.' WHERE id=3675;
UPDATE npc_text SET Text0_0='Wenn Ihr mir tatsächlich die Haut der Bestie brächtet? Tja... Ich werde es Euch zeigen...' WHERE id=3676;
UPDATE npc_text SET Text0_0='Herbstkatalog der Thoriumbruderschaft:$B$BDarin findet der geneigte Abenteurer die besten Schnäppchen für all seine Bedürfnisse! Und er erfährt gar, wo all diese schwierig zu beschaffenden Komponenten und Gegenstände zu bekommen sind.' WHERE id=3677;
UPDATE npc_text SET Text0_0='Der Titel dieses Buches ist in einer verschnörkelten Handschrift geschrieben und lautet \'Maureen Dalsons Tagebuch\'.' WHERE id=3693;
UPDATE npc_text SET Text0_0='Ein Tagebuch mit vergilbten Seiten liegt aufgeschlagen da, sodass man den letzten Eintrag lesen kann:$B$B"Ich weiß nicht, was passieren wird, jetzt, wo Harold sich verwandelt hat. Es hat mich meine ganze Kraft gekostet, ihn zu dem einzigen Ort zu schleppen, den ich finden konnte, in dem er sicher eingesperrt ist und nicht herauskommen kann. Den Schlüssel habe ich bei mir und ich kann nur beten, dass es funktionieren wird...$B$BIch hoffe, ich schaffe es, hier herauszukommen, aber dazu brauche ich Geld. Unsere ganzen Sachen sind alle im Schrank, glaube ich... und jetzt, wo ich darüber nachdenke - trägt Harold den Schlüssel dazu nicht bei sich?$B$BIch muss darüber schlafen. Im Augenblick bin ich todmüde."' WHERE id=3694;
UPDATE npc_text SET Text0_0='Wenn Ihr ausgefranste Monstrositätenstickereien wollt, dann müsst Ihr wohl eine kleine Reise nach Stratholme unternehmen.$B$BFrüher war das die Heimatstadt von Lichtbringer höchstselbst, doch jetzt haben es die Diener von Kel\'Thuzad überrannt.$B$BStellt Euch den Gefahren der brennenden Stadt und erschlagt die fleischigen Monstrositäten! Nehmt ihnen Euren Schatz ab: ausgefranste Monstrositätenstickereien!' WHERE id=3713;
UPDATE npc_text SET Text0_0='Das letzte Sanktum der verrückten Eiferer. Dort erschaffen sie ihre scharlachroten Uniformen, die auf magische Weise von dem verzauberten scharlachroten Faden magieerfüllt werden!$B$BAuf nach Stratholme, Abenteurer!' WHERE id=3714;
UPDATE npc_text SET Text0_0='Das beste Konservierungsmittel für Fleisch, das Mensch und Geißel bekannt ist: Frostraunens Balsamierungsflüssigkeit. Es gibt nur einen einzigen Ort, an dem ein Abenteurer diese Flüssigkeit finden kann, und man könnte sich denken, dass der Lich, dessen Namen sie trägt, es nicht allzu gern sieht, wenn jemand diesen Ort aufsucht.$B$BAber... wenn Ihr die Flüssigkeit wollt, dann müsst Ihr wohl zur Scholomance...' WHERE id=3715;
UPDATE npc_text SET Text0_0='Das Grundstück, auf dem die Scholomance errichtet wurde, erhielt Kel\'Thuzad von der Familie Barov. Als Gegenleistung für den Grund und Boden - ein Ort, an dem der Kult der Verdammten die üble Magie der Geißel studieren konnte - schenkte Kel\'Thuzad den Barovs Unsterblichkeit, indem er ihnen den Untod gewährte.$B$BIm Schattengewölbe findet Ihr sowohl die Schattenhaut als auch deren Besitzerin, Lady Illucia Barov.$B$BNie gab es einen besseren Spiegel für die Eitlen und die Toten!' WHERE id=3716;
UPDATE npc_text SET Text0_0='Eines der Haupterzeugnisse der Thoriumbruderschaft.$B$BArkanitkristalle werden aus reichhaltigen Thoriumadern gewonnen. Durch die Fachkenntnis mächtiger Alchemisten entstehen Arkanitbarren daraus.$B$BSucht einen Minenarbeiter auf, falls Ihr nicht selbst einer seid... und sucht Euch einen guten Alchemisten! Ihr werdet beide benötigen.' WHERE id=3717;
UPDATE npc_text SET Text0_0='Willkommen an der Front, $C. Politiker mögen ja vielleicht an einem Tag untereinander gemeinsame Sache machen und am nächsten Tag Ränke gegeneinander schmieden, aber so etwas können wir uns hier nicht leisten. Dies ist das Bollwerk, hier kämpfen wir jeden Tag erneut um unser Überleben.$B$BWenn Ihr hergekommen seid, um Euch als $GHeld:Heldin; zu beweisen, dann werdet Ihr hier reichlich Gelegenheit dazu haben... vor allem, sobald wir auf Andorhal vorstoßen - auf Andorhal und auf die bösartige Macht, die dort die Geißel kontrolliert.' WHERE id=3753;
UPDATE npc_text SET Text0_0='Willkommen an der Front, $C. Das Königreich Sturmwind und seine Verbündeten haben mich hierher geschickt, um der wachsenden Bedrohung durch die Geißel zu begegnen. Hier im Zugwindlager kämpfen wir jeden Tag erneut um unser Überleben.$B$BWenn Ihr hergekommen seid, um Euch als $GHeld:Heldin; zu beweisen, dann werdet Ihr hier reichlich Gelegenheit dazu haben... vor allem, sobald wir auf Andorhal vorstoßen - auf Andorhal und auf die bösartige Macht, die dort die Geißel kontrolliert.' WHERE id=3754;
UPDATE npc_text SET Text0_0='Es ist schön zu sehen, dass es auch andere gibt, die in Stratholme gegen die Geißel kämpfen, denn hier ist ihre Macht am größten.$B$BSeid gegrüßt. Mein Name ist Aurius. Einst war ich ein Paladin der Silbernen Hand, doch es erging mir wie anderen, deren Glaube ins Wanken geriet... ich kam vom rechten Wege ab. Die Geißel verführte mich, und beinahe wäre ich zu einem Todesritter geworden!$B$BSchließlich erkannte ich die Gefahr und suchte in dieser Kapelle Zuflucht. Es ist ein heiliger Ort - hier wurde der erste Paladin, Uther Lichtbringer, in den Orden der Silbernen Hand aufgenommen.' WHERE id=3755;
UPDATE npc_text SET Text0_0='Es bleibt mir nicht mehr viel Zeit in dieser Welt, aber wenn es der Wille des Lichts ist, dann wird die Zeit ausreichen, dass ich mein Schicksal erfüllen kann. Wenn Ihr Euch Baron Totenschwur stellt, werde ich bei Euch sein.' WHERE id=3756;
UPDATE npc_text SET Text0_0='Lasst mich allein. Meine Wunden sind schwer und ich fürchte, ich werde ihnen erliegen. Ich danke Euch, $N. Ich werde an diesem Ort sterben, doch dank Euch... sterbe ich in Frieden.' WHERE id=3757;
UPDATE npc_text SET Text0_0='Stellt meine Geduld nicht auf die Probe, sterbliches Geschöpf.' WHERE id=3758;
UPDATE npc_text SET Text0_0='Hütet Eure Zunge, $R, sonst reiße ich sie Euch aus dem Schädel und verwende sie als Hornwärmer.' WHERE id=3759;
UPDATE npc_text SET Text0_0='Was ich immer mache natürlich. Ich trauere. Ich wehklage. Ich hasse. Ich plane. Und wenn der Kreislauf vorüber ist, dann trauere ich wieder. Was genau wollt Ihr von mir? Ich habe viel zu tun.' WHERE id=3760;
UPDATE npc_text SET Text0_0='Euch?$B$B<Lorax lacht.>$B$BDas bezweifle ich.$B$BIch stehe in diesen Ruinen und träume. Träume von dem Tag, an dem ich Goraluk Hammerbruch fange, diesen elenden Dieb.$B$BWir hatten eine Vereinbarung, er und ich. Eine Vereinbarung, die er gebrochen hat. Verraten.' WHERE id=3761;
UPDATE npc_text SET Text0_0='Ich sollte ihm das Dämonenschmieden beibringen und dafür seine Seele erhalten. Die Seele eines Schwarzfelsorcs ist nicht sonderlich viel wert, doch in diesen traurigen Zeiten ist jede Seele, ganz egal welche, besser als gar nichts.' WHERE id=3762;
UPDATE npc_text SET Text0_0='Er nahm die ungeschmiedete runenbedeckte Brustplatte, die für die Herstellung der dämonengeschmiedeten Brustplatte benötigt wurde, an sich und floh, ohne mich zu bezahlen. Doch ich bin kein Dummkopf - das ungeschmiedete Bruststück ist völlig nutzlos, wenn man nicht über das Rezept oder die Kenntnis verfügt, wie man das besagte Wissen anwenden muss.$B$BHmmm... ob er sich wohl in den sicheren Mauern seiner Stadt, der Schwarzfelsspitze, verschanzt hat und versucht, den Gegenstand aus dem Rohmaterial herzustellen? Ich weiß, dass er es nicht wagt, die Stadt zu verlassen!' WHERE id=3763;
UPDATE npc_text SET Text0_1='Seit wir in diesen Krater gekommen sind, hat Dadanga eine Vorliebe für die großen Blutblütensprösslinge entwickelt, die hier überall wachsen.$B$BIch bin sicher, sie würde sich über einen kleinen Imbiss freuen, falls Ihr ihr ein paar bringen möchtet...' WHERE id=3793;
UPDATE npc_text SET Text0_0='He, Mann. Geht ruhig da rübba zu Meister Gadrin. Wenn Ihr Euch für ihn um diesen irren Zalazane kümmert, dann helf ich Euch auch.' WHERE id=3794;
UPDATE npc_text SET Text0_0='He, Mann, ich bin Gadrins Assistent. Ich maloche freiwillig hier, das hilft mir durch die Hexendoktorschule. Weil Ihr ihm helft, helf ich Euch für umsonst. Was braucht\'a repariert?' WHERE id=3795;
UPDATE npc_text SET Text0_0='Ich... Ich bin schwer verletzt...' WHERE id=3797;
UPDATE npc_text SET Text0_0='Hört zu, $R. Hört gut zu...$B$BIn der Nacht kamen die Drachenreiter auf dem schwarzen Klan. Sie schlugen schnell zu, aber mit tödlicher Präzision. Sie packten junge Welpen wie mich und flohen im Schutze der Dunkelheit.$B$BMit Entsetzen musste ich mit ansehen, wie sie meine Brüder und Schwestern ihren Experimenten unterzogen. Aus unserer Essenz sollte der chromatische Drachenschwarm erschaffen werden.' WHERE id=3798;
UPDATE npc_text SET Text0_0='Nefarian hatte befohlen, dass ungeeignete Versuchsobjekte unverzüglich getötet werden sollten, doch das taten diese elenden Bestien nicht, ohne erst ein \'großzügiges\' Maß an Folter einzusetzen.$B$BSobald wir dann vollständig \'gebrochen\' waren, sammelten sie ein, was noch übrig war, und warfen uns diesen Rohlingen hin. Unsere Körper dienten ihrem kranken Spiel: Entweder wurden wir in die Lava geworfen oder - noch schlimmer - der blutrünstigen Legion da unten direkt in die Arme.' WHERE id=3799;
UPDATE npc_text SET Text0_0='Ihr seid hinzugekommen, als sie mich gerade den Bestien dort unten vorwerfen wollten, und dafür danke ich Euch. Vielleicht kann ich - oder besser, können wir vom blauen Drachenschwarm Euch ebenfalls helfen...$B$BIhr seid so weit gekommen, $R, Ihr habt viel gewagt. Seid Ihr bereit, noch weiter zu gehen und Nefarian und seine Legion des Schwarzfels zu bekämpfen?' WHERE id=3800;
UPDATE npc_text SET Text0_0='Wenn Ihr hier seid, um Großdrachen zu jagen, dann tut Euch keinen Zwang an. An welche Adresse soll ich den Beileidsbrief schicken?' WHERE id=3801;
UPDATE npc_text SET Text0_0='Euer Gestank wird jeden Großdrachen im Umkreis von etlichen Meilen anlocken. Sagt mir, was Ihr wollt, und fasst Euch kurz.' WHERE id=3802;
UPDATE npc_text SET Text0_0='Die Geister haben mir gesagt, dass Ihr kommen würdet, dass Ihr mich aus einem ganz bestimmten Grund aufsuchen würdet, aber dass vielleicht noch nicht einmal Ihr selbst diesen Grund kennt. Also, wisst Ihr, warum Ihr hier seid?' WHERE id=3803;
UPDATE npc_text SET Text0_1='Oh, hallo! Ich heiße Sarah. Was kann ich für Euch tun?' WHERE id=3804;
UPDATE npc_text SET Text0_1='Der Wald hat mir von Eurer Gegenwart berichtet, aber mir nicht gesagt, warum Ihr hier seid. So sprecht denn und klärt mich auf.' WHERE id=3805;
UPDATE npc_text SET Text0_0='He, Mann, alles klar?' WHERE id=3806;
UPDATE npc_text SET Text0_1='Ich heiße Euch willkommen, $N. Und der Smaragdkreis heißt Euch willkommen.$B$BHoffentlich gelingt es uns gemeinsam, diesen einst so mächtigen Wald zu verjüngen und zu beschützen. Viele Mächte versuchen, ihn zu zerstören... von außen... und von innen.$B$BLasst Euch nicht von der Macht hier verlocken. Sie würde Euch nur verderben, wie sie dieses ganze Land verdorben hat.' WHERE id=3807;
UPDATE npc_text SET Text0_0='Euch fehlt der Mumm für den Ritt auf einem Greifen, was? Na, Ihr habt Glück, es gibt noch eine Möglichkeit, bei der Ihr mit beiden Beinen auf dem Boden bleibt - mehr oder weniger... Ich spreche von dem gnomischen Fortbewegungsmittel, der Tiefenbahn. Ganz bequem im hinteren Teil des Zwergendistrikts zu finden.', Text0_1='Euch fehlt der Mumm für den Ritt auf einem Greifen, was? Na, Ihr habt Glück, es gibt noch eine Möglichkeit, bei der Ihr mit beiden Beinen auf dem Boden bleibt - mehr oder weniger... Ich spreche von dem gnomischen Fortbewegungsmittel, der Tiefenbahn. Ganz bequem im hinteren Teil des Zwergendistrikts zu finden.', Text1_0='Ihr habt also von der Bahn gehört, wie? Unheimlich viel Spaß macht die! Ihr findet sie im hinteren Teil des Zwergendistrikts. Ach ja, behaltet unbedingt Eure Arme und Beine in der Bahn, während sie fährt!', Text1_1='Ihr habt also von der Bahn gehört, wie? Unheimlich viel Spaß macht die! Ihr findet sie im hinteren Teil des Zwergendistrikts. Ach ja, behaltet unbedingt Eure Arme und Beine in der Bahn, während sie fährt!' WHERE id=3813;
UPDATE npc_text SET Text0_0='Jeder will mit der Bahn fahren, die is\' echt zum Schießen! Also, wenn Ihr die Bahn sucht: Die is\' drüben in der Tüftlerstadt, \'n kleines Stück östlich vor den Toren von Eisenschmiede.', Text0_1='Jeder will mit der Bahn fahren, die is\' echt zum Schießen! Also, wenn Ihr die Bahn sucht: Die is\' drüben in der Tüftlerstadt, \'n kleines Stück östlich vor den Toren von Eisenschmiede.', Text1_0='Ach, die Bahn, die Bahn... jeder will mit der Bahn fahren. Was ist aus den guten alten Zeiten geworden, in denen man auf dem Rücken eines Greifen ritt und sich krampfhaft am Sattel festhielt, während man durch die Lüfte flog? $B$BPah! Geht nur und fahrt mit Eurer Bahn... sie ist drüben in der Tüftlerstadt, da, wo auch die ganzen anderen Gnomenapparate rumstehen.', Text1_1='Ach, die Bahn, die Bahn... jeder will mit der Bahn fahren. Was ist aus den guten alten Zeiten geworden, in denen man auf dem Rücken eines Greifen ritt und sich krampfhaft am Sattel festhielt, während man durch die Lüfte flog? $B$BPah! Geht nur und fahrt mit Eurer Bahn... sie ist drüben in der Tüftlerstadt, da, wo auch die ganzen anderen Gnomenapparate rumstehen.' WHERE id=3814;
UPDATE npc_text SET Text0_0='Das Buch spricht von Großdrachen, die gegen einen anderen, mächtigeren Großdrachen mit Namen Todesschwinge ein Bündnis eingingen. Nach allem, was Ihr dem Buch entnehmen könnt, wäre der chromatische Drachenschwarm beinahe auf die Welt losgelassen worden. Die Aspekte waren bereit.' WHERE id=3815;
UPDATE npc_text SET Text0_0='Es hat gerade eins auf der Terrasse der Händler aufgemacht. Wenn Ihr von der Bank aus den nach Südosten führenden Pfad entlang geht, könnt Ihr es nicht verfehlen.', Text0_1='Es hat gerade eins auf der Terrasse der Händler aufgemacht. Wenn Ihr von der Bank aus den nach Südosten führenden Pfad entlang geht, könnt Ihr es nicht verfehlen.', Text1_0='Oh, die immerwährende Gier nach materiellen Dingen. Das Glück ist jedenfalls mit Euch, Ihr könnt das Auktionshaus auf der Terrasse der Händler finden.', Text1_1='Oh, die immerwährende Gier nach materiellen Dingen. Das Glück ist jedenfalls mit Euch, Ihr könnt das Auktionshaus auf der Terrasse der Händler finden.' WHERE id=3833;
UPDATE npc_text SET Text0_0='Es befindet sich im Handelsdistrik, Bürger. In einer fast geraden Linie von den Toren von Sturmwind aus.', Text0_1='Es befindet sich im Handelsdistrik, Bürger. In einer fast geraden Linie von den Toren von Sturmwind aus.', Text1_0='Noch so ein Geschäftemacher, wie? Na ja, das Auktionshaus ist jedenfalls im Handelsdistrikt. In der Nähe der Bank.', Text1_1='Noch so ein Geschäftemacher, wie? Na ja, das Auktionshaus ist jedenfalls im Handelsdistrikt. In der Nähe der Bank.' WHERE id=3834;
UPDATE npc_text SET Text0_0='Hallo, $GReisender:Reisende;. Es ist selten, dass ich Besuch hier draußen bekomme.$B$BIch heiße Rivern und ich bilde die Frostsäbler von Winterquell zu Reittieren aus.$B$BDiese Katzen sind die wildesten aller Säbler und am schwierigsten zu zähmen. Nur jemand, der aufs Höchste geachtet wird und dieses Vertrauen auch verdient, darf den Versuch unternehmen, auf einer von ihnen zu reiten.$B$BEin solches Reittier verkaufe ich nicht einfach so jedem dahergelaufenen Abenteurer; oh nein, Ihr müsst hart dafür arbeiten und Euch nicht nur meine Anerkennung, sondern auch die der Säbler verdienen.' WHERE id=3854;
UPDATE npc_text SET Text0_0='Soll angeblich existieren. Nur ein großer chromatischer Drachkin oder Großdrache könnte einen solchen Knochenpanzer besitzen.' WHERE id=3855;
UPDATE npc_text SET Text0_0='Ist gewöhnlich bei den geringeren Angehörigen des chromatischen Drachenschwarms zu finden: Welpen, Brutlinge und geringe Wyrmkin.' WHERE id=3856;
UPDATE npc_text SET Text0_0='Legenden besagen, dass an Orten extremer Konflikte - und zwar auf dem ganzen Gebiet dessen, was wir heute als die Pestländer kennen - bisweilen reine und heilige Quellen aus dem Boden entspringen. Zur Erinnerung an all diejenigen, die im Krieg gegen die Geißel gefallen sind, wird das Wasser dieser Quellen \'Blut von Helden\' genannt, von Fachleuten ebenso wie von ganz gewöhnlichen Leuten.' WHERE id=3857;
UPDATE npc_text SET Text0_0='Die Fäden, die die Monstrositäten von Stratholme zusammenhalten, sind bei den meisten Handwerksfachleuten heiß begehrt. Sie sind extrem widerstandsfähig - so gut wie unzerreißbar.' WHERE id=3858;
UPDATE npc_text SET Text0_0='Wird von der Familie Barov aus der Scholomance als Spiegel für die Seelen verwendet, die die Familienmitglieder nicht mehr besitzen. Ein höchst wertvolles Andenken.' WHERE id=3859;
UPDATE npc_text SET Text0_0='Es gibt viele Häuser, in denen man in Sturmwind etwas zu trinken bekommt, aber nur eines, in dem man die Nacht verbringen kann. Geht zur \'Güldenen Rose\' drüben im Handelsdistrikt. Ich habe gehört, ein Zimmer kostet dort nicht allzu viel, und die Bettwäsche ist sauber.', Text0_1='Es gibt viele Häuser, in denen man in Sturmwind etwas zu trinken bekommt, aber nur eines, in dem man die Nacht verbringen kann. Geht zur \'Güldenen Rose\' drüben im Handelsdistrikt. Ich habe gehört, ein Zimmer kostet dort nicht allzu viel, und die Bettwäsche ist sauber.', Text1_0='Ah, \'Die Güldene Rose\' mit ihren weichen Daunenbetten und warmen Bädern... wenn ich nur an dieses Gasthaus denke, werde ich... ::gähn:: Ihr habt\'s gut, wenn Ihr auf dem Weg dorthin seid... es liegt im Westen des Handelsdistrikts. Schöne, weiche Kopfkissen, warme Laken... ::gähn::', Text1_1='Ah, ja, \'Die Güldene Rose\' mit ihren weichen Federbetten und ihrem warmen Badewasser... wenn ich nur an dieses Gasthaus denke, werde ich ::gähn:: Ihr habt\'s gut, wenn Ihr auf dem Weg dorthin seid... es liegt im Westen des Handelsdistrikts.... schöne, weiche Kopfkissen, warme Bettdecken... ::gähn::' WHERE id=3860;
UPDATE npc_text SET Text0_0='Praktischerweise steht ein Briefkasten genau zwischen der Bank von Sturmwind und dem Gasthaus zur Güldenen Rose im Handelsdistrikt.', Text0_1='Praktischerweise steht ein Briefkasten genau zwischen der Bank von Sturmwind und dem Gasthaus zur Güldenen Rose im Handelsdistrikt.', Text1_0='Ich weiß nicht genau, wie sie das machen... aber diese Briefkästen müssen magische Portale oder so was sein. Ich sehe den ganzen Tag lang Leute, die um den Briefkasten neben der Bank herumstehen und aus diesem kleinen Kasten Schwerter und Schilde und Rüstungen herausziehen!', Text1_1='Ich weiß nicht genau, wie sie das machen... aber diese Briefkästen müssen magische Portale oder so was sein. Ich sehe den ganzen Tag lang Leute, die um den Briefkasten neben der Bank herumstehen und aus diesem kleinen Kasten Schwerter und Schilde und Rüstungen herausziehen!' WHERE id=3861;
UPDATE npc_text SET Text0_1='Hallo, $C. Ich bin Jessir Mondsichel. Ich komme aus Darnassus und bin auf der Suche nach zwei Freunden, die im Teufelswald nach Hinweisen auf den Schattenrat Ausschau halten wollten.$B$BEs scheint, als versuchten diese üblen Kreaturen, die ja für einen Großteil des Leidens des Orcvolkes verantwortlich sind, wieder etwas von ihrer Macht zurückzugewinnen.$B$BMeine Freunde gingen nach Jaedenar, um ein für alle Male festzustellen, was dort vorging oder eben nicht.$B$BIch hatte erwartet, sie hier vorzufinden, als ich ankam, aber jetzt sind sie schon mehrere Tage überfällig.' WHERE id=3864;
UPDATE npc_text SET Text0_1='So kalt... und so hungrig.$B$BMein armer Freund. Ich hoffe, du hast Frieden gefunden. Ich will mir gar nicht vorstellen, was sie dir hätten antun können...$B$BIhr da! Ja, Ihr da! Bitte, Ihr müsst mir helfen zu fliehen. Einer von denen hat einen Schlüssel, könnt Ihr den finden? Ich weiß, dass irgendeiner den hat. Wenn Ihr ihn bekommt, dann könnt Ihr mir helfen, hier herauszukommen. Trey ist wahrscheinlich schon tot, aber noch ist Zeit! Könnt Ihr mir bitte helfen?' WHERE id=3865;
UPDATE npc_text SET Text0_1='Hallo! Ich bin gerade von der Erkundung der Ruinen von Kel\'Theril im Südwesten zurückgekehrt. Jetzt möchte ich die Wildekin erforschen, die dort oben auf dem Grat im Eulenflügeldickicht lagern.$B$BHabt Ihr schon mit Erelas Himmelsbern in Rut\'theran gesprochen? Er hat mich gebeten, hierher zu kommen, und vielleicht hat er ja auch für Euch eine Aufgabe.' WHERE id=3866;
UPDATE npc_text SET Text0_1='Habt Ihr Behüter Backus gesehen? Er ist ja sooo tapfer...$B$BIch finde, er ist der beste Mann, den die Nachtwache hat. Ob er mich wohl überhaupt bemerkt... ?' WHERE id=3868;
UPDATE npc_text SET Text0_0='Danke! Ihr habt mich befreit!', Text0_1='Danke! Ihr habt mich befreit!', Text1_0='Oh, danke! Ich war so lange in diesem Alptraum gefangen...', Text1_1='Oh, danke! Ich war so lange in diesem Alptraum gefangen...', Text2_0='Endlich ist meine Seele frei!', Text2_1='Endlich ist meine Seele frei!', Text3_0='Danke! Die Schlacht um Darroheim ist mir zum Verhängnis geworden, aber Ihr habt mich befreit!', Text3_1='Danke! Die Schlacht um Darroheim ist mir zum Verhängnis geworden, aber Ihr habt mich befreit!', Text4_0='Ah! Wenn Ihr einen Überlebenden von Darroheim seht, dann sagt ihm doch bitte, dass ich frei bin!', Text4_1='Ah! Wenn Ihr einen Überlebenden von Darroheim seht, dann sagt ihm doch bitte, dass ich frei bin!' WHERE id=3873;
UPDATE npc_text SET Text0_1='Der Smaragdkreis ist bestrebt, die Wunden des Teufelswaldes zu heilen. Obgleich das Land eben nach jenem Ding benannt ist, das es verderbt hat, hoffen wir dennoch, dass wir eines Tages in der Lage sein werden, das Land von der Verderbnis zu befreien.' WHERE id=3874;
UPDATE npc_text SET Text0_0='Zug-zug, $Gtapferer:tapfere:c; $C! Ich habe hier die besten Wölfe, die Ihr Euch als Reittiere nur vorstellen könnt. Nirgendwo werdet Ihr ein Tier finden, das eine dem Wolf vergleichbare Ausdauer und vergleichbare Überlebensinstinkte hat. Bitte, seht sie Euch ruhig selbst an.', Text0_1='Zug-zug, $Gtapferer:tapfere:c; $C! Ich habe hier die besten Wölfe, die Ihr Euch als Reittiere nur vorstellen könnt. Nirgendwo werdet Ihr ein Tier finden, das eine dem Wolf vergleichbare Ausdauer und vergleichbare Überlebensinstinkte hat. Bitte, seht sie Euch ruhig selbst an.' WHERE id=3893;
UPDATE npc_text SET Text0_0='nen schönen Tach Euch, $Gtapferer:tapfere:c; $C. Ich weiß, Ihr sucht nach \'nem guten Reittier. \'s gibt kein besseres Reittier als \'n Raptor am Zügel, das sach ich Euch, $GMann:Mädel;! Schaut Euch die an, die ich anzubieten hab - sie sin\' aus der allerbesten Zucht, die Ihr nur irgendwo finden könnt.', Text0_1='nen schönen Tach Euch, $Gtapferer:tapfere:c; $C. Ich weiß, Ihr sucht nach \'nem guten Reittier. \'s gibt kein besseres Reittier als \'n Raptor am Zügel, das sach ich Euch, $GMann:Mädel;! Schaut Euch die an, die ich anzubieten hab - sie sin\' aus der allerbesten Zucht, die Ihr nur irgendwo finden könnt.' WHERE id=3896;
UPDATE npc_text SET Text0_0='Sie geben mir die Leichen, und ich schmeiße sie in die Grube.' WHERE id=3935;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N. Ich bin Myolor Zornesbeben, Bewahrer der großen Schmiede.' WHERE id=3937;
UPDATE npc_text SET Text0_0='Ihr verfügt über eine ganz außerordentliche Schmiedefertigkeit, $N. Es ist an der Zeit, eine Entscheidung zu treffen. Zwei Wege könnt Ihr nun einschlagen: den des Waffenschmieds oder den des Rüstungsschmieds.$B$BDoch seid gewarnt: Sobald Ihr Euch für einen der beiden Wege entschieden habt, könnt Ihr diese Entscheidung nie wieder rückgängig machen. Jeder der beiden Wege erfordert eine derartige Disziplin, dass für Mischformen kein Platz ist.' WHERE id=3938;
UPDATE npc_text SET Text0_1='Die Zeit ist gekommen, $GWaffenbruder:Waffenschwester;, um der Geißel die Nacht wieder zu entringen!' WHERE id=3939;
UPDATE npc_text SET Text0_1='Die Zeit ist gekommen, $GWaffenbruder:Waffenschwester;, um der Geißel die Nacht wieder zu entringen!' WHERE id=3940;
UPDATE npc_text SET Text0_0='Ich freue mich, Euch durch Elunes Gnade das beste Reittier anbieten zu können, das es in ganz Teldrassil - und vielleicht auf der ganzen Welt - gibt: den Nachtsäbler. Schon seit Jahren verlässt sich die Schildwachenarmee auf diese wertvollen Gefährten.$B$BNehmt Euch ruhig Zeit und untersucht sie alle eingehend. Ich bin sicher, Ihr werdet keine Enttäuschung erleben.', Text0_1='Ich freue mich, Euch durch Elunes Gnade das beste Reittier anbieten zu können, das es in ganz Teldrassil - und vielleicht auf der ganzen Welt - gibt: den Nachtsäbler. Schon seit Jahren verlässt sich die Schildwachenarmee auf diese wertvollen Gefährten.$B$BNehmt Euch ruhig Zeit und untersucht sie alle eingehend. Ich bin sicher, Ihr werdet keine Enttäuschung erleben.' WHERE id=3942;
UPDATE npc_text SET Text0_0='Äh... Ich glaube, Ihr seid ein klein wenig zu groß, um auf einem Roboschreiter zu reiten, $Gmein Herr:meine Dame;. Ihr würdet vermutlich die präzise eingestellten Servoausgleichswellen zerquetschen!', Text0_1='Äh... Ich glaube, Ihr seid ein klein wenig zu groß, um auf einem Roboschreiter zu reiten, $Gmein Herr:meine Dame;. Ihr würdet vermutlich die präzise eingestellten Servoausgleichswellen zerquetschen!' WHERE id=3944;
UPDATE npc_text SET Text0_0='Wenn Ihr nach einem schnellen und sicheren Fortbewegungsmittel sucht, dann seid Ihr am richtigen Ort! Erst kürzlich sind die neuesten Modelle des Roboschreiters eingetroffen, und ich zeige Euch gern alles, was sie zu bieten haben!', Text0_1='Wenn Ihr nach einem schnellen und sicheren Fortbewegungsmittel sucht, dann seid Ihr am richtigen Ort! Erst kürzlich sind die neuesten Modelle des Roboschreiters eingetroffen, und ich zeige Euch gern alles, was sie zu bieten haben!' WHERE id=3945;
UPDATE npc_text SET Text0_0='Wenn ein Schmied mit seinem handwerklichen Können Euren Stand erreicht hat, dann stelle ich ihn vor eine Wahl.$B$BIhr müsst Euch zwar nicht entscheiden, aber ich empfehle es Euch... denn Ihr wollt doch sicher nicht für alle Zeiten ein Neophyt bleiben, oder? Es sind die Fachleute unter den Waffen- und Rüstungsschmieden, die unsere Armeen beliefern, für unsere Siege verantwortlich sind und Anerkennung finden!$B$BDoch seid gewarnt, $N. Sobald Ihr Eure Entscheidung getroffen und die notwendigen Aufgaben erfüllt habt, gibt es kein Zurück mehr. Man kann immer nur auf einer Hochzeit tanzen.' WHERE id=3953;
UPDATE npc_text SET Text0_0='Ich bin Krathok, Vorarbeiter dieser Handwerker.' WHERE id=3954;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $R.' WHERE id=3959;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C!' WHERE id=3960;
UPDATE npc_text SET Text0_0='Was wollt Ihr?' WHERE id=3961;
UPDATE npc_text SET Text0_0='Möge das Licht mit Euch sein, $C. Wie kann ich helfen?', Text0_1='Möge das Licht mit Euch sein, $C. Wie kann ich helfen?' WHERE id=3974;
UPDATE npc_text SET Text0_0='Hey, auch wenn Ihr nich\' dem Licht folgt wie\'n Ritter der Silbernen Hand, könnt Ihr uns trotzdem immer um Hilfe bitten.', Text0_1='Hey, auch wenn Ihr nich\' dem Licht folgt wie\'n Ritter der Silbernen Hand, könnt Ihr uns trotzdem immer um Hilfe bitten.' WHERE id=3975;
UPDATE npc_text SET Text0_0='Möge das Licht Euch heute beschützen.', Text0_1='Möge das Licht Euch heute beschützen.' WHERE id=3976;
UPDATE npc_text SET Text0_0='Möge das Licht Euch heute beschützen.', Text0_1='Möge das Licht Euch heute beschützen.' WHERE id=3977;
UPDATE npc_text SET Text0_0='Ein heftiger Sturm zieht auf - ich kann das fühlen, da mein ungeschütztes Knie schmerzt. Ich hoffe nur, dass der uns nicht erwischt.' WHERE id=3978;
UPDATE npc_text SET Text0_0='Na gut, $N, dann kümmern wir uns mal um diese Kessel! Wenn Ihr die benötigten Komponenten sammeln wollt, dann holt Euch erst einen glasartigen Fokussierer von mir. Den müsst Ihr bei Euch tragen, um die notwendigen Rohstoffe zu identifizieren, die die Anhänger der Geißel bei sich tragen und mit deren Hilfe wir die Kessel \'umdrehen\' können.$B$BAußerdem braucht Ihr eine Dosis Arkanbeschleuniger für die Kessel. Der ist weder billig noch sonderlich stabil. Sobald Ihr ihn habt, bleiben Euch nur dreißig Minuten, um ihn anzuwenden, bevor er seine Wirksamkeit verliert - also solltet Ihr gut planen, wann Ihr ihn kauft.' WHERE id=3979;
UPDATE npc_text SET Text0_0='Ausgehend von der Toxinprobe, die Ihr uns besorgt habt, glauben wir, dass Ihr die folgenden Dinge benötigt: einen Arkanbeschleuniger, sechs knöcherne Aufstachler und vier Stück Runenstoff. Beschafft Euch die Aufstachler von untoten Skeletten hier in den Pestländern, während Ihr den glasartigen Fokussierer bei Euch tragt.$B$BSobald Ihr diese Gegenstände alle zusammenhabt, solltet Ihr das Gegenmittel in den Kessel füllen können.' WHERE id=3980;
UPDATE npc_text SET Text0_0='Ausgehend von der Toxinprobe, die Ihr uns besorgt habt, glauben wir, dass Ihr die folgenden Dinge benötigt: einen Arkanbeschleuniger, fünf somatische Verstärker und vier Stück Runenstoff. Beschafft Euch die Verstärker von untoten Zombies oder Ghulen hier in den Pestländern, während Ihr den glasartigen Fokussierer bei Euch tragt.$B$BSobald Ihr diese Gegenstände alle zusammenhabt, solltet Ihr das Gegenmittel in den Kessel füllen können.' WHERE id=3981;
UPDATE npc_text SET Text0_0='Ausgehend von der Toxinprobe, die Ihr uns besorgt habt, glauben wir, dass Ihr die folgenden Dinge benötigt: einen Arkanbeschleuniger, fünf somatische Verstärker und vier Stück Runenstoff. Beschafft Euch die Verstärker von untoten Zombies oder Ghulen hier in den Pestländern, während Ihr den glasartigen Fokussierer bei Euch tragt.$B$BSobald Ihr diese Gegenstände alle zusammenhabt, solltet Ihr das Gegenmittel in den Kessel füllen können.' WHERE id=3982;
UPDATE npc_text SET Text0_0='Ausgehend von der Toxinprobe, die Ihr uns besorgt habt, glauben wir, dass Ihr die folgenden Dinge benötigt: einen Arkanbeschleuniger, vier Ektoplasmaresonatoren und vier Stück Runenstoff. Beschafft Euch die Resonatoren von Gespenstern oder geisterhaften Untoten hier in den Pestländern, während Ihr den glasartigen Fokussierer bei Euch tragt.$B$BSobald Ihr diese Gegenstände alle zusammenhabt, solltet Ihr das Gegenmittel in den Kessel füllen können.' WHERE id=3983;
UPDATE npc_text SET Text0_0='Dieser Job ist eindeutig mehr wert als tausenddreihundert im Monat.' WHERE id=3984;
UPDATE npc_text SET Text0_0='Na gut, $N, dann kümmern wir uns mal um diese Kessel! Wenn Ihr die benötigten Komponenten sammeln wollt, dann holt Euch erst einen glasartigen Fokussierer von mir. Den müsst Ihr bei Euch tragen, um die notwendigen Rohstoffe zu identifizieren, die die Anhänger der Geißel bei sich tragen und mit deren Hilfe wir die Kessel \'umdrehen\' können.$B$BAußerdem braucht Ihr eine Dosis Arkanbeschleuniger für die Kessel. Der ist weder billig noch sonderlich stabil. Sobald Ihr ihn habt, bleiben Euch nur dreißig Minuten, um ihn anzuwenden, bevor er seine Wirksamkeit verliert - also solltet Ihr gut planen, wann Ihr ihn kauft.' WHERE id=3985;
UPDATE npc_text SET Text0_0='Die Zone wird noch immer von der Seuche der Geißel beherrscht und es liegt noch viel Arbeit vor uns, ehe die Seuche genügend verändert sein wird, um einen Einfluss auf die Geißel zu haben. Leistet einen kriegswichtigen Beitrag: Sammelt die nötigen Rohstoffe ein!', Text0_1='Die Zone wird noch immer von der Seuche der Geißel beherrscht und es liegt noch viel Arbeit vor uns, ehe die Seuche genügend verändert sein wird, um einen Einfluss auf die Geißel zu haben. Leistet einen kriegswichtigen Beitrag: Sammelt die nötigen Rohstoffe ein!' WHERE id=3996;
UPDATE npc_text SET Text0_0='Die Seuche der Geißel ist fast am Wendepunkt angelangt - nur noch einige wenige Kessel müssen \'umgedreht\' werden, dann gewinnen wir die Oberhand! Ihr müsst unbedingt Euren Beitrag leisten und uns helfen!', Text0_1='Die Seuche der Geißel ist fast am Wendepunkt angelangt - nur noch einige wenige Kessel müssen \'umgedreht\' werden, dann gewinnen wir die Oberhand! Ihr müsst unbedingt Euren Beitrag leisten und uns helfen!' WHERE id=3997;
UPDATE npc_text SET Text0_0='Wir sind der Geißel überlegen - sie werden die Auswirkungen der veränderten Seuche noch mindestens einen ganzen weiteren Tag lang zu spüren bekommen! Diesen Vorsprung müssen wir aber wahren, also sabotiert unbedingt alle Kessel, die Ihr finden könnt!', Text0_1='Wir sind der Geißel überlegen - sie werden die Auswirkungen der veränderten Seuche noch mindestens einen ganzen weiteren Tag lang zu spüren bekommen! Diesen Vorsprung müssen wir aber wahren, also sabotiert unbedingt alle Kessel, die Ihr finden könnt!' WHERE id=3999;
UPDATE npc_text SET Text0_0='Der Jägerlehrer heißt Thotar. Sucht ihn auf - Ihr findet ihn im Bau im südwestlichen Teil von Klingenhügel.', Text0_1='Der Jägerlehrer heißt Thotar. Sucht ihn auf - Ihr findet ihn im Bau im südwestlichen Teil von Klingenhügel.', Text1_0='Ihr seid $Gein Jäger:eine Jägerin;? Na, wie dem auch sei, wenn Ihr Thotar sucht: Er versteckt sich im Bau im südwestlichen Teil der Stadt vor der Sonne.', Text1_1='Ihr seid $Gein Jäger:eine Jägerin;? Na, wie dem auch sei, wenn Ihr Thotar sucht: Er versteckt sich im Bau im südwestlichen Teil der Stadt vor der Sonne.' WHERE id=4013;
UPDATE npc_text SET Text0_0='Sprecht in Sen\'jin, südlich von hier, mit Un\'Thuwa.', Text0_1='Sprecht in Sen\'jin, südlich von hier, mit Un\'Thuwa.', Text1_0='Das sind Trollangelegenheiten. Sucht Un\'Thuwa auf - sie befindet sich in Sen\'jin, unten im Süden.', Text1_1='Das sind Trollangelegenheiten. Sucht Un\'Thuwa auf - sie befindet sich in Sen\'jin, unten im Süden.' WHERE id=4014;
UPDATE npc_text SET Text0_0='Dann ist Tai\'jin die Richtige für Euch. Ihr findet sie in der Kaserne im Osten der Stadt.', Text0_1='Dann ist Tai\'jin die Richtige für Euch. Ihr findet sie in der Kaserne im Osten der Stadt.', Text1_0='Sie heißt Tai\'jin, und Ihr findet sie in der Kaserne im Osten von Klingenhügel. Sie wird Euch lehren, die Götter zu achten. Sorgt dafür, dass Ihr ihr ebenfalls den nötigen Respekt erweist!', Text1_1='Sie heißt Tai\'jin, und Ihr findet sie in der Kaserne im Osten von Klingenhügel. Sie wird Euch lehren, die Götter zu achten. Sorgt dafür, dass Ihr ihr ebenfalls den nötigen Respekt erweist!' WHERE id=4015;
UPDATE npc_text SET Text0_0='Sucht Kaplak auf - er ist oben auf dem Bau, im südwestlichen Teil der Stadt.', Text0_1='Sucht Kaplak auf - er ist oben auf dem Bau, im südwestlichen Teil der Stadt.', Text1_0='Warum wollt Ihr denn bitte schön mit Kaplak sprechen? Na, ist ja auch egal. Ihr findet ihn oben auf dem Bau, im südwestlichen Teil von Klingenhügel. Sorgt nur dafür, dass Ihr Eure Hände in Euren eigenen Taschen lasst!', Text1_1='Warum wollt Ihr denn bitte schön mit Kaplak sprechen? Na, ist ja auch egal. Ihr findet ihn oben auf dem Bau, im südwestlichen Teil von Klingenhügel. Sorgt nur dafür, dass Ihr Eure Hände in Euren eigenen Taschen lasst!' WHERE id=4016;
UPDATE npc_text SET Text0_0='Da müsst Ihr mit Swart reden. Ihr findet ihn in der Kaserne im Osten von Klingenhügel.', Text0_1='Da müsst Ihr mit Swart reden. Ihr findet ihn in der Kaserne im Osten von Klingenhügel.', Text1_0='Der Kriegshäuptling leitet Veränderungen ein, die der Horde den Sieg bringen werden! Sucht Swart auf - er ist in der Kaserne im Osten von Klingenhügel.', Text1_1='Der Kriegshäuptling leitet Veränderungen ein, die der Horde den Sieg bringen werden! Sucht Swart auf - er ist in der Kaserne im Osten von Klingenhügel.' WHERE id=4017;
UPDATE npc_text SET Text0_0='Im Osten von Klingenhügel, hinter der Kaserne, findet Ihr Dhugru Blutgier.', Text0_1='Im Osten von Klingenhügel, hinter der Kaserne, findet Ihr Dhugru Blutgier.', Text1_0='Dhugru Blutgier versteckt sich hinter der Kaserne im Osten von Klingenhügel. Und wehe, ich finde heraus, dass Ihr mit der Brennenden Legion im Bund seid!', Text1_1='Dhugru Blutgier versteckt sich hinter der Kaserne im Osten von Klingenhügel. Und wehe, ich finde heraus, dass Ihr mit der Brennenden Legion im Bund seid!' WHERE id=4018;
UPDATE npc_text SET Text0_0='Redet in der Kaserne im Osten von Klingenhügel mit Tarshaw Raunarbe.', Text0_1='Redet in der Kaserne im Osten von Klingenhügel mit Tarshaw Raunarbe.', Text1_0='Redet in der Kaserne im Osten von Klingenhügel mit Tarshaw Raunarbe. Bringt der Horde Stolz und Ehre!', Text1_1='Redet in der Kaserne im Osten von Klingenhügel mit Tarshaw Raunarbe. Bringt der Horde Stolz und Ehre!' WHERE id=4019;
UPDATE npc_text SET Text0_0='Sprecht in Sen\'jin, südlich von hier, mit Miao\'zan.', Text0_1='Sprecht in Sen\'jin, südlich von hier, mit Miao\'zan.', Text1_0='Ich habe gehört, dass in Sen\'jin jemand namens Miao\'zan Tränke für die Trolle braut. Fragt doch dort einmal nach.', Text1_1='Ich habe gehört, dass in Sen\'jin jemand namens Miao\'zan Tränke für die Trolle braut. Fragt doch dort einmal nach.' WHERE id=4020;
UPDATE npc_text SET Text0_0='Ihr meint bestimmt Dwukk. Den findet Ihr an der geheizten Schmiede im Nordwesten der Stadt.', Text0_1='Ihr meint bestimmt Dwukk. Den findet Ihr an der geheizten Schmiede im Nordwesten der Stadt.', Text1_0='Wenn Ihr glaubt, dass Ihr stark genug seid, hochwertiges Metall zu Waffen und Rüstungen für die Horde zusammenzuhämmern, dann sucht Dwukk auf. Den findet Ihr an der heißen Schmiede im nordöstlichen Teil von Klingenhügel.', Text1_1='Wenn Ihr glaubt, dass Ihr stark genug seid, hochwertiges Metall zu Waffen und Rüstungen für die Horde zusammenzuhämmern, dann sucht Dwukk auf. Den findet Ihr an der heißen Schmiede im nordöstlichen Teil von Klingenhügel.' WHERE id=4021;
UPDATE npc_text SET Text0_0='Wandert die Straße entlang Richtung Norden nach Orgrimmar. Einen der Grunzer dort könnt Ihr bitten, Euch den Weg zum Kochkunstlehrer zu beschreiben.', Text0_1='Wandert die Straße entlang Richtung Norden nach Orgrimmar. Einen der Grunzer dort könnt Ihr bitten, Euch den Weg zum Kochkunstlehrer zu beschreiben.', Text1_0='Hmmmm, Essen. Der Weg ist zum Glück nicht allzu weit, aber Ihr müsst trotzdem nach Orgrimmar und einen der Grunzer dort fragen.', Text1_1='Hmmmm, Essen. Der Weg ist zum Glück nicht allzu weit, aber Ihr müsst trotzdem nach Orgrimmar und einen der Grunzer dort fragen.' WHERE id=4022;
UPDATE npc_text SET Text0_0='In Orgrimmar gibt es einen. Ein Grunzer wird Euch den Weg dort schon beschreiben können. Folgt von hier aus einfach der Straße nach Norden - Ihr könnt gar nicht fehlgehen.', Text0_1='In Orgrimmar gibt es einen. Ein Grunzer wird Euch den Weg dort schon beschreiben können. Folgt von hier aus einfach der Straße nach Norden - Ihr könnt gar nicht fehlgehen.', Text1_0='Ich wüsste nicht, dass es hier so jemanden gäbe. Am besten, Ihr geht nach Orgrimmar im Norden und sprecht dort mit einem Grunzer.', Text1_1='Ich wüsste nicht, dass es hier so jemanden gäbe. Am besten, Ihr geht nach Orgrimmar im Norden und sprecht dort mit einem Grunzer.' WHERE id=4023;
UPDATE npc_text SET Text0_0='In Klingenhügel gibt es einen Ingenieurskunstlehrer. Er heißt Mukdrak, und normalerweise ist er in der Nähe der Schmiede anzutreffen.', Text0_1='In Klingenhügel gibt es einen Ingenieurskunstlehrer. Er heißt Mukdrak, und normalerweise ist er in der Nähe der Schmiede anzutreffen.', Text1_0='Bäh, das ist ja Goblinarbeit! Aber es gibt trotzdem hier in Klingenhügel einen Orc, der Euch solche Sachen beibringen kann. Sucht drüben bei der Schmiede nach Mukdrak.', Text1_1='Bäh, das ist ja Goblinarbeit! Aber es gibt trotzdem hier in Klingenhügel einen Orc, der Euch solche Sachen beibringen kann. Sucht drüben bei der Schmiede nach Mukdrak.' WHERE id=4024;
UPDATE npc_text SET Text0_0='So einen findet Ihr in der Kaserne im Osten von Klingenhügel. Seht Euch nach Rawrk um.', Text0_1='So einen findet Ihr in der Kaserne im Osten von Klingenhügel. Seht Euch nach Rawrk um.', Text1_0='Natürlich haben wir hier in Klingenhügel einen Lehrer für Erste Hilfe, so viel, wie wir kämpfen! Fragt in der Kaserne im Osten der Stadt nach Rawrk.', Text1_1='Natürlich haben wir hier in Klingenhügel einen Lehrer für Erste Hilfe, so viel, wie wir kämpfen! Fragt in der Kaserne im Osten der Stadt nach Rawrk.' WHERE id=4025;
UPDATE npc_text SET Text0_0='Ich habe unten im Süden am Strand der Dunkelspeere mal einen Troll namens Lau\'Tiki angeln gesehen. Das ist gleich außerhalb von Sen\'jin.', Text0_1='Ich habe unten im Süden am Strand der Dunkelspeere mal einen Troll namens Lau\'Tiki angeln gesehen. Das ist gleich außerhalb von Sen\'jin.', Text1_0='Dann ist Lau\'Tiki der Richtige für Euch. Ihr findet ihn südlich von hier am Strand der Dunkelspeere, ein Stückchen hinter Sen\'jin.', Text1_1='Dann ist Lau\'Tiki der Richtige für Euch. Ihr findet ihn südlich von hier am Strand der Dunkelspeere, ein Stückchen hinter Sen\'jin.' WHERE id=4026;
UPDATE npc_text SET Text0_0='Mishiki ist die, die Ihr sucht. Sie hat eine Hütte in Sen\'jin, südlich von hier.', Text0_1='Mishiki ist die, die Ihr sucht. Sie hat eine Hütte in Sen\'jin, südlich von hier.', Text1_0='Blumen? In dieser Einöde? Wenn Euch überhaupt jemand beibringen kann, wie man hier Blumen findet, dann Mishiki. Sie lebt im Süden, in Sen\'jin.', Text1_1='Blumen? In dieser Einöde? Wenn Euch überhaupt jemand beibringen kann, wie man hier Blumen findet, dann Mishiki. Sie lebt im Süden, in Sen\'jin.' WHERE id=4027;
UPDATE npc_text SET Text0_0='Dazu werdet Ihr nach Orgrimmar müssen. Folgt einfach der Straße aus Klingenhügel hinaus nach Norden durch die Schlucht, dann kommt Ihr automatisch hin. Und wenn Ihr angekommen seid, fragt einen der Grunzer dort nach dem Weg.', Text0_1='Dazu werdet Ihr nach Orgrimmar müssen. Folgt einfach der Straße aus Klingenhügel hinaus nach Norden durch die Schlucht, dann kommt Ihr automatisch hin. Und wenn Ihr angekommen seid, fragt einen der Grunzer dort nach dem Weg.', Text1_0='Wenn Ihr lernen wollt, wie man Leder und Häute bearbeitet, dann geht nach Orgrimmar im Norden. Einer der Grunzer in Orgrimmar wird Euch sagen können, wie Ihr zum Lederverarbeitungslehrer kommt.', Text1_1='Wenn Ihr lernen wollt, wie man Leder und Häute bearbeitet, dann geht nach Orgrimmar im Norden. Einer der Grunzer in Orgrimmar wird Euch sagen können, wie Ihr zum Lederverarbeitungslehrer kommt.' WHERE id=4028;
UPDATE npc_text SET Text0_0='Ja klar. Wir haben einen Bergbaulehrer hier in der Stadt. Seht Euch nach Krunn um - er ist im nordwestlichen Teil der Stadt beim Gasthaus zu finden.', Text0_1='Ja klar. Wir haben einen Bergbaulehrer hier in der Stadt. Seht Euch nach Krunn um - er ist im nordwestlichen Teil der Stadt beim Gasthaus zu finden.', Text1_0='In Durotar läuft das Bergbaugeschäft ziemlich gut, hab ich mir sagen lassen. So gut sogar, dass wir hier in Klingenhügel doch tatsächlich unseren eigenen Bergbaulehrer haben! Krunn heißt er, und Ihr findet ihn an der geheizten Schmiede im Nordwesten der Stadt.', Text1_1='In Durotar läuft das Bergbaugeschäft ziemlich gut, hab ich mir sagen lassen. So gut sogar, dass wir hier in Klingenhügel doch tatsächlich unseren eigenen Bergbaulehrer haben! Krunn heißt er, und Ihr findet ihn an der geheizten Schmiede im Nordwesten der Stadt.' WHERE id=4029;
UPDATE npc_text SET Text0_0='In Orgrimmar gibt es einen Kürschnerlehrer. Um dorthin zu kommen, müsst Ihr der Straße aus Klingenhügel hinaus nach Norden folgen. Wenn Ihr in Orgrimmar angekommen seid, fragt einen Grunzer dort nach dem weiteren Weg.', Text0_1='In Orgrimmar gibt es einen Kürschnerlehrer. Um dorthin zu kommen, müsst Ihr der Straße aus Klingenhügel hinaus nach Norden folgen. Wenn Ihr in Orgrimmar angekommen seid, fragt einen Grunzer dort nach dem weiteren Weg.', Text1_0='In Orgrimmar gibt es einen. Geht nach Norden und sprecht mit einem ihrer Grunzer.', Text1_1='In Orgrimmar gibt es einen. Geht nach Norden und sprecht mit einem ihrer Grunzer.' WHERE id=4030;
UPDATE npc_text SET Text0_0='Dann müsst Ihr nach Orgrimmar. Die Straße, die Klingenhügel Richtung Norden verlässt, führt genau hin. Redet einfach mit einem der Grunzer dort - die können Euch sagen, wo Ihr hin müsst.', Text0_1='Dann müsst Ihr nach Orgrimmar. Die Straße, die Klingenhügel Richtung Norden verlässt, führt genau hin. Redet einfach mit einem der Grunzer dort - die können Euch sagen, wo Ihr hin müsst.', Text1_0='Das Nähen könnt Ihr in Orgrimmar lernen. Das liegt im Norden. Vielleicht kann einer der Grunzer in Orgrimmar Euch weiterhelfen.', Text1_1='Das Nähen könnt Ihr in Orgrimmar lernen. Das liegt im Norden. Vielleicht kann einer der Grunzer in Orgrimmar Euch weiterhelfen.' WHERE id=4031;
UPDATE npc_text SET Text0_0='Hä? In Orgrimmar gibt\'s \'ne Bank. Folgt einfach der Straße nach Norden, dann kommt Ihr hin. Und wenn Ihr dort seid, redet mit einem der Grunzer da.', Text0_1='Hä? In Orgrimmar gibt\'s \'ne Bank. Folgt einfach der Straße nach Norden, dann kommt Ihr hin. Und wenn Ihr dort seid, redet mit einem der Grunzer da.', Text1_0='Stimmt, ich muss auch ein paar Sachen loswerden. Folgt einfach der Straße nach Norden bis Orgrimmar und fragt dann einen der Grunzer dort.', Text1_1='Stimmt, ich muss auch ein paar Sachen loswerden. Folgt einfach der Straße nach Norden bis Orgrimmar und fragt dann einen der Grunzer dort.' WHERE id=4032;
UPDATE npc_text SET Text0_0='Den Windreitermeister findet Ihr in Orgrimmar. Folgt der Straße nordwärts, dann kommt Ihr hin. Und wenn Ihr dort seid, lasst Euch von einem der Grunzer da den weiteren Weg beschreiben.', Text0_1='Den Windreitermeister findet Ihr in Orgrimmar. Folgt der Straße nordwärts, dann kommt Ihr hin. Und wenn Ihr dort seid, lasst Euch von einem der Grunzer da den weiteren Weg beschreiben.', Text1_0='Seht Ihr denn nicht, dass ich beschäftigt bin? Folgt der Straße nach Norden bis Orgrimmar und fragt einen der Grunzer dort. Ihr könnt es nicht verfehlen.', Text1_1='Seht Ihr denn nicht, dass ich beschäftigt bin? Folgt der Straße nach Norden bis Orgrimmar und fragt einen der Grunzer dort. Ihr könnt es nicht verfehlen.' WHERE id=4033;
UPDATE npc_text SET Text0_0='Ich könnte selbst was zu trinken und etwas Ruhe vertragen. Ihr findet es im Nordwesten von Klingenhügel. Grüßt Gastwirt Grosk von mir.', Text0_1='Ich könnte selbst was zu trinken und etwas Ruhe vertragen. Ihr findet es im Nordwesten von Klingenhügel. Grüßt Gastwirt Grosk von mir.', Text1_0='Das ist das andere große Gebäude in Klingenhügel, also das, das nicht die Kaserne ist. Wenn Ihr dort seid, dann sprecht mit Gastwirt Grosk. Alles klar?', Text1_1='Das ist das andere große Gebäude in Klingenhügel, also das, das nicht die Kaserne ist. Wenn Ihr dort seid, dann sprecht mit Gastwirt Grosk. Alles klar?' WHERE id=4034;
UPDATE npc_text SET Text0_0='Welchen Lehrer sucht Ihr?', Text0_1='Welchen Lehrer sucht Ihr?' WHERE id=4035;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=4036;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=4037;
UPDATE npc_text SET Text0_1='Jetzt, wo die Kessel sabotiert sind, können wir das Gegenmittel an die Umgebung abgeben. Dann wollen wir doch mal sehen, wie es der Geißel schmeckt, wenn es diesmal ihren Mitgliedern passiert!$B$BSprecht mit Alchemist Arbington; er kann Euch nähere Informationen zu den Reagenzien geben, die wir für die Kessel verwenden müssen. Ich selbst werde alle Berichte darüber sammeln, wie wir insgesamt damit vorankommen, die Seuche gegen die Geißel zu richten!' WHERE id=4039;
UPDATE npc_text SET Text0_1='Jetzt, wo die Kessel sabotiert sind, können wir das Gegenmittel an die Umgebung abgeben und es der Geißel mit gleicher Münze heimzahlen. Wollen wir doch mal sehen, wie es ihr schmeckt!$B$BSprecht mit Apotheker Zitters; er kann Euch nähere Informationen zu den Reagenzien geben, die wir für die Kessel verwenden müssen. Ich selbst werde alle Berichte darüber sammeln, wie wir insgesamt damit vorankommen, die Seuche gegen die Geißel zu richten!' WHERE id=4040;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $Gjunger Herr:junge Herrin;. Ich bin Eva Sarkhoff, und das ist mein Mann Lucien. Es ist mir ein Vergnügen, Eure Bekanntschaft zu machen.' WHERE id=4041;
UPDATE npc_text SET Text0_1='Aber selbstverständlich dürft Ihr das, $Gwerter Herr:junge Dame;. Einst waren wir Bedienstete im Hause Barov - das heutzutage als \'die Scholomance\' bekannt ist.$B$BIch hatte alle Dienstmädchen unter mir und Lucien war Meister Barovs Butler.$B$BWir hatten jahrzehntelang für die Barovs gearbeitet, solange bis...$B$B<Eva bricht in Tränen aus.>' WHERE id=4042;
UPDATE npc_text SET Text0_1='Ach, es ist einfach schrecklich, $Gjunger Herr:Fräulein;. Einst war das Haus der Barovs voller Leben... voller Pracht. Von all den zahlreichen Residenzen, die den Barovs im ganzen Land gehörten, war das Herrenhaus ihr Hauptwohnsitz.$B$BAber im Laufe der Zeit fiel Lucien und mir auf, dass die Barovs immer depressiver wurden. Niedergeschlagen. Der Herr und die Herrin wurden oft von Wut und Verfolgungswahn heimgesucht. Bei Nacht konnten wir hören, wie sich die Barovs in ihren Gemächern stritten. Nach allem, was wir mit anhörten, schien es, als habe ihre Gier gewissermaßen \'den Damm bersten lassen\'.' WHERE id=4043;
UPDATE npc_text SET Text0_1='Wir nehmen an, dass die Barovs, um ihr Vermögen über ihre eigentliche Lebensspanne hinaus zu bewahren, einen Handel mit einem mächtigen Magier eingingen, einem Menschen namens Kel\'Thuzad.$B$B<Eva erschaudert.>$B$BAus diesem Handel entsprang die Schule der Totenbeschwörung: ein Ort, an dem der Kult der Verdammten entstand und der später zu ihrem Mittelpunkt wurde.$B$BMit jeder Woche, die verging, wirkte das Haus der Barovs heruntergekommener. Es wirkte... verzerrt.$B$BDunkle Wesen begannen sich im Haus einzunisten. Es wurde unmöglich, das Haus instand zu halten... und es war ekelhaft.' WHERE id=4044;
UPDATE npc_text SET Text0_1='Wohin denn? Wir kannten doch nichts anderes - wir hatten nie etwas anderes gekannt und würden nie etwas anderes kennen.$B$BSchließlich verloren wir den Kontakt zu den Barovs, und letztendlich wurden wir vom Rest des Haushalts getrennt. Ich weiß nicht, was mit den anderen Dienstboten passierte - nur, dass Schreie zu hören waren. Gequälte Schreie.$B$BWir wussten, dass wir nirgendwo anders hingehen konnten, und so mussten Lucien und ich uns in unserer Unterkunft verstecken. Während des Tages war es in der Schule immer relativ ruhig - das waren die Momente, in denen sich Lucien nach draußen schlich und ein bisschen was zu essen und zu trinken suchte.' WHERE id=4045;
UPDATE npc_text SET Text0_1='So ging das ungefähr ein halbes Jahr lang. Wir mussten mit ansehen, wie das Haus eine schreckliche Veränderung durchmachte. Scheußliche Monster streiften völlig frei durch das Herrenhaus. In jeder Ecke, jedem Winkel waren dunkle Kultisten mit den unterschiedlichsten Utensilien für Rituale und Opfer zugange. Wir wussten, dass wir uns nicht viel länger würden verstecken können.$B$BUnd wie erwartet, wurden wir entdeckt und gefangen genommen. Sie zogen uns all unsere Kleider aus und legten uns in einen Kreis aus Blut. Dort saßen wir tagelang, nackt und starr vor Schrecken.' WHERE id=4046;
UPDATE npc_text SET Text0_1='Ungefesselt? In dieser Welt gibt es Dinge, die einen weit mehr festhalten als Gitterstäbe und Fesseln, $Gjunger Herr:junge Herrin;. Die Untoten umringten uns, quälten uns unentwegt mit entsetzlichen und verderbten Handlungen.$B$BUnd schließlich kam er. Er stellte sich uns als Doktor Theolen Krastinov vor, doch für uns war er allzu bald nur noch \'Der Schlächter\'.' WHERE id=4047;
UPDATE npc_text SET Text0_1='Jetzt erkannten wir auch, was es mit den Schreien auf sich gehabt hatte. Der Schlächter fügte uns Schmerzen zu, von denen wir zuvor nicht im Traum geglaubt hätten, dass es so etwas geben könnte. Er benutzte uns für zahllose Experimente, in denen er eine Seuche zu erschaffen versuchte.$B$BDie Tage wurden zu Wochen. Wir wären schon am ersten Tag gestorben, wenn dieses unbarmherzige Monster uns nicht auf magische Weise am Leben erhalten hätte.' WHERE id=4048;
UPDATE npc_text SET Text0_1='Der Schlächter sprach ständig vom \'Blut Unschuldiger\' und von seinem dunklen Meister, \'Kirtonos\'. Davon, wie er seinen Meister beschwichtigen müsse.$B$BSchließlich hatte die Bestie ihre Experimente beendet. Alles Leben war uns entzogen worden. Unser Geist zerbrach. Der Tod streckte die Arme nach uns aus, und wir waren nur zu bereit, uns seiner süßen Umarmung hinzugeben. Doch in seiner unendlichen Grausamkeit riss der Schlächter uns von der Schwelle des Todes fort.$B$BWir sollten am Leben erhalten und seinen ausgehungerten Ghulen zum Fraß vorgeworfen werden. Lachend beobachtete er, wie die Scheusale unser Fleisch verschlangen.' WHERE id=4049;
UPDATE npc_text SET Text0_1='Wir fühlen nichts. Unsere Seelen sind an diese Zwischenwelt gebunden. Wir können sie erst verlassen, wenn unsere Überreste gefunden wurden und unser Geist Ruhe findet.' WHERE id=4050;
UPDATE npc_text SET Text0_0='Ah, wenn Ihr einen solchen Ort besuchen wollt, dann müsst Ihr zu den imposanten Höhen von Donnerfels. Folgt dem Pfad aus dem Dorf der Bluthufe heraus nach Norden. Am Fuß der Klippen gibt es einen Aufzug, der Euch nach oben bringt. Wenn Ihr oben angekommen seid, fragt einen der Behüter von Donnerfels dort nach dem Weg.', Text0_1='Ah, wenn Ihr einen solchen Ort besuchen wollt, dann müsst Ihr zu den imposanten Höhen von Donnerfels. Folgt dem Pfad aus dem Dorf der Bluthufe heraus nach Norden. Am Fuß der Klippen gibt es einen Aufzug, der Euch nach oben bringt. Wenn Ihr oben angekommen seid, fragt einen der Behüter von Donnerfels dort nach dem Weg.', Text1_0='Einen solchen Ort gibt es in unserem Dorf nicht. Aber auf den erhabenen Höhen von Donnerfels schon. Geht auf der Straße aus Bluthuf hinaus und nach Norden, bis Ihr zum Fuß des Steilhanges kommt. Nehmt dann den Aufzug nach oben und sprecht dort mit einem Behüter von Donnerfels. Mögen Eure Vorfahren über Euch wachen.', Text1_1='Einen solchen Ort gibt es in unserem Dorf nicht. Aber auf den erhabenen Höhen von Donnerfels schon. Geht auf der Straße aus Bluthuf hinaus und nach Norden, bis Ihr zum Fuß des Steilhanges kommt. Nehmt dann den Aufzug nach oben und sprecht dort mit einem Behüter von Donnerfels. Mögen Eure Vorfahren über Euch wachen.' WHERE id=4051;
UPDATE npc_text SET Text0_0='In der großen Stadt Donnerfels im Norden gibt es einen Windreitermeister. Nehmt am Fuß der Klippen von Donnerfels den Aufzug nach oben und fragt dort einen der Behüter von Donnerfels nach dem Weg.', Text0_1='In der großen Stadt Donnerfels im Norden gibt es einen Windreitermeister. Nehmt am Fuß der Klippen von Donnerfels den Aufzug nach oben und fragt dort einen der Behüter von Donnerfels nach dem Weg.', Text1_0='Nichts kommt dem Schweben durch die Luft gleich, wenn man seinen Geist befreien möchte. In unserem großen Stammeszentrum Donnerfels gibt es einen Ort, wo dies möglich ist. Die Straße, die das Dorf der Bluthufe Richtung Norden verlässt, wird Euch hinführen. Wenn Ihr den Aufzug nach oben genommen habt, fragt einen der Behüter von Donnerfels nach dem weiteren Weg.', Text1_1='Nichts kommt dem Schweben durch die Luft gleich, wenn man seinen Geist befreien möchte. In unserem großen Stammeszentrum Donnerfels gibt es einen Ort, wo dies möglich ist. Die Straße, die das Dorf der Bluthufe Richtung Norden verlässt, wird Euch hinführen. Wenn Ihr den Aufzug nach oben genommen habt, fragt einen der Behüter von Donnerfels nach dem weiteren Weg.' WHERE id=4052;
UPDATE npc_text SET Text0_0='Gastwirt Kauth kann sich um Eure Bedürfnisse kümmern. Ihr findet ihn im Westen des Dorfes in einem der Langhäuser.', Text0_1='Gastwirt Kauth kann sich um Eure Bedürfnisse kümmern. Ihr findet ihn im Westen des Dorfes in einem der Langhäuser.', Text1_0='Jedes Ding auf dieser Welt hat seinen Ort und seine Zeit, auch die Entspannung. Im westlichen Teil des Dorfs der Bluthufe führt Gastwirt Kauth ein Langhaus für alle, die der Ruhe bedürfen. Dort werdet Ihr ihn finden.', Text1_1='Jedes Ding auf dieser Welt hat seinen Ort und seine Zeit, auch die Entspannung. Im westlichen Teil des Dorfs der Bluthufe führt Gastwirt Kauth ein Langhaus für alle, die der Ruhe bedürfen. Dort werdet Ihr ihn finden.' WHERE id=4053;
UPDATE npc_text SET Text0_0='Dann sucht Ihr den Rat von Gennia Runentotem. Ihr findet sie in dem großen Zelt im Osten des Dorfes.', Text0_1='Dann sucht Ihr den Rat von Gennia Runentotem. Ihr findet sie in dem großen Zelt im Osten des Dorfes.', Text1_0='Der Weg der Druiden ähnelt dem der Schamanen. Gennia Runentotem ist von solchem Wissen durchdrungen. Sucht sie auf - Ihr findet sie in dem großen Zelt im östlichen Teil des Dorfs der Bluthufe.', Text1_1='Der Weg der Druiden ähnelt dem der Schamanen. Gennia Runentotem ist von solchem Wissen durchdrungen. Sucht sie auf - Ihr findet sie in dem großen Zelt im östlichen Teil des Dorfs der Bluthufe.' WHERE id=4054;
UPDATE npc_text SET Text0_0='Yaw Scharfmähne erwartet Euch am nördlichen Rand des Dorfes. Er kann Euch die Kunst des Jagens lehren.', Text0_1='Yaw Scharfmähne erwartet Euch am nördlichen Rand des Dorfes. Er kann Euch die Kunst des Jagens lehren.', Text1_0='Es ist eine Angelegenheit großen Stolzes, in den Augen der Erdenmutter eins mit der Natur zu werden. Wenn Ihr wirklich die Kunst des Jagens erlernen wollt, dann müsst Ihr Yaw Scharfmähne im nördlichen Teil des Dorfs der Bluthufe aufsuchen.', Text1_1='Es ist eine Angelegenheit großen Stolzes, in den Augen der Erdenmutter eins mit der Natur zu werden. Wenn Ihr wirklich die Kunst des Jagens erlernen wollt, dann müsst Ihr Yaw Scharfmähne im nördlichen Teil des Dorfs der Bluthufe aufsuchen.' WHERE id=4055;
UPDATE npc_text SET Text0_0='Östlich im Dorf der Bluthufe steht ein großes Zelt. Dort wartet Narm Himmelsjäger auf Euch.', Text0_1='Östlich im Dorf der Bluthufe steht ein großes Zelt. Dort wartet Narm Himmelsjäger auf Euch.', Text1_0='Im Dorf lebt ein Weiser namens Narm Himmelsjäger. Er kann Eure Ausbildung zum Schamanen weiterführen.', Text1_1='Im Dorf lebt ein Weiser namens Narm Himmelsjäger. Er kann Eure Ausbildung zum Schamanen weiterführen.' WHERE id=4056;
UPDATE npc_text SET Text0_0='Krang Steinhuf steht in seinem Ausbildungskreis im Südosten des Dorfes bereit.', Text0_1='Krang Steinhuf steht in seinem Ausbildungskreis im Südosten des Dorfes bereit.', Text1_0='Wenn Ihr den Weg des Kriegers einschlagen wollt, dann müsst Ihr Krang Steinhuf aufsuchen. Ihr findet ihn im Südosten des Dorfes in seinem Ausbildungskreis, wo er meist seine Schüler unterweist.', Text1_1='Wenn Ihr den Weg des Kriegers einschlagen wollt, dann müsst Ihr Krang Steinhuf aufsuchen. Ihr findet ihn im Südosten des Dorfes in seinem Ausbildungskreis, wo er meist seine Schüler unterweist.' WHERE id=4057;
UPDATE npc_text SET Text0_0='Zwar haben wir hier im Dorf der Bluthufe keinen Alchemielehrer, aber in Donnerfels im Norden gibt es einen. Folgt der Straße dorthin und fragt dann einen der Behüter von Donnerfels nach dem Weg, wenn Ihr oben seid.', Text0_1='Zwar haben wir hier im Dorf der Bluthufe keinen Alchemielehrer, aber in Donnerfels im Norden gibt es einen. Folgt der Straße dorthin und fragt dann einen der Behüter von Donnerfels nach dem Weg, wenn Ihr oben seid.', Text1_0='Die Früchte der Erdenmutter in der Herstellung von Tränken zu verwenden, die uns in unserem ewigen Kampf helfen können, ist ein ehrenvolles Unterfangen. Wenn Ihr dies lernen wollt, dann müsst Ihr nach Norden reisen, in unsere große Hauptstadt, nach Donnerfels. Wenn Ihr oben angekommen seid, solltet Ihr einen der Behüter von Donnerfels nach dem genauen Weg fragen.', Text1_1='Die Früchte der Erdenmutter in der Herstellung von Tränken zu verwenden, die uns in unserem ewigen Kampf helfen können, ist ein ehrenvolles Unterfangen. Wenn Ihr dies lernen wollt, dann müsst Ihr nach Norden reisen, in unsere große Hauptstadt, nach Donnerfels. Wenn Ihr oben angekommen seid, solltet Ihr einen der Behüter von Donnerfels nach dem genauen Weg fragen.' WHERE id=4058;
UPDATE npc_text SET Text0_0='In unserer großen Hauptstadt Donnerfels im Norden gibt es einen Schmiedekunstlehrer. Wenn Euer Sinn nach solchem Wissen steht, dann begebt Euch dorthin und fragt einen der Behüter von Donnerfels nach dem Weg.', Text0_1='In unserer großen Hauptstadt Donnerfels im Norden gibt es einen Schmiedekunstlehrer. Wenn Euer Sinn nach solchem Wissen steht, dann begebt Euch dorthin und fragt einen der Behüter von Donnerfels nach dem Weg.', Text1_0='Ihr wünscht, die Knochen der Erdenmutter zu Werkzeugen zu formen, die der Horde helfen können. Gut, gut. Folgt der Straße aus dem Dorf der Bluthufe hinaus und nach Norden, bis Ihr zu dem Aufzug kommt, der auf das Plateau von Donnerfels führt. Dort wird Euch ein Behüter von Donnerfels weiterhelfen.', Text1_1='Ihr wünscht, die Knochen der Erdenmutter zu Werkzeugen zu formen, die der Horde helfen können. Gut, gut. Folgt der Straße aus dem Dorf der Bluthufe hinaus und nach Norden, bis Ihr zu dem Aufzug kommt, der auf das Plateau von Donnerfels führt. Dort wird Euch ein Behüter von Donnerfels weiterhelfen.' WHERE id=4059;
UPDATE npc_text SET Text0_0='In dem großen Zelt im Nordwesten des Dorfes findet Ihr Pyall Leisetreter. Er kann Euch beibringen, was Ihr zu lernen wünscht.', Text0_1='In dem großen Zelt im Nordwesten des Dorfes findet Ihr Pyall Leisetreter. Er kann Euch beibringen, was Ihr zu lernen wünscht.', Text1_0='Mir läuft das Wasser im Mund zusammen, wenn ich nur daran denke. Pyall Leisetreter ist der beste Koch im ganzen Dorf der Bluthufe. Ihr findet ihn in dem großen Zelt im Nordwesten des Dorfes.', Text1_1='Mir läuft das Wasser im Mund zusammen, wenn ich nur daran denke. Pyall Leisetreter ist der beste Koch im ganzen Dorf der Bluthufe. Ihr findet ihn in dem großen Zelt im Nordwesten des Dorfes.' WHERE id=4060;
UPDATE npc_text SET Text0_0='Dann führt Euch Euer Weg fort von hier und nach Donnerfels im Norden, $Gmein Freund:meine Liebe;. Wenn Ihr dort seid, wendet Euch an einen der Behüter von Donnerfels. Die können Euch genau sagen, wo Ihr finden könnt, wen Ihr sucht.', Text0_1='Dann führt Euch Euer Weg fort von hier und nach Donnerfels im Norden, $Gmein Freund:meine Liebe;. Wenn Ihr dort seid, wendet Euch an einen der Behüter von Donnerfels. Die können Euch genau sagen, wo Ihr finden könnt, wen Ihr sucht.', Text1_0='Solch geheimnisvolle Weisheit findet man nur im großen Donnerfels. Folgt der Straße aus dem Dorf der Bluthufe hinaus nach Norden, bis Ihr am Fuße des Steilhanges einen Aufzug seht. Nehmt diesen Aufzug nach oben und fragt dort einen der Behüter von Donnerfels nach dem weiteren Weg.', Text1_1='Solch geheimnisvolle Weisheit findet man nur im großen Donnerfels. Folgt der Straße aus dem Dorf der Bluthufe hinaus nach Norden, bis Ihr am Fuße des Steilhanges einen Aufzug seht. Nehmt diesen Aufzug nach oben und fragt dort einen der Behüter von Donnerfels nach dem weiteren Weg.' WHERE id=4061;
UPDATE npc_text SET Text0_0='Vira Junghuf praktiziert in dem Langhaus im Westen des Dorfes.', Text0_1='Vira Junghuf praktiziert in dem Langhaus im Westen des Dorfes.', Text1_0='Verbände, Gegengifte und solche Dinge sind ein unvermeidlicher Bestandteil des Lebens auf den Ebenen. Vira Junghuf kann Euch dieses Wissen vermitteln. Ihr findet sie in dem Langhaus im westlichen Teil des Dorfs der Bluthufe.', Text1_1='Verbände, Gegengifte und solche Dinge sind ein unvermeidlicher Bestandteil des Lebens auf den Ebenen. Vira Junghuf kann Euch dieses Wissen vermitteln. Ihr findet sie in dem Langhaus im westlichen Teil des Dorfs der Bluthufe.' WHERE id=4062;
UPDATE npc_text SET Text0_0='Dann ist Uthan Stillwasser der Richtige für Euch. Ihr findet ihn westlich vom Dorf, wo er im Steinbullensee angelt.', Text0_1='Dann ist Uthan Stillwasser der Richtige für Euch. Ihr findet ihn westlich vom Dorf, wo er im Steinbullensee angelt.', Text1_0='Wenn Ihr einem Bullen einen Fisch gebt, dann hat er einen Tag lang zu essen. Wenn Ihr einen Bullen das Angeln lehrt, dann hat er sein Lebtag lang zu essen. Uthan Stillwasser kann Euch das Angeln beibringen. Ihn und sein Wissen findet Ihr westlich vom Dorf der Bluthufe an den Ufern des Steinbullensees.', Text1_1='Wenn Ihr einem Bullen einen Fisch gebt, dann hat er einen Tag lang zu essen. Wenn Ihr einen Bullen das Angeln lehrt, dann hat er sein Lebtag lang zu essen. Uthan Stillwasser kann Euch das Angeln beibringen. Ihn und sein Wissen findet Ihr westlich vom Dorf der Bluthufe an den Ufern des Steinbullensees.' WHERE id=4063;
UPDATE npc_text SET Text0_0='Wenn Ihr die Kunst der Kräuterkunde erlernen wollt, dann müsst Ihr Euch nach Donnerfels begeben. Folgt der Straße aus dem Dorf der Bluthufe hinaus nach Norden und nehmt den Aufzug hoch auf das Plateau. Dort könnt Ihr einen Behüter von Donnerfels nach dem weiteren Weg fragen.', Text0_1='Wenn Ihr die Kunst der Kräuterkunde erlernen wollt, dann müsst Ihr Euch nach Donnerfels begeben. Folgt der Straße aus dem Dorf der Bluthufe hinaus nach Norden und nehmt den Aufzug hoch auf das Plateau. Dort könnt Ihr einen Behüter von Donnerfels nach dem weiteren Weg fragen.', Text1_0='In der großen Stadt Donnerfels weit im Norden gibt es solche, die bereit sind, Euch in der Kräuterkunde auszubilden. Folgt der Straße aus dem Dorf hinaus nach Norden und nehmt am Fuße des Steilhanges den Aufzug hinauf auf das Plateau. Dort gibt es viele Behüter von Donnerfels, die Euch weiterhelfen können.', Text1_1='In der großen Stadt Donnerfels weit im Norden gibt es solche, die bereit sind, Euch in der Kräuterkunde auszubilden. Folgt der Straße aus dem Dorf hinaus nach Norden und nehmt am Fuße des Steilhanges den Aufzug hinauf auf das Plateau. Dort gibt es viele Behüter von Donnerfels, die Euch weiterhelfen können.' WHERE id=4064;
UPDATE npc_text SET Text0_0='In dem großen Zelt im Nordwesten des Dorfes lebt Chaw Starkfell. Er wird Euch alles beibringen, was Ihr im Moment über die Kunst der Lederverarbeitung wissen müsst.', Text0_1='In dem großen Zelt im Nordwesten des Dorfes lebt Chaw Starkfell. Er wird Euch alles beibringen, was Ihr im Moment über die Kunst der Lederverarbeitung wissen müsst.', Text1_0='Wenn wir Tiere entweder ihres Fleisches wegen oder zu unserem Schutze erlegen, dann ehren wir die Erdenmutter und erweisen den Tieren unsere Achtung, wenn wir ihre Bälge verwenden. Craw Starkfell kann Euch solche Dinge lehren. Ihr findet ihn in dem großen Zelt im nordwestlichen Teil des Dorfs der Bluthufe.', Text1_1='Wenn wir Tiere entweder ihres Fleisches wegen oder zu unserem Schutze erlegen, dann ehren wir die Erdenmutter und erweisen den Tieren unsere Achtung, wenn wir ihre Bälge verwenden. Craw Starkfell kann Euch solche Dinge lehren. Ihr findet ihn in dem großen Zelt im nordwestlichen Teil des Dorfs der Bluthufe.' WHERE id=4065;
UPDATE npc_text SET Text0_0='Hmmm, ich glaube, Ihr müsst nach Donnerfels, wenn Ihr das lernen wollt. Verlasst das Dorf der Bluthufe auf der Straße gen Norden. So kommt Ihr zum Fuß der Klippen von Donnerfels. Nehmt den Aufzug nach oben. Dort sollte Euch jeder Behüter von Donnerfels weiterhelfen können.', Text0_1='Hmmm, ich glaube, Ihr müsst nach Donnerfels, wenn Ihr das lernen wollt. Verlasst das Dorf der Bluthufe auf der Straße gen Norden. So kommt Ihr zum Fuß der Klippen von Donnerfels. Nehmt den Aufzug nach oben. Dort sollte Euch jeder Behüter von Donnerfels weiterhelfen können.', Text1_0='Ich habe gehört, dass es in Donnerfels im Norden einen Bergbaulehrer gibt. Folgt der Straße aus dem Dorf hinaus nach Norden und nehmt am Fuße des Steilhanges den Aufzug hinauf auf das Plateau. Jeder Behüter von Donnerfels sollte Euch von dort aus weiterhelfen können.', Text1_1='Ich habe gehört, dass es in Donnerfels im Norden einen Bergbaulehrer gibt. Folgt der Straße aus dem Dorf hinaus nach Norden und nehmt am Fuße des Steilhanges den Aufzug hinauf auf das Plateau. Jeder Behüter von Donnerfels sollte Euch von dort aus weiterhelfen können.' WHERE id=4066;
UPDATE npc_text SET Text0_0='Ihr könnt Yonn Pelzscherer in dem großen Zelt im Nordwesten des Dorfs der Bluthufe finden.', Text0_1='Ihr könnt Yonn Pelzscherer in dem großen Zelt im Nordwesten des Dorfs der Bluthufe finden.', Text1_0='Ah ja, Kürschnerei. Es gibt niemanden, der mit einem Kürschnermesser besser umgehen kann als Yonn Pelzscherer. Ihr findet ihn in dem großen Zelt im Nordwesten des Dorfes.', Text1_1='Ah ja, Kürschnerei. Es gibt niemanden, der mit einem Kürschnermesser besser umgehen kann als Yonn Pelzscherer. Ihr findet ihn in dem großen Zelt im Nordwesten des Dorfes.' WHERE id=4067;
UPDATE npc_text SET Text0_0='Leider haben wir hier im Dorf der Bluthufe niemanden, der die Schneiderei lehrt, aber in Donnerfels könnt Ihr einen Lehrer finden. Folgt der Straße aus dem Dorf hinaus und nach Norden, bis Ihr zu einigen Aufzügen kommt. Nehmt einen der Aufzüge nach oben und fragt dort einen der Behüter von Donnerfels nach dem Weg.', Text0_1='Leider haben wir hier im Dorf der Bluthufe niemanden, der die Schneiderei lehrt, aber in Donnerfels könnt Ihr einen Lehrer finden. Folgt der Straße aus dem Dorf hinaus und nach Norden, bis Ihr zu einigen Aufzügen kommt. Nehmt einen der Aufzüge nach oben und fragt dort einen der Behüter von Donnerfels nach dem Weg.', Text1_0='Schneiderei, sagt Ihr? Hmmm. Ach ja, natürlich! Dazu werdet Ihr nach Donnerfels im Norden gehen müssen. Folgt dem Pfad, der aus dem Dorf herausführt, um dorthin zu gelangen. Wenn Ihr am Fuß der Klippen angekommen seid, nehmt den Aufzug nach oben und sprecht mit einem der Behüter von Donnerfels.', Text1_1='Schneiderei, sagt Ihr? Hmmm. Ach ja, natürlich! Dazu werdet Ihr nach Donnerfels im Norden gehen müssen. Folgt dem Pfad, der aus dem Dorf herausführt, um dorthin zu gelangen. Wenn Ihr am Fuß der Klippen angekommen seid, nehmt den Aufzug nach oben und sprecht mit einem der Behüter von Donnerfels.' WHERE id=4068;
UPDATE npc_text SET Text0_0='Welchen Lehrer sucht Ihr?', Text0_1='Welchen Lehrer sucht Ihr?' WHERE id=4069;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=4070;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=4072;
UPDATE npc_text SET Text0_0='In Unterstadt gibt es einen Fledermausführer. Folgt der Straße aus Brill hinaus nach Süden, und geht dann an der Einmündung nach Westen. Da seht Ihr dann die Ruinen von Lordaeron zu Eurer Linken. Betretet die Ruinen und fahrt hinunter nach Unterstadt. Dort könnt Ihr einen Wächter von Unterstadt nach dem weiteren Weg fragen.', Text0_1='In Unterstadt gibt es einen Fledermausführer. Folgt der Straße aus Brill hinaus nach Süden, und geht dann an der Einmündung nach Westen. Da seht Ihr dann die Ruinen von Lordaeron zu Eurer Linken. Betretet die Ruinen und fahrt hinunter nach Unterstadt. Dort könnt Ihr einen Wächter von Unterstadt nach dem weiteren Weg fragen.', Text1_0='Ihr wollt wegfliegen? Dazu werdet Ihr nach Unterstadt müssen. Verlasst die Stadt auf der Straße nach Süden und haltet Euch dann Richtung Südwesten, bis Ihr bei den Ruinen von Lordaeron angekommen seid. Im hinteren Teil der Ruinen könnt Ihr mit einem Aufzug hinunter nach Unterstadt fahren und dort einen der Wächter von Unterstadt nach dem weiteren Weg fragen.', Text1_1='Ihr wollt wegfliegen? Dazu werdet Ihr nach Unterstadt müssen. Verlasst die Stadt auf der Straße nach Süden und haltet Euch dann Richtung Südwesten, bis Ihr bei den Ruinen von Lordaeron angekommen seid. Im hinteren Teil der Ruinen könnt Ihr mit einem Aufzug hinunter nach Unterstadt fahren und dort einen der Wächter von Unterstadt nach dem weiteren Weg fragen.' WHERE id=4075;
UPDATE npc_text SET Text0_0='Rupert Boch heißt er. Ich habe gehört, er hält sich in einem der Zimmer der Taverne Zur Galgenschlinge am südöstlichen Ende der Stadt auf.', Text0_1='Rupert Boch heißt er. Ich habe gehört, er hält sich in einem der Zimmer der Taverne Zur Galgenschlinge am südöstlichen Ende der Stadt auf.', Text1_0='Dann solltet Ihr mit Rupert Boch sprechen. Er und seine Leute haben ein Zimmer im ersten Stock der Taverne Zur Galgenschlinge am südöstlichen Ende von Brill.', Text1_1='Dann solltet Ihr mit Rupert Boch sprechen. Er und seine Leute haben ein Zimmer im ersten Stock der Taverne Zur Galgenschlinge am südöstlichen Ende von Brill.' WHERE id=4080;
UPDATE npc_text SET Text0_0='Seht Euch in der Gaststube der Taverne Zur Galgenschlinge nach Austil de Mon um. Die Taverne findet Ihr am südöstlichen Rand von Brill.', Text0_1='Seht Euch in der Gaststube der Taverne Zur Galgenschlinge nach Austil de Mon um. Die Taverne findet Ihr am südöstlichen Rand von Brill.', Text1_0='Tja, das wäre dann wohl Austil de Mon. Der treibt sich immer in einer Taverne am südöstlichen Ende der Stadt herum. Sie heißt \'Taverne Zur Galgenschlinge\', Ihr könnt sie gar nicht verfehlen.', Text1_1='Tja, das wäre dann wohl Austil de Mon. Der treibt sich immer in einer Taverne am südöstlichen Ende der Stadt herum. Sie heißt \'Taverne Zur Galgenschlinge\', Ihr könnt sie gar nicht verfehlen.' WHERE id=4081;
UPDATE npc_text SET Text0_0='Unsere Alchemistin heißt Carolai Anise. Ihr findet sie in einem Haus am nordwestlichen Rand von Brill.', Text0_1='Unsere Alchemistin heißt Carolai Anise. Ihr findet sie in einem Haus am nordwestlichen Rand von Brill.', Text1_0='Carolai Anise ist bestimmt wieder mal dabei, ihr Haus am nordwestlichen Ende der Stadt in die Luft zu jagen. Warum seht Ihr nicht mal nach, ob Ihr ihr helfen könnt?', Text1_1='Carolai Anise ist bestimmt wieder mal dabei, ihr Haus am nordwestlichen Ende der Stadt in die Luft zu jagen. Warum seht Ihr nicht mal nach, ob Ihr ihr helfen könnt?' WHERE id=4082;
UPDATE npc_text SET Text0_0='Ihr findet Vance Niederglimm in einem Zimmer im ersten Stock der Taverne Zur Galgenschlinge am südöstlichen Ende von Brill.', Text0_1='Ihr findet Vance Niederglimm in einem Zimmer im ersten Stock der Taverne Zur Galgenschlinge am südöstlichen Ende von Brill.', Text1_0='Ich habe gehört, dass Vance Niederglimm in der Taverne Zur Galgenschlinge am südöstlichen Ende der Stadt ein Zimmer gemietet hat. Ich glaube, er hat das Zimmer neben dem Lesezimmer.', Text1_1='Ich habe gehört, dass Vance Niederglimm in der Taverne Zur Galgenschlinge am südöstlichen Ende der Stadt ein Zimmer gemietet hat. Ich glaube, er hat das Zimmer neben dem Lesezimmer.' WHERE id=4085;
UPDATE npc_text SET Text0_0='Clyde Kellen, unser örtlicher Barschfänger, angelt am liebsten im Blendwassersee, ein kleines Stück östlich von Brill.', Text0_1='Clyde Kellen, unser örtlicher Barschfänger, angelt am liebsten im Blendwassersee, ein kleines Stück östlich von Brill.', Text1_0='Ja, Clyde Kellen ist eigentlich immer am Ufer des Blendwassersees ein Stückchen östlich von Brill und angelt. Passt aber auf, dass Ihr da draußen keinem Psychopathen mit Maske über den Weg lauft. Letztes Jahr mussten wir deswegen das Kinderferienlager schließen.', Text1_1='Ja, Clyde Kellen ist eigentlich immer am Ufer des Blendwassersees ein Stückchen östlich von Brill und angelt. Passt aber auf, dass Ihr da draußen keinem Psychopathen mit Maske über den Weg lauft. Letztes Jahr mussten wir deswegen das Kinderferienlager schließen.' WHERE id=4088;
UPDATE npc_text SET Text0_0='Seht Euch nach Faruza um. Das letzte Mal habe ich sie zwischen den Ställen und dem Haus am nordwestlichen Rand der Stadt gesehen.', Text0_1='Seht Euch nach Faruza um. Das letzte Mal habe ich sie zwischen den Ställen und dem Haus am nordwestlichen Rand der Stadt gesehen.', Text1_0='Mir sind, ehrlich gesagt, die traditionellen Heilmethoden lieber: Blutegel, Maden, Knochensägen und all solche Sachen. Aber jedem das Seine. Faruza, unsere örtliche Kräuterkundelehrerin, findet Ihr in Brill in der Nähe der Ställe.', Text1_1='Mir sind, ehrlich gesagt, die traditionellen Heilmethoden lieber: Blutegel, Maden, Knochensägen und all solche Sachen. Aber jedem das Seine. Faruza, unsere örtliche Kräuterkundelehrerin, findet Ihr in Brill in der Nähe der Ställe.' WHERE id=4089;
UPDATE npc_text SET Text0_0='Das dürfte Shelene Rhobart sein. Folgt der Straße Richtung Südosten aus Brill hinaus und wendet Euch an der Kreuzung nach Osten. Sie ist in einem Lager an der Straße.', Text0_1='Das dürfte Shelene Rhobart sein. Folgt der Straße Richtung Südosten aus Brill hinaus und wendet Euch an der Kreuzung nach Osten. Sie ist in einem Lager an der Straße.', Text1_0='Es geht doch nichts über das Gefühl, wenn sich etwas Totes an einem reibt. Ihr findet Shelene Rhobart, wenn Ihr die Stadt auf der Straße nach Südosten verlasst und Euch dann an der Kreuzung Richtung Osten haltet. Dort ist sie dann ein kleines Stück weiter in einem Lager an der Straße.', Text1_1='Es geht doch nichts über das Gefühl, wenn sich etwas Totes an einem reibt. Ihr findet Shelene Rhobart, wenn Ihr die Stadt auf der Straße nach Südosten verlasst und Euch dann an der Kreuzung Richtung Osten haltet. Dort ist sie dann ein kleines Stück weiter in einem Lager an der Straße.' WHERE id=4090;
UPDATE npc_text SET Text0_0='Rand Rhobart ist ziemlich gut darin, Teufelsfledermäuse und solches Getier zu häuten. Ihr findet ihn in einem Lager an der Straße südöstlich von Brill. Folgt der Straße Richtung Südosten aus der Stadt und wendet Euch an der Kreuzung nach Osten.', Text0_1='Rand Rhobart ist ziemlich gut darin, Teufelsfledermäuse und solches Getier zu häuten. Ihr findet ihn in einem Lager an der Straße südöstlich von Brill. Folgt der Straße Richtung Südosten aus der Stadt und wendet Euch an der Kreuzung nach Osten.', Text1_0='Wollt Ihr etwa $GPräparator:Präparatorin; werden? Ich kann Leute nicht ausstehen, die denken, sie könnten mich ausstopfen und an die Wand hängen, nur weil sie glauben, dass ich tot sei. Nein? Gut! Dann sucht nach Rand Rhobart - er ist in einem Lager südöstlich der Stadt. Ihr kommt zu ihm, wenn Ihr der Straße aus Brill hinaus nach Südosten folgt und dann an der Kreuzung nach links geht.', Text1_1='Wollt Ihr etwa $GPräparator:Präparatorin; werden? Ich kann Leute nicht ausstehen, die denken, sie könnten mich ausstopfen und an die Wand hängen, nur weil sie glauben, dass ich tot sei. Nein? Gut! Dann sucht nach Rand Rhobart - er ist in einem Lager südöstlich der Stadt. Ihr kommt zu ihm, wenn Ihr der Straße aus Brill hinaus nach Südosten folgt und dann an der Kreuzung nach links geht.' WHERE id=4092;
UPDATE npc_text SET Text0_0='Bowen Brisboise ist der Richtige für Euch. Ihr findet ihn in seinem Haus südwestlich von Brill, auf der anderen Seite der Brücke beim verlassenen Anwesen.', Text0_1='Bowen Brisboise ist der Richtige für Euch. Ihr findet ihn in seinem Haus südwestlich von Brill, auf der anderen Seite der Brücke beim verlassenen Anwesen.', Text1_0='Schwarz, grau und dunkellila sind die vorgeschriebenen Farben. Seht zu, dass Ihr nicht vom Weg der rechten Mode abkommt, $Gmein Freund:meine Liebe;. Bowen Brisboise kann Euch dabei helfen, das zu vermeiden. Ihr findet ihn im verlassenen Anwesen auf der anderen Seite der Brücke südwestlich der Stadt.', Text1_1='Schwarz, grau und dunkellila sind die vorgeschriebenen Farben. Seht zu, dass Ihr nicht vom Weg der rechten Mode abkommt, $Gmein Freund:meine Liebe;. Bowen Brisboise kann Euch dabei helfen, das zu vermeiden. Ihr findet ihn im verlassenen Anwesen auf der anderen Seite der Brücke südwestlich der Stadt.' WHERE id=4093;
UPDATE npc_text SET Text0_0='Welchen Lehrer sucht Ihr?', Text0_1='Welchen Lehrer sucht Ihr?' WHERE id=4095;
UPDATE npc_text SET Text0_0='Welcher Beruf?', Text0_1='Welcher Beruf?' WHERE id=4096;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=4097;
UPDATE npc_text SET Text0_1='Ihr habt mir bereits jetzt einen großen Dienst erwiesen, $N. Dafür danke ich Euch.$B$BDas Mindeste, was ich tun kann, ist, Euch zu erzählen, was mich hierher gebracht hat. Mein langjähriger Freund Trey Lichtschmied und ich kamen nach Jaedenar, um Nachforschungen über den Schattenrat anzustellen, als sie uns überrumpelten und gefangen nahmen. Nachdem sie uns all unsere Ausrüstung abgenommen hatten, steckten sie mich hier hinein und brachten Trey tiefer in die Schattenfeste... Ich kann seine Schreie noch immer von dort unten hören, $N. Sie klangen... unmenschlich. Ich weiß nicht genau, was sie ihm angetan haben, aber sie werden dafür bezahlen.' WHERE id=4113;
UPDATE npc_text SET Text0_0='Es ist ihnen nicht bewusst, dass sie tot sind, $R. Sie sind dazu verdammt, die letzte Erinnerung wieder und wieder zu durchleben, die sie hatten, ehe die Stadt bei lebendigem Leibe von innen heraus aufgefressen wurde. Den meisten von ihnen geht es zumindest so. Einige hatten nicht so viel Glück.$B$BIhr glaubt, die Geißel hätte dies getan? Nein, die Geißel existierte zu dem Zeitpunkt noch nicht, und wenn es sie gegeben hätte, dann hätten wir uns zumindest aufs Heftigste zur Wehr gesetzt.$B$BOh nein. Das alles haben wir einer Seuche zu verdanken, die aus der Scholomance kam und die Stadt verheerte. Es war entsetzlich. Es war grauenhaft. Niemand überlebte.$B$BUnd jetzt lasst mich bitte in meinem Kummer allein.' WHERE id=4117;
UPDATE npc_text SET Text0_0='Seht Ihr nicht, dass wir versuchen, uns auf Uthers Besuch vorzubereiten? Jetzt geht mir aus dem Weg, ich habe viel zu tun!', Text0_1='Seht Ihr nicht, dass wir versuchen, uns auf Uthers Besuch vorzubereiten? Jetzt geht mir aus dem Weg, ich habe viel zu tun!', Text1_0='Habt Ihr unser Brot schon mal probiert? Bäcker Meistersohn muss der beste Küchenchef sein, der je gelebt hat!', Text1_1='Habt Ihr unser Brot schon mal probiert? Bäcker Meistersohn muss der beste Küchenchef sein, der je gelebt hat!', Text2_0='Willkommen in Darrowehr, Fremdling.', Text2_1='Willkommen in Darrowehr, Fremdling.', Text3_0='Wir sind normalerweise kein unhöfliches Völkchen, Fremdling, wir sind nur alle schrecklich in Eile, weil bis zu Uthers Ankunft alles perfekt sein soll. Es passiert nicht oft, dass Lichtbringer höchstpersönlich in die Stadt kommt.', Text3_1='Wir sind normalerweise kein unhöfliches Völkchen, Fremdling, wir sind nur alle schrecklich in Eile, weil bis zu Uthers Ankunft alles perfekt sein soll. Es passiert nicht oft, dass Lichtbringer höchstpersönlich in die Stadt kommt.', Text4_0='Unsere wunderschöne Insel erlebt wieder einen wundervollen Tag.', Text4_1='Unsere wunderschöne Insel erlebt wieder einen wundervollen Tag.' WHERE id=4118;
UPDATE npc_text SET Text0_0='Passt bloß auf, Fremdling. Wir dulden keine Übeltäter in unserer Stadt.', Text0_1='Passt bloß auf, Fremdling. Wir dulden keine Übeltäter in unserer Stadt.', Text1_0='Ich behalte Euch im Auge, $R.', Text1_1='Ich behalte Euch im Auge, $R.', Text2_0='Es hat sich eine Menge Wut und Feindseligkeit in mir angestaut. Ihr müsst mir nur einen Grund geben, das alles rauszulassen, Gossengezücht.', Text2_1='Es hat sich eine Menge Wut und Feindseligkeit in mir angestaut. Ihr müsst mir nur einen Grund geben, das alles rauszulassen, Gossengezücht.', Text3_0='Na, Ihr seht aus wie ein hundsgewöhnliches Gassenkind!', Text3_1='Na, Ihr seht aus wie ein hundsgewöhnliches Gassenkind!', Text4_0='In Darrowehr ist das Herumlungern, Betteln und Kämpfen nicht gestattet, und auch sonst nichts von dem, an das Ihr gerade denkt.', Text4_1='In Darrowehr ist das Herumlungern, Betteln und Kämpfen nicht gestattet, und auch sonst nichts von dem, an das Ihr gerade denkt.' WHERE id=4119;
UPDATE npc_text SET Text0_0='Manchen blieben die Qualen erspart, die Erinnerungen verursachen können.' WHERE id=4130;
UPDATE npc_text SET Text0_0='Das Haus Barov hält mich ganz schön auf Trab. Ständig kommt da irgendwelcher Müll raus...', Text1_0='Meine genaue Arbeitsplatzbeschreibung lautet \'Spezialist für Abfallentsorgung\'.', Text2_0='Vielleicht solltet Ihr in diese Schall verstärkenden Geräte hineinsprechen, die an meinen FURORFÄUSTEN befestigt sind!', Text3_0='Ach, meine süße Brandy, ich vermisse sie so!' WHERE id=4133;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Ich bin Offizier Garush, und ich bin stolz darauf, Mitglied einer weltweiten Organisation namens Argentumdämmerung zu sein. Wir haben uns einer einzigen Sache verschrieben, und zwar dem schnellen und gnadenlosen Kampf gegen jedes Element des Bösen, das in Azeroth auftaucht, ganz gleich wo. Wir sind keine politische Organisation und möchten das auch nie werden. Wie gesagt verfolgen wir nur ein einziges Ziel, und für dieses Ziel ist sowohl die Allianz als auch die Horde unser Freund. Es sind Freunde, die uns dabei helfen, das Böse auszumerzen!$B$BFür die Dämmerung, $Gmein Bruder:meine Schwester;!' WHERE id=4134;
UPDATE npc_text SET Text0_1='Seid mir gegrüßt. Ich bin Offizier Reinherz, und ich bin stolz darauf, Mitglied einer weltweiten Organisation namens Argentumdämmerung zu sein. Wir haben uns einem einfachen Ziel verschrieben - dem schnellen und gnadenlosen Kampf gegen jedes Element des Bösen, das in Azeroth auftaucht, ganz gleich, wo. Wir sind keine politische Organisation und möchten das auch nie werden. Aus diesem Grund ist sowohl die Allianz als auch die Horde unser Freund - es sind Freunde, die uns dabei helfen, das Böse auszumerzen!$B$BFür die Dämmerung, $Gmein Bruder:meine Schwester;!' WHERE id=4135;
UPDATE npc_text SET Text0_1='Die Argentumdämmerung grüßt Euch, $C. Mein Name ist Hasana, und bin einer der wenigen offiziellen Rüstmeister für Ausrüstung und Material der Dämmerung.$B$BWenn Ihr in unserem Auftrag das Böse bekämpft - und Euch damit unseren Respekt und unser Vertrauen verdient -, erhaltet Ihr von Offizier Garush Ehrenmarken. Diese Marken kann man auf zweierlei Art verwenden: entweder, um Euer Ansehen in unseren Reihen zu verbessern, oder um sie als Zahlungsmittel beim Erwerb von besonderen Gegenständen einzusetzen.$B$BIhr werdet sehen: Je mehr Ansehen Ihr bei der Dämmerung erlangt, umso mehr Gegenstände stehen Euch zur Verfügung.' WHERE id=4173;
UPDATE npc_text SET Text0_1='Schließt Euch dem Kampf gegen das Böse an! Nehmt heute noch die Anstecknadel der Argentumdämmerung entgegen!' WHERE id=4174;
UPDATE npc_text SET Text0_0='Die Argentumdämmerung grüßt Euch, $C! Ich heiße Funkellicht, und ich bin einer der offiziellen Rüstmeister für Ausrüstung und Material der Dämmerung.$B$BWenn Ihr in unserem Auftrag das Böse bekämpft - und Euch damit unseren Respekt und unser Vertrauen verdient -, erhaltet Ihr von Offizier Reinherz Ehrenmarken. Diese Marken kann man auf zweierlei Art verwenden: entweder, um Euer Ansehen in unseren Reihen zu verbessern, oder um sie als Zahlungsmittel beim Erwerb von besonderen Gegenständen einzusetzen.$B$BIhr werdet sehen: Je mehr Ansehen Ihr bei der Dämmerung erlangt, umso mehr Gegenstände stehen Euch zur Verfügung.' WHERE id=4193;
UPDATE npc_text SET Text0_0='Schließt Euch dem Kampf gegen das Böse an! Nehmt heute noch die Anstecknadel der Argentumdämmerung entgegen!' WHERE id=4194;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $C. Mein Name ist Miranda Knackschloss, und bin eine der wenigen offiziellen Rüstmeisterinnen für Ausrüstung und Material der Dämmerung.$B$BWenn Ihr in unserem Auftrag das Böse bekämpft - und Euch damit unseren Respekt und unser Vertrauen verdient -, erhaltet Ihr von Fürst Zverenhoff Ehrenmarken. Diese Marken kann man auf zweierlei Art verwenden: entweder, um Euer Ansehen in unseren Reihen zu verbessern, oder um sie als Zahlungsmittel beim Erwerb von besonderen Gegenständen einzusetzen.$B$BIhr werdet sehen: Je mehr Ansehen Ihr bei der Dämmerung erlangt, umso mehr Gegenstände stehen Euch zur Verfügung.' WHERE id=4213;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C.$B$BDie Schildwachen da drüben haben mir erlaubt, mich in ihrem Lager aufzuhalten, aber ich musste ihnen versprechen, dass es keinen Ärger geben würde. Ich bin sicher, Ihr versteht, dass hier ein wenig... Diplomatie angebracht ist, ja?$B$BIch untersuche gewisse Störungen, denen das Land unterworfen ist, und weil dies auch ihre Sache fördert, stimmten die Schildwachen zu, dass meine Gegenwart hier der unsicheren Allianz zwischen allen Völkern von Azeroth nur zugutekommen könne. Wenn Ihr mir helfen möchtet, dann weiß ich das zu schätzen, aber dennoch: Versucht bitte, keinen Ärger zu verursachen.' WHERE id=4233;
UPDATE npc_text SET Text0_0='Willkommen in Stahlrosts Depot!' WHERE id=4253;
UPDATE npc_text SET Text0_0='Welchem Volk man angehört, sagt nichts darüber aus, wie viel Ehre man besitzt, $R. Solange Ihr Euch auf meinem Bauernhof befindet, bitte ich Euch, diesen Grundsatz zu beherzigen.$B$BIch habe Orcs gekannt, die ebenso ehrenvoll waren wie der edelste Ritter, und Menschen, die so böse waren wie der übelste Angehörige der Geißel.$B$BDoch ich möchte Euch nicht mit Geschichten aus meiner Jugend langweilen. Es gibt viel zu tun - falls es das ist, was Ihr wünscht.' WHERE id=4254;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N.' WHERE id=4258;
UPDATE npc_text SET Text0_0='Ihr sucht doch etwas. Was ist es?', Text0_1='Ihr sucht doch etwas. Was ist es?' WHERE id=4259;
UPDATE npc_text SET Text0_0='Eine Bank? Also, die nächste Bank hier in der Gegend ist das Zählhaus von Sturmwind. Das ist in Sturmwind, nicht weit von hier. Einfach der Straße folgen, und wenn Ihr in der Stadt seid, liegt es in der Nähe der Tore.', Text0_1='Eine Bank? Also, die nächste Bank hier in der Gegend ist das Zählhaus von Sturmwind. Das ist in Sturmwind, nicht weit von hier. Einfach der Straße folgen, und wenn Ihr in der Stadt seid, liegt es in der Nähe der Tore.', Text1_0='Ach, Ihr meint bestimmt das Zählhaus Sturmwind. Sobald Ihr Sturmwind betreten habt, liegt es ganz in der Nähe der Tore.', Text1_1='Ach, Ihr meint bestimmt das Zählhaus Sturmwind. Sobald Ihr Sturmwind betreten habt, liegt es ganz in der Nähe der Tore.' WHERE id=4260;
UPDATE npc_text SET Text0_0='Tja, in Sturmwind gibt es einen Greifenmeister, falls Ihr irgendwohin fliegen müsst. Ihr solltet die Greifen zu Eurer Rechten landen sehen, wenn Ihr das Tal der Helden durchquert.', Text0_1='Tja, in Sturmwind gibt es einen Greifenmeister, falls Ihr irgendwohin fliegen müsst. Ihr solltet die Greifen zu Eurer Rechten landen sehen, wenn Ihr das Tal der Helden durchquert.', Text1_0='Der Greifenmeister überwacht die Greifenflüge von Sturmwind aus. Wenn Ihr einen Flug unternehmen möchtet, dann solltet Ihr Euch dorthin begeben.', Text1_1='Der Greifenmeister überwacht die Greifenflüge von Sturmwind aus. Wenn Ihr einen Flug unternehmen möchtet, dann solltet Ihr Euch dorthin begeben.' WHERE id=4261;
UPDATE npc_text SET Text0_0='Ich glaube, Aldwin Laughlin ist noch immer der amtierende Gildenmeister von Sturmwind. Wenn Ihr durch das innere Tor von Sturmwind geht, schaut nach links: Dort seht Ihr sein Heim.', Text0_1='Ich glaube, Aldwin Laughlin ist noch immer der amtierende Gildenmeister von Sturmwind. Wenn Ihr durch das innere Tor von Sturmwind geht, schaut nach links: Dort seht Ihr sein Heim.', Text1_0='All diese offiziellen Dinge werden heutzutage in Sturmwind abgewickelt. Ihr könntet einmal im Besucherzentrum von Sturmwind vorbeischauen, wenn Ihr eine Gilde gründen möchtet.', Text1_1='All diese offiziellen Dinge werden heutzutage in Sturmwind abgewickelt. Ihr könntet einmal im Besucherzentrum von Sturmwind vorbeischauen, wenn Ihr eine Gilde gründen möchtet.' WHERE id=4262;
UPDATE npc_text SET Text0_0='Da müsst Ihr gar nicht weiter schauen als bis zum Gasthaus Zur Höhle des Löwen in Goldhain. Sie haben dort eine große Auswahl an Bier und weiche Daunenbetten - Ihr könntet Euch also gar keinen besseren Ort wünschen, um Euch etwas auszuruhen.', Text0_1='Da müsst Ihr gar nicht weiter schauen als bis zum Gasthaus Zur Höhle des Löwen in Goldhain. Sie haben dort eine große Auswahl an Bier und weiche Daunenbetten - Ihr könntet Euch also gar keinen besseren Ort wünschen, um Euch etwas auszuruhen.', Text1_0='Das Gasthaus Zur Höhle des Löwen kann sich mit jedem anderen Gasthof messen, den es hier in der Gegend gibt. Schaut doch einmal vorbei und lasst es Euch von Gastwirt Farley behaglich machen.', Text1_1='Das Gasthaus Zur Höhle des Löwen kann sich mit jedem anderen Gasthof messen, den es hier in der Gegend gibt. Schaut doch einmal vorbei und lasst es Euch von Gastwirt Farley behaglich machen.' WHERE id=4263;
UPDATE npc_text SET Text0_0='Es gibt etliche Lehrer in Elwynn. Was genau sucht Ihr?', Text0_1='Es gibt etliche Lehrer in Elwynn. Was genau sucht Ihr?' WHERE id=4264;
UPDATE npc_text SET Text0_0='Druiden? Hier in der Gegend habe ich keine Druiden gesehen. Aber in Sturmwind gibt es einen Distrikt, wo hauptsächlich Nachtelfen leben.', Text0_1='Druiden? Hier in der Gegend habe ich keine Druiden gesehen. Aber in Sturmwind gibt es einen Distrikt, wo hauptsächlich Nachtelfen leben.' WHERE id=4265;
UPDATE npc_text SET Text0_0='Ich habe gehört, dass es in Sturmwind einen Zwerg gibt, der recht berühmt für seine Jagdkünste ist. Vielleicht solltet Ihr ihn einmal fragen, ob er Euch helfen kann.', Text0_1='Ich habe gehört, dass es in Sturmwind einen Zwerg gibt, der recht berühmt für seine Jagdkünste ist. Vielleicht solltet Ihr ihn einmal fragen, ob er Euch helfen kann.' WHERE id=4266;
UPDATE npc_text SET Text0_0='Mit ihrer Hilfe für die örtlichen Wachen hat sich Josetta für diese Stadt wirklich als Segen erwiesen. Wenn Ihr sie sucht, weil Ihr Euch $G zum Priester:zur Priesterin; ausbilden lassen wollt, dann findet Ihr sie im Gasthaus Zur Höhle des Löwen.', Text0_1='Mit ihrer Hilfe für die örtlichen Wachen hat sich Josetta für diese Stadt wirklich als Segen erwiesen. Wenn Ihr sie sucht, weil Ihr Euch $G zum Priester:zur Priesterin; ausbilden lassen wollt, dann findet Ihr sie im Gasthaus Zur Höhle des Löwen.', Text1_0='Ich wette, Josetta wäre mehr als glücklich, Euch ein paar neue Zauber beizubringen. Vermutlich ist sie oben in ihrem Zimmer im Gasthaus Zur Höhle des Löwen.', Text1_1='Ich wette, Josetta wäre mehr als glücklich, Euch ein paar neue Zauber beizubringen. Vermutlich ist sie oben in ihrem Zimmer im Gasthaus Zur Höhle des Löwen.' WHERE id=4267;
UPDATE npc_text SET Text0_0='Zaldimar könnte Euch vermutlich ein paar neue Zauber beibringen. Er hat vor kurzem seine Vorliebe für das Gasthaus Zur Höhle des Löwen entdeckt und sich dort ein Zimmer genommen. Vielleicht solltet Ihr ja dort einmal nachsehen, ob er zuhause ist.', Text0_1='Zaldimar könnte Euch vermutlich ein paar neue Zauber beibringen. Er hat vor kurzem seine Vorliebe für das Gasthaus Zur Höhle des Löwen entdeckt und sich dort ein Zimmer genommen. Vielleicht solltet Ihr ja dort einmal nachsehen, ob er zuhause ist.', Text1_0='Ich weiß nicht so sonderlich viel über Magie, aber es gibt einen Magier namens Zaldimar, der Euch vermutlich helfen kann. Er wohnt im Gasthaus Zur Höhle des Löwen.', Text1_1='Ich weiß nicht so sonderlich viel über Magie, aber es gibt einen Magier namens Zaldimar, der Euch vermutlich helfen kann. Er wohnt im Gasthaus Zur Höhle des Löwen.' WHERE id=4268;
UPDATE npc_text SET Text0_0='Normalerweise ist Bruder Wilhelm hinter der Schmiede und quatscht mit Lyria. Er ist immer bereit, Paladinkollegen neue Fertigkeiten beizubringen.', Text0_1='Normalerweise ist Bruder Wilhelm hinter der Schmiede und quatscht mit Lyria. Er ist immer bereit, Paladinkollegen neue Fertigkeiten beizubringen.', Text1_0='Bruder Wilhelm trainiert oft hinter der Schmiede von Goldhain mit Lyria Du Lac. Von ihm könntet Ihr vermutlich noch das eine oder andere über den Beruf des Paladins lernen.', Text1_1='Bruder Wilhelm trainiert oft hinter der Schmiede von Goldhain mit Lyria Du Lac. Von ihm könntet Ihr vermutlich noch das eine oder andere über den Beruf des Paladins lernen.' WHERE id=4269;
UPDATE npc_text SET Text0_0='Ein Schurke? Hmmm... nun ja, ich habe gehört, dass Keryn Sylvius sich mit diesen zwielichtigen Typen herumtreibt. Vielleicht solltet Ihr mal mit ihr sprechen und zusehen, was Ihr von ihr erfahren könnt.', Text0_1='Ein Schurke? Hmmm... nun ja, ich habe gehört, dass Keryn Sylvius sich mit diesen zwielichtigen Typen herumtreibt. Vielleicht solltet Ihr mal mit ihr sprechen und zusehen, was Ihr von ihr erfahren könnt.', Text1_0='Ich wette, Keryn Sylvius kennt solche Typen, wie Ihr sie sucht. Die Gesellschaft, in der Keryn Sylvius verkehrt, ist nicht gerade... Na, wie dem auch sei, sie hält sich meistens im Gasthaus Zur Höhle des Löwen auf.', Text1_1='Ich wette, Keryn Sylvius kennt solche Typen, wie Ihr sie sucht. Die Gesellschaft, in der Keryn Sylvius verkehrt, ist nicht gerade... Na, wie dem auch sei, sie hält sich meistens im Gasthaus Zur Höhle des Löwen auf.' WHERE id=4270;
UPDATE npc_text SET Text0_0='Nur wenige hier in der Gegend sind so geschickt mit der Klinge wie Lyria Du Lac. Seht doch einmal nach, ob sie sich hinter der Schmiede aufhält und ob sie bereit ist, Euch etwas Neues beizubringen.', Text0_1='Nur wenige hier in der Gegend sind so geschickt mit der Klinge wie Lyria Du Lac. Seht doch einmal nach, ob sie sich hinter der Schmiede aufhält und ob sie bereit ist, Euch etwas Neues beizubringen.', Text1_0='Ihr solltet mit Lyria reden, falls Ihr Euch ausbilden lassen wollt. Man findet sie oft hinter der Schmiede, wo sie darauf wartet, dass irgendein Rüstungsteil oder eine Waffe fertig wird.', Text1_1='Ihr solltet mit Lyria reden, falls Ihr Euch ausbilden lassen wollt. Man findet sie oft hinter der Schmiede, wo sie darauf wartet, dass irgendein Rüstungsteil oder eine Waffe fertig wird.' WHERE id=4271;
UPDATE npc_text SET Text0_0='Ach so... einer von DER Sorte. Tja, hier in der Gegend ist Maximillian der Einzige, der sich mit den dunklen Künsten auskennt. Ihr findet ihn vermutlich in der Dunkelheit des Kellers vom Gasthaus Zur Höhle des Löwen.', Text0_1='Ach so... einer von DER Sorte. Tja, hier in der Gegend ist Maximillian der Einzige, der sich mit den dunklen Künsten auskennt. Ihr findet ihn vermutlich in der Dunkelheit des Kellers vom Gasthaus Zur Höhle des Löwen.', Text1_0='Ein Hexenmeister? Also, wenn ich ein Hexenmeister wäre, dann würde ich mich vermutlich am dunkelsten Ort aufhalten, den es hier so gibt - und das ist der Keller vom Gasthaus Zur Höhle des Löwen. Dort solltet Ihr zuerst nachsehen.', Text1_1='Ein Hexenmeister? Also, wenn ich ein Hexenmeister wäre, dann würde ich mich vermutlich am dunkelsten Ort aufhalten, den es hier so gibt - und das ist der Keller vom Gasthaus Zur Höhle des Löwen. Dort solltet Ihr zuerst nachsehen.' WHERE id=4272;
UPDATE npc_text SET Text0_0='Ich kenne ein paar Leute hier in der Gegend, die den einen oder anderen Beruf ausüben. An welchen Beruf hattet Ihr denn gedacht?', Text0_1='Ich kenne ein paar Leute hier in der Gegend, die den einen oder anderen Beruf ausüben. An welchen Beruf hattet Ihr denn gedacht?' WHERE id=4273;
UPDATE npc_text SET Text0_0='Der Einsiedler Mallory ist dafür bekannt, dass er gelegentlich für die Leute in der Stadt den einen oder anderen Trank zusammenbraut. Er hat eine Hütte in den Hügeln zwischen Sturmwind und Nordhain.', Text0_1='Der Einsiedler Mallory ist dafür bekannt, dass er gelegentlich für die Leute in der Stadt den einen oder anderen Trank zusammenbraut. Er hat eine Hütte in den Hügeln zwischen Sturmwind und Nordhain.', Text1_0='Es gibt da einen Alchemisten, der in den Hügeln zwischen Nordhain und Sturmwind lebt und Euch vielleicht helfen kann. Warum stattet Ihr dem nicht mal einen Besuch ab?', Text1_1='Es gibt da einen Alchemisten, der in den Hügeln zwischen Nordhain und Sturmwind lebt und Euch vielleicht helfen kann. Warum stattet Ihr dem nicht mal einen Besuch ab?' WHERE id=4274;
UPDATE npc_text SET Text0_0='Argus ist der ortsansässige Schmied in Goldhain. Ich bin sicher, er könnte Euch den Beruf des Schmieds beibringen.', Text0_1='Argus ist der ortsansässige Schmied in Goldhain. Ich bin sicher, er könnte Euch den Beruf des Schmieds beibringen.', Text1_0='Ihr sucht bestimmt nach Schmied Argus. Er überwacht normalerweise die Schmiede in Goldhain, falls Ihr Euch ausbilden lassen wollt.', Text1_1='Ihr sucht bestimmt nach Schmied Argus. Er überwacht normalerweise die Schmiede in Goldhain, falls Ihr Euch ausbilden lassen wollt.' WHERE id=4275;
UPDATE npc_text SET Text0_0='Tomas ist zwar vielleicht nicht der Beste aller Köche, aber ein paar seiner Gerichte sind wirklich lecker. Momentan arbeitet er im Gasthaus Zur Höhle des Löwen, falls Ihr den Beruf erlernen möchtet.', Text0_1='Tomas ist zwar vielleicht nicht der Beste aller Köche, aber ein paar seiner Gerichte sind wirklich lecker. Momentan arbeitet er im Gasthaus Zur Höhle des Löwen, falls Ihr den Beruf erlernen möchtet.', Text1_0='Warum versucht Ihr es nicht einmal im Gasthaus Zur Höhle des Löwen? Der Koch dort kann Euch vielleicht ein paar leckere Rezepte beibringen.', Text1_1='Warum versucht Ihr es nicht einmal im Gasthaus Zur Höhle des Löwen? Der Koch dort kann Euch vielleicht ein paar leckere Rezepte beibringen.' WHERE id=4276;
UPDATE npc_text SET Text0_0='Nee, ich glaube nicht, dass es hier im Wald von Elwynn irgendwelche Verzauberer gibt. Aber in Sturmwind hat so ein Typ namens Lucan einen Laden. Bei dem könntet Ihr mal nachfragen, ob er bereit ist, Euch auszubilden.', Text0_1='Nee, ich glaube nicht, dass es hier im Wald von Elwynn irgendwelche Verzauberer gibt. Aber in Sturmwind hat so ein Typ namens Lucan einen Laden. Bei dem könntet Ihr mal nachfragen, ob er bereit ist, Euch auszubilden.', Text1_0='Hmmm... Die einzigen Verzauberer, die ich hier in der Gegend kenne, sitzen in Sturmwind. Warum fragt Ihr nicht dort mal eine der Wachen?', Text1_1='Hmmm... Die einzigen Verzauberer, die ich hier in der Gegend kenne, sitzen in Sturmwind. Warum fragt Ihr nicht dort mal eine der Wachen?' WHERE id=4277;
UPDATE npc_text SET Text0_0='Normalerweise überlasse ich so was den Gnomen. Wenn Ihr den Beruf erlernen wollt, dann bin ich sicher, dass Ihr in Sturmwind ein paar Gnome finden könnt, die bereit sind, Euch auszubilden.', Text0_1='Normalerweise überlasse ich so was den Gnomen. Wenn Ihr den Beruf erlernen wollt, dann bin ich sicher, dass Ihr in Sturmwind ein paar Gnome finden könnt, die bereit sind, Euch auszubilden.', Text1_0='Es gibt garantiert einen Gnom in Sturmwind, bei dem Ihr den Beruf lernen könnt. Ich bin sicher, eine der Wachen von Sturmwind kann Euch den Weg weisen.', Text1_1='Es gibt garantiert einen Gnom in Sturmwind, bei dem Ihr den Beruf lernen könnt. Ich bin sicher, eine der Wachen von Sturmwind kann Euch den Weg weisen.' WHERE id=4278;
UPDATE npc_text SET Text0_0='Ich hatte vor kurzem eine unangenehme Begegnung mit ein paar Defias hier aus der Gegend, aber Michelle Belle hat mich wieder ziemlich gut zusammengeflickt. Besucht sie doch im Gasthaus Zur Höhle des Löwen und findet heraus, ob sie bereit ist, Euch Unterricht zu erteilen.', Text0_1='Ich hatte vor kurzem eine unangenehme Begegnung mit ein paar Defias hier aus der Gegend, aber Michelle Belle hat mich wieder ziemlich gut zusammengeflickt. Besucht sie doch im Gasthaus Zur Höhle des Löwen und findet heraus, ob sie bereit ist, Euch Unterricht zu erteilen.', Text1_0='Michelle Belle kann einfach wunderbar mit Kranken umgehen. Das kann sie Euch zwar vermutlich nicht beibringen, aber sie kann Euch zumindest anständig in erster Hilfe ausbilden. Zurzeit wohnt sie im Gasthaus Zur Höhle des Löwen.', Text1_1='Michelle Belle kann einfach wunderbar mit Kranken umgehen. Das kann sie Euch zwar vermutlich nicht beibringen, aber sie kann Euch zumindest anständig in erster Hilfe ausbilden. Zurzeit wohnt sie im Gasthaus Zur Höhle des Löwen.' WHERE id=4279;
UPDATE npc_text SET Text0_0='Lee Braun ist den ganzen Tag oben am Kristallsee und angelt. Er hat bestimmt ein paar Geschichten, die er Euch erzählen, und ein paar gute Tipps, die er Euch verraten kann.', Text0_1='Lee Braun ist den ganzen Tag oben am Kristallsee und angelt. Er hat bestimmt ein paar Geschichten, die er Euch erzählen, und ein paar gute Tipps, die er Euch verraten kann.', Text1_0='Lee hat schon ein paar arg seltsame Sachen aus dem Kristallsee gefischt. Mir hat er mal eine Geschichte von so einer komischen Maske erzählt, die er vor ein paar Monaten zu Tage gefördert hat. Aber wenn Ihr mich fragt: Ich glaube, er hatte an dem Abend in der Höhle des Löwen einfach nur ein paar zu viel gekippt.', Text1_1='Lee hat schon ein paar arg seltsame Sachen aus dem Kristallsee gefischt. Mir hat er mal eine Geschichte von so einer komischen Maske erzählt, die er vor ein paar Monaten zu Tage gefördert hat. Aber wenn Ihr mich fragt: Ich glaube, er hatte an dem Abend in der Höhle des Löwen einfach nur ein paar zu viel gekippt.' WHERE id=4280;
UPDATE npc_text SET Text0_0='Fräulein Pomeroy sammelt Kräuter für einen Alchemisten, der in den Hügeln zwischen Nordhain und Sturmwind lebt. Schaut doch mal, ob sie in seiner Hütte ist, und fragt sie, ob sie Euch ihren Beruf beibringt.', Text0_1='Fräulein Pomeroy sammelt Kräuter für einen Alchemisten, der in den Hügeln zwischen Nordhain und Sturmwind lebt. Schaut doch mal, ob sie in seiner Hütte ist, und fragt sie, ob sie Euch ihren Beruf beibringt.', Text1_0='Es gibt eine Kräuterkundige, die in den Hügeln östlich von Sturmwind bei unserem Alchemisten lebt. Das passt doch gut zusammen, findet Ihr nicht auch?', Text1_1='Es gibt eine Kräuterkundige, die in den Hügeln östlich von Sturmwind bei unserem Alchemisten lebt. Das passt doch gut zusammen, findet Ihr nicht auch?' WHERE id=4281;
UPDATE npc_text SET Text0_0='Drüben am Kristallsee lebt eine Dame namens Adele Fädler. Aus dem überschüssigen Leder, das sie von Helene bekommt, stellt sie Flicken für die örtlichen Wachen her. Vermutlich solltet Ihr sie einmal aufsuchen, wenn Ihr den Beruf lernen wollt.', Text0_1='Drüben am Kristallsee lebt eine Dame namens Adele Fädler. Aus dem überschüssigen Leder, das sie von Helene bekommt, stellt sie Flicken für die örtlichen Wachen her. Vermutlich solltet Ihr sie einmal aufsuchen, wenn Ihr den Beruf lernen wollt.', Text1_0='Adele Fädler hat sich hier in Elwynn einen guten Namen im Beruf des Lederers gemacht. Ihr findet sie in ihrem Haus oben am Kristallsee, wo sie auch ihre Werkstatt hat.', Text1_1='Adele Fädler hat sich hier in Elwynn einen guten Namen im Beruf des Lederers gemacht. Ihr findet sie in ihrem Haus oben am Kristallsee, wo sie auch ihre Werkstatt hat.' WHERE id=4282;
UPDATE npc_text SET Text0_0='Wenn Ihr den Bergbau erlernen wollt, dann solltet Ihr am besten nach Sturmwind in den Zwergendistrikt gehen. Einer der Zwerge dort kann Euch den Beruf bestimmt lehren.', Text0_1='Wenn Ihr den Bergbau erlernen wollt, dann solltet Ihr am besten nach Sturmwind in den Zwergendistrikt gehen. Einer der Zwerge dort kann Euch den Beruf bestimmt lehren.', Text1_0='Ich schätze, in Sturmwind hättet Ihr mehr Glück. Fragt eine von den Wachen da oben, die werden Euch den Weg schon weisen.', Text1_1='Ich schätze, in Sturmwind hättet Ihr mehr Glück. Fragt eine von den Wachen da oben, die werden Euch den Weg schon weisen.' WHERE id=4283;
UPDATE npc_text SET Text0_0='Helene Pelzschneider hat viel Erfahrung in der Kürschnerei, weil sie schon seit Jahren die Tiere für Adele Fädler häutet, sodass diese dann Lederwaren herstellen kann. Ihr könnt Helene in ihrem Haus am Kristallsee finden.', Text0_1='Helene Pelzschneider hat viel Erfahrung in der Kürschnerei, weil sie schon seit Jahren die Tiere für Adele Fädler häutet, sodass diese dann Lederwaren herstellen kann. Ihr könnt Helene in ihrem Haus am Kristallsee finden.', Text1_0='In dem Haus am Kristallsee oben wohnt eine ausgezeichnete Kürschnerin. Sie kann Euch bestimmt zeigen, wie Ihr am besten in den Beruf des Kürschners einsteigt.', Text1_1='In dem Haus am Kristallsee oben wohnt eine ausgezeichnete Kürschnerin. Sie kann Euch bestimmt zeigen, wie Ihr am besten in den Beruf des Kürschners einsteigt.' WHERE id=4284;
UPDATE npc_text SET Text0_0='Eldrin ist der Einzige im Wald von Elwynn, den ich als Schneider bezeichnen würde. Ihr findet ihn im Holzfällerlager des Osttals, wenn Ihr den Beruf erlernen wollt.', Text0_1='Eldrin ist der Einzige im Wald von Elwynn, den ich als Schneider bezeichnen würde. Ihr findet ihn im Holzfällerlager des Osttals, wenn Ihr den Beruf erlernen wollt.', Text1_0='Ihr wollt Euch also $Gzum Schneider:zur Schneiderin; ausbilden lassen? Hmmm... Ich glaube, es gibt einen Schneider drüben im Holzfällerlager des Osttals, der Euch vielleicht das Grundwissen beibringen könnte.', Text1_1='Ihr wollt Euch also $Gzum Schneider:zur Schneiderin; ausbilden lassen? Hmmm... Ich glaube, es gibt einen Schneider drüben im Holzfällerlager des Osttals, der Euch vielleicht das Grundwissen beibringen könnte.' WHERE id=4285;
UPDATE npc_text SET Text0_0='Was ist denn Euer Ziel?', Text0_1='Was ist denn Euer Ziel?' WHERE id=4287;
UPDATE npc_text SET Text0_0='Die nächste Bank ist der \'Tresor\' in Eisenschmiede, $GJungchen:Mädel;.', Text0_1='Die nächste Bank ist der \'Tresor\' in Eisenschmiede, $GJungchen:Mädel;.', Text1_0='Der \'Tresor\' in Eisenschmiede ist die nächstgelegene Bank hier in der Gegend.', Text1_1='Der \'Tresor\' in Eisenschmiede ist die nächstgelegene Bank hier in der Gegend.' WHERE id=4288;
UPDATE npc_text SET Text0_0='Hier in Dun Morogh werdet Ihr keinen Greifenmeister finden, $GJungchen:Mädel;. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr mit einem Greifen irgendwohin fliegen wollt.', Text0_1='Hier in Dun Morogh werdet Ihr keinen Greifenmeister finden, $GJungchen:Mädel;. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr mit einem Greifen irgendwohin fliegen wollt.', Text1_0='Der nächstgelegene Ort, um einen Greifenmeister zu finden, ist in Eisenschmiede, ganz in der Nähe der großen Schmiede. Wenn Ihr also mit einem Greifen reisen wollt, müsst Ihr nach Eisenschmiede.', Text1_1='Der nächstgelegene Ort, um einen Greifenmeister zu finden, ist in Eisenschmiede, ganz in der Nähe der großen Schmiede. Wenn Ihr also mit einem Greifen reisen wollt, müsst Ihr nach Eisenschmiede.' WHERE id=4289;
UPDATE npc_text SET Text0_0='Diese ganzen Beamtentypen sitzen alle in Eisenschmiede. Also müsst Ihr ins Besucherzentrum von Eisenschmiede, um Euch da die Papiere für die Gildengründung zu beschaffen.', Text0_1='Diese ganzen Beamtentypen sitzen alle in Eisenschmiede. Also müsst Ihr ins Besucherzentrum von Eisenschmiede, um Euch da die Papiere für die Gildengründung zu beschaffen.', Text1_0='Wenn Ihr Papiere zur Gildengründung braucht, dann müsst Ihr wohl nach Eisenschmiede ins Besucherzentrum.', Text1_1='Wenn Ihr Papiere zur Gildengründung braucht, dann müsst Ihr wohl nach Eisenschmiede ins Besucherzentrum.' WHERE id=4290;
UPDATE npc_text SET Text0_0='Die Donnerbräus führen in Kharanos ein gutes Gasthaus, das ihren Namen trägt. Schaut doch einmal auf ein Bierchen vorbei und ruht Euch dort aus.', Text0_1='Die Donnerbräus führen in Kharanos ein gutes Gasthaus, das ihren Namen trägt. Schaut doch einmal auf ein Bierchen vorbei und ruht Euch dort aus.', Text1_0='Soso, ein Gasthaus sucht Ihr? Na, da is\' die Brauerei Donnerbräu nich\' zu schlagen. Ruht Euch aus, wenn Ihr\'s denn nötig habt, aber ihr müsst Euch unbedingt auch \'n Bierchen oder zwei genehmigen, wenn Ihr dort seid.', Text1_1='Soso, ein Gasthaus sucht Ihr? Na, da is\' die Brauerei Donnerbräu nich\' zu schlagen. Ruht Euch aus, wenn Ihr\'s denn nötig habt, aber ihr müsst Euch unbedingt auch \'n Bierchen oder zwei genehmigen, wenn Ihr dort seid.' WHERE id=4291;
UPDATE npc_text SET Text0_0='Für welche Klasse sucht Ihr denn einen Lehrer?', Text0_1='Für welche Klasse sucht Ihr denn einen Lehrer?' WHERE id=4292;
UPDATE npc_text SET Text0_0='Hier in der Gegend gibt\'s nur einen Jäger, zu dem ich Euch mit gutem Gewissen schicken kann, un\' das is\' Grif Wildherz. Den findet Ihr direkt vor der Schmiede in Kharanos.', Text0_1='Hier in der Gegend gibt\'s nur einen Jäger, zu dem ich Euch mit gutem Gewissen schicken kann, un\' das is\' Grif Wildherz. Den findet Ihr direkt vor der Schmiede in Kharanos.', Text1_0='Also wenn Ihr mich fragt, ich finde, Grif verbringt \'n bisschen zu viel Zeit mit den Tieren. Aber niemand versteht sich so auf das Jägerhandwerk wie er. Ihr findet ihn vor der Schmiede in Kharanos.', Text1_1='Also wenn Ihr mich fragt, ich finde, Grif verbringt \'n bisschen zu viel Zeit mit den Tieren. Aber niemand versteht sich so auf das Jägerhandwerk wie er. Ihr findet ihn vor der Schmiede in Kharanos.' WHERE id=4293;
UPDATE npc_text SET Text0_0='In der Brauerei Donnerbräu haben sie einen neuen Magier, der Euch vielleicht ein paar neue Sprüche beibringen kann. Ich glaube, er heißt Magis Funkelmantel.', Text0_1='In der Brauerei Donnerbräu haben sie einen neuen Magier, der Euch vielleicht ein paar neue Sprüche beibringen kann. Ich glaube, er heißt Magis Funkelmantel.', Text1_0='Seid Ihr sicher, dass Ihr Euch nich\' zum Krieger ausbilden lassen wollt? Na, es is\' ja schließlich Eure Haut, die Ihr auf\'m Schlachtfeld zu Markte tragen tut. Wenn Ihr echt lernen wollt, wie dieses Fingerwackelzeugs funktioniert, dann solltet Ihr am besten mit Magis Funkelmantel in der Brauerei Donnerbräu reden.', Text1_1='Seid Ihr sicher, dass Ihr Euch nich\' zum Krieger ausbilden lassen wollt? Na, es is\' ja schließlich Eure Haut, die Ihr auf\'m Schlachtfeld zu Markte tragen tut. Wenn Ihr echt lernen wollt, wie dieses Fingerwackelzeugs funktioniert, dann solltet Ihr am besten mit Magis Funkelmantel in der Brauerei Donnerbräu reden.' WHERE id=4294;
UPDATE npc_text SET Text0_0='Ach... ein Paladin? Na, $GJungchen:Mädel;, da habt Ihr Glück! Azar Hammerkraft ist zurück und hält sich momentan in der Brauerei Donnerbräu in Kharanos auf.', Text0_1='Ach... ein Paladin? Na, $GJungchen:Mädel;, da habt Ihr Glück! Azar Hammerkraft ist zurück und hält sich momentan in der Brauerei Donnerbräu in Kharanos auf.', Text1_0='Ihr braucht \'ne Ausbildung? Also, Azar Hammerkraft is\' immer dafür gut, jüngeren Paladinen was beizubringen. Warum geht Ihr nich\' mal zu ihm? Er is\' in der Brauerei Donnerbräu.', Text1_1='Ihr braucht \'ne Ausbildung? Also, Azar Hammerkraft is\' immer dafür gut, jüngeren Paladinen was beizubringen. Warum geht Ihr nich\' mal zu ihm? Er is\' in der Brauerei Donnerbräu.' WHERE id=4295;
UPDATE npc_text SET Text0_0='Soso, ein Priester, ja? Na, der gute alte Maxan ist garantiert auf einen Schluck in der Brauerei Donnerbräu. Ihr findet ihn bestimmt dort, wenn Ihr ein bisschen Unterweisung braucht.', Text0_1='Soso, ein Priester, ja? Na, der gute alte Maxan ist garantiert auf einen Schluck in der Brauerei Donnerbräu. Ihr findet ihn bestimmt dort, wenn Ihr ein bisschen Unterweisung braucht.', Text1_0='Den Maxan Anvol findet Ihr garantiert in der Brauerei Donnerbräu, wo er sich \'n paar genehmigt. Warum geht Ihr nich\' selber hin, trinkt \'n Schluck und seht zu, ob er Euch nich\' was Neues beibringen kann.', Text1_1='Den Maxan Anvol findet Ihr garantiert in der Brauerei Donnerbräu, wo er sich \'n paar genehmigt. Warum geht Ihr nich\' selber hin, trinkt \'n Schluck und seht zu, ob er Euch nich\' was Neues beibringen kann.' WHERE id=4296;
UPDATE npc_text SET Text0_0='Tja, dann scheint\'s, als wär\'n wir beide auf der Suche nach Hogral Bakkan. Als der waschechte Zwerg, der er is\', wird er früher oder später in der Brauerei Donnerbräu auftauchen, um dort \'n Bierchen zu trinken... vielleicht is\' er sogar schon dort.', Text0_1='Tja, dann scheint\'s, als wär\'n wir beide auf der Suche nach Hogral Bakkan. Als der waschechte Zwerg, der er is\', wird er früher oder später in der Brauerei Donnerbräu auftauchen, um dort \'n Bierchen zu trinken -- vielleicht is\' er sogar schon dort.', Text1_0='Ihr sucht nach Hogral? Un\', habt Ihr auch inner Taverne gekuckt? Hey! Wenn Ihr nach \'nem Schurken sucht, und noch dazu nach \'nem Zwerg, dann müsst Ihr doch im Gasthaus anfangen tun! Jetz\' geht schon und seht da nach.', Text1_1='Ihr sucht nach Hogral? Un\', habt Ihr auch inner Taverne gekuckt? Hey! Wenn Ihr nach \'nem Schurken sucht, und noch dazu nach \'nem Zwerg, dann müsst Ihr doch im Gasthaus anfangen tun! Jetz\' geht schon und seht da nach.' WHERE id=4297;
UPDATE npc_text SET Text0_0='Hexenmeister... Das Gesindel mag ich nicht. Wie diesen Grimrizz zum Beispiel, der vor der Brauerei Donnerbräu einen Laden aufgemacht hat... Wahrscheinlich wird das Bier schal, nur weil er da ist! Hexenmeister... igitt!', Text0_1='Hexenmeister... Das Gesindel mag ich nicht. Wie diesen Grimrizz zum Beispiel, der vor der Brauerei Donnerbräu einen Laden aufgemacht hat... Wahrscheinlich wird das Bier schal, nur weil er da ist! Hexenmeister... igitt!', Text1_0='Diese Art von Magie is\' ganz übel, das sag ich Euch. Ich hab keine Ahnung, warum die Grimrizz erlaubt ha\'m, sich ausgerechnet vor der Brauerei Donnerbräu einzunisten! Haltet Euch bloß von dem fern, sag ich Euch!', Text1_1='Diese Art von Magie is\' ganz übel, das sag ich Euch. Ich hab keine Ahnung, warum die Grimrizz erlaubt ha\'m, sich ausgerechnet vor der Brauerei Donnerbräu einzunisten! Haltet Euch bloß von dem fern, sag ich Euch!' WHERE id=4298;
UPDATE npc_text SET Text0_0='Hey! Jetzt redet Ihr, wie mir das gefallen tut! Ich wette, Granis flinkaxt kann Euch noch \'n paar Dinge beibringen, was das Rumwedeln mit Stahl angeht. Er ist grad in Kharanos in der Brauerei Donnerbräu.', Text0_1='Hey! Jetzt redet Ihr, wie mir das gefallen tut! Ich wette, Granis flinkaxt kann Euch noch \'n paar Dinge beibringen, was das Rumwedeln mit Stahl angeht. Er ist grad in Kharanos in der Brauerei Donnerbräu.', Text1_0='Oh ja, Granis Flinkaxt könnt \'n Fisch unter\'n Tisch saufen un\' mit seiner Axt immer noch das Ziel treffen. Aber wenn Ihr Euch ausbilden lassen wollt, dann solltet Ihr ihn vielleicht abpassen, solange man noch versteht, was er redet.', Text1_1='Oh ja, Granis Flinkaxt könnt \'n Fisch unter\'n Tisch saufen un\' mit seiner Axt immer noch das Ziel treffen. Aber wenn Ihr Euch ausbilden lassen wollt, dann solltet Ihr ihn vielleicht abpassen, solange man noch versteht, was er redet.' WHERE id=4299;
UPDATE npc_text SET Text0_0='Für welchen Beruf sucht Ihr denn einen Lehrer?', Text0_1='Für welchen Beruf sucht Ihr denn einen Lehrer?' WHERE id=4300;
UPDATE npc_text SET Text0_0='Nö, hier in Dun Morogh haben wir keine Alchemisten. Da solltet Ihr am besten mal in Eisenschmiede suchen... zum Beispiel bei den Gnomen in der Tüftlerstadt.', Text0_1='Nö, hier in Dun Morogh haben wir keine Alchemisten. Da solltet Ihr am besten mal in Eisenschmiede suchen... zum Beispiel bei den Gnomen in der Tüftlerstadt.', Text1_0='Ich habe gehört, oben in Eisenschmiede gibt es einen von Gnomen geführten Laden, der sich auf Alchemie spezialisiert hat. Dort solltet Ihr Euch mal erkundigen.', Text1_1='Ich habe gehört, oben in Eisenschmiede gibt es einen von Gnomen geführten Laden, der sich auf Alchemie spezialisiert hat. Dort solltet Ihr Euch mal erkundigen.' WHERE id=4301;
UPDATE npc_text SET Text0_0='Hey! Das ist doch mal eine nützliche Fertigkeit. Tognus bearbeitet den Amboss so heftig, wie man das sonst kaum je sieht. Warum geht Ihr nicht mal hin, redet mit ihm und seht, was er Euch beibringen kann?', Text0_1='Hey! Das ist doch mal eine nützliche Fertigkeit. Tognus bearbeitet den Amboss so heftig, wie man das sonst kaum je sieht. Warum geht Ihr nicht mal hin, redet mit ihm und seht, was er Euch beibringen kann?', Text1_0='Wenn Ihr nach Kharanos reinkommt, dann hört Ihr wahrscheinlich schon von weitem, wie Tognus auf sei\'m Amboss rumkloppt. Aber wenn Ihr Euch \'ne Weile nich\' die Ohr\'n geputzt habt, dann kuckt doch einfach mal inner Schmiede vorbei.', Text1_1='Wenn Ihr nach Kharanos reinkommt, dann hört Ihr wahrscheinlich schon von weitem, wie Tognus auf sei\'m Amboss rumkloppt. Aber wenn Ihr Euch \'ne Weile nich\' die Ohr\'n geputzt habt, dann kuckt doch einfach mal inner Schmiede vorbei.' WHERE id=4302;
UPDATE npc_text SET Text0_0='\'n Donnerbier und \'n Happen von Gremlocks Essen geben echt \'ne prächtige Mahlzeit ab. Ihr solltet ihn mal ansprechen, ob er Euch ausbildet, wenn Ihr $G\'n Koch:\'ne Köchin; werden wollt.', Text0_1='\'n Donnerbier und \'n Happen von Gremlocks Essen geben echt \'ne prächtige Mahlzeit ab. Ihr solltet ihn mal ansprechen, ob er Euch ausbildet, wenn Ihr $G\'n Koch:\'ne Köchin; werden wollt.', Text1_0='Gremlock hat beim Kochen zwar nich\' immer das allerglücklichste Händchen, aber er hat doch so ein, zwei leckere Rezepte auf Lager, die\'s sich zu lernen lohnt. Redet doch mal mit ihm, er is\' in der Brauerei Donnerbräu, und lasst Euch was von ihm beibringen.', Text1_1='Gremlock hat beim Kochen zwar nich\' immer das allerglücklichste Händchen, aber er hat doch so ein, zwei leckere Rezepte auf Lager, die\'s sich zu lernen lohnt. Redet doch mal mit ihm, er is\' in der Brauerei Donnerbräu, und lasst Euch was von ihm beibringen.' WHERE id=4303;
UPDATE npc_text SET Text0_0='Hier draußen in Dun Morogh gibt\'s keine Verzauberer, jedenfalls nich\', dass ich was davon wissen tät. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr von \'nem Verzauberer den Beruf lernen wollt.', Text0_1='Hier draußen in Dun Morogh gibt\'s keine Verzauberer, jedenfalls nich\', dass ich was davon wissen tät. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr von \'nem Verzauberer den Beruf lernen wollt.', Text1_0='Ihr solltet Euch mal in Eisensschmiede umhör\'n, wenn Ihr unbedingt das Verzaubern lernen wollt. Ich glaub, ich hab in der Nähe vom Greifenmeister so\'n Laden geseh\'n, als ich das letzte Mal da oben war.', Text1_1='Ihr solltet Euch mal in Eisensschmiede umhör\'n, wenn Ihr unbedingt das Verzaubern lernen wollt. Ich glaub, ich hab in der Nähe vom Greifenmeister so\'n Laden geseh\'n, als ich das letzte Mal da oben war.' WHERE id=4304;
UPDATE npc_text SET Text0_0='Ihr könntet drüben in Stahlrosts Depot mal nach Bronk fragen. Wenn er nich\' an irgend \'nem komischen Gerät rumbastelt, dann quatscht er meistens mit den Piloten über denen ihre Dampfpanzer.', Text0_1='Ihr könntet drüben in Stahlrosts Depot mal nach Bronk fragen. Wenn er nich\' an irgend \'nem komischen Gerät rumbastelt, dann quatscht er meistens mit den Piloten über denen ihre Dampfpanzer.', Text1_0='Bei all den Schrauben, die Bronk in sei\'m Leben so festgezogen hat, sin\' bei ihm selber ein paar locker geworden, wenn Ihr versteht, was ich meine. Aber er hat trotzdem noch\'n ganz guten Kopf für die Ingenieurskunst - der baut echt die verrücktesten Dinger! Ihr solltet ihm ruhig mal\'n Besuch abstatten, drüben in Stahlrosts Depot.', Text1_1='Bei all den Schrauben, die Bronk in sei\'m Leben so festgezogen hat, sin\' bei ihm selber ein paar locker geworden, wenn Ihr versteht, was ich meine. Aber er hat trotzdem noch\'n ganz guten Kopf für die Ingenieurskunst - der baut echt die verrücktesten Dinger! Ihr solltet ihm ruhig mal\'n Besuch abstatten, drüben in Stahlrosts Depot.' WHERE id=4305;
UPDATE npc_text SET Text0_0='Erste Hilfe is\' zwar vielleicht nich\' so wirksam wie die Heilung durch \'n Priester, aber sie kann ganz schön nützlich sein! Ich würd vorschlagen, Ihr redet mal mit Thamner in der Brauerei Donnerbräu und lernt von ihm, was Ihr könnt.', Text0_1='Erste Hilfe is\' zwar vielleicht nich\' so wirksam wie die Heilung durch \'n Priester, aber sie kann ganz schön nützlich sein! Ich würd vorschlagen, Ihr redet mal mit Thamner in der Brauerei Donnerbräu und lernt von ihm, was Ihr könnt.', Text1_0='Der gute alte Thamner - hat mich grad vor\'n paar Tagen zusammengeflickt, als ich \'n Zusammenstoß mit so \'nem widerlichen Trogg hatte. Un\' ich bin wieder ganz wie neu, sag ich Euch! Ich wette, Thamner würd Euch gern zeigen, wie man gescheit \'n Verband anlegt.', Text1_1='Der gute alte Thamner - hat mich grad vor\'n paar Tagen zusammengeflickt, als ich \'n Zusammenstoß mit so \'nem widerlichen Trogg hatte. Un\' ich bin wieder ganz wie neu, sag ich Euch! Ich wette, Thamner würd Euch gern zeigen, wie man gescheit \'n Verband anlegt.' WHERE id=4306;
UPDATE npc_text SET Text0_0='Hier gibt\'s nich\' viel zu angeln, aber das bisschen, was es zu angeln gibt, das schnappt sich Paxton, darauf könnt Ihr Gift nehmen. Ihr könnt ihn draußen am Eiswellensee finden, wo er angeln tut.', Text0_1='Hier gibt\'s nich\' viel zu angeln, aber das bisschen, was es zu angeln gibt, das schnappt sich Paxton, darauf könnt Ihr Gift nehmen. Ihr könnt ihn draußen am Eiswellensee finden, wo er angeln tut.', Text1_0='Hier gibt\'s nur\'n paar Orte, wo man gescheit angeln kann. Paxton kennt sie alle, und dazu garantiert noch \'n paar, die er Euch nich\' verraten tut. Warum geht Ihr nich\' mal zum Eiswellensee und hört Euch an, was er so erzählt?', Text1_1='Hier gibt\'s nur\'n paar Orte, wo man gescheit angeln kann. Paxton kennt sie alle, und dazu garantiert noch \'n paar, die er Euch nich\' verraten tut. Warum geht Ihr nich\' mal zum Eiswellensee und hört Euch an, was er so erzählt?' WHERE id=4307;
UPDATE npc_text SET Text0_0='Schon erstaunlich zu sehen, dass selbst in der Kälte von Dun Morogh \'n paar kleine Blümchen überleben, aber hier draußen werdet Ihr keine Kräuterkundigen finden. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr Euch in dem Beruf ausbilden lassen wollt.', Text0_1='Schon erstaunlich zu sehen, dass selbst in der Kälte von Dun Morogh \'n paar kleine Blümchen überleben, aber hier draußen werdet Ihr keine Kräuterkundigen finden. Da müsst Ihr schon nach Eisenschmiede, wenn Ihr Euch in dem Beruf ausbilden lassen wollt.', Text1_0='Nööö. Hier draußen kann Euch keiner Kräuterkunde beibringen. Aber ich glaub, in Eisenschmiede habt Ihr mehr Glück.', Text1_1='Nööö. Hier draußen kann Euch keiner Kräuterkunde beibringen. Aber ich glaub, in Eisenschmiede habt Ihr mehr Glück.' WHERE id=4308;
UPDATE npc_text SET Text0_0='Wenn Ihr die Lederverarbeitung lernen wollt, dann müsst Ihr wohl nach Eisenschmiede. Ich hab gehört, in der Nähe der großen Schmiede gibt\'s \'n netten Laden, wo Ihr den Beruf erlernen könnt.', Text0_1='Wenn Ihr die Lederverarbeitung lernen wollt, dann müsst Ihr wohl nach Eisenschmiede. Ich hab gehört, in der Nähe der großen Schmiede gibt\'s \'n netten Laden, wo Ihr den Beruf erlernen könnt.', Text1_0='Was die Lederverarbeitung angeht, habt Ihr in Eisenschmiede die besten Aussichten. Wenn ich mich richtig erinner, gibt\'s bei der großen Schmiede \'n Laden, wo Ihr finden solltet, was Ihr sucht.', Text1_1='Was die Lederverarbeitung angeht, habt Ihr in Eisenschmiede die besten Aussichten. Wenn ich mich richtig erinner, gibt\'s bei der großen Schmiede \'n Laden, wo Ihr finden solltet, was Ihr sucht.' WHERE id=4310;
UPDATE npc_text SET Text0_0='Ihr habt doch wohl nich\' geglaubt, Ihr würdet irgendwo \'ne Stadt ohne eigenen Minenarbeiter finden, oder? Also wenn Ihr Yarr sucht: Den findet man normalerweise drüben bei Stahlrosts Depot ein Stückchen außerhalb von Kharanos.', Text0_1='Ihr habt doch wohl nich\' geglaubt, Ihr würdet irgendwo \'ne Stadt ohne eigenen Minenarbeiter finden, oder? Also wenn Ihr Yarr sucht: Den findet man normalerweise drüben bei Stahlrosts Depot ein Stückchen außerhalb von Kharanos.', Text1_0='Ein Minenarbeiter? Ihr braucht nich\' fragen, ob es einen gibt, $GJunge:Mädel;, bloß wo! Yann is\' der, mit dem Ihr reden müsst, und den findet Ihr, glaub ich, in Stahlrosts Depot, wo er sich verstecken tut.', Text1_1='Ein Minenarbeiter? Ihr braucht nich\' fragen, ob es einen gibt, $GJunge:Mädel;, bloß wo! Yann is\' der, mit dem Ihr reden müsst, und den findet Ihr, glaub ich, in Stahlrosts Depot, wo er sich verstecken tut.' WHERE id=4311;
UPDATE npc_text SET Text0_0='Hier draußen in Dun Morogh werd\' Ihr kein\' Kürschner finden, $GJungchen:Mädel;, da müsst Ihr schon in Eisenschmiede oder so suchen, wenn Ihr Euch ausbilden lassen wollt.', Text0_1='Hier draußen in Dun Morogh werd\' Ihr kein\' Kürschner finden, $GJungchen:Mädel;, da müsst Ihr schon in Eisenschmiede oder so suchen, wenn Ihr Euch ausbilden lassen wollt.', Text1_0='Normalerweise bringen die Kürschner all ihre Bälge nach Eisenschmiede, um sie dort trocknen zu lassen. Warum begebt Ihr Euch nich\' dorthin und seht zu, ob Ihr dort nich\' jemanden findet, der Euch ausbildet?', Text1_1='Normalerweise bringen die Kürschner all ihre Bälge nach Eisenschmiede, um sie dort trocknen zu lassen. Warum begebt Ihr Euch nich\' dorthin und seht zu, ob Ihr dort nich\' jemanden findet, der Euch ausbildet?' WHERE id=4312;
UPDATE npc_text SET Text0_0='Oh, dieser Tage verlässt Steinbraue Eisenschmiede nich\' mehr sehr oft. Wenn Ihr also den Beruf des Schneiders lernen wollt, dann müsst Ihr ihm wohl in Eisenschmiede \'n Besuch abstatten.', Text0_1='Oh, dieser Tage verlässt Steinbraue Eisenschmiede nich\' mehr sehr oft. Wenn Ihr also den Beruf des Schneiders lernen wollt, dann müsst Ihr ihm wohl in Eisenschmiede \'n Besuch abstatten.', Text1_0='Na, da werdet Ihr nich\' viel Glück haben, wenn Ihr hier draußen \'n Schneider sucht. Aber in Eisenschmiede gibt\'s ne ganze Menge guter Schneider. Warum fragt Ihr dort nicht mal \'ne Wache.', Text1_1='Na, da werdet Ihr nich\' viel Glück haben, wenn Ihr hier draußen \'n Schneider sucht. Aber in Eisenschmiede gibt\'s ne ganze Menge guter Schneider. Warum fragt Ihr dort nicht mal \'ne Wache.' WHERE id=4313;
UPDATE npc_text SET Text0_0='Es gibt viele Orte, die Ihr in Teldrassil besuchen könnt. Sagt mir, was Ihr sucht. Was ist es?', Text0_1='Es gibt viele Orte, die Ihr in Teldrassil besuchen könnt. Sagt mir, was Ihr sucht. Was ist es?' WHERE id=4316;
UPDATE npc_text SET Text0_0='Die Bank befindet sich in der friedlichen Stadt Darnassus. Dorthin müsst Ihr Euch begeben, wenn Ihr Eure Besitztümer einlagern wollt.', Text0_1='Die Bank befindet sich in der friedlichen Stadt Darnassus. Dorthin müsst Ihr Euch begeben, wenn Ihr Eure Besitztümer einlagern wollt.', Text1_0='Wenn Ihr eine Bank sucht, so führen Euch Eure Wege in das Herz von Darnassus. Dort werdet Ihr einen Aufbewahrungsort für Eure Schätze finden.', Text1_1='Wenn Ihr eine Bank sucht, so führen Euch Eure Wege in das Herz von Darnassus. Dort werdet Ihr einen Aufbewahrungsort für Eure Schätze finden.' WHERE id=4317;
UPDATE npc_text SET Text0_0='Um zur Fähre von Rut\'theran zu kommen, müsst Ihr nach Darnassus reisen und dort das Portal nach Rut\'theran am Fuße von Teldrassil nehmen.', Text0_1='Um zur Fähre von Rut\'theran zu kommen, müsst Ihr nach Darnassus reisen und dort das Portal nach Rut\'theran am Fuße von Teldrassil nehmen.', Text1_0='Eine Fähre verlässt Rut\'theran, das am Fuß von Teldrassil liegt, in Richtung Auberdine. Wenn Ihr das Portal in Darnassus benutzt, kommt Ihr sicher nach Rut\'theran.', Text1_1='Eine Fähre verlässt Rut\'theran, das am Fuß von Teldrassil liegt, in Richtung Auberdine. Wenn Ihr das Portal in Darnassus benutzt, kommt Ihr sicher nach Rut\'theran.' WHERE id=4318;
UPDATE npc_text SET Text0_0='Solch offizielle Angelegenheiten werden nur in Darnassus abgewickelt. Ihr müsst dorthin reisen und den Prozess der Gründung Eurer Gilde dort beginnen.', Text0_1='Solch offizielle Angelegenheiten werden nur in Darnassus abgewickelt. Ihr müsst dorthin reisen und den Prozess der Gründung Eurer Gilde dort beginnen.', Text1_0='Dokumente wie die Papiere zur Gildengründung erhaltet Ihr beim Gildenmeister in Darnassus.', Text1_1='Dokumente wie die Papiere zur Gildengründung erhaltet Ihr beim Gildenmeister in Darnassus.' WHERE id=4319;
UPDATE npc_text SET Text0_0='In den Betten, die Gastwirt Keldamyr im Gasthaus von Dolanaar anbietet, findet jeder Reisende geruhsamen Schlaf.', Text0_1='In den Betten, die Gastwirt Keldamyr im Gasthaus von Dolanaar anbietet, findet jeder Reisende geruhsamen Schlaf.', Text1_0='Wenn Ihr etwas Zeit benötigt, um Euch auszuruhen und wieder zu Kräften zu kommen, wird im Gasthaus von Dolanaar bestens für Euch gesorgt. Gastwirt Keldamyr wird sich persönlich darum kümmern, dass es Euch an nichts mangelt.', Text1_1='Wenn Ihr etwas Zeit benötigt, um Euch auszuruhen und wieder zu Kräften zu kommen, wird im Gasthaus von Dolanaar bestens für Euch gesorgt. Gastwirt Keldamyr wird sich persönlich darum kümmern, dass es Euch an nichts mangelt.' WHERE id=4320;
UPDATE npc_text SET Text0_0='In und um Dolanaar findet Ihr zahlreiche Lehrer, die über einen umfassenden Erfahrungsschatz verfügen, an dem sie Euch teilhaben lassen können. Welcher Lehrer soll es sein?', Text0_1='In und um Dolanaar findet Ihr zahlreiche Lehrer, die über einen umfassenden Erfahrungsschatz verfügen, an dem sie Euch teilhaben lassen können. Welcher Lehrer soll es sein?' WHERE id=4322;
UPDATE npc_text SET Text0_0='Ihr wünscht die Hilfe eines Druiden? Dann solltet Ihr Kal am Mondbrunnen aufsuchen, ein kleines Stückchen südlich vom Gasthaus in Dolanaar.', Text0_1='Ihr wünscht die Hilfe eines Druiden? Dann solltet Ihr Kal am Mondbrunnen aufsuchen, ein kleines Stückchen südlich vom Gasthaus in Dolanaar.', Text1_0='Der Druide, Kal heißt er, verbringt seine Zeit meist am Mondbrunnen in Dolanaar. Ich bin sicher, er kann Euch die Ausbildung geben, die Ihr benötigt.', Text1_1='Der Druide, Kal heißt er, verbringt seine Zeit meist am Mondbrunnen in Dolanaar. Ich bin sicher, er kann Euch die Ausbildung geben, die Ihr benötigt.' WHERE id=4323;
UPDATE npc_text SET Text0_0='Wenn Ihr einen Jäger sucht, dann solltet Ihr Dazalar einen Besuch abstatten. Meist findet man ihn vor dem Gasthaus in Dolanaar.', Text0_1='Wenn Ihr einen Jäger sucht, dann solltet Ihr Dazalar einen Besuch abstatten. Meist findet man ihn vor dem Gasthaus in Dolanaar.', Text1_0='Dazalar ist in ganz Dolanaar und Umgebung für seine Jagdkunst bekannt. Warum redet Ihr nicht einmal mit ihm und seht, was er Euch beibringen kann?', Text1_1='Dazalar ist in ganz Dolanaar und Umgebung für seine Jagdkunst bekannt. Warum redet Ihr nicht einmal mit ihm und seht, was er Euch beibringen kann?' WHERE id=4324;
UPDATE npc_text SET Text0_0='In Dolanaar ist eine Priesterin namens Laurna Morgenglanz zuhause. Ich glaube, sie ist gleich nördlich vom Gasthaus zu finden.', Text0_1='In Dolanaar ist eine Priesterin namens Laurna Morgenglanz zuhause. Ich glaube, sie ist gleich nördlich vom Gasthaus zu finden.', Text1_0='Laurna Morgenglanz bietet allen, die in und um Dolanaar herum wohnen, ihre Heilkünste an. Vielleicht kann sie Euch ja $Gzum Priester:zur Priesterin; ausbilden.', Text1_1='Laurna Morgenglanz bietet allen, die in und um Dolanaar herum wohnen, ihre Heilkünste an. Vielleicht kann sie Euch ja $Gzum Priester:zur Priesterin; ausbilden.' WHERE id=4325;
UPDATE npc_text SET Text0_0='Ein Schurke? Gewöhnlich schicke ich ja keine Leute zu solchen Typen, aber ich glaube, Jannok ist der, nach dem Ihr sucht. Ihr findet ihn im Gasthaus in Dolanaar.', Text0_1='Ein Schurke? Gewöhnlich schicke ich ja keine Leute zu solchen Typen, aber ich glaube, Jannok ist der, nach dem Ihr sucht. Ihr findet ihn im Gasthaus in Dolanaar.', Text1_0='Ihr sucht einen Schurken? Ich denke, auf Jannok passt diese Beschreibung recht gut. Warum geht Ihr nicht rüber zum Gasthaus von Dolanaar und redet mit ihm?', Text1_1='Ihr sucht einen Schurken? Ich denke, auf Jannok passt diese Beschreibung recht gut. Warum geht Ihr nicht rüber zum Gasthaus von Dolanaar und redet mit ihm?' WHERE id=4326;
UPDATE npc_text SET Text0_0='Kyra Windklinge weiß die Klinge sehr gewandt zu führen. Sie sollte Euch einigen Unterricht erteilen können. Ihr findet sie im Gasthaus in Dolanaar.', Text0_1='Kyra Windklinge weiß die Klinge sehr gewandt zu führen. Sie sollte Euch einigen Unterricht erteilen können. Ihr findet sie im Gasthaus in Dolanaar.', Text1_0='Ich hatte einmal das Vergnügen, Kyra Windklinge kämpfen zu sehen. Wenn ich daran denke, was ich da gesehen habe, bin ich fest davon überzeugt, dass sie Euch eine gute Ausbildung angedeihen lassen wird. Ihr findet sie im Gasthaus in Dolanaar.', Text1_1='Ich hatte einmal das Vergnügen, Kyra Windklinge kämpfen zu sehen. Wenn ich daran denke, was ich da gesehen habe, bin ich fest davon überzeugt, dass sie Euch eine gute Ausbildung angedeihen lassen wird. Ihr findet sie im Gasthaus in Dolanaar.' WHERE id=4327;
UPDATE npc_text SET Text0_0='Der Weg derer, die einen Beruf erlernen wollen, ist lang, doch er lohnt sich. Welchen Weg möchtet Ihr einschlagen?', Text0_1='Der Weg derer, die einen Beruf erlernen wollen, ist lang, doch er lohnt sich. Welchen Weg möchtet Ihr einschlagen?' WHERE id=4328;
UPDATE npc_text SET Text0_0='Cyndra ist zwar nicht so übersprudelnd wie ihre Tränke manchmal, aber sie ist lieb und freundlich, und das macht sie zu einer guten Lehrerin ihres Handwerks. Ihr findet sie ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.', Text0_1='Cyndra ist zwar nicht so übersprudelnd wie ihre Tränke manchmal, aber sie ist lieb und freundlich, und das macht sie zu einer guten Lehrerin ihres Handwerks. Ihr findet sie ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.', Text1_0='Cyndras Wissen in der Alchemie ist nicht umfassend, doch ich bin sicher, es reicht aus, damit Ihr eine Grundausstattung bekommt. Sie übt ihren Beruf ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar aus.', Text1_1='Cyndras Wissen in der Alchemie ist nicht umfassend, doch ich bin sicher, es reicht aus, damit Ihr eine Grundausstattung bekommt. Sie übt ihren Beruf ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar aus.' WHERE id=4329;
UPDATE npc_text SET Text0_0='Zarrin scheint über ein recht umfangreiches Wissen über die Kochkunst zu verfügen. Warum fragt Ihr ihn nicht einmal, ob er Euch einige seiner Rezepte verrät? Für gewöhnlich kocht er im Gasthaus in Dolanaar.', Text0_1='Zarrin scheint über ein recht umfangreiches Wissen über die Kochkunst zu verfügen. Warum fragt Ihr ihn nicht einmal, ob er Euch einige seiner Rezepte verrät? Für gewöhnlich kocht er im Gasthaus in Dolanaar.', Text1_0='Wir könnten durchaus noch einen weiteren Koch hier in der Gegend um Dolanaar gebrauchen - nicht, dass Zarrin ein schlechter Koch wäre, versteht sich. Warum erkundigt Ihr Euch nicht mal, was er Euch beibringen kann?', Text1_1='Wir könnten durchaus noch einen weiteren Koch hier in der Gegend um Dolanaar gebrauchen - nicht, dass Zarrin ein schlechter Koch wäre, versteht sich. Warum erkundigt Ihr Euch nicht mal, was er Euch beibringen kann?' WHERE id=4330;
UPDATE npc_text SET Text0_0='Alanna Rabenauge schreibt einen Teil ihrer Begabung zum Verzaubern ihrer Verbundenheit mit den Geistern in der Lichtung des Orakels zu. Ihr solltet sie dort jetzt antreffen können.', Text1_0='Alanna verbringt die meiste Zeit in der Lichtung des Orakels und übt dort ihren Beruf aus. Solltet Ihr den Beruf des Verzauberers lernen wollen, dann müsst Ihr Euch dorthin begeben, wenn Ihr sie finden wollt.' WHERE id=4331;
UPDATE npc_text SET Text0_0='Byancie hat den Schildwachen, die hier in der Gegend durch die Wildnis patrouillieren, schon oft mit ihren Diensten helfen können. Ihr solltet mit ihr sprechen, wenn Ihr erste Hilfe erlernen möchtet.', Text0_1='Byancie hat den Schildwachen, die hier in der Gegend durch die Wildnis patrouillieren, schon oft mit ihren Diensten helfen können. Ihr solltet mit ihr sprechen, wenn Ihr erste Hilfe erlernen möchtet.', Text1_0='Schon oft hat sich Byancies Fertigkeit in der ersten Hilfe als äußerst nützlich erwiesen. Ihr findet sie ein kleines Stückchen nördlich vom Gasthaus in Dolanaar, falls Ihr von ihr lernen möchtet.', Text1_1='Schon oft hat sich Byancies Fertigkeit in der ersten Hilfe als äußerst nützlich erwiesen. Ihr findet sie ein kleines Stückchen nördlich vom Gasthaus in Dolanaar, falls Ihr von ihr lernen möchtet.' WHERE id=4332;
UPDATE npc_text SET Text0_0='Alle Angler sind wegen der jüngsten Angriffe am Al\'Amethsee nach Darnassus gezogen. Ihr solltet versuchen, dort jemanden zu finden, der Euch ausbildet.', Text1_0='Durch die Bäumlinge ist es für die Angler zu schwierig geworden, in der Gegend um Dolanaar irgendetwas zu fangen. Daher solltet Ihr Euch in Darnassus jemanden suchen, der Euch ausbildet.' WHERE id=4333;
UPDATE npc_text SET Text0_0='Irgendwie schafft Malorne es, auch die empfindlichsten Kräuter zu pflücken, ohne sie zu beschädigen. Er ist mit Sicherheit derjenige, von dem Ihr lernen solltet. Er lebt ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.', Text0_1='Irgendwie schafft Malorne es, auch die empfindlichsten Kräuter zu pflücken, ohne sie zu beschädigen. Er ist mit Sicherheit derjenige, von dem Ihr lernen solltet. Er lebt ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.', Text1_0='Die Kräuterkunde erfordert eine Behutsamkeit, die nur jemand wie Malorne Euch beibringen kann. Sucht ihn auf: Er lebt ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.', Text1_1='Die Kräuterkunde erfordert eine Behutsamkeit, die nur jemand wie Malorne Euch beibringen kann. Sucht ihn auf: Er lebt ein kleines Stückchen ostwärts vom Gasthaus in Dolanaar.' WHERE id=4334;
UPDATE npc_text SET Text0_0='Die Schopfweber üben ihren Beruf in ihrem Haus an der Straße nach Darnassus aus. Nadyia ist eine ausgezeichnete Lehrerin in Sachen Lederverarbeitung. Ihr solltet wirklich einmal sehen, was sie Euch beibringen kann.', Text0_1='Die Schopfweber üben ihren Beruf in ihrem Haus an der Straße nach Darnassus aus. Nadyia ist eine ausgezeichnete Lehrerin in Sachen Lederverarbeitung. Ihr solltet wirklich einmal sehen, was sie Euch beibringen kann.', Text1_0='Schon viele Jahre geht Nadyia dem Beruf des Lederers nach und sie hat es darin zu erstaunlicher Kunstfertigkeit gebracht. Reist auf der Straße nach Darnassus gen Westen und besucht sie - vielleicht kann sie Euch ja etwas von ihrem Wissen vermitteln.', Text1_1='Schon viele Jahre geht Nadyia dem Beruf des Lederers nach und sie hat es darin zu erstaunlicher Kunstfertigkeit gebracht. Reist auf der Straße nach Darnassus gen Westen und besucht sie - vielleicht kann sie Euch ja etwas von ihrem Wissen vermitteln.' WHERE id=4335;
UPDATE npc_text SET Text0_0='Radnaal findet Ihr vor seinem Haus an der Straße nach Darnassus, wo er seinen Beruf ausübt. Ihr solltet ruhig bei ihm vorbeischauen und sehen, was er Euch beibringen kann.', Text0_1='Radnaal findet Ihr vor seinem Haus an der Straße nach Darnassus, wo er seinen Beruf ausübt. Ihr solltet ruhig bei ihm vorbeischauen und sehen, was er Euch beibringen kann.' WHERE id=4336;
UPDATE npc_text SET Text0_0='Ich bin sicher, dass es in Darnassus einen Schneider gibt, der Euch ausbilden kann. Wenn Ihr dort ankommt, fragt eine der Schildwachen von Darnassus, wo Ihr einen Schneider finden könnt.', Text1_0='Viele der Materialien, die ein Schneider brauchen kann, kommen aus Darnassus. Also ist es kein Wunder, dass sie alle dorthin gezogen sind. Wenn Ihr $GSchneider:Schneiderin werden möchtet, dann solltet Ihr am besten nach Darnassus gehen.' WHERE id=4337;
UPDATE npc_text SET Text0_0='Hallo, $C. Ich habe hier am Bollwerk mein Lager aufgeschlagen, um mich auf meine Reise in die Pestländer vorzubereiten. Ich habe Gerüchte gehört, denen zufolge sich vor nicht allzu langer Zeit ein großer Hexer, ein Mensch übrigens, in Herdweiler niedergelassen haben soll. Er sei vom Scharlachroten Kreuzzug beauftragt worden, für deren Krieger und Jäger neue Bögen und Schusswaffen herzustellen. Es heißt, dass er eine erstaunliche Waffe konstruiert habe, ehe der Kreuzzug ihn tötete.$B$BIch vermute mal, ihr Misstrauen gegenüber dem Arkanen ist gewachsen...' WHERE id=4353;
UPDATE npc_text SET Text0_1='Seid gegrüßt, $C. Hab\' nicht viel Zeit zum Reden - ich lagere hier in Zugwind nur kurz, um mich ein bisschen auszuruhen, ehe ich nach Herdweiler aufbreche. Ich habe gehört, dass, bevor sie alle verrückt wurden, die Leute vom Scharlachroten Kreuzzug einen Magier auf ihrer Lohnliste hatten, der den Auftrag hatte, für ihre Soldaten mächtige Bögen und Schusswaffen herzustellen. Ich weiß nicht, ob er damit Erfolg hatte oder nicht... Der Kreuzzug tötete ihn, bevor auch nur eines seiner Produkte den Weg ans Tageslicht fand. Vielleicht brachten sie ihn ja um, um das Geheimnis zu bewahren - oder sie misstrauen dem Arkanen wirklich so sehr.' WHERE id=4354;
UPDATE npc_text SET Text0_1='Ha... hallo, $N. Es tut mir leid - ich muss mich noch von meinen Wunden erholen.$B$BDiese Scharlachroten Kreuzzügler sind härtere Gegner, als ich gedacht hätte. Die haben mich beinahe fertig gemacht.$B$BIhr solltet ebenfalls vorsichtig sein. Es würde mir gar nicht gefallen, wenn die Euch überrumpeln würden.' WHERE id=4355;
UPDATE npc_text SET Text0_1='Meine Familie arbeitete früher im Sägewerk des Nordkamms - das liegt in dem Gebiet, wo heute die westlichen Pestländer sind. Sie arbeiteten für die Familie Dumah, aber jetzt hat der Scharlachrote Kreuzzug das Sägewerk zusammen mit dem Rest von Herdweiler einfach übernommen!$B$BIch werde nicht ruhen noch rasten, ehe diese Eiferer nicht von Azeroths Angesicht getilgt worden sind.' WHERE id=4356;
UPDATE npc_text SET Text0_0='Vor langer Zeit, vor dem letzten großen Krieg, führte mein Vater das Sägewerk des Nordkamms. Das Sägewerk war sein ganzes Leben, es war sein Ein und Alles. Doch dann kam der Krieg, und so sehr ihn das auch schmerzte, wir mussten nach Sturmwind flüchten.$B$BIn jenen Tagen verloren wir eine ganze Reihe von Freunden und Verwandten, und es schmerzt mich zu wissen, dass der Scharlachrote Kreuzzug das Sägewerk für seine eigenen Zwecke mit Beschlag belegt hat.$B$BDeswegen bin ich zurückgekommen.' WHERE id=4357;
UPDATE npc_text SET Text0_0='Wir sind die letzten Angehörigen des Volks der Furbolgs... Noch ist der Stamm der Holzschlundfeste zwar nicht verderbt, doch von Fremden schlägt uns dieselbe Feindseligkeit entgegen wie den anderen. Die meisten Leute glauben, es sei gar nicht möglich, dass es Furbolgs geben könnte, die der Verderbnis entgangen sind...$B$BIn ihrer Wut auf all diejenigen, die sich übereilt Feinde machen, greifen meine Brüder jetzt alle an, die sich ihnen nähern. Sie trauen niemandem mehr.$B$BAber wenn Ihr Euch ihnen gegenüber bewusst friedlich verhaltet, dann sehen sie Euch vielleicht als Verbündeten an.' WHERE id=4393;
UPDATE npc_text SET Text0_0='Ihr müsst einen Weg finden, wie Ihr ihnen zeigen könnt, dass Ihr auf derselben Seite kämpft wie sie. Selbst wenn die Furbolgs der Holzschlundfeste Euch jetzt vielleicht noch angreifen, werdet Ihr bestimmt einen Weg finden, wenn Ihr Euch bemüht.$B$BIhr dürft aber nicht vergessen, dass es Eure Chancen, die Furbolgs der Holzschlundfeste als Verbündete zu gewinnen, massiv einschränkt, wenn Ihr sie direkt angreift.$B$BEin guter Anfang wäre es, sich mit den Furbolgs der Totenwaldfelle auseinander zu setzen, die vielerorts im Teufelswald zu finden sind. Diese Furbolgs sind die Feinde des Stammes der Holzschlundfeste, und wenn Ihr die tötet, dann würde das Eure Stellung bei den Furbolgs der Holzschlundfeste stark verbessern.' WHERE id=4394;
UPDATE npc_text SET Text0_0='Wir von der Holzschlundfeste können Euch zwar die unbehelligte Durchreise durch unser Land gewähren, aber zuerst müsst Ihr Euch als unserer Hilfe würdig erweisen.' WHERE id=4395;
UPDATE npc_text SET Text0_0='Die Furbolgs der Winterfelle, die hier in Winterquell leben, waren einst unsere Verbündeten... doch eines Tages änderte sich das.$B$BGetrieben von den verqueren Forderungen ihres Oberhäuptlings begannen sie, alle anzugreifen, mit denen sie in Berührung kamen, einschließlich unseres eigenen Stammes. Jetzt bleiben sie unter sich...' WHERE id=4396;
UPDATE npc_text SET Text0_0='Ich traue nur sehr wenigen Eurer Art, $N. Aber ich kann sehen, dass Ihr $Gein Freund:eine Freundin; seid.' WHERE id=4398;
UPDATE npc_text SET Text0_0='Ja, ja. Ich sehe Euch.$B$BMacht Euch auf den Weg. Ihr habt vielleicht das Vertrauen von ein paar von uns erworben, aber es gehört mehr dazu als das, um Euch mir zu beweisen!' WHERE id=4399;
UPDATE npc_text SET Text0_0='Wenn Ihr beweist, dass Ihr $Gein echter Verbündeter:eine echte Verbündete; seid, dann ziehe ich vielleicht in Erwägung, Euch auszubilden.' WHERE id=4401;
UPDATE npc_text SET Text0_0='Ich nehme an, das Licht ist mit Euch, $N. Kann ich Euch bei irgendetwas helfen?', Text0_1='Ich nehme an, das Licht ist mit Euch, $N. Kann ich Euch bei irgendetwas helfen?' WHERE id=4433;
UPDATE npc_text SET Text0_0='Wendet Euch nicht vom Licht ab, $C. Es könnte eines Tages das Einzige sein, was Euch rettet.', Text0_1='Wendet Euch nicht vom Licht ab, $C. Es könnte eines Tages das Einzige sein, was Euch rettet.' WHERE id=4434;
UPDATE npc_text SET Text0_0='Das Licht möge Euch beschützen, $C.', Text0_1='Das Licht möge Euch beschützen, $C.' WHERE id=4435;
UPDATE npc_text SET Text0_0='Ich hoffe, das Licht ist mit Euch, $N. Gibt es irgendetwas, das ich für Euch tun kann?', Text0_1='Ich hoffe, das Licht ist mit Euch, $N. Gibt es irgendetwas, das ich für Euch tun kann?' WHERE id=4436;
UPDATE npc_text SET Text0_0='Elune sei mit Euch, $C.', Text0_1='Elune sei mit Euch, $C.' WHERE id=4437;
UPDATE npc_text SET Text0_0='Mein Wissen und meine Kenntnisse gehören Euch. Was kann ich für Euch tun, $N?', Text0_1='Mein Wissen und meine Kenntnisse gehören Euch. Was kann ich für Euch tun, $N?' WHERE id=4438;
UPDATE npc_text SET Text0_0='Ja, $C?', Text0_1='Ja, $C?' WHERE id=4439;
UPDATE npc_text SET Text0_0='Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!', Text0_1='Passt bloß auf, $C. Achtet unsere Bräuche, sonst möge Euch und Eure ganze Familie ein Fluch treffen!' WHERE id=4440;
UPDATE npc_text SET Text0_0='Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.', Text0_1='Ich tu, was ich kann, um Euch was über den Weg des Priesters zu erzählen, wenn\'s das ist, warum Ihr hier seid.' WHERE id=4441;
UPDATE npc_text SET Text0_0='Was kann dieser unbedeutende Diener für Euch tun, $N? Euch Unterricht geben, vielleicht?', Text0_1='Was kann dieser unbedeutende Diener für Euch tun, $N? Euch Unterricht geben, vielleicht?' WHERE id=4442;
UPDATE npc_text SET Text0_0='Ich bin auf der Suche nach wem, $C. Also lasst mich doch bitte in Ruh - es is\' wirklich wichtig, dass ich den find.' WHERE id=4443;
UPDATE npc_text SET Text0_0='Seid still! Seht Ihr denn nicht, dass der Meister spricht?', Text0_1='Seid still! Seht Ihr denn nicht, dass der Meister spricht?', Text1_0='Klappert nicht so viel und hört lieber zu!', Text1_1='Klappert nicht so viel und hört lieber zu!', Text2_0='Wir müssen uns konzentrieren, wenn unser Drachenschwarm Wirklichkeit werden soll!', Text2_1='Wir müssen uns konzentrieren, wenn unser Drachenschwarm Wirklichkeit werden soll!', Text3_0='Seid ruhig! Ich kann es mir nicht leisten, auch nur eine Sekunde dieses Vortrags zu verpassen!', Text3_1='Seid ruhig! Ich kann es mir nicht leisten, auch nur eine Sekunde dieses Vortrags zu verpassen!', Text4_0='Habt Ihr Eure Studentenkleidung vergessen? Glaubt Ihr etwa, Mode sei wichtiger als die Herrschaft der Geißel?', Text4_1='Habt Ihr Eure Studentenkleidung vergessen? Glaubt Ihr etwa, Mode sei wichtiger als die Herrschaft der Geißel?', Text5_0='Habt Ihr die gestrige Aufgabe beendet? Wenn ja, dann setzt Euch zu mir!', Text5_1='Habt Ihr die gestrige Aufgabe beendet? Wenn ja, dann setzt Euch zu mir!', Text6_0='Passt hier drin besser auf, $GJungchen:Mädchen;. Der Meister sieht alles...', Text6_1='Passt hier drin besser auf, $GJungchen:Mädchen;. Der Meister sieht alles...', Text7_0='Habt Ihr Eure Lehrbücher mitgebracht? Kann ich mit Euch zusammen hineinschauen?', Text7_1='Habt Ihr Eure Lehrbücher mitgebracht? Kann ich mit Euch zusammen hineinschauen?' WHERE id=4445;
UPDATE npc_text SET Text0_0='Was, darf ich fragen, tut Ihr hier? Zieht Eure Studentenkleidung an und studiert!', Text1_0='Seid ruhig! Glaubt Ihr etwa, unsere verseuchten Großdrachen schlüpfen von selbst und bilden sich von ganz allein aus?', Text2_0='Kommt Ihr aus der brennenden Steppe? Habt Ihr meine schwarzen Dracheneier?' WHERE id=4446;
UPDATE npc_text SET Text0_0='Euer Geist ist nicht konzentriert. Verbergt Ihr etwas vor mir?', Text1_0='Ich spüre, wie Euch eine Unruhe umwabert...', Text2_0='Eure Zukunft ist umschattet. Konzentriert Euch und fragt noch einmal.', Text3_0='Ihr seht nicht wie ein Schüler aus...' WHERE id=4447;
UPDATE npc_text SET Text0_0='Dieser Kodo sieht kein bisschen aus wie das Tier, das ich ursprünglich angelockt habe! Ich frage mich, ob ich den Kombobulator wohl auch auf mich selbst anwenden könnte...', Text0_1='Dieser Kodo sieht kein bisschen aus wie das Tier, das ich ursprünglich angelockt habe! Ich frage mich, ob ich den Kombobulator wohl auch auf mich selbst anwenden könnte...', Text1_0='Warum sind kleine Kreaturen wie Goblins nur immer so fasziniert von größeren Kreaturen?', Text1_1='Warum sind kleine Kreaturen wie Goblins nur immer so fasziniert von größeren Kreaturen?', Text2_0='Wie schaffen es die Goblins nur, diesen Viechern Packs aufzubinden? Kodos sind zehnmal größer als sie!', Text2_1='Wie schaffen es die Goblins nur, diesen Viechern Packs aufzubinden? Kodos sind zehnmal größer als sie!' WHERE id=4449;
UPDATE npc_text SET Text0_1='Die Pestländer sind ein ausgezeichneter Ort, um einen Schlag gegen die Geißel zu führen!' WHERE id=4450;
UPDATE npc_text SET Text0_0='Grüße, $C. Ihr könnt mit mir sprechen, es sei denn, Euer Anliegen betrifft meine Herrin Magatha direkt.$B$BIch erledige die meisten Aufgaben für sie, damit sie sich auf wichtigere Dinge konzentrieren kann. Vielleicht gibt es einiges, das Ihr mit mir besprechen könnt, ehe Ihr um eine Audienz bei ihr nachsucht?' WHERE id=4473;
UPDATE npc_text SET Text0_0='Hallo, $N.' WHERE id=4474;
UPDATE npc_text SET Text0_0='Sprecht mich an, wann immer Ihr wollt, $N. Ihr habt Magatha gut gedient. Ich bin sicher, sie wird auch in Zukunft auf Eure Hilfe zurückgreifen.' WHERE id=4475;
UPDATE npc_text SET Text0_0='Seid nicht so vermessen zu glauben, dass Ihr zu mir sprechen könnt, als wärt Ihr mir ebenbürtig, $C. Sylvanas ist meine Königin und ich werde tun, was sie mir befiehlt.$B$BWenn Ihr nicht den gebührenden Respekt zeigt, kann ich Euch versichern, ist es uns ein Leichtes, einen mehr als passenden Platz im wirbelnden Nether für Euch zu finden.$B$BDoch es gibt keinen Grund, warum wir nicht miteinander sprechen können, wenn Ihr der dunklen Fürstin zu dienen wünscht...' WHERE id=4476;
UPDATE npc_text SET Text0_0='In meinen Augen sind alle Angehörigen der Horde gleich, $N. Wir haben alle viel gelitten, und wenn wir nicht unsere Ehre und unsere Weisheit hätten, dann wären wir nicht besser als die Geißel oder als mein eigenes Volk, solange unser Blut noch durch den Dämon Mannoroth besudelt war.$B$BEs ist Eure Pflicht, der Horde beizustehen und unsere Lebensweise zu verteidigen. Doch es ist auch Eure Pflicht zu wissen, wann Ihr Feinden ebenso wie Freunden Schonung und Mitgefühl gewähren solltet.$B$BEines müsst Ihr verstehen. Dies ist die neue Horde, nicht länger eine dämonengeborene Armee, die über keinen freien Willen verfügt.' WHERE id=4477;
UPDATE npc_text SET Text0_0='Ich muss mich bei Euch entschuldigen - ich war in Bezug auf meine Identität nicht ganz ehrlich zu Euch. Ich musste mir erst sicher sein, dass ich Euch trauen konnte... nur wenige Personen sind bereit, einem alten und in Vergessenheit geratenen Einsiedler zu helfen.$B$BDoch Ihr habt gezeigt, dass Ihr eine dieser Personen seid: eine Persönlichkeit voll Redlichkeit und Ehre.' WHERE id=4493;
UPDATE npc_text SET Text0_0='Mein Sohn Taelan war noch ein kleiner Junge, als ich wegen Verrates für schuldig befunden, aus der Allianz verbannt und zu einem Leben im Exil verurteilt wurde. Ich beschloss, hierzubleiben und über meinen Sohn zu wachen, irgendwie dafür zu sorgen, dass er zu einem ehrenhaften Mann heranwachsen würde.$B$BWie ich gehofft hatte, folgte er mir in das Amt des Lords von Mardenholde; aber dann - grausame Ironie des Schicksals - schloss er sich dem Scharlachroten Kreuzzug an. Er ist jetzt ihr Hochlord, $R.' WHERE id=4494;
UPDATE npc_text SET Text0_0='So ist es. Der Scharlachrote Kreuzzug ist ein fehlgeleiteter Zweig des Ordens der Silbernen Hand.$B$BIhr müsst mir glauben, $R: Taelan ist ein guter Mann. Aber er braucht jemanden, der ihm den rechten Weg weist. Er muss sich erinnern... daran erinnern, was es heißt, edel und ehrenvoll zu handeln. Ich weiß, dass er tief in seinem Herzen weiß, dass das, was er tut, falsch ist. Werdet Ihr ihm helfen? Werdet Ihr ihm helfen, sich zu erinnern?' WHERE id=4495;
UPDATE npc_text SET Text0_0='Hört gut auf das, was man Euch sagt, und zeigt kein voreiliges Vertrauen oder Misstrauen. Denn selbst Freunde verfolgen auch ihre eigenen Ziele, $C.$B$BUnser großer Kriegshäuptling weiß das, und um zu beweisen, dass es in der Horde Hexenmeister gibt, die sie nicht von innen heraus zerstören oder die Kontrolle über sie erlangen wollen, habe ich mich freiwillig dazu gemeldet, unserem jungen Anführer zu helfen, wo ich nur kann.' WHERE id=4513;
UPDATE npc_text SET Text0_0='Woo Pings Ausdauer ist einfach erstaunlich. Ich habe schon gesehen, wie er stundenlang an seinen Techniken gefeilt hat, dass mir die Übungspuppe fast schon leid getan hat. Ich kann mir niemanden vorstellen, der besser dafür geeignet wäre, Euch im bewaffneten Kampf zu unterrichten.', Text0_1='Woo Pings Ausdauer ist einfach erstaunlich. Ich habe schon gesehen, wie er stundenlang an seinen Techniken gefeilt hat, dass mir die Übungspuppe fast schon leid getan hat. Ich kann mir niemanden vorstellen, der besser dafür geeignet wäre, Euch im bewaffneten Kampf zu unterrichten.', Text1_0='Meister Woo Ping beherrscht fast jede Waffe, die dem Menschen bekannt ist. Besonders tödlich ist er aber mit seinen Schwerttechniken. Falls Ihr etwas Ausbildung braucht... nun ja, er verbringt einen Großteil seiner Zeit drüben in \'Wellers Arsenal\' und wartet darauf, dass neue Waffenlieferungen eintreffen.', Text1_1='Meister Woo Ping beherrscht fast jede Waffe, die dem Menschen bekannt ist. Besonders tödlich ist er aber mit seinen Schwerttechniken. Falls Ihr etwas Ausbildung braucht... nun ja, er verbringt einen Großteil seiner Zeit drüben in \'Wellers Arsenal\' und wartet darauf, dass neue Waffenlieferungen eintreffen.' WHERE id=4516;
UPDATE npc_text SET Text0_0='Im Lauf der Jahrtausende hat Ilyenia Mondfeuer den meisterlichen Gebrauch zahlreicher Waffen erlernt und nutzt nun ihr Können, um auch andere darin zu unterrichten. Ihr findet sie auf der Terrasse der Krieger, wenn Ihr Unterricht wünscht.', Text0_1='Im Lauf der Jahrtausende hat Ilyenia Mondfeuer den meisterlichen Gebrauch zahlreicher Waffen erlernt und nutzt nun ihr Können, um auch andere darin zu unterrichten. Ihr findet sie auf der Terrasse der Krieger, wenn Ihr Unterricht wünscht.', Text1_0='Den größten Teil meiner Ausbildung an der Waffe habe ich von Ilyenia Mondfeuer auf der Terrasse der Krieger erhalten. Wenn Ihr den Kampf mit einer neuen Waffe lernen wollt, so tätet Ihr gut daran, sie aufzusuchen.', Text1_1='Den größten Teil meiner Ausbildung an der Waffe habe ich von Ilyenia Mondfeuer auf der Terrasse der Krieger erhalten. Wenn Ihr den Kampf mit einer neuen Waffe lernen wollt, so tätet Ihr gut daran, sie aufzusuchen.' WHERE id=4517;
UPDATE npc_text SET Text0_0='Es gibt nicht viele Waffen, mit denen Bixi und Buliwyf nicht schon einen Orc aufgeschlitzt oder zerschmettert hätten. Sie treiben sich immer am Schlachtholzwaffenladen herum, falls Ihr ein bisschen Unterricht bekommen wollt.', Text0_1='Es gibt nicht viele Waffen, mit denen Bixi und Buliwyf nicht schon einen Orc aufgeschlitzt oder zerschmettert hätten. Sie treiben sich immer am Schlachtholzwaffenladen herum, falls Ihr ein bisschen Unterricht bekommen wollt.', Text1_0='Ihr wollt lernen, wie Ihr \'ne neue Waffe benutz\'n sollt, hm? Tja, da solltet Ihr ma\' mit Bixi oder Buliwyf red\'n. Die sin\' drüben beim Schlachtholzwaffenladen, $GJungchen:Mädel;. Die kennen sich mit jeder Waffe aus, von \'ner Axt bis zu... äh... \'nem Zweihänder.', Text1_1='Ihr wollt lernen, wie Ihr \'ne neue Waffe benutz\'n sollt, hm? Tja, da solltet Ihr ma\' mit Bixi oder Buliwyf red\'n. Die sin\' drüben beim Schlachtholzwaffenladen, $GJungchen:Mädel;. Die kennen sich mit jeder Waffe aus, von \'ner Axt bis zu... äh... \'nem Zweihänder.' WHERE id=4518;
UPDATE npc_text SET Text0_0='Wollt Ihr den Umgang mit einer neuen Waffe lernen? Dann sucht Sayoc und Hanashi in den \'Legendären Waffen\' im Tal der Ehre auf.', Text0_1='Wollt Ihr den Umgang mit einer neuen Waffe lernen? Dann sucht Sayoc und Hanashi in den \'Legendären Waffen\' im Tal der Ehre auf.', Text1_0='Fragt in den \'Legendären Waffen\' im Tal der Ehre nach Sayoc und Hanashi. Aber passt auf, was Ihr sagt, wenn Ihr dort seid, oder Sayoc verfüttert ein Stück von Euch an den Grinsenden Hund als kleinen Imbiss.', Text1_1='Fragt in den \'Legendären Waffen\' im Tal der Ehre nach Sayoc und Hanashi. Aber passt auf, was Ihr sagt, wenn Ihr dort seid, oder Sayoc verfüttert ein Stück von Euch an den Grinsenden Hund als kleinen Imbiss.' WHERE id=4519;
UPDATE npc_text SET Text0_0='Ah, Ihr sucht die Weisheit von Ansekhwa. Sucht ihn auf der unteren Anhöhe bei \'Waffen von Donnerfels\' auf.', Text0_1='Ah, Ihr sucht die Weisheit von Ansekhwa. Sucht ihn auf der unteren Anhöhe bei \'Waffen von Donnerfels\' auf.', Text1_0='Niemand ist so geschickt im Umgang mit Waffen wie Ansekhwa. Es heißt, die Geister der besten Krieger unter unseren verehrten Vorfahren leiten ihn. Sucht ihn auf der unteren Anhöhe auf, dann bringt er Euch bei, was Ihr wissen möchtet.', Text1_1='Niemand ist so geschickt im Umgang mit Waffen wie Ansekhwa. Es heißt, die Geister der besten Krieger unter unseren verehrten Vorfahren leiten ihn. Sucht ihn auf der unteren Anhöhe auf, dann bringt er Euch bei, was Ihr wissen möchtet.' WHERE id=4520;
UPDATE npc_text SET Text0_0='Ihr Archibald suchen. Er vor Waffenhändlerladen auf inneres Ring von Kriegsviertel.', Text0_1='Ihr Archibald suchen. Er vor Waffenhändlerladen auf inneres Ring von Kriegsviertel.', Text1_0='Kleiner Verlassener kennen alle Waffen. Er Archibald. Ihr ihn finden auf inneres Ring von Kriegsviertel.', Text1_1='Kleiner Verlassener kennen alle Waffen. Er Archibald. Ihr ihn finden auf inneres Ring von Kriegsviertel.' WHERE id=4521;
UPDATE npc_text SET Text0_0='Es wäre klug, Euch zu erklären, welche Mühen unsere Feinde auf sich nehmen, um sich direkt unter unseren Augen zu verstecken.$B$BEines müsst Ihr vor allem wissen: Unser Feind ist der Schattenrat. Ganz gleich, welche Aufgabe ich Euch gebe oder gegen wen ich Euch zu handeln heiße, diese eine einfache Tatsache dürft Ihr nie vergessen.$B$BEs existieren viele Kulte innerhalb des Rates, doch nur aus dem einen Grund, um seine Handlungen vor den Narren zu verbergen.$B$BGruppen wie die Brennende Klinge, die Arguswacht und die Sengende Klinge sind alles Werkzeuge des Rates. Erachtet sie als ein und dasselbe.' WHERE id=4522;
UPDATE npc_text SET Text0_0='Ah, jemand, der zu uns gehört. Es tut gut zu sehen, dass unsere Reihen so schnell anschwellen, dass ich mir die Gesichter der einzelnen Mitglieder nicht länger merken kann.$B$BThrall, dieser Narr, glaubt, dass ich hart daran arbeite, den Ruf der Hexenmeister zu verbessern und zu beweisen, dass wir seine ergebenen Diener sind. Er ist ein Idiot! Die Brennende Legion wird diese ganze Welt in Asche verwandeln, nachdem sie ihr alles Leben vollständig entzogen hat.' WHERE id=4533;
UPDATE npc_text SET Text0_0='Was für eine Naivität. Die Brennende Klinge IST der Schattenrat! Viele Kulte und Organisationen haben sich unter der Herrschaft des Rates versammelt, aber sie alle sind nur die Verlängerungen seines Willens. Ich mag vielleicht die Brennende Klinge anführen, aber ich bin ebenso sehr eine Spielfigur des Rates, wie Ihr meine Spielfigur seid!$B$BSeht Euch die Sengende Klinge an - die meisten von ihnen glauben, sie wären die nächste Gruppe mächtiger Hexenmeister; sie glauben, sie würden den Schattenrat ersetzen können!' WHERE id=4534;
UPDATE npc_text SET Text0_0='Haha! Ja, alle von ihnen, mit Ausnahme ihrer Anführer: dem Satyr Bazzalan und dem Hexenmeister Jergosh. Daher hetze ich auch ständig Thralls Gefolgsleute auf sie. So gewinnt Thrall den Eindruck, dass ich unter seiner großen Stadt das Böse ausmerze. Doch in Wahrheit schlage ich so zwei Fliegen mit einer Klappe: Ich töte Thralls Anhänger und stelle gleichzeitig fest, ob die Mitglieder der Sengenden Klinge auch wirklich würdig sind, sich mir anzuschließen.' WHERE id=4535;
UPDATE npc_text SET Text0_0='Ich werde es Euch sagen. Ihr seid offensichtlich bereits dabei, Euch uns zu beweisen, da Ihr so schnell zu einem Offizier gemacht wurdet. Vielleicht habe ich später einige Aufgaben für Euch, wenn Ihr dann noch einmal zu mir kommt. Der Rat liebt vor allem diejenigen, die in den arkanen Künsten bewandert sind, aber neue Diener sind jederzeit willkommen, ganz gleich, welchen Beruf sie ausüben.' WHERE id=4536;
UPDATE npc_text SET Text0_0='Als Angehöriger der Forscherliga suche ich hier in der Gegend nach Artefakten. Es heißt, in Winterquell gäbe es hochgradig magische Gegenstände... die meisten davon schwer bewacht.' WHERE id=4573;
UPDATE npc_text SET Text0_1='Wir leben in gefährlichen Zeiten. Passt auf Euch auf.' WHERE id=4633;
UPDATE npc_text SET Text0_1='Hallo! Was führt Euch nach Winterquell? Dieses kalte und weite Land mag trostlos erscheinen, doch es ist ein Ort zahlreicher Geheimnisse...' WHERE id=4634;
UPDATE npc_text SET Text0_0='Setzt Euch und hört Euch meine Geschichte an, $R.' WHERE id=4673;
UPDATE npc_text SET Text0_0='Hallo, $G Süßer:Süße;, wollt Ihr vielleicht nach Grom\'gol? Ich hab gehört, da unten im Dschungel wird\'s ganz schön heiß und schwül. Vielleicht könnt ich ja für \'n kleinen Kurzurlaub zu Euch stoßen, Euch mal \'ne richtige Dschungelkatze zeigen. Kommt und reist in den wunderbaren, herrlichen tropischen Dschungel des Schlingendorntals. Ja, genau, auch Ihr könnt Eure Zeit mit Sonnenbaden an kristallklaren, blauen Stränden verbringen, während ich den ganzen Tag lang in dieser unerträglichen Hitze rumstehen muss und niemanden zum Ansehen habe außer meinen Bruder Frezza! Gute Reise!', Text0_1='Hallo, $G Süßer:Süße;, wollt Ihr vielleicht nach Grom\'gol? Ich hab gehört, da unten im Dschungel wird\'s ganz schön heiß und schwül. Vielleicht könnt ich ja für \'n kleinen Kurzurlaub zu Euch stoßen, Euch mal \'ne richtige Dschungelkatze zeigen. Kommt und reist in den wunderbaren, herrlichen tropischen Dschungel des Schlingendorntals. Ja, genau, auch Ihr könnt Eure Zeit mit Sonnenbaden an kristallklaren, blauen Stränden verbringen, während ich den ganzen Tag lang in dieser unerträglichen Hitze rumstehen muss und niemanden zum Ansehen habe außer meinen Bruder Frezza! Gute Reise!' WHERE id=4693;
UPDATE npc_text SET Text0_0='Da steckt man dem Chef einfach \'ne kleine Münze in die Hand und das kommt dabei raus. Also, ich liebe meinen Job. Und wenn ich es noch irgendwie schaffen könnte, dass diese kleine Wildkatze Snurk hierher versetzt wird... das wär einfach das Höchste. Aber ich wette, Ihr seid nicht hier, um über meine Geschäftspraktiken zu reden. Ihr wollt zum Zeppelin nach Unterstadt, hab ich Recht? Na, das ist doch Klasse, denn der dockt hier direkt hinter mir an. Ach ja, und wenn Ihr Snurk seht, dann legt ein gutes Wort für mich ein, ja?', Text0_1='Da steckt man dem Chef einfach \'ne kleine Münze in die Hand und das kommt dabei raus. Also, ich liebe meinen Job. Und wenn ich es noch irgendwie schaffen könnte, dass diese kleine Wildkatze Snurk hierher versetzt wird... das wär einfach das Höchste. Aber ich wette, Ihr seid nicht hier, um über meine Geschäftspraktiken zu reden. Ihr wollt zum Zeppelin nach Unterstadt, hab ich Recht? Na, das ist doch Klasse, denn der dockt hier direkt hinter mir an. Ach ja, und wenn Ihr Snurk seht, dann legt ein gutes Wort für mich ein, ja?' WHERE id=4694;
UPDATE npc_text SET Text0_0='Der erschlagene Geist von Mondklaue starrt Euch forschend an, ganz so, als ob er etwas von Euch wollte.' WHERE id=4714;
UPDATE npc_text SET Text0_0='Der Geist sieht Euch forschend in die Augen und ergreift Eure Hand. Ihr fühlt, wie Wärme durch Euren Körper schießt und Euch beinahe in die Knie zwingt.$B$BAls die Wärme vergeht, spürt Ihr die vage Präsenz des Geistes eines Bären in Euch, die Eure Entschlossenheit stärkt.' WHERE id=4715;
UPDATE npc_text SET Text0_0='Wir waren alle Zeugen schrecklicher Tragödien, aber wir dürfen nicht daran verzweifeln.$B$BErinnert euch stattdessen an all dies, damit wir eines Tages die Abrechnung erleben dürfen.' WHERE id=4716;
UPDATE npc_text SET Text0_0='Der Geist des Bären sieht Euch mit einem ruhigen und friedfertigen Blick an. Ihr hört, wie eine Stimme spricht, ganz so, als ob sie von dem Bären käme, doch die Kreatur macht keine Anzeichen zu sprechen.$B$B"Seid mir gegrüßt, $Gmein junger Freund:meine junge Freundin;. Wenn Ihr zu mir gekommen seid, weil Ihr Rat sucht, dann kann ich Euch vielleicht dabei helfen zu finden, was Ihr sucht."' WHERE id=4719;
UPDATE npc_text SET Text0_0='Ich stelle die Kraft dar, die Euch als $Gjungen Druiden:junge Druidin; stärkt. Ihr seid zu mir gekommen, um diese Kraft zu erlernen, Kind, und ich werde sie Euch lehren. Ich werde Euch alles lehren, was den Geisterbären ausmacht, so Ihr denn zuhört und versteht.$B$BUm zu wissen, was es heißt, auf meinen Geist zurückzugreifen, müsst Ihr verstehen, wie wichtig die Kraft des Körpers und auch die Kraft des Herzens sind.' WHERE id=4721;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N', Text0_1='Seid gegrüßt, $N' WHERE id=4732;
UPDATE npc_text SET Text0_0='Die Kraft des Körpers: Das heißt Stärke und schnelles Handeln. Ihr müsst darauf achten, dass Ihr ständig in Form seid, und zwar sowohl körperlich als auch geistig. Der Umfang des Bären unterstreicht seine Kraft, denn er ist im Kampf ein wilder Gegner. Doch der Umfang des Bären täuscht über seine geschmeidige Beweglichkeit und seinen scharfen Geist hinweg. Dieses Überraschungsmoment müsst Ihr zu Eurem Vorteil nutzen.$B$BIhr müsst Euch auf die Kraft des Körpers des Bären verlassen, wenn Ihr den Weg der Klaue meistern wollt.' WHERE id=4733;
UPDATE npc_text SET Text0_0='Die Kraft des Herzens gibt Euch die Entschlossenheit zu handeln, und zwar aufgrund von reinen und aufrechten Absichten zu handeln. Das Gleichgewicht beizubehalten bedeutet nicht, selbstgefällig oder oberflächlich zu werden.$B$BFür alles, an das Ihr glaubt, müsst Ihr Entschlossenheit zeigen, und Ihr müsst bereit sein, dafür zu kämpfen. Die Bärenmutter zeigt unendliche Hingabe, wenn es darum geht, ihre Jungen zu beschützen, ebenso wie ein Bärenälterer, der seinen Bau beschützt. Diese Entschlossenheit, diese Kraft des Herzens, müsst Ihr erlernen, wenn Ihr den Weg der Klaue meistern wollt.' WHERE id=4734;
UPDATE npc_text SET Text0_0='Es bleibt abzuwarten, ob Ihr bereit seid, Kind. Sogar die weisesten und ältesten Druiden sind nie völlig bereit, wenn der Moment kommt, in dem sie zur letzten Prüfung antreten müssen.$B$BIhr habt meine Worte gehört, nun müsst Ihr weiterziehen. Beherzigt, was ich Euch gelehrt habe. Die Zeit wird kommen, da Eure Kraft einer Prüfung unterzogen wird. Dann müsst Ihr Euch Eurem Feind stellen, wie auch ein Bär dies täte - mit starkem Körper und starkem Herzen. Lernt aus dem Kampf, $Gjunger Druide:junge Druidin;. Und nun geht... mit meinem Segen.' WHERE id=4735;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Ihr seid in Nachthafen, ja, in der ganzen Mondlichtung, willkommen. Der Zirkel des Cenarius heißt alle willkommen, die der Herrschaft und der Befehlsgewalt der Natur ihren Respekt zollen.$B$BBitte achtet, solange Ihr hier seid, unbedingt darauf, in allen Dingen das Gleichgewicht zu wahren. Störungen sind hier nicht gern gesehen, und unsere Aufseher sind dazu ausgebildet, alle Feindseligkeiten unverzüglich zu beenden, ohne Fragen zu stellen.' WHERE id=4753;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N' WHERE id=4773;
UPDATE npc_text SET Text0_0='Bemüht Euch darum, in allen Dingen das Gleichgewicht zu bewahren, $C. Darin liegt das Geheimnis wahrer Harmonie.' WHERE id=4774;
UPDATE npc_text SET Text0_0='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid, um Euch ausbilden zu lassen, dann bin ich nur zu gern bereit, Euch etwas zu lehren.$B$BEs ist nie falsch, den Platz, den man in diesem, wie ich es nenne, Großen Gleichgewicht innehat, von neuem zu bestätigen. Wir sind die Bewahrer dieses Gleichgewichtes, und so müssen wir ständig klaren Verstandes und starken Herzens sein. Hierbei hilft uns die Ausbildung.' WHERE id=4775;
UPDATE npc_text SET Text0_0='Die Schlacht ist vorüber, und die Bevölkerung von Darroheim ist gerettet.$B$BDank Eures Beistandes, $N, ist sie gerettet. Ihr seid wahrlich $Gein Held:eine Heldin; für Darroheim.' WHERE id=4777;
UPDATE npc_text SET Text0_0='Dunkelheit hatte sich über meine Augen gelegt, doch Ihr habt mich aus meiner unheiligen Trance gerissen. Ihr habt mich gerettet, $N.$B$BUnd nun bitte ich Euch... vergebt mir.' WHERE id=4778;
UPDATE npc_text SET Text0_0='Willkommen, $Gmein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichts, jetzt und immerdar, während Malfurion in Träumen liegt. Vergesst das niemals.' WHERE id=4779;
UPDATE npc_text SET Text0_0='Unser Meister Malfurion ist noch immer im Traum verloren, $C. Er ist das Leuchtfeuer, das uns den Weg erhellt, und wir müssen alles tun, was in unserer Macht steht, um dieses Licht zu bewahren.' WHERE id=4780;
UPDATE npc_text SET Text0_0='Der Segen von Cenarius möge Euch begleiten, $C. Unser Anführer, Erzdruide Hirschhaupt, führt uns und ganz Teldrassil in ein neues Leben!' WHERE id=4781;
UPDATE npc_text SET Text0_0='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BEs gehen Gerüchte um von Zwistigkeiten innerhalb des Zirkels des Cenarius, aber Ihr solltet solch leichtfertigem Gerede keinen Glauben schenken. Unser Erzdruide, Fandral Hirschhaupt, führt uns und ganz Teldrassil zum Ruhm!', Text0_1='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BEs gehen Gerüchte um von Zwistigkeiten innerhalb des Zirkels des Cenarius, aber Ihr solltet solch leichtfertigem Gerede keinen Glauben schenken. Unser Erzdruide, Fandral Hirschhaupt, führt uns und ganz Teldrassil zum Ruhm!' WHERE id=4782;
UPDATE npc_text SET Text0_0='Selbst an Orten, die mit kalten steinernen Mauern umgeben sind, lassen sich Ruhe und Frieden finden. Um genau zu sein, lassen sie sich an jedem Ort finden, an dem Ihr Euch aufhaltet - Ihr müsst einfach nur in Euch hineinhorchen, um das Gleichgewicht zu erlangen.', Text0_1='Selbst an Orten, die mit kalten steinernen Mauern umgeben sind, lassen sich Ruhe und Frieden finden. Um genau zu sein, lassen sie sich an jedem Ort finden, an dem Ihr Euch aufhaltet - Ihr müsst einfach nur in Euch hineinhorchen, um das Gleichgewicht zu erlangen.' WHERE id=4783;
UPDATE npc_text SET Text0_0='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.' WHERE id=4784;
UPDATE npc_text SET Text0_1='Wir streben danach, das Gleichgewicht zu erhalten, so wie Cenarius es gelehrt hat, $C. Er ist das Leuchtfeuer, das uns den Weg erhellt, und wir müssen alles tun, was in unserer Macht steht, um dieses Licht zu bewahren.' WHERE id=4785;
UPDATE npc_text SET Text0_1='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichtes, jetzt und immerdar, wie Cenarius es uns gelehrt hat. Vergesst das niemals.' WHERE id=4786;
UPDATE npc_text SET Text0_1='Ich bereite mich gerade darauf vor, auf den Grat hinaufzusteigen, $N. Dabei könnte ich etwas Hilfe gebrauchen - wollt Ihr nicht vielleicht mit mir kommen?' WHERE id=4788;
UPDATE npc_text SET Text0_0='Solche wie Euch bilde ich nicht aus, $C.', Text0_1='Solche wie Euch bilde ich nicht aus, $C.' WHERE id=4793;
UPDATE npc_text SET Text0_0='Wir sind nichts als Dunkelheit und Schatten. Ewig. Unsichtbar.', Text0_1='Wir sind nichts als Dunkelheit und Schatten. Ewig. Unsichtbar.' WHERE id=4794;
UPDATE npc_text SET Text0_0='Seid mir gegrüßt, junger Schurke. Was sucht Ihr?', Text0_1='Seid mir gegrüßt, junger Schurke. Was sucht Ihr?' WHERE id=4795;
UPDATE npc_text SET Text0_0='Ich kann Euch nicht ausbilden. Da müsst Ihr mit dem Lehrer für Eure Klasse sprechen.', Text0_1='Ich kann Euch nicht ausbilden. Da müsst Ihr mit dem Lehrer für Eure Klasse sprechen.' WHERE id=4796;
UPDATE npc_text SET Text0_0='Kann Euch nich\' helfen, $N. Ich bilde bloß Schurken aus.', Text0_1='Kann Euch nich\' helfen, $N. Ich bilde bloß Schurken aus.' WHERE id=4797;
UPDATE npc_text SET Text0_0='Kann Euch nich\' helfen, $N. Ich bilde bloß Schurken aus.' WHERE id=4798;
UPDATE npc_text SET Text0_1='Tut mir leid, aber ich bilde bloß Schurken aus.' WHERE id=4799;
UPDATE npc_text SET Text0_0='Ah, ein Kunde! Sprecht mit meiner großartigsten Erfindung, dem Kaufotron 1000. Er hat alles, was Ihr braucht... einige der seltensten Gegenstände, die Ihr in Kalimdor angeboten sehen werdet... ganz davon abgesehen, dass seine Programmierung es ihm nicht erlaubt zu feilschen. Also ist der angeschriebene Preis auch der Preis, den Ihr bezahlt.' WHERE id=4813;
UPDATE npc_text SET Text0_0='Zweifellos seid Ihr auch nur wieder $Gein unfähiger Einfaltspinsel, der:eine unfähige Trine, die; von der dunklen Fürstin geschickt wurde, damit ich, ihr Held, Euch ausbilde.$B$B<Nathanos seufzt.>$B$BIch habe jeden einzelnen von Euch in fein säuberlichen Einzelteilen zu ihr zurückgeschickt, und ich sehe keinen Grund, warum es diesmal anders laufen sollte.$B$BDoch an mir liegt es nicht zu fragen, sondern allein den Willen der dunklen Fürstin auszuführen.' WHERE id=4814;
UPDATE npc_text SET Text0_0='Heute ist Euer Glückstag - ich bin gerade mit der Wartung des Superverkaufomaten 680 fertig geworden. Wir haben Massen von Sachen zu Tiefstpreisen zu verkaufen. Schaut Euch ruhig um... und da glaubt mein Bruder Cork echt, sein Kaufotron 1000 würde meinen kleinen Bot hier ausstechen!' WHERE id=4815;
UPDATE npc_text SET Text0_0='Kann Euch nich\' helfen, $N. Ich bilde bloß Schurken aus.', Text0_1='Kann Euch nich\' helfen, $N. Ich bilde bloß Schurken aus.' WHERE id=4833;
UPDATE npc_text SET Text0_0='Was kann ich\'n für Euch tun, $N?' WHERE id=4834;
UPDATE npc_text SET Text0_0='Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?', Text0_1='Ihr seht aus wie jemand, in dessen Gegenwart ich auf meinen Geldbeutel achten sollte. Kann ich Euch mit etwas behilflich sein?' WHERE id=4835;
UPDATE npc_text SET Text0_0='Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $C, hm? Vielleicht kann Euch einer Eurer Lehrer weiterhelfen.', Text0_1='Ich glaub nicht, dass Ihr irgendwas lernen wollt, was ich Euch beibringen kann. $C, hm? Vielleicht kann Euch einer Eurer Lehrer weiterhelfen.' WHERE id=4837;
UPDATE npc_text SET Text0_0='Ihr seid gekommen, um vom Meister zu lernen, wie?' WHERE id=4838;
UPDATE npc_text SET Text0_0='Seid mir gegrüßt. Möge die Dämmerung Euch in diesen wechselvollen Zeiten bewahren.' WHERE id=4841;
UPDATE npc_text SET Text0_0='Ihr habt noch kein richtiges Reittier gehabt, solange Ihr noch nie die Zügel eines Barak Tor\'ol-Bergwidders von bester Zucht in den Händen hattet, $Gmein Freund:meine Liebe;. Bitte, seht Euch diese trefflichen Gefährten für jeden Abenteurer ruhig ausführlich an. Sie werden Euch gute Dienste leisten, das könnt Ihr mir glauben.', Text0_1='Ihr habt noch kein richtiges Reittier gehabt, solange Ihr noch nie die Zügel eines Barak Tor\'ol-Bergwidders von bester Zucht in den Händen hattet, $Gmein Freund:meine Liebe;. Bitte, seht Euch diese trefflichen Gefährten für jeden Abenteurer ruhig ausführlich an. Sie werden Euch gute Dienste leisten, das könnt Ihr mir glauben.' WHERE id=4855;
UPDATE npc_text SET Text0_0='Ich bin die neueste Version eines automatisierten Verkäufers... Eines Tages werden wir die Welt beherrschen... Aber sagt meinem Chef nicht, dass ich mir von seinen Waren etwas Geld beiseite lege...' WHERE id=4856;
UPDATE npc_text SET Text0_0='Meine Programmierung erlaubt es mir nicht, im Preis eines Gegenstandes, ganz gleich welchen Gegenstandes, nachzulassen.' WHERE id=4857;
UPDATE npc_text SET Text0_0='Viele Jahre bester Zuchttechniken und ausgezeichneter Pflege versetzen mich in die glückliche Lage, Euch jetzt diese Pferde zeigen zu können. Bitte, nehmt Euch ruhig Zeit und betrachtet sie alle in Ruhe, ehe Ihr Euch ein Reittier aussucht. Ein Pferd ist ein Begleiter für viele Jahre, $Gmein Freund:liebe Freundin;. Enthaltet Euch nicht selbst etwas vor - kauft heute noch hier Euer Ross!', Text0_1='Viele Jahre bester Zuchttechniken und ausgezeichneter Pflege versetzen mich in die glückliche Lage, Euch jetzt diese Pferde zeigen zu können. Bitte, nehmt Euch ruhig Zeit und betrachtet sie alle in Ruhe, ehe Ihr Euch ein Reittier aussucht. Ein Pferd ist ein Begleiter für viele Jahre, $Gmein Freund:liebe Freundin;. Enthaltet Euch nicht selbst etwas vor - kauft heute noch hier Euer Ross!' WHERE id=4859;
UPDATE npc_text SET Text0_0='Bevor man eine Jagd beginnt, ist es sinnvoll, jemanden zu fragen, wonach man überhaupt suchen soll, bevor man anfängt, danach zu suchen.', Text0_1='Bevor man eine Jagd beginnt, ist es sinnvoll, jemanden zu fragen, wonach man überhaupt suchen soll, bevor man anfängt, danach zu suchen.' WHERE id=4861;
UPDATE npc_text SET Text0_0='Kodowildtiere, $Gmein Freund:meine Liebe;! Von klein auf ausgebildet und in den goldenen Ebenen von Mulgore aufgezogen, eignen sich diese großartigen Tiere einfach ideal zum Reiten! Bitte schaut Euch meine Auswahl in Ruhe an und seht selbst, warum wir Tauren uns auf diese prachtvollen Kreaturen verlassen!', Text0_1='Kodowildtiere, $Gmein Freund:meine Liebe;! Von klein auf ausgebildet und in den goldenen Ebenen von Mulgore aufgezogen, eignen sich diese großartigen Tiere einfach ideal zum Reiten! Bitte schaut Euch meine Auswahl in Ruhe an und seht selbst, warum wir Tauren uns auf diese prachtvollen Kreaturen verlassen!' WHERE id=4862;
UPDATE npc_text SET Text0_1='Willkommen beim Zirkel des Cenarius, $C.' WHERE id=4863;
UPDATE npc_text SET Text0_0='Ich kann Euch viel Wissen vermitteln, $C. Ihr müsst lernen, mit Ehre zu jagen.' WHERE id=4866;
UPDATE npc_text SET Text0_1='Ich bilde Jäger aus und lehre sie die Künste und Traditionen unserer Vorfahren.' WHERE id=4867;
UPDATE npc_text SET Text0_0='Wir Tauren sind leidenschaftliche Jäger, das liegt unserer Art im Blut.' WHERE id=4868;
UPDATE npc_text SET Text0_0='Ich freue mich, Euch im Namen der Königlichen Apothekervereinigung die besten Reittiere anbieten zu können, die den Verlassenen zur Verfügung stehen. Diese Tiere haben, so wie wir alle, Drangsal und Leid erfahren müssen, aber auch sie sind stärker und widerstandsfähiger daraus hervorgegangen, als dies dem rein sterblichen Fleisch möglich gewesen wäre. Bitte, seht sie Euch ruhig an und entscheidet selbst.', Text0_1='Ich freue mich, Euch im Namen der Königlichen Apothekervereinigung die besten Reittiere anbieten zu können, die den Verlassenen zur Verfügung stehen. Diese Tiere haben, so wie wir alle, Drangsal und Leid erfahren müssen, aber auch sie sind stärker und widerstandsfähiger daraus hervorgegangen, als dies dem rein sterblichen Fleisch möglich gewesen wäre. Bitte, seht sie Euch ruhig an und entscheidet selbst.' WHERE id=4869;
UPDATE npc_text SET Text0_1='Kommt und lernt den Weg des Jägers. Stolz, Ehre, Tradition.' WHERE id=4870;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Widderreitens beibringen, vorausgesetzt, Ihr habt das nötige Kleingeld, um die Stunde zu bezahlen!', Text0_1='Ich kann Euch die Feinheiten des Widderreitens beibringen, vorausgesetzt, Ihr habt das nötige Kleingeld, um die Stunde zu bezahlen!' WHERE id=4871;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Reitens auf Skelettpferden beibringen, vorausgesetzt, Ihr verfügt über die nötige Barschaft, um die Stunde bezahlen zu können!', Text0_1='Ich kann Euch die Feinheiten des Reitens auf Skelettpferden beibringen, vorausgesetzt, Ihr verfügt über die nötige Barschaft, um die Stunde bezahlen zu können!' WHERE id=4873;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Kodoreitens beibringen, vorausgesetzt, Ihr habt genug Geld dabei, um die Stunde bezahlen zu können!', Text0_1='Ich kann Euch die Feinheiten des Kodoreitens beibringen, vorausgesetzt, Ihr habt genug Geld dabei, um die Stunde bezahlen zu können!' WHERE id=4874;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Raptorreitens beibringen, vorausgesetzt, Ihr habt genug Gold in der Tasche, um die Stunde bezahlen zu können!', Text0_1='Ich kann Euch die Feinheiten des Raptorreitens beibringen, vorausgesetzt, Ihr habt genug Gold in der Tasche, um die Stunde bezahlen zu können!' WHERE id=4875;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Reitens auf Pferden beibringen, vorausgesetzt, Ihr tragt genug Gold bei Euch, um die Stunde bezahlen zu können!', Text0_1='Ich kann Euch die Feinheiten des Reitens auf Pferden beibringen, vorausgesetzt, Ihr tragt genug Gold bei Euch, um die Stunde bezahlen zu können!' WHERE id=4876;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Tigerreitens beibringen, vorausgesetzt, Ihr habt genug Gold, um die Stunde zu bezahlen!', Text0_1='Ich kann Euch die Feinheiten des Tigerreitens beibringen, vorausgesetzt, Ihr habt genug Gold, um die Stunde zu bezahlen!' WHERE id=4877;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Roboschreiterlenkens beibringen, vorausgesetzt, Ihr habt Geld genug, um die Stunde zu bezahlen! Wollt Ihr nun lernen, wie man einen Roboschreiter reitet oder nicht?', Text0_1='Ich kann Euch die Feinheiten des Roboschreiterlenkens beibringen, vorausgesetzt, Ihr habt Geld genug, um die Stunde zu bezahlen! Wollt Ihr nun lernen, wie man einen Roboschreiter reitet oder nicht?' WHERE id=4878;
UPDATE npc_text SET Text0_0='Ich kann Euch die Feinheiten des Wolfreitens beibringen, vorausgesetzt, Ihr verfügt über ausreichende Mittel, um die Stunde bezahlen zu können!', Text0_1='Ich kann Euch die Feinheiten des Wolfreitens beibringen, vorausgesetzt, Ihr verfügt über ausreichende Mittel, um die Stunde bezahlen zu können!' WHERE id=4879;
UPDATE npc_text SET Text0_0='Es wäre eine Verschwendung sowohl Eures Goldes als auch meiner Zeit, wenn ich Euch beibringen würde, wie man einen Roboschreiter steuert, $C. Ihr seid einfach zu groß dafür!', Text0_1='Es wäre eine Verschwendung sowohl Eures Goldes als auch meiner Zeit, wenn ich Euch beibringen würde, wie man einen Roboschreiter steuert, $C. Ihr seid einfach zu groß dafür!' WHERE id=4881;
UPDATE npc_text SET Text0_0='Ich bilde junge Jäger und Jägerinnen aus und vermittle ihnen die Traditionen, die bereits seit vielen Generationen weitergetragen werden.' WHERE id=4888;
UPDATE npc_text SET Text0_0='Vergesst nicht, alle Dinge hängen miteinander zusammen. Wenn es in diesem Land keine Tiere mehr gäbe, dann würde unsereins an Einsamkeit der Seele sterben, denn was den Tieren widerfährt, widerfährt auch den Tauren. Und alles, was Azeroth widerfährt, widerfährt allen, die es bewohnen.' WHERE id=4889;
UPDATE npc_text SET Text0_0='Wenn Ihr hier seid, um Euch in der Taktik und der Strategie des Jagens ausbilden zu lassen, dann seid Ihr am richtigen Ort.' WHERE id=4890;
UPDATE npc_text SET Text0_0='He, Mann! Seid Ihr schon mal tief in den Ozean hinuntergetaucht? Ich verrat Euch mal ein kleines Geheimnis, ey... Tief unten im Ozean finden sich blubbernde Felsspalten, und dank denen kann man unter Wasser atmen. Aber das bleibt unser Geheimnis, ja? Ich kann keine Konkurrenten für meine Schalentierfallen brauchen... Ihr versteht doch?' WHERE id=4891;
UPDATE npc_text SET Text0_0='Seht Euch die Weisheit und das Wissen der Tiere an, so seht Ihr Euch selbst. Beobachtet, wie die Eulen das tun, nehmt die Geschicklichkeit der Säblerkatze an und macht Euch den mächtigen Geist des Wolfs zu Eigen.', Text0_1='Seht Euch die Weisheit und das Wissen der Tiere an, so seht Ihr Euch selbst. Beobachtet, wie die Eulen das tun, nehmt die Geschicklichkeit der Säblerkatze an und macht Euch den mächtigen Geist des Wolfs zu Eigen.' WHERE id=4893;
UPDATE npc_text SET Text0_1='Ihr seid kein Druide!' WHERE id=4913;
UPDATE npc_text SET Text0_1='Möchtet Ihr vielleicht nach Rut\'theran fliegen, mit freundlicher Unterstützung des Zirkels des Cenarius?' WHERE id=4914;
UPDATE npc_text SET Text0_1='Wenn Ihr nach Donnerfels zurückfliegen wollt, dann solltet Ihr mit Bunthen Steppenwind reden, meinem Taurenkollegen.' WHERE id=4915;
UPDATE npc_text SET Text0_0='Ihr seid kein Druide!' WHERE id=4916;
UPDATE npc_text SET Text0_0='Wenn Ihr nach Rut\'theran zurückfliegen wollt, dann solltet Ihr mit Silva Filnaveth reden, meinem Gegenstück auf Seiten der Nachtelfen.' WHERE id=4917;
UPDATE npc_text SET Text0_0='Möchtet Ihr vielleicht nach Donnerfels fliegen, mit freundlicher Unterstützung des Zirkels des Cenarius?' WHERE id=4918;
UPDATE npc_text SET Text0_0='Die Zeiten, in denen wir leben, sind so wechselhaft wie die Winde, $C. Es ist wichtig, dass wir nicht vergessen, was in dieser Welt wahrhaftig und von Dauer ist, und dass wir das bewahren. Alles andere ist nur verlorene Zeit.' WHERE id=4933;
UPDATE npc_text SET Text0_0='Kein anderes Volk von Azeroth hat so viel gelitten wie das Unsrige, $C. Dem Tod ins Gesicht zu lachen, ist für uns alle zur zweiten Natur geworden.' WHERE id=4934;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $N, und willkommen im Camp Narache. Die Kunde Eures Kommens hat sich wie ein Lauffeuer im Stamm verbreitet.' WHERE id=4935;
UPDATE npc_text SET Text0_0='Ah, die Schönheit des Laubschattentals erfreut meine Sinne immer wieder!' WHERE id=4936;
UPDATE npc_text SET Text0_0='Hmmm, Ihr seht mir recht handfest aus. Vielleicht könnt Ihr mir bei ein, zwei Dingen zur Hand gehen. Außer ein paar Lehrlingen, die noch grün hinter den Ohren sind und anderes zu tun haben, gibt es hier nicht viel Hilfe.' WHERE id=4937;
UPDATE npc_text SET Text0_0='He, Bürger! Ihr seht recht kräftig aus. Wir Wachen sind hier eher spärlich gesät. Daher könnte ich Eure Hilfe gut gebrauchen...' WHERE id=4938;
UPDATE npc_text SET Text0_0='Da Ihr mir geholfen habt, sage ich Euch nun, was ich über dieses Land und die Dinge, die es hier zu sehen gibt, weiß. Mein Wissen ist nicht groß, doch vielleicht kann ich einige Dinge aufzeigen, die jemanden wie Euch möglicherweise interessieren könnten.' WHERE id=4953;
UPDATE npc_text SET Text0_0='Mein Wissen ob die Lehren der Nachtelfen ist zwar nur begrenzt, aber es heißt, dass vor langer Zeit - damals, als den Nachtelfen die Kunst des Wirkens von Arkanzaubern noch heilig war - dass jedenfalls damals Azshara die Größte von ihnen allen war. Sie war die Königin und eine wunderschöne Frau. Doch leider sollte sich ihre Sucht nach dem Arkanen eines Tages als Katalysator für die schlimmsten Ereignisse in der Geschichte dieser Welt erweisen.' WHERE id=4954;
UPDATE npc_text SET Text0_0='Einst war das eine große Stadt der Nachtelfen, in der die Hochgeborenen regierten und mächtige Arkanmagie ausübten. Doch nun sind sie verflucht, spuken im ganzen Land, und ihre zerstörte Stadt wurde von den Grollflossennaga überrannt. Ich meine gehört zu haben, dass die Naga in den Ruinen nach mächtigen Artefakten der Hochgeborenen suchen.' WHERE id=4955;
UPDATE npc_text SET Text0_0='Es heißt, im Norden lebe ein mächtiger Magier, ein Mensch, der aus irgendeinem unbekannten Grund Wache über das Land hält. Ich bin selbst noch nicht dorthin gereist, aber es heißt, dass Gäste dort jederzeit willkommen seien und sogar seine Gunst gewinnen könnten, wenn sie bereit sind, Aufträge für ihn zu erledigen.' WHERE id=4956;
UPDATE npc_text SET Text0_0='In den letzten Jahren habe ich mit angesehen, wie den Furbolgs schreckliche Dinge widerfahren sind - sie waren Opfer in vielerlei Hinsicht. Doch was aus diesem Stamm geworden ist, weiß ich nicht so recht. Wurden sie von derselben Seuche verderbt, die auch über den Teufelswald gekommen ist? Oder sind sie vielleicht einfach wütend darüber, was ihrem einst friedlichen Volk widerfahren ist, und versuchen sich lediglich zu verteidigen? Wie dem auch sei, die meisten von ihnen geben sich allen, die in ihr Gebiet eindringen, gegenüber feindlich.' WHERE id=4957;
UPDATE npc_text SET Text0_0='Die blauen Großdrachen selbst sind nicht hier, aber Anhänger von Malygos halten sich hier auf. Ich weiß nicht warum, aber eines kann ich Euch sagen: Überall, wo die blauen Großdrachen sind, könnt Ihr mit Arkanmagie rechnen. Ich wäre nicht überrascht, wenn die Drachenbrut hier irgendetwas beschützt... vielleicht etwas, das die Hochgeborenen zurückgelassen haben.' WHERE id=4958;
UPDATE npc_text SET Text0_1='Ich sollte mir einen Gehilfen suchen; ich mag es gar nicht, den ganzen Tag nur hier zu sitzen und Angelbedarf zu verkaufen. Ich würde viel lieber angeln gehen und anschließend Angelbedarf verkaufen.' WHERE id=4959;
UPDATE npc_text SET Text0_1='Throm-ka, $C. Ich habe heute schlechte Laune, also lasst mich bitte allein, es sei denn, Ihr könnt mir helfen.' WHERE id=4960;
UPDATE npc_text SET Text0_0='Ich kann Euch nicht ausbilden, $C.', Text0_1='Ich kann Euch nicht ausbilden, $C.' WHERE id=4973;
UPDATE npc_text SET Text0_0='Mein Name ist Ulysses Rackmore, ich bin Kapitän der GALLANT. Und dies ist das Logbuch, in dem die letzten Tage meines dem Untergang geweihten Schiffes verzeichnet sind.$B$BWir waren mit einer Lieferung Vorräte unterwegs zur Mondfederfeste, als Naga die Seiten der GALLANT angriffen. Wir konnten den Angriff abwehren, aber wir wussten, dass sie zurückkommen würden. Da ich den Verlust meiner wertvollen Besitztümer fürchtete, segelte ich die GALLANT zu einer Insel vor der Küste von Desolace - einer Insel, auf der Ruinen von alten Gebäuden der Nachtelfen zu finden sind. Und dort habe ich meine Schatztruhe zurückgelassen.' WHERE id=4974;
UPDATE npc_text SET Text0_0='Meine Truhe ist eine geniale Konstruktion: Man benötigt zwei Schlüssel, um sie zu öffnen, einen aus Gold und einen aus Silber. Den ersten Schlüssel, den aus Silber, gab ich meinem ersten Maat; den goldenen Schlüssel behielt ich selbst.$B$BDoch wir waren dazu verdammt, beide zu verlieren. Ich verlor meinen Schlüssel während des nächsten Nagaangriffs, und der meines ersten Maats ging am nächsten Tag verloren, als wir von Kaltschnappmakruras angegriffen wurden und diese ihn mit sich in die Tiefe zogen.$B$BIch bin sicher, dass die Naga und Makruras die Schlüssel haben. Ich bin nur froh, dass die beiden Völker verfeindet sind und deswegen die Schlüssel nicht gemeinsam verwenden werden.' WHERE id=4975;
UPDATE npc_text SET Text0_0='Nach dem Angriff der Makruras wussten wir, dass die GALLANT keinem weiteren Angriff standhalten würde. Und so gaben die wenigen überlebenden Matrosen und ich mein Schiff auf.$B$BWir befinden uns jetzt am Strand und werden bald in Richtung Stadt Nijelspitze aufbrechen, doch wir wissen, dass große Gefahren zwischen uns und dieser Zuflucht liegen, und ich fürchte, dass Desolace uns verschlingen wird.$B$BIch schreibe dies jetzt in der Hoffnung, dass jemand von unserer Not erfährt und eines Tages Rache an diesen Scheusalen aus dem Meer nehmen wird, die unseren Untergang bedeuteten.' WHERE id=4976;
UPDATE npc_text SET Text0_1='Der Schrein von Remulos ist für uns vom Zirkel des Cenarius hier in der Mondlichtung ein hochheiliger Ort - also benehmt Euch bitte auf eine Art und Weise, die einem Besucher angemessen ist.$B$BEinem Besucher, der nicht meinen Krummsäbel im Rücken spüren will, versteht sich.' WHERE id=4977;
UPDATE npc_text SET Text0_1='Möge Cenarius über Euch wachen, $GBruder:Schwester;.$B$BMein Name ist Tajarri und ich habe geschworen, diesen Schrein zu beschützen. Es ist mir eine Ehre, Bewahrer Remulos als Aufseher in diesem hochheiligen Land zu dienen.' WHERE id=4978;
UPDATE npc_text SET Text0_0='Bevor die Geißel kam, machte ich gute Geschäfte. Ich habe alles an- und verkauft. Einfach alles!$B$BUnd ich hatte geradezu unglaublich irre Preise!$B$BDa könnt Ihr jeden fragen. Jeden, der nicht tot ist, jedenfalls.' WHERE id=4979;
UPDATE npc_text SET Text0_0='Hallo, $N! Seid Ihr hier, um etwas zu kaufen? Oder vielleicht, um etwas zu verkaufen?$B$BBessere Preise findet Ihr nirgendwo oder ich will nicht Augustus der Meschuggene heißen!' WHERE id=4980;
UPDATE npc_text SET Text0_0='Das hier ist ein gefährlicher Ort, $N. Seid lieber vorsichtig.' WHERE id=4982;
UPDATE npc_text SET Text0_0='Vergeudet meine Zeit nicht, $C.', Text0_1='Vergeudet meine Zeit nicht, $C.' WHERE id=4984;
UPDATE npc_text SET Text0_0='Solche wie Euch bilde ich nicht aus, $C. Sucht Euch einen anderen Lehrer, ehe ich wütend werde.', Text0_1='Solche wie Euch bilde ich nicht aus, $C. Sucht Euch einen anderen Lehrer, ehe ich wütend werde.' WHERE id=4985;
UPDATE npc_text SET Text0_0='Immer mit der Ruhe, Mann. Ich bild bloß Kriegers aus.', Text0_1='Immer mit der Ruhe, Mann. Ich bild bloß Kriegers aus.' WHERE id=4986;
UPDATE npc_text SET Text0_0='Was kann ich\'n für dich tun, Mann?', Text0_1='Was kann ich\'n für dich tun, Mann?' WHERE id=4987;
UPDATE npc_text SET Text0_0='Aufgrund einer akuten Inkompatibilität Eurer Klassenspezifikationen bin ich leider nicht in der Lage, auf welche Weise auch immer zur Ausmerzung eines eventuellen Defizits im Bereich Eurer Fähigkeiten beizutragen.', Text0_1='Aufgrund einer akuten Inkompatibilität Eurer Klassenspezifikationen bin ich leider nicht in der Lage, auf welche Weise auch immer zur Ausmerzung eines eventuellen Defizits im Bereich Eurer Fähigkeiten beizutragen.' WHERE id=4988;
UPDATE npc_text SET Text0_0='Exzellent! Ihr erfüllt die geforderten Parameter und Grundvoraussetzungen! Was kann ich für Euch tun?', Text0_1='Exzellent! Ihr erfüllt die geforderten Parameter und Grundvoraussetzungen! Was kann ich für Euch tun?' WHERE id=4989;
UPDATE npc_text SET Text0_0='Hegnar Schlingerschuss, zu Euren Diensten!' WHERE id=4990;
UPDATE npc_text SET Text0_0='Euresgleichen kann ich nicht ausbilden, $C.', Text0_1='Euresgleichen kann ich nicht ausbilden, $C.' WHERE id=4993;
UPDATE npc_text SET Text0_0='Was kann ich für Euch tun, mein Kind?', Text0_1='Was kann ich für Euch tun, mein Kind?' WHERE id=4994;
UPDATE npc_text SET Text0_0='Einst herrschten der Feuerlord und seine Brüder über die ganze Welt, sterbliches Wesen. Er war ein Diener der Alten Götter und am Kampf gegen die Titanen um die Herrschaft über diese Welt beteiligt. Die siegreichen Titanen verbannten meinen Meister und seine Brüder auf die Ebene der Elementare, auf der sie bis zum Ende aller Zeiten verbleiben sollten.' WHERE id=4995;
UPDATE npc_text SET Text0_0='Ich kann euch nichts lehren, Jungchen.' WHERE id=4996;
UPDATE npc_text SET Text0_0='Was zieht Ihr vor, Schusswaffen oder Bögen?', Text0_1='Was zieht Ihr vor, Schusswaffen oder Bögen?' WHERE id=4997;
UPDATE npc_text SET Text0_0='Ich verfüge nicht über das Wissen, das Ihr sucht.', Text0_1='Ich verfüge nicht über das Wissen, das Ihr sucht.' WHERE id=4998;
UPDATE npc_text SET Text0_0='Was kann ich\'n für Euch tun?', Text0_1='Was kann ich\'n für Euch tun?' WHERE id=4999;
UPDATE npc_text SET Text0_0='Bei der Ausbildung kann ich Euch nich\' helfen, $GJungchen:Mädchen;. $GEin:Eine:c; $C, das wär die richtige Wahl, wenn Ihr \'n Lehrer sucht.', Text0_1='Bei der Ausbildung kann ich Euch nich\' helfen, $GJungchen:Mädchen;. $GEin:Eine:c; $C, das wär die richtige Wahl, wenn Ihr \'n Lehrer sucht.' WHERE id=5000;
UPDATE npc_text SET Text0_0='Wie?', Text0_1='Wie?' WHERE id=5001;
UPDATE npc_text SET Text0_0='Vergeudet meine Zeit nicht.', Text0_1='Vergeudet meine Zeit nicht.' WHERE id=5002;
UPDATE npc_text SET Text0_0='Ich kann Euch nicht ausbilden.' WHERE id=5003;
UPDATE npc_text SET Text0_0='He, Maan, du bist $C? Ja dann musst du einen von euren Lehrern finden, Maan.', Text0_1='He, Maan, du bist $C? Ja dann musst du einen von euren Lehrern finden, Maan.' WHERE id=5004;
UPDATE npc_text SET Text0_0='Die Geister haben Euch zu mir geführt. Wie kann ich Euch zu Diensten sein?', Text0_1='Die Geister haben Euch zu mir geführt. Wie kann ich Euch zu Diensten sein?' WHERE id=5005;
UPDATE npc_text SET Text0_0='Mögen die Geister mit Euch sein.', Text0_1='Mögen die Geister mit Euch sein.' WHERE id=5006;
UPDATE npc_text SET Text0_0='Du hast \'nen starken Geist, Mann. Was kann dieser Troll für dich tun?', Text0_1='Du hast \'nen starken Geist, Mann. Was kann dieser Troll für dich tun?' WHERE id=5007;
UPDATE npc_text SET Text0_0='Dem Geistern soll\'n Euch beschütz\'n, Mann.', Text0_1='Dem Geistern soll\'n Euch beschütz\'n, Mann.' WHERE id=5008;
UPDATE npc_text SET Text0_0='Wo wollt Ihr hin? Egal - wenn Ihr schnell dorthin kommen wollt, dann sucht nicht weiter!' WHERE id=5009;
UPDATE npc_text SET Text0_0='Ich versuche, die Späherkuppe so gut wie möglich versorgt zu halten, aber das geht nicht immer, so oft, wie wir im Einsatz sind.' WHERE id=5010;
UPDATE npc_text SET Text0_0='Vor gerade mal dreihundert Jahren beschwor ein leichtsinniger Zwerg namens Thaurissan den Meister aus seinem feurigen Reich. Seine Rückkehr in diese jämmerliche Welt verwüstete das umgebende Land und erschuf diesen vulkanischen Kern. Seit jener Zeit schläft der mächtige Ragnaros unter seinem Berg.' WHERE id=5011;
UPDATE npc_text SET Text0_0='Wir, seine Diener, die wir ihm die Treue geschworen haben, tun, was er uns befiehlt, und setzen seine eiserne Herrschaft durch. Nun, da er von den Energien seines feurigen Reiches abgeschnitten wurde, ist Ragnaros nur noch ein Schatten seines wahren Selbst. Doch es steckt noch mehr als genug Kraft in ihm, um Euch den Garaus zu machen.' WHERE id=5012;
UPDATE npc_text SET Text0_0='Guten Tag, $Gder Herr:gnädige Frau;. Ich würde Euch ja die Hand geben, aber wie Ihr sehen könnt, bin ich gerade mitten in was drin. Kann ich Euch behilflich sein?' WHERE id=5013;
UPDATE npc_text SET Text0_0='Für den Anfang macht Ihr Euch zwar gut, aber Ihr müsst noch sehr viel lernen, $N.' WHERE id=5014;
UPDATE npc_text SET Text0_1='Ich wünsche mir nichts sehnlicher, als an den Kämpfen teilnehmen zu können, die vor den Stadtmauern toben. Doch die Pflicht zwingt mich, mein Wissen mit all jenen zu teilen, die an meiner Stelle kämpfen, und so bleibe ich in diesem kleinen Haus hier eingesperrt... vorerst.' WHERE id=5016;
UPDATE npc_text SET Text0_0='Ishnu-alah, $GBruder:Schwester;. Bringt Ihr Neuigkeiten aus fernen Landen? Mein Herz sehnt sich nach Nachrichten aus Teldrassil.' WHERE id=5019;
UPDATE npc_text SET Text0_0='Es ist mir eine Ehre, mein Wissen mit Euch zu teilen, $N. Setzt es weise ein.' WHERE id=5020;
UPDATE npc_text SET Text0_0='Ihr müsst Euch einen erfahreneren Lehrer suchen, denn meine Fähigkeiten habt Ihr bereits überschritten. Ich rate Euch, sprecht mit Lilyssia Nachtluft ganz hier in der Nähe - sie verfügt über großes Wissen, das sie Euch vermitteln kann.' WHERE id=5021;
UPDATE npc_text SET Text0_1='Die Druiden sagen, dass es nur eine Frage der Zeit sei, ehe die Natur die Horde aus Kalimdor vertreibt. Und dennoch verbreitet sich die Verderbnis immer weiter über unser Land, sogar hier auf Teldrassil.' WHERE id=5022;
UPDATE npc_text SET Text0_1='Gut gemacht! Ihr lernt immer mehr über die Wirkweise von Tränken.' WHERE id=5023;
UPDATE npc_text SET Text0_1='Elune-adore, $Gmein Bruder:meine Schwester;. Ihr seid hier willkommen.' WHERE id=5025;
UPDATE npc_text SET Text0_1='Ist es nicht erstaunlich, was man aus den Pflanzen des Waldes alles herstellen kann? Elune hat uns wahrlich mit ihrer Freigiebigkeit gesegnet.' WHERE id=5026;
UPDATE npc_text SET Text0_1='Es ist nett von Euch, mich aufzusuchen, aber Eure Fertigkeiten übertreffen die meinen. Sprecht doch einfach mit Sylvanna Mondwald dort drüben. Sie sucht noch nach einem weiteren Schüler.' WHERE id=5027;
UPDATE npc_text SET Text0_0='Hat Euch etwa meine herzallerliebste Kali geschickt? Ich liebe die Kleine einfach über alles.' WHERE id=5028;
UPDATE npc_text SET Text0_0='Ich nehme nicht an, dass Ihr hier seid, weil Ihr über meine süße Kali reden wollt, oder? Na gut. Dann reden wir halt über Tränke. Hat Euch schon mal jemand gesagt, dass Ihr zu eingleisig denkt?' WHERE id=5029;
UPDATE npc_text SET Text0_0='Ihr seid über meine Fähigkeiten hinausgewachsen, $N. Geht und belästigt Tally Brausefitz - das ist eine Gnomin, die in der Tüftlerstadt von Eisenschmiede lebt. Ach, und wenn Ihr eh dorthin kommt, dann richtet Kali liebe Grüße von mir aus.' WHERE id=5030;
UPDATE npc_text SET Text0_0='Lasst Euch von diesen großen, unschuldigen Augen bloß nich\' täuschen. Tally is\' \'ne echte Expertin, gar keine Frage. Ich lern jeden Tag was Neues. Was glaubt\'n Ihr, warum ich wohl sonst hier in der Tüftlerstadt bin? Seh ich etwa wie \'n Ingenieur aus? Na eben.' WHERE id=5031;
UPDATE npc_text SET Text0_0='Ihr seht aus wie jemand, der auf Abenteuer aus is\'. Also ich hatt davon schon genug, als wir Trolle hierher kamen. Jetzt nehm ich jeden Tag, wie er kommt, und bilde andere Leute darin aus, mit den Abenteuern fertig zu werden.' WHERE id=5034;
UPDATE npc_text SET Text0_0='In den Flaschen da auf dem Tisch sind lauter Tränke, die ich heute gebraut habe. Und da heißt es, ein Topf, den man beobachtet, fängt nie an zu kochen! Ha!' WHERE id=5037;
UPDATE npc_text SET Text0_0='Die haben gesagt, ich wär doof, aber ich werd\'s ihnen zeigen! Ich werd jeden Topf beobachten, den ich hab, und denen beweisen, dass man trotzdem Sachen da drin kochen kann! Ja, ich hab grad \'ne Glückssträhne! Also, wer is\' jetzt doof, hä? Hä?' WHERE id=5038;
UPDATE npc_text SET Text0_0='Da kann ich Euch nicht helfen. Ihr wisst mehr über Tränke als ich. Und außerdem muss ich auf die Töpfe Acht geben. Redet mit Yelmak da drüben. Der weiß viel über Tränke.' WHERE id=5039;
UPDATE npc_text SET Text0_0='Bena arbeitet, als wäre sie besessen. Tag und Nacht sitzt sie über diesen Tränken und manchmal vergisst sie darüber sogar, zu schlafen oder zu essen. Ich weiß ja, dass unsere untoten Verbündeten Hilfe brauchen, aber ich fürchte langsam, sie fügt sich damit selbst Schaden zu.' WHERE id=5040;
UPDATE npc_text SET Text0_0='Ich muss Hunderte von Phiolen zerbrochen haben, als ich damals meine ersten Tränke braute. In meinen groben Händen war das Glas irgendwie so zerbrechlich... Ich hoffe, Ihr werdet vorsichtiger sein, als ich es war.' WHERE id=5041;
UPDATE npc_text SET Text0_0='Eure Lernfähigkeit ist größer als die meine. Jetzt müsst Ihr mit Bena Winterhuf dort drüben sprechen, die Euch weiter unterrichten kann. Aber bitte, nehmt nicht zu viel von ihrer Zeit in Anspruch. Sie braucht ihre Ruhe.' WHERE id=5042;
UPDATE npc_text SET Text0_1='Schön, mal ein freundliches Gesicht zu sehen. Apotheker Johaan redet nicht viel mit mir, es sei denn, er will etwas. Er ist einfach schon zu lange aus dem Grab raus. Irgendwann werd ich auch so sein, hab ich gehört. Schaurig, das. Aber vielleicht ist es mir dann ja egal.' WHERE id=5043;
UPDATE npc_text SET Text0_1='Seid Ihr hier, weil Ihr was über Tränke lernen wollt? Ich kann Euch viel beibringen. Ich bin vielleicht tot, aber mein Gehirn hab ich nicht verloren. Noch nicht.' WHERE id=5044;
UPDATE npc_text SET Text0_0='Ja, es stimmt, einst war ich ein Mensch. Was sonst vielleicht ein Nachteil wäre, gereicht uns jetzt aber zum Vorteil, $N, denn ich kenne die menschlichen Schwächen genauestens.' WHERE id=5046;
UPDATE npc_text SET Text0_0='Wohlan denn. Schneidet von einem dieser Exemplare noch einen Klumpen Fleisch ab. Prüfen wir einmal, was Ihr bisher gelernt habt. Das kann dann als Grundlage für das weitere Vorgehen dienen.' WHERE id=5047;
UPDATE npc_text SET Text0_0='Euer Wissen um Tränke hat das meine überflügelt, aber Ihr müsst noch viel lernen. Geht die Stufen im Südosten hinunter und sucht Doktor Marsh auf. Er wird Euch lehren, was wahres Leiden - ähem. Er ist ein Verlassener, der Euch weiter ausbilden wird.' WHERE id=5048;
UPDATE npc_text SET Text0_0='Hört auf, mich so anzusehen. Ich bin vielleicht kein Abenteurer wie Ihr, aber das heißt noch lange nicht, dass ich nicht auch ein paar Muskeln haben kann. Dieses ganze Umgerühre macht einen ganz schön stark.' WHERE id=5049;
UPDATE npc_text SET Text0_1='Lasst Euch von den Knopfaugen und dem Rauschebart bloß nicht täuschen. Vosur ist ein begabter Lehrling und ein guter Freund, auch wenn ich immer einen steifen Nacken bekomme, wenn ich mit ihm rede.' WHERE id=5054;
UPDATE npc_text SET Text0_1='Ähm, Ihr wisst bereits alles, was ich Euch beibringen könnte. Vielleicht ist es an der Zeit, dass Ihr mit jemand anderem sprecht. Versucht es mal bei Ainethil, einer Nachtelfin, die in Darnassus auf der Terrasse der Handwerker lebt. Die Gute hat echtes Talent!' WHERE id=5055;
UPDATE npc_text SET Text0_1='Ishnu-dal-dieb, $N. Seid Ihr hier, weil Ihr nach Wissen strebt?' WHERE id=5059;
UPDATE npc_text SET Text0_1='Selbst die kleinsten Veränderungen bei den Zutaten eines Trankes können diesen zutiefst verändern. Geduld und Genauigkeit, $Gmein Junge:mein Kind;, sind Dinge, die Euch in jeder Lebenslage von Nutzen sein werden.' WHERE id=5064;
UPDATE npc_text SET Text0_1='Meine Fertigkeit im Herstellen von Tränken ist der Euren unterlegen. Ihr solltet lieber mit Ainethil dort drüben sprechen. Sie wird sehen, dass Ihr bereit für weiterführende Lektionen seid.' WHERE id=5065;
UPDATE npc_text SET Text0_1='Ach, wieder jung zu sein und zum ersten Mal den Zauber der Tränke zu entdecken... Vielen Dank, dass Ihr mich an all das erinnert habt, $N. Und nun geht und teilt Eure jugendliche Begeisterung mit Milla Fairancora dort drüben.' WHERE id=5067;
UPDATE npc_text SET Text0_1='Ich habe in all den Jahren viel über Tränke gelernt, $N, und all dieses Wissen steht Euch zur Verfügung. Ihr müsst nur fragen.' WHERE id=5068;
UPDATE npc_text SET Text0_0='Ich bin beschäftigt, $N. Sagt Whuut, er soll sich darum kümmern, ganz egal, was es ist. Der Kleine hat schon den ganzen Tag im eigenen Saft geschmort. Wenn das noch viel länger so weiter geht, dann wird er noch irgendwem wehtun.' WHERE id=5069;
UPDATE npc_text SET Text0_0='Es ist mir eine Ehre, Euch kennen zu lernen, $N, aber Eure Fertigkeiten im Tränkebrauen sind größer als die meinen. Sucht Doktor Herbert Halsey auf, das ist ein Untoter, den Ihr im Apothekarium von Unterstadt findet. Er kann Euch von jetzt an weiterhelfen.' WHERE id=5070;
UPDATE npc_text SET Text0_0='Da habt Ihr den falschen Orc erwischt. Anfänger bilde ich nicht aus. Redet mit dem jungen Whuut da drüben... aber verwendet keine zu komplizierten Wörter.' WHERE id=5072;
UPDATE npc_text SET Text0_0='Noch $Gein Schüler:eine Schülerin;, wie? Na gut. Mal sehen, ob ich Euch helfen kann.' WHERE id=5073;
UPDATE npc_text SET Text0_0='Glyx und ich haben eine ungeschriebene Übereinkunft: Er beschafft mir meine Vorräte, und ich mache ihn reich. Doch, ernsthaft! Was dieser Halsabschneider mir für so einen jämmerlichen Beutel zerkrümeltes Königsblut abknöpft - das treibt selbst einem erwachsenen Goblin das Wasser in die Augen.' WHERE id=5074;
UPDATE npc_text SET Text0_1='Als ich die Untoten zum ersten Mal sah, musste ich weinen, denn ich konnte mir das Grauen und die Einsamkeit, denen sie ausgesetzt sind, kaum vorstellen. Aber dann sprach die Erdenmutter zu mir, ihre Stimme sang in meinem Blut. Und sie sprach: "Rette sie."' WHERE id=5079;
UPDATE npc_text SET Text0_1='Könnt Ihr nicht spüren, welchen Schmerz die Erdenmutter ob ihrer verlorenen Kinder, der Untoten, empfindet? Sagt schnell, was Ihr zu sagen habt, $N. Jede Sekunde kommt mich teuer zu stehen. Wir müssen ihnen helfen, ihren Weg zurück zu ihr zu finden!' WHERE id=5080;
UPDATE npc_text SET Text0_1='Ich kann es mit meinem Gewissen nicht vereinbaren, Zeit mit der Ausbildung von Anfängern zu verbringen, solange unsere Verbündeten, die Untoten, so schrecklich leiden müssen. Bitte sprecht stattdessen mit Kray dort drüben - er kann Euch vorläufig weiterhelfen.' WHERE id=5081;
UPDATE npc_text SET Text0_1='Eure Fertigkeiten im Tränkebrauen übertreffen die meinen. Sucht Doktor Herbert Halsey auf; das ist ein Untoter, den Ihr im Apothekarium von Unterstadt findet. Er verfügt über das Wissen, um Euch weiter auszubilden, und außerdem könnt Ihr dort aus erster Hand mit ansehen, welches Leid seinem Volk widerfährt.' WHERE id=5083;
UPDATE npc_text SET Text0_0='Noch eine Störung. Wie soll ich denn so bitte schön arbeiten? Nein, darauf müsst Ihr nicht antworten - sehe ich etwa so aus, als ob mich interessieren würde, was Ihr zu sagen habt? Warum geht Ihr nicht einfach und nervt Herrn Ich-bin-ja-ach-so-wichtig stattdessen?' WHERE id=5084;
UPDATE npc_text SET Text0_0='Noch ein Schüler. Na toll. Deswegen gehört Lydon auch zur Königlichen Snobvereinigung und kriegt die Forschungsaufträge, während ich mich immer und immer wieder als Lehrer betätigen darf. Wenn ich noch einen einzigen weiteren Preis für gutes Lehren gewinne, dann komm ich niemals davon weg.' WHERE id=5088;
UPDATE npc_text SET Text0_0='Jetzt, wo Algernon mir meine Vorräte liefert, kann ich bequem hier arbeiten und muss nie wieder auch nur ein einziges Kraut suchen gehen. Und wenn ich nie wieder raus in die Natur müsste, wär\'s mir nur recht!' WHERE id=5089;
UPDATE npc_text SET Text0_0='Ich kann Euch kein Wissen mehr vermitteln. Geht durch den Bogengang und dann die Rampe im Südosten hinunter. Sprecht dort mit Doktor Herbert Halsey. Das ist ein Verlassener, der Euch die weiterführenden Techniken erläutern kann.' WHERE id=5090;
UPDATE npc_text SET Text0_0='Ich finde Eure Dreistigkeit, einfach so zu mir zu kommen und um Ausbildung zu bitten, nicht sonderlich komisch. Geht durch den Bogengang und dann die Rampe im Südosten hinunter. Sprecht dort mit Doktor Martin Felben. Das ist ein Verlassener, der Euch die Grundlagen beibringen kann.' WHERE id=5092;
UPDATE npc_text SET Text0_1='Dieser Trank scheint von Mal zu Mal weniger Schaden zu heilen, wenn ich ihn braue, obwohl ich jedes Mal dieselben Zutaten verwende. Das lässt nur eine Schlussfolgerung zu: Die Pflanzen verändern sich. Aber warum und in was, kann ich nicht sagen.' WHERE id=5094;
UPDATE npc_text SET Text0_1='Es ist an der Zeit, dass Ihr einen fortgeschritteneren Lehrer aufsucht. In der Festung Mondfeder in Feralas lebt die Nachtelfin Kylanna Windraunen. Sie ist ein genialer Kopf und kann Euch zweifellos weiter ausbilden.' WHERE id=5095;
UPDATE npc_text SET Text0_1='Eure Fertigkeiten im Tränkebrauen müssen erst noch wachsen, ehe ich Euch als $GSchüler:Schülerin; aufnehmen kann. Vielleicht können Euch in der Zwischenzeit ja Sylvanna Mondwald oder Milla Fairancora dort drüben helfen.' WHERE id=5097;
UPDATE npc_text SET Text0_1='Eure Ausbildung macht gute Fortschritte. Ihr könnt stolz auf das sein, was Ihr erreicht habt.' WHERE id=5098;
UPDATE npc_text SET Text0_0='Meine Kollegen und ich sammeln die tödlichsten Substanzen, die wir kennen, und testen ihre Auswirkungen an unseren Feinden. Sobald ich alles zusammen habe, was ich brauche, werde ich Seuchen brauen, wie die Welt sie noch nie gesehen hat.' WHERE id=5099;
UPDATE npc_text SET Text0_0='Unglaublich. Eure Tränke zeugen von einem Wissen, das dem meinen gleichkommt. Ich kann Euch nicht weiter ausbilden. Sprecht lieber mit dem Orc Rogvar - Ihr findet ihn in Steinard in den Sümpfen des Elends. Er ist für seine bahnbrechenden Experimente bekannt.' WHERE id=5100;
UPDATE npc_text SET Text0_0='Ihr seid noch nicht weit genug, um bei mir zu lernen. Sprecht mit Doktor Martin Felben dort drüben, oder geht die Rampe im Südosten hinauf und sucht Doktor Marsch auf. Das sind Verlassene, die Euch das beibringen können, was Ihr braucht.' WHERE id=5102;
UPDATE npc_text SET Text0_0='Wollt Ihr etwas lernen? Dann konzentriert Euch, $N. Es gilt, viel Stoff durchzunehmen!' WHERE id=5103;
UPDATE npc_text SET Text0_1='Einst hieß ich die Dunkelheit willkommen, denn sie war die Schwester der Nacht. Doch nun ist es ständig dunkel, auch bei Tag, und die Wälder seufzen und sehnen sich nach Nahrung. Wenn wir nicht schnell handeln, dann wird sich diese Verseuchung immer weiter ausbreiten und bald die ganze Welt bedecken.' WHERE id=5104;
UPDATE npc_text SET Text0_1='Eure Fertigkeiten zeigen, dass Ihr Euch Eurer Kunst nachhaltig verschrieben habt, doch ich möchte erst noch einen weiteren Beweis für Eure Hingabe sehen. Ich werde Euch eine Aufgabe übertragen. Wenn Ihr sie erfüllt, dann bin ich es zufrieden.' WHERE id=5106;
UPDATE npc_text SET Text0_0='Kriegshäuptling Thrall selbst hat mich gebeten, hier heraus zu kommen. Es war mir eine Ehre, seinem Befehl nachzukommen, und es fällt mir auch nicht schwer, hier zu sein. Ich hätte mir keinen besseren Posten als Steinard wünschen können - fast jedes Kraut, das ich vielleicht brauchen könnte, wächst hier draußen.' WHERE id=5108;
UPDATE npc_text SET Text0_0='Hmmm, ja, wie ich sehe, habt Ihr ein gewisses, wenn auch kleines, Talent, was das Tränkebrauen angeht. Ob Ihr würdig seid, $Gmein Schüler:meine Schülerin; zu werden, bleibt jedoch abzuwarten. Ich habe eine Aufgabe für Euch. Beeindruckt mich.' WHERE id=5110;
UPDATE npc_text SET Text0_0='Nur eine winzigkleine Anpassung... ah, jetzt!' WHERE id=5112;
UPDATE npc_text SET Text0_0='Ihr wollt also $GIngenieur:Ingenieurin; werden, hm? Tja, da seid Ihr hier genau richtig, bei Muradin!' WHERE id=5114;
UPDATE npc_text SET Text0_0='Wo ist mein Spinner? Irgendwo hatte ich den doch gerade noch...' WHERE id=5115;
UPDATE npc_text SET Text0_1='Ich habe gehört, das neue Henweigh 6000 rollt bald vom Band. Ich kann es kaum noch erwarten!' WHERE id=5118;
UPDATE npc_text SET Text0_1='Ich würde Euch liebend gern unterrichten! Meiner Schwester damit zeigen, dass sie nicht die Einzige ist, die was von der Ingenieurskunst versteht...' WHERE id=5120;
UPDATE npc_text SET Text0_1='Hallo! Ich heiße Jenna! Und Ihr?' WHERE id=5121;
UPDATE npc_text SET Text0_1='Ihr wollt ausgebildet werden? Von mir? Phantastisch! Das Einzige, was noch besser ist, als Kugeln und Sprengkörper herzustellen, ist, dieses Wissen mit jemandem zu teilen! Dann mal los!' WHERE id=5123;
UPDATE npc_text SET Text0_1='Ah, heute ist ein wunderbarer Tag, um etwas in die Luft zu jagen!' WHERE id=5124;
UPDATE npc_text SET Text0_1='Ihr müsst langsam anfangen, Sachen zu lernen, die über meinen Horizont gehen. Redet mit Lilliam Spindelfunks, der vor dem Zelt da drüben steht. Der wird Euch zeigen, wo\'s langgeht.' WHERE id=5125;
UPDATE npc_text SET Text0_1='Diese Art von Arbeit ist nichts für zaghafte Gemüter. Ein Onkel von mir hat eine Hand dabei verloren! Der arme alte Stummel... Na, wie dem auch sei, wenn Ihr immer noch Interesse habt, dann kann ich Euch vielleicht ein paar Sachen zeigen.' WHERE id=5126;
UPDATE npc_text SET Text0_0='Wie? Wollt Ihr etwas?' WHERE id=5127;
UPDATE npc_text SET Text0_0='Da kann ich Euch nich\' helfen! Ihr seid schon soweit, bei jemandem zu lernen, der schon länger dabei is\' als ich. Wartet mal... Ihr seid so $Geiner:eine; von der Allianz, also müsst Ihr mit Lilliam Spindelfunks in Sturmwind reden.' WHERE id=5128;
UPDATE npc_text SET Text0_0='Ja, ich zeige Euch ein paar Sachen. Schauen wir mal, wie Ihr Euch macht, vielleicht überrascht Ihr mich ja!' WHERE id=5129;
UPDATE npc_text SET Text0_0='(schnüffelt) Riecht Ihr das? Findet Ihr den Geruch von Schmierfett nicht auch einfach nur toll?' WHERE id=5130;
UPDATE npc_text SET Text0_0='Ich bau gern Sachen. Aber noch viel lieber jag ich Sachen in die Luft!' WHERE id=5133;
UPDATE npc_text SET Text0_0='Ich kann ein paar Sachen. Soll ich sie Euch zeigen?' WHERE id=5135;
UPDATE npc_text SET Text0_0='Wer sagt, Orcs sin\' nich\' schlau? Man braucht viel Hirnschmalz, um Ingenieur zu werden!' WHERE id=5136;
UPDATE npc_text SET Text0_0='Wisst Ihr, wer richtig schlau is\'? Nogg! Der is\' sogar noch schlauer als ich. Geht einfach hin und sagt Nogg, Thund hätt Euch geschickt! Der is\' der da an der Schalttafel da drüben.' WHERE id=5137;
UPDATE npc_text SET Text0_0='Ihr denkt also, Ihr seid schlau genug? Ihr glaubt, Ihr habt es drauf? Das werden wir gleich sehen!' WHERE id=5138;
UPDATE npc_text SET Text0_0='Die Dunkle Fürstin hat gesagt, das Wissen sei unsere stärkste Waffe in diesem Krieg.' WHERE id=5139;
UPDATE npc_text SET Text0_0='Ihr seid bereit für höhere Dinge. Sprecht mit Franklin Lloyd, rechts von mir. Er wird Euch von jetzt an anleiten.' WHERE id=5140;
UPDATE npc_text SET Text0_0='Ich bin nie zu beschäftigt, um mit Fremden zu sprechen! Fast nie jedenfalls...' WHERE id=5142;
UPDATE npc_text SET Text0_0='Wartet mal... vierzehn plus sieben, zwei im Sinn... äh, ja?' WHERE id=5147;
UPDATE npc_text SET Text0_0='Ohne Gegenleistung tu ich nichts! Ich bring Euch bei, was ich weiß, aber erst möchte ich, dass Ihr eine kleine Aufgabe für mich erledigt.' WHERE id=5149;
UPDATE npc_text SET Text0_0='Irgendwann könnt Ihr von mir lernen, aber noch nicht jetzt. Warum redet Ihr nicht erst einmal mit Glimm Springspross, hier rechts von mir? Sobald Ihr etwas Erfahrung habt, könnt Ihr gern wieder zu mir kommen.' WHERE id=5150;
UPDATE npc_text SET Text0_1='Die Pläne für meinen sechs Meter großen Truthahn-Beträufler sind beinahe fertig! Jetzt brauche ich nur noch einen sechs Meter großen Truthahn...' WHERE id=5152;
UPDATE npc_text SET Text0_0='Wenn der Tag doch nur ein paar Stunden mehr hätte...' WHERE id=5157;
UPDATE npc_text SET Text0_0='Ihr habt Euch das Vorrecht verdient, bei einem Lehrer für Fortgeschrittene lernen zu dürfen. Geht doch rüber zu meinem Kumpel Roxxik.' WHERE id=5158;
UPDATE npc_text SET Text0_0='Jeder Tag stellt eine Prüfung dar, da bildet der heutige Tag keine Ausnahme. Wenn Ihr diese Prüfung besteht, dann werde ich Euch einen Teil meines Wissens anvertrauen.' WHERE id=5159;
UPDATE npc_text SET Text0_0='Ihr habt Euch noch nicht das Recht verdient, Euch von mir ausbilden lassen zu dürfen. Sprecht erst mit Thund da drüben am Tisch. Kommt wieder, wenn er sagt, dass Ihr bereit dazu seid.' WHERE id=5160;
UPDATE npc_text SET Text0_0='Ich hab keine Zeit für Geschwätz, ich bin ein Profi!' WHERE id=5162;
UPDATE npc_text SET Text0_0='Mal sehen, wie fähig Ihr seid, ja? Wenn Ihr diese Aufgabe zu meiner Zufriedenheit erledigt, dann werde ich die Zeit aufwenden, die es kostet, um Euch auszubilden.' WHERE id=5164;
UPDATE npc_text SET Text0_0='Wenn meine Berechnungen korrekt sind, wird das Rad bald veraltet sein.' WHERE id=5167;
UPDATE npc_text SET Text0_0='Die Dinge, die ich Euch beibringen würde, würden Euch das Gehirn zu Brei werden lassen. Kommt wieder, wenn Ihr genug gelernt habt!' WHERE id=5170;
UPDATE npc_text SET Text0_0='Manchmal überrasche ich sogar mich selbst!' WHERE id=5172;
UPDATE npc_text SET Text0_0='Ich kann nicht lehren, was ich nicht weiß! Wenn es nur jemanden gäbe, der mehr weiß als ich... wartet, da fällt mir gerade jemand ein! Geht und sucht Buzzek Pratzenschwing in Gadgetzan auf!' WHERE id=5173;
UPDATE npc_text SET Text0_0='Netter Versuch, aber Ihr braucht etwas mehr Erfahrung, bevor ich Euch ausbilden kann! Zurück in den Schützengraben!' WHERE id=5175;
UPDATE npc_text SET Text0_0='Ich arbeite an einer Methode, Öl unter der Erde zwischen großen Städten zu transportieren, aber alle behaupten, damit würde ich in die Röhre kucken.' WHERE id=5177;
UPDATE npc_text SET Text0_0='Zeigt mir, was Ihr draufhabt! Es wird Euch nicht Leid tun, das verspreche ich!' WHERE id=5179;
UPDATE npc_text SET Text0_1='Seid gegrüßt! Was führt Euch an meine Türschwelle?' WHERE id=5181;
UPDATE npc_text SET Text0_1='Ich fürchte, ich kann jemandem mit Eurer Erfahrung nichts beibringen. Aber Ihr habt das Ende des Weges noch nicht erreicht. Sprecht mit Lucan Cordell hinter dem Tresen dort. Er wird Euch durch den nächsten Abschnitt Eurer Reise leiten.' WHERE id=5182;
UPDATE npc_text SET Text0_1='Hmm... tja, Ihr scheint fähig genug zu sein. Wenn Ihr wirklich lernen wollt, dann wird Euch nichts im Weg stehen! Ich werde Euch auf Euren ersten Schritten entlang des Weges leiten, der hoffentlich lang und erfüllend sein wird.' WHERE id=5183;
UPDATE npc_text SET Text0_0='Es gibt andere Möglichkeiten als Hammer und Amboss, um Stahl zu stärken.' WHERE id=5184;
UPDATE npc_text SET Text0_0='Ihr seid aber reichlich neugierig, was? Exzellent! Passt gut auf, das hier könnte Eure Haut retten, wenn es hart auf hart kommt!' WHERE id=5186;
UPDATE npc_text SET Text0_1='Die Göttin hat mir Weisheit gewährt, Fremdling. Wenn es ihr Wille ist, werde ich sie an Euch weitergeben.' WHERE id=5187;
UPDATE npc_text SET Text0_1='Eure Fertigkeiten übersteigen mein Wissen. Es ist an der Zeit für Euch, neuen Herausforderungen gegenüberzutreten. Taladan wird Euch bei der Vorbereitung helfen. Er steht dort drüben am Tisch.' WHERE id=5188;
UPDATE npc_text SET Text0_1='Elunes Geheimnisse sind mannigfaltig, aber sie können nicht mit jedem Dahergelaufenen geteilt werden.' WHERE id=5190;
UPDATE npc_text SET Text0_0='Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!' WHERE id=5193;
UPDATE npc_text SET Text0_0='Hmm, tja, Ihr habt es also bis hierher geschafft... Dann sprecht mal mit Lavinia Raab dort drüben bei dem anderen Bücherregal. Wenn Ihr es Euch mit ihr nicht verderbt, könnt Ihr vielleicht noch Einiges lernen.' WHERE id=5194;
UPDATE npc_text SET Text0_0='Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!', Text0_1='Ich habe keine Zeit zu verschwenden, Fremdling. Erklärt Euer Anliegen oder verschwindet!' WHERE id=5195;
UPDATE npc_text SET Text0_0='Was würde ich nicht alles geben, um an einem Tag wie diesem dort draußen im Flachland zu sein... aber meine Pflicht hält mich hier.' WHERE id=5196;
UPDATE npc_text SET Text0_0='Ich kann Euch nichts lehren, was Ihr nicht schon wisst. Das Wissen meines Vaters übersteigt mein eigenes aber um vieles. Geht hinein und sprecht mit ihm. Viel Glück!' WHERE id=5197;
UPDATE npc_text SET Text0_0='Es gibt viele Geheimnisse, die mit Geduld und Entschlossenheit enthüllt werden können. Lasst uns also beginnen...' WHERE id=5198;
UPDATE npc_text SET Text0_0='Throm-ka! Was sucht Ihr denn, Fremdling?' WHERE id=5199;
UPDATE npc_text SET Text0_0='Ihr seid hier falsch. Sprecht mit dem Experten Godan dort drüben. Von ihm werdet Ihr mehr erfahren.' WHERE id=5200;
UPDATE npc_text SET Text0_0='Es ist glorreich, im Kampf zu sterben! Aber mit dem, was ich Euch lehre, sterbt Ihr vielleicht nicht so schnell, hm?' WHERE id=5201;
UPDATE npc_text SET Text0_0='Wer seid Ihr? Was wollt Ihr?' WHERE id=5202;
UPDATE npc_text SET Text0_0='Sehe ich aus wie der Große Zampano der Verzauberer? Eure Fertigkeiten erfordern die Führung eines Fachmanns! Geht und sucht Kitta Feuerwind am Turm von Azora auf.' WHERE id=5203;
UPDATE npc_text SET Text0_0='Das soll ein Witz sein, nicht wahr? Ich schätze, Ihr sucht nach dem ANDEREN kleinen Burschen, Thonys Säulenstein. Er ist unten. Und jetzt verschwindet!' WHERE id=5205;
UPDATE npc_text SET Text0_0='Wenn Ihr habt, was nötig ist, werde ich Euch gern den Weg weisen.' WHERE id=5207;
UPDATE npc_text SET Text0_0='Ein wahrer Profi kennt seine Grenzen und ich schäme mich nicht zuzugeben, dass Eure Fertigkeiten die meinen übersteigen. Ich möchte, dass Ihr Kitta Feuerwind am Turm von Azora besucht. Sie ist sehr berühmt und das mit gutem Grund.' WHERE id=5208;
UPDATE npc_text SET Text0_0='Ihr scheint recht selbstsicher zu sein. Da seid Ihr doch bestimmt mit einem Test Eurer Fertigkeiten einverstanden...' WHERE id=5209;
UPDATE npc_text SET Text0_0='Ihr seid nicht bereit. Sprecht mit Betty Quinn, sie steht dort drüben an der Treppe. Kehrt zu mir zurück, wenn Ihr eine höhere Erleuchtung erreicht habt.' WHERE id=5210;
UPDATE npc_text SET Text0_0='Wenn Ihr Naga jagen wollt, werdet Ihr alle Hilfe benötigen, die Ihr bekommen könnt. Tief im Wasser könnt Ihr blubbernde Felsspalten finden, die genug Luftblasen erzeugen, dass Ihr unter Wasser atmen könnt. Schwimmt nahe heran, damit Ihr die Blasen nutzen könnt.' WHERE id=5212;
UPDATE npc_text SET Text0_0='Es ist wahr, dass ich über Fertigkeiten verfüge, die ich jene lehre, die würdig sind. Könntet Ihr dazugehören?' WHERE id=5213;
UPDATE npc_text SET Text0_0='Ich glaube, dass Ihr dieser Aufgabe gewachsen seid. Werdet Ihr die Herausforderung annehmen?' WHERE id=5215;
UPDATE npc_text SET Text0_1='Wenn Ihr eine wahrlich lohnende Suche wünscht, dann ist ein der Verzauberkunst gewidmetes Leben das richtige für Euch!' WHERE id=5218;
UPDATE npc_text SET Text0_0='Die Verzauberkunst wird seit Generationen in unserer Familie gepflegt. Ich habe sie schon in jungen Jahren gelernt, so wie mein Sohn jetzt.' WHERE id=5223;
UPDATE npc_text SET Text0_0='Mot hat Euch doch wohl nicht zu mir geschickt, oder? Er sollte es besser wissen. Ihr seid noch nicht bereit, das zu verstehen, was ich Euch lehre. Sucht meinen Sohn Mot auf, er ist da draußen, und beendet zuerst die Ausbildung bei ihm.' WHERE id=5226;
UPDATE npc_text SET Text0_0='Ah, sehr gut. Ich erkenne an, dass Ihr ein würdiger Schüler seid. Jetzt kann Eure Ausbildung bei mir beginnen!' WHERE id=5227;
UPDATE npc_text SET Text0_1='Ich bekomme nicht viel Besuch. Ihr sucht also nach etwas Bestimmtem, ja?' WHERE id=5228;
UPDATE npc_text SET Text0_1='Wenn ich hier nicht tagein, tagaus beschäftigt wäre, hätte ich das Können erworben, um jemanden wie Euch auszubilden! Leider ist das nicht der Fall. Sprecht mit Hgarth in Sonnenfels im Steinkrallengebirge.' WHERE id=5229;
UPDATE npc_text SET Text0_1='Hmm, eine Hand wäscht die andere. Wenn Ihr tut, was ich sage, und es gut macht, können wir vielleicht Eure Ausbildung erörtern.' WHERE id=5230;
UPDATE npc_text SET Text0_1='Nein, nein, Ihr benötigt VIEL mehr Ausbildung, bevor Ihr bereit seid, von mir zu lernen! Geht und sprecht mit diesem verklemmten Pfuscher Malcomb Wynn. Das ist der Bücherwurm da drüben.' WHERE id=5231;
UPDATE npc_text SET Text0_0='Seid gegrüßt! Wollt Ihr vielleicht die Mysterien des Arkanen ergründen? Hm?', Text0_1='Seid gegrüßt! Wollt Ihr vielleicht die Mysterien des Arkanen ergründen? Hm?' WHERE id=5233;
UPDATE npc_text SET Text0_0='Einige Abenteurer glauben, dass die Verzauberei nicht funktioniere. Im Allgemeinen ändern sie ihre Meinung, wenn ich sie zum Kampf herausfordere!' WHERE id=5238;
UPDATE npc_text SET Text0_0='Ich bin Euren Fertigkeiten nicht gewachsen, Fremdling. Sprecht mit Hgarth in Sonnenfels im Steinkrallengebirge. Er wird Euch mehr Wissen vermitteln können als ich es kann.' WHERE id=5239;
UPDATE npc_text SET Text0_0='Ihr könntet möglicherweise geeignet sein. Lasst es uns herausfinden!' WHERE id=5240;
UPDATE npc_text SET Text0_0='Ihr seid nicht geeignet, um bei mir zu lernen, Fremdling. Wendet Euch an meinen Assistenten Jhag dort drüben.' WHERE id=5241;
UPDATE npc_text SET Text0_1='Was führt Euch zum Turm?' WHERE id=5243;
UPDATE npc_text SET Text0_1='Ihr habt den Gipfel Eurer Ausbildung fast erreicht. Um Euer Talent als Verzauberer wirklich voll auszuschöpfen, müsst Ihr noch eine Lehrerin aufsuchen. Ihr Name ist Annora und Ihr findet sie in Uldaman.' WHERE id=5244;
UPDATE npc_text SET Text0_1='Ihr seid eifrig, das ist gut. Ihr seid aber noch nicht bereit, von jemandem wie mir zu lernen.' WHERE id=5246;
UPDATE npc_text SET Text0_1='Eure Fertigkeiten werden wahrhaft außergewöhnlich. Setzen wir jetzt also Eure Ausbildung fort...' WHERE id=5247;
UPDATE npc_text SET Text0_0='Seid Ihr gekommen, das Wissen von Hgarth zu suchen?' WHERE id=5248;
UPDATE npc_text SET Text0_0='Es gibt nur eine, deren Wissen das meine übersteigt. Ihr seid bereit, ihr Wissen zu empfangen. Sie heißt Annora und Ihr findet sie... in Uldaman.' WHERE id=5249;
UPDATE npc_text SET Text0_0='Ihr habt neue Höhen auf Eurem Weg zur Meisterschaft erreicht. Lasst uns sehen, ob wir Euch noch weiter bringen können.' WHERE id=5252;
UPDATE npc_text SET Text0_1='Seid gegrüßt. Ihr habt meine ewig währende Dankbarkeit dafür, dass Ihr es mit diesen schrecklichen Kreaturen aufgenommen habt! Ich dachte schon, ich müsste mich für immer verstecken!' WHERE id=5253;
UPDATE npc_text SET Text0_1='Eure Ausbildung ist fast abgeschlossen. Nur noch eine letzte Aufgabe steht zwischen Euch und dem Höhepunkt all Eurer Bemühungen.' WHERE id=5255;
UPDATE npc_text SET Text0_1='Es gibt noch immer Lektionen, die Ihr von anderen lernen müsst, bevor ich mein Wissen mit Euch teilen kann. Seid fleißig und gebt die Hoffnung nicht auf.' WHERE id=5256;
UPDATE npc_text SET Text0_1='Seit mein Ehemann an die Front gezogen ist, hat Helene hier mir Kraft gegeben. Ihr gutes Herz und ihr scharfes Schwert haben mir durch harte Zeiten geholfen. Ich weiß nicht, was ich ohne sie getan hätte.' WHERE id=5257;
UPDATE npc_text SET Text0_1='Oh je, das tut mir Leid. Ich habe nichts Neues, was ich Euch beibringen kann. Wie wäre es, wenn Ihr mit Simon Gerber in der Altstadt von Sturmwind sprecht? Das ist ein Mensch mit starken Muskeln und traumhaften roten Haaren.' WHERE id=5258;
UPDATE npc_text SET Text0_0='Ich weiß bereits viel über Stil. Jetzt muss ich nur noch die Techniken der Lederverarbeitung meistern. Gut, dass Simon ein echter Kumpel ist und mir dabei hilft.' WHERE id=5260;
UPDATE npc_text SET Text0_0='Es gibt nichts, was ich Euch lehren könnte, außer vielleicht ein besseres Auge für Farbe und Details. Sprecht mit Simon Gerber dort drüben, er weiß viel über die fortgeschrittenen Techniken für die Lederverarbeitung.' WHERE id=5261;
UPDATE npc_text SET Text0_0='Dieses Outfit bringt gut das Blau in meinen Augen zur Geltung, meint Ihr nicht? Es ist klar, dass ich über viele Fertigkeiten verfüge. Also kann ich es mir leisten, ein paar meiner Tricks preiszugeben. Hört gut zu, ja?' WHERE id=5262;
UPDATE npc_text SET Text0_1='Willkommen in unserem Heim, $N. Radnaal und ich können Euch wenig bieten, aber wir heißen Euch gern hier willkommen.' WHERE id=5263;
UPDATE npc_text SET Text0_1='Ihr müsst Eure Stiche kleiner machen, $N. Dann werden weniger Gegenstände aus Euren Köchern und Munitionsbeuteln fallen. Außerdem mag niemand eine ausgebeulte Tunika.' WHERE id=5265;
UPDATE npc_text SET Text0_1='Wenn Ihr an Ledernäharbeiten interessiert seid, seid Ihr am richtigen Ort. Faldron und Telonis sind wahre Künstler und ich hoffe, viel von ihnen zu lernen.' WHERE id=5273;
UPDATE npc_text SET Text0_1='Oh, es tut mir Leid, aber Ihr wisst jetzt mehr über Leder als ich. Sprecht mit Faldron dort drüben, wenn Ihr mehr lernen wollt.' WHERE id=5274;
UPDATE npc_text SET Text0_1='Es ist ja so aufregend, hier in Darnassus zu sein, umgeben von talentierten Menschen! Ich weiß einfach, dass ich meine Familie stolz machen werde. Und wenn ich sehr viel Glück habe, bittet mich die Schildwache vielleicht, ihren Streitkräften an der Front beizutreten!' WHERE id=5275;
UPDATE npc_text SET Text0_1='Dieser Balg ist noch nicht bereit zur Verarbeitung. Ich wünschte, es würde nicht so lange dauern, Leder herzustellen. Es muss doch eine Möglichkeit geben, diese ganze Warterei zu verkürzen. Das kann doch für meine Gesundheit nicht gut sein, oder?' WHERE id=5276;
UPDATE npc_text SET Text0_1='Ah, welch Unglück! Meine Fertigkeiten sind nicht gut genug, um Euch von Nutzen zu sein. Kann man nicht ändern. Sprecht mit Fimble Feinspindel dort drüben beim Webstuhl. Er arbeitet schon länger als ich mit Leder.' WHERE id=5277;
UPDATE npc_text SET Text0_1='Au prima, endlich was zu tun während der Wartezeit. Ihr rettet mir wahrlich das Leben, $N!' WHERE id=5278;
UPDATE npc_text SET Text0_1='Gefällt Euch der Teppich? Ich habe das Biest selbst getötet und gehäutet. Mögen alle Zentauren ebenso leicht fallen.' WHERE id=5279;
UPDATE npc_text SET Text0_1='Ich kann Euch nichts mehr beibringen. Sprecht mit Karolek, wenn Ihr Eure Fertigkeiten in der Lederverarbeitung weiter verfeinern wollt.' WHERE id=5280;
UPDATE npc_text SET Text0_1='Zu dumm, dass wir nicht die Menschen und ihre Verbündeten häuten können. Diese ganze Haut ist leider verloren. Aber wer wollte schon das dünne Leder tragen, das man daraus machen könnte...' WHERE id=5281;
UPDATE npc_text SET Text0_1='Haut, wunderbare Haut. Sie hält die Innereien... drin. Euch ist wahrscheinlich aufgefallen, dass es meiner Haut nicht so gut geht. Wenn ich nur daran denke, dass ich mir mal um meine Poren Sorgen gemacht habe. Jetzt ist kaum noch Haut übrig, um die ich mich sorgen kann. Irgendwie ist das sehr befreiend.' WHERE id=5282;
UPDATE npc_text SET Text0_1='Bevor die Seuche kam, wusste ich nichts über Leder. Jetzt weiß ich genug, um andere zu lehren. Der Tod hat mir viel Zeit gelassen.' WHERE id=5284;
UPDATE npc_text SET Text0_0='Fleisch, Haut, Knochen... ein guter Jäger lässt nichts verderben. Jedes Stückchen Leder kann gerettet werden, um vielleicht die Rüstung eines Verbündeten zu verstärken. Daher ist jeder von uns allein zwar stark, aber zusammen werden wir unbesiegbar.' WHERE id=5285;
UPDATE npc_text SET Text0_0='Wir leben und sterben und das Land bleibt, auf ewig, unberührt von unseren Schritten. Daher kann das Land nicht uns gehören, $N. Vielmehr gehören wir ihm. Daher schützen wir das Land, denn ohne das Land können wir nicht hoffen zu überleben.' WHERE id=5287;
UPDATE npc_text SET Text0_0='Die Höhlen unter uns sind eine Quelle merkwürdiger und wundersamer Bälge, aber Ihr müsst Euch in Acht nehmen, wenn Ihr in ihre Tiefen vordringt, $N. Denkt daran, dass Eure eigene Haut viel wertvoller ist.' WHERE id=5288;
UPDATE npc_text SET Text0_0='Sogar hier in der Wildnis sucht Ihr Wissen. Euer Wissensdurst ist ein bewundernswerter Zug.' WHERE id=5290;
UPDATE npc_text SET Text0_0='Cairne selbst lebt hier, sagt man. Wenn ich ein passendes Geschenk angefertigt habe, werde ich ihn aufsuchen. Vielleicht hat er Worte der Weisheit für mich.' WHERE id=5291;
UPDATE npc_text SET Text0_0='Euer Talent geht über das meinige hinaus. Sprecht mit Tarn, sein Wissen über Leder ist viel größer als meines. Ich bin sicher, er würde einen weiteren Schüler willkommen heißen.' WHERE id=5292;
UPDATE npc_text SET Text0_0='Säubert Eure Hände, bevor Ihr mit einem Projekt beginnt. Die natürliche Schönheit des Leders würde leiden, wenn es mit Blut oder Schmutz verunreinigt würde.' WHERE id=5293;
UPDATE npc_text SET Text0_0='Als ich meinen Weg aus dem Grab grub, dachte ich, meine Familie würde mich mit offenen Armen empfangen. Stattdessen verscheuchte sie mich aus dem Dorf und schrie in einer Sprache, die ich nicht mehr verstehen konnte.' WHERE id=5294;
UPDATE npc_text SET Text0_0='Ich kann Euch nicht helfen; Eure Arbeit geht über meine hinaus. Sprecht mit Arthur Moor, dort drüben bei den Särgen. Er kann Euch weiter ausbilden.' WHERE id=5295;
UPDATE npc_text SET Text0_1='Ihr übersteigt meine beschränkten Fertigkeiten. Sucht die Nachtelfin Ainethil auf der Terrasse der Handwerker in Darnassus auf. Sie kann Euch mehr beibringen. Ich wünschte nur, ich könnte mit Euch gehen.' WHERE id=5314;
UPDATE npc_text SET Text0_1='Meine Lehren wären in diesem Stadium zu fortgeschritten für Euch. Wenn Ihr mehr über Tränke lernen möchtet, sprecht stattdessen mit Tel\'Athir hier.' WHERE id=5316;
UPDATE npc_text SET Text0_1='Hört gut zu, $N, denn ich habe viel Wissen mitzuteilen.' WHERE id=5317;
UPDATE npc_text SET Text0_0='Randal ist ein kleiner Geck, aber man kann sich auf ihn verlassen, was mehr ist, als ich von vielen Schülern sagen kann, die ich aufnehme. Nun ja, man kann sie wohl nicht dafür schelten, dass sie auf Abenteuer aus sind.' WHERE id=5320;
UPDATE npc_text SET Text0_0='Es tut mir Leid, aber Ihr habt die Grenzen dessen erreicht, was ich Euch lehren kann. Wenn Ihr mehr lernen wollt, geht zu Telonis auf der Terrasse der Handwerker in Darnassus. Er ist ziemlich talentiert, wie ich höre.' WHERE id=5321;
UPDATE npc_text SET Text0_0='Kommt zu mir zurück, wenn Ihr mehr Ausbildung genossen habt. Randal Worth dort drüben kann Euch dabei helfen.' WHERE id=5323;
UPDATE npc_text SET Text0_0='Hmm, ich denke, ich kann einen weiteren Schüler aufnehmen. Also los, steht nicht einfach so rum. Sagt mir, was Ihr lernen wollt.' WHERE id=5324;
UPDATE npc_text SET Text0_0='Darianna ist viel versprechend, aber sie ist so voller Energie, dass es für sie schwierig ist, still zu halten und zuzuhören. Mit ihrer Kraft und Beweglichkeit fürchte ich, dass es nicht lange dauert, bis sie zum Kampf gerufen wird. Wir müssen sie schnell ausbilden.' WHERE id=5325;
UPDATE npc_text SET Text0_0='Wenn Ihr für weitere Ausbildung hier seid, habt Ihr meine Fähigkeiten überschätzt. Sprecht mit Telonis dort drüben, er kann Euch helfen.' WHERE id=5326;
UPDATE npc_text SET Text0_0='Viel zu tun! Ich war nicht mehr so beschäftigt, seit mein Cousin aus Versehen allen Stoff und alles Leder in Gnomeregan verdampfen ließ. Mein Gott, war das hier vielleicht zugig danach. Ja, ja, $N, das waren noch Zeiten...' WHERE id=5330;
UPDATE npc_text SET Text0_0='Die stärksten Bälge kommen von Kodos, die sich zum Sterben in diese Einöde begeben haben.' WHERE id=5335;
UPDATE npc_text SET Text0_0='Ich biete Lehrlingen keine Ausbildung. Sucht hier im Brachland nach dem Tauren Waldor in den Höhlen des Wehklagens. Er kann Euch bei allem helfen, was Ihr benötigt.' WHERE id=5338;
UPDATE npc_text SET Text0_0='Seit Ihr gekommen, um Euch weiter ausbilden zu lassen? Dann streift den Staub der Straße von Eurer Kleidung und lasst uns mit der Arbeit beginnen.' WHERE id=5339;
UPDATE npc_text SET Text0_0='Meine Axt hat viele Schlachten gesehen und ich habe sie immer griffbereit. Feindliche Streitkräfte haben schon etliche Male ihren Weg in unsere Hauptstadt erkämpft, aber habt keine Angst: Wir konnten sie immer wieder zurücktreiben.' WHERE id=5340;
UPDATE npc_text SET Text0_0='Ich kann Euch nicht mit weiterer Ausbildung dienen: Eure Fertigkeit bei der Lederverarbeitung kommt der meinen gleich. Ihr solltet mit Una dort drüben sprechen. Ich werde Eure Fertigkeit bestätigen, falls das nötig ist.' WHERE id=5341;
UPDATE npc_text SET Text0_0='Ich bin zufrieden mit Eurem bisherigen Fortschritt. Ganz sicher hat die Erdenmutter Eure Hände inspiriert.' WHERE id=5344;
UPDATE npc_text SET Text0_0='Wir sind auf diesem Kontinent im Moment nicht sehr fest etabliert, aber die Menschen können uns nicht für immer zurückschlagen. Dann werden sie für unsere Sklaverei bezahlen. Wir werden ihnen für ihren Verrat die lügnerischen Zungen herausschneiden.' WHERE id=5345;
UPDATE npc_text SET Text0_0='Ich würde Euch ausbilden, wenn ich könnte, aber Ihr seid zu gut für mich. Ich sage Euch etwas: Sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Sie hat wirklich Talent.' WHERE id=5346;
UPDATE npc_text SET Text0_0='Ob ich beschäftigt bin? Was soll das heißen? Glaubt Ihr etwa, dass ich hier den ganzen Tag stehe und Daumen drehe? Wenn Ihr hier seid, um etwas zu kaufen, dann sprecht mit Handor.' WHERE id=5350;
UPDATE npc_text SET Text0_0='Ihr werdet von mir nichts Neues lernen, Ihr wisst bereits zu viel. Wenn Ihr mehr Ausbildung wollt, dann sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Richtet ihr meine Grüße aus, ja?' WHERE id=5351;
UPDATE npc_text SET Text0_0='Die verwinkelten Straßen von Orgrimmar wurden geschaffen, um Eindringlinge abzuschrecken. Könnt Ihr Euch vorstellen, wie schnell sich einer dieser kümmerlichen Gnome hier verirrt? Ha! Wenn er Glück hat, dann bringe ich ihn zur Strecke und behalte ihn als Haustier.' WHERE id=5354;
UPDATE npc_text SET Text0_0='Vor meinem Tod war ich Bibliothekar; ich hatte nichts mit Kriegen und Seuchen zu tun. Ich habe nie darum gebeten, zu dem hier zu werden. Auf der anderen Seite werde ich nicht kampflos aufgeben, jetzt, wo meine Gedanken wieder meine eigenen sind.' WHERE id=5355;
UPDATE npc_text SET Text0_0='Ich freue mich sagen zu können, dass Eure Talente die meinen übersteigen. Und jetzt geht weg. Ähm, sprecht mit der Taurin Una in den nordwestlichen Läden des Zentralplateaus von Donnerfels. Sie ist sehr bewandert, ganz gewiss.' WHERE id=5356;
UPDATE npc_text SET Text0_0='Ich hatte mich zuerst im Magieviertel eingerichtet, aber dort war es sehr laut. Das hier ist eine deutliche Verbesserung, abgesehen von ein paar hartnäckigen Schülern, die mich hier aufspüren, obwohl ich umgezogen bin.' WHERE id=5359;
UPDATE npc_text SET Text0_1='Elune hat den Nachtelfen viele Gaben gewährt. Im Gegenzug ist es unsere Verantwortung, diese Wälder vor der dämonischen Besudelung zu bewahren, die die Horde immer mit sich bringt.' WHERE id=5360;
UPDATE npc_text SET Text0_1='Überprüft Eure Maße immer doppelt, bevor Ihr beginnt, das Leder zu zerschneiden. Wir können es uns nicht leisten, Material zu verschwenden.' WHERE id=5364;
UPDATE npc_text SET Text0_0='Im Silberwald entlang den Rändern des nördlichen Gezeitenstroms liegt ein verfallenes Boot am Strand. Westlich davon befindet sich das, was Ihr sucht. Schwimmt Ihr jedoch zu weit hinaus, werdet Ihr vor Erschöpfung im Meer sterben. Was Ihr sucht, befindet sich auf dem Meeresboden in einer Schließkassette und wartet darauf, dass Ihr es an Euch nehmt.$B$BEs wird für Euch schwer werden, einen Tauchgang in solche Tiefen ohne Hilfe zu überleben, doch fasst Euch ein Herz; ein blubbernder Felsspalt bietet neue Atemluft und hilft bei der Suche nach dieser Hälfte des Anhängers. Benutzt ihn weise.' WHERE id=5374;
UPDATE npc_text SET Text0_1='Im nördlichen Teil von Dunkelküste verläuft der Klippenquell. Er fließt von den Ländern herunter, die als der Teufelswald bekannt sind. Wo der Fluss in den Nebelrand fließt, müsst Ihr gen Norden reisen, dorthin, wo der tiefste Teil des Ozeans beginnt. Bei einer Steinformation, die einige wegen ihrer Form die Steinklaue nennen, könnt Ihr finden, was Ihr sucht.$B$BDoch seid gewarnt - bei der Steinklaue sammeln sich Drescher und greifen alle an, die in ihr Gebiet eindringen.' WHERE id=5375;
UPDATE npc_text SET Text0_1='Im nordöstlichen Brachland liegt ein Teich, der durch Verschmutzung gekippt ist. Die Einheimischen nennen ihn das Schlickermoor; die Venture Company hat das einst kristallblaue Wasser in einen Sumpf aus industriellen Nebenprodukten und Abfällen verwandelt. Im Herzen dieses Moores liegt, was Ihr sucht, $N.$B$BDoch seid gewarnt - die Venture Company toleriert keine Eindringlinge und sie sieht jeden, der nicht für sie arbeitet, als einen solchen an. Als Beschützer der Natur wird sie besonders gegen Euch eingestellt sein, da sie diese Aktivitäten heftigst ablehnt.' WHERE id=5376;
UPDATE npc_text SET Text0_0='Das Gras ist weich und üppig. Die Luft ist frisch, voller Licht und Geräusche. Alles, was ich gesehen habe, deutet darauf hin, dass Teldrassil ein Traum ist, der wahr geworden ist. Warum ist dann mein Herz voller düsterer Vorahnungen?' WHERE id=5393;
UPDATE npc_text SET Text0_0='Ihr seid weiter fortgeschritten als meine Fertigkeit, die ich Euch lehren könnte. Reist jetzt zum Nistgipfel im Hinterland und sprecht mit dem Zwerg Drakk Steinhand. Seine Arbeit ist großartig, viel besser als meine eigene.' WHERE id=5394;
UPDATE npc_text SET Text0_0='Versucht nicht, dem Leder Euren Willen aufzuzwingen. Lasst vielmehr seine natürliche Veranlagung diktieren, welche Form und Funktion es annehmen wird. Das hier ist kein Kampf, der gewonnen werden soll. Arbeitet mit dem Leder, nicht dagegen.' WHERE id=5397;
UPDATE npc_text SET Text0_1='Die Nadel ist klein, aber der Faden ist lang und die Stiche sind fein. Unser Pakt mit Thrall und den anderen wird halten. Die Horde wird nicht aus diesen Ländern vertrieben werden.' WHERE id=5398;
UPDATE npc_text SET Text0_1='Sogar meine Talente haben eine Grenze und Ihr habt sie erreicht. Herzlichen Glückwunsch, $N. Euer nächster Halt sollte das Camp Mojache in Feralas sein. Die Tauren Hahrana Eisenhaut lebt dort; sie ist für ihre Fertigkeit bekannt.' WHERE id=5399;
UPDATE npc_text SET Text0_1='Es ist so lange her, seitdem ich selbst ein Novize war, dass ich nicht glaube, für Euch der beste Lehrer zu sein. Sprecht lieber mit Tran oder Mak dort drüben. Einer von ihnen sollte Eure Fragen beantworten können.' WHERE id=5401;
UPDATE npc_text SET Text0_1='Cairne hat uns in ein Land gebracht, das reich an Möglichkeiten für die Zukunft ist. Wir hatten Glück und daher geben wir dieses Glück an andere weiter. Aus diesem Grund begann ich zu unterrichten, $N.' WHERE id=5402;
UPDATE npc_text SET Text0_0='Ich kann verblüffende Dinge mit den Materialien tun, die ich hier draußen finde. Bei Muradins Bart, es gibt phantastische Kreaturen in diesen Hügeln! Stellt Euch ihnen entgegen, $N, und bringt mir ein paar großartige Geschichten zurück.' WHERE id=5403;
UPDATE npc_text SET Text0_0='Es geht doch nichts über eine kleine Herausforderung. Meint Ihr nicht auch? Und darum werdet Ihr genau tun, was ich sage.' WHERE id=5405;
UPDATE npc_text SET Text0_1='Alle Anfragen für meine Arbeiten müssen persönlich vom Käufer gemacht werden. Orwin bittet mich häufig, das zu überdenken, aber ich werde hier mehr gebraucht. Seht Euch um: Viele dieser Kämpfer vertrauen mir, was ihre Rüstung angeht.' WHERE id=5407;
UPDATE npc_text SET Text0_1='Ich wünschte nur, ich hätte mehr Schüler wie Euch. Nicht viele werden so weit in feindliches Gebiet vordringen. Ich verbeuge mich vor Eurer Courage.' WHERE id=5408;
UPDATE npc_text SET Text0_0='Ich erkenne auf den ersten Blick, dass Eure Garderobe Pflege bitter nötig hat.' WHERE id=5413;
UPDATE npc_text SET Text0_0='Ihr würdet also gern ein paar Dinge lernen? Ich wäre erfreut, Euch dabei zu helfen.' WHERE id=5415;
UPDATE npc_text SET Text0_0='Das Leben ist wie ein sorgfältig gewebter Wandteppich.' WHERE id=5416;
UPDATE npc_text SET Text0_0='Hmm... bei den Dingen, die Ihr lernen müsst, bin ich etwas überfragt. Sprecht mit Sellandus gleich hier rechts den Weg entlang. Er hat mir alles beigebracht, was ich weiß!' WHERE id=5417;
UPDATE npc_text SET Text0_0='Sehr gut, lasst Eure Reise auf dem Weg der Schneiderei beginnen und verliert dabei niemals den Faden.' WHERE id=5418;
UPDATE npc_text SET Text0_0='Nadelarbeit erfordert eine ruhige Hand. Und Geduld... vor allem Geduld.' WHERE id=5419;
UPDATE npc_text SET Text0_0='Es ist Zeit für Euch, nach Darnassus zu reisen und mit Me\'lynn zu sprechen. Sie wird Euch von nun an leiten.' WHERE id=5420;
UPDATE npc_text SET Text0_0='Ich wäre erfreut, Euch unter meine Fittiche zu nehmen. Lasst uns beginnen.' WHERE id=5421;
UPDATE npc_text SET Text0_0='Willkommen in meinem Laden. Was kann ich für Euch tun?' WHERE id=5422;
UPDATE npc_text SET Text0_0='Wartet einen Moment, ich bin nur ein Assistent. Wenn Ihr die wirklich tollen Sachen lernen wollt, dann sprecht mit Steinbraue dort drüben.' WHERE id=5423;
UPDATE npc_text SET Text0_0='Exzellent! Ich hatte auf einen neuen Schüler gehofft. Also gut, dann lasst uns direkt beginnen!' WHERE id=5424;
UPDATE npc_text SET Text0_0='Wir verwenden viele Tierpelze und Häute bei unseren Kreationen.' WHERE id=5425;
UPDATE npc_text SET Text0_0='Ich weiß ein paar Dinge, aber ich bin kein Experte. Zu Eurem Glück ist Magar aber einer. Er ist im hinteren Teil des Ladens. Seht, ob er Euch helfen kann, aber verärgert ihn ja nicht. Er ist sehr reizbar.' WHERE id=5426;
UPDATE npc_text SET Text0_0='Ich werde Euch beibringen, was ich weiß.' WHERE id=5427;
UPDATE npc_text SET Text0_0='Kein Teil der Tiere, die wir jagen, wird verschwendet.' WHERE id=5428;
UPDATE npc_text SET Text0_0='Ihr sucht nach größerem Wissen. Tepa ist mit solchem Wissen gesegnet. Sie sollte irgendwo in der Nähe sein...' WHERE id=5429;
UPDATE npc_text SET Text0_0='Ich freue mich, Eure Bekanntschaft zu machen. Kann ich Euch helfen?' WHERE id=5431;
UPDATE npc_text SET Text0_0='Ich bin kenntnisreich, aber ich bin kein Experte. Wisst Ihr, mit wem Ihr sprechen solltet? Rhiannon! Sie ist nicht weit weg. Fragt in Unterstadt nach ihr.' WHERE id=5432;
UPDATE npc_text SET Text0_0='So... Ihr möchtet also Schneider werden? Sehr gut, wir werden sehen, ob Ihr das Zeug dazu habt.' WHERE id=5433;
UPDATE npc_text SET Text0_0='Wir bekommen hier draußen nicht viel Besuch. Kann ich Euch für etwas interessieren?' WHERE id=5434;
UPDATE npc_text SET Text0_0='Ich bin auch nur ein Schüler. Rhiannon ist die Expertin. Sie ist bei den Kisten dort drüben. Von ihr könnt Ihr sehr viel lernen.' WHERE id=5435;
UPDATE npc_text SET Text0_0='Ja, ich glaube, Ihr habt Potenzial. Vielleicht kann ich Euch das eine oder andere beibringen.' WHERE id=5436;
UPDATE npc_text SET Text0_1='Es gibt keine bessere Methode, um den Kopf frei zu bekommen, als zu weben.' WHERE id=5437;
UPDATE npc_text SET Text0_1='Meine Kenntnisse werden immer noch erweitert und Euer Wissen übersteigt das meine. Wenn Ihr mehr lernen wollt, müsst Ihr mit Me\'lynn sprechen. Sie wird Euch leiten.' WHERE id=5438;
UPDATE npc_text SET Text0_0='He, Mann, was kann ich für Euch tun?' WHERE id=5440;
UPDATE npc_text SET Text0_0='Ich fürchte, dass ich nicht mehr sanft oder bescheiden bin. Das Alter und zahllose Schlachten haben meine Haut und Seele verhärtet. Vielleicht hat mich Cairne daher nach Sonnenfels geschickt... die sanften Winde und die warme Sonne lindern den Schmerz.' WHERE id=5443;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Unsere Fledermäuse sind gut ausgebildet und wohl genährt. Ihr werdet keine schnelleren finden.' WHERE id=5453;
UPDATE npc_text SET Text0_0='Wie kann ich zu Diensten sein?' WHERE id=5454;
UPDATE npc_text SET Text0_0='Seid gegrüßt und willkommen. Zu lächeln fällt mir nicht leicht, aber glaubt mir, ich bin erfreut, Euch dienen zu können.' WHERE id=5455;
UPDATE npc_text SET Text0_1='Der Hippogryph ist ein nobles Tier, stolz und schnell. Es ist eine Ehre, ihn zu trainieren, und er kann seinen Reiter schnell über große Entfernungen transportieren.' WHERE id=5473;
UPDATE npc_text SET Text0_0='Um unsere Verbindung zum Festland aufrechtzuerhalten, haben wir Hippogryphen, die regelmäßig zwischen Rut\'theran und Dunkelküste hin und her fliegen.' WHERE id=5474;
UPDATE npc_text SET Text0_1='Die Fische vor der Küste von Teldrassil sind sehr groß und robust. Man behauptet, dass das schnelle Wachstum unseres neuen Weltenbaums die Ursache sei.' WHERE id=5475;
UPDATE npc_text SET Text0_0='Obwohl die Gewässer der Dunkelküste gefährlicher als die von Teldrassil sind, finden viele Fischer dort ihr Auskommen.' WHERE id=5476;
UPDATE npc_text SET Text0_0='Es ist mir eine Ehre, Abenteurern zu dienen, denn sie sind zukünftige Helden.' WHERE id=5477;
UPDATE npc_text SET Text0_0='Die schnellsten Greife diesseits vom Nistgipfel. Wollt Ihr\'s mal versuchen?' WHERE id=5479;
UPDATE npc_text SET Text0_0='Am nördlichen Dock könnt Ihr ein Schiff nehmen, das Euch nach Rut\'theran und Darnassus bringt. Am südlichen Dock könnt Ihr eine Passage über das große Meer zum Hafen von Sturmwind buchen. Beim westlichen Dock am Ende des Stegs findet Ihr ein Schiff, das Euch zur Azurmythosinsel nahe der Exodar bringt. Ich wünsche Euch eine sichere Reise!' WHERE id=5480;
UPDATE npc_text SET Text0_0='Dieses Dock hier? Tja, Ihr könnt das Schiff kriegen, das zu diesem Nachtelfen-Ort fährt. Wie war der Name? Ähm, Auberdine, glaube ich. Ich bin mir nicht ganz sicher, ich belade und entlade nur Waren.' WHERE id=5481;
UPDATE npc_text SET Text0_0='Diese Fässer zur Ausgrabungsstätte zu bringen ist harte Arbeit, aber es gibt so viele, die davon abhängig sind, dass wir das Sprengpulver dorthin bringen.$B$BWir müssen alles in unserer Macht Stehende gegen die Dunkeleisenzwerge tun!' WHERE id=5494;
UPDATE npc_text SET Text0_0='Meine Windreiter sind dafür ausgebildet, schnell durch die heiße Luft des Brachlandes zu fliegen.' WHERE id=5495;
UPDATE npc_text SET Text0_0='Das Brachland mit seiner heißen Sonne und feindlichen Bewohnern ist der perfekte Ort, um Eure Kraft und Seelenstärke zu testen.' WHERE id=5496;
UPDATE npc_text SET Text0_0='Seid gegrüßt. Wenn Ihr wegen Lederwaren hier seid, werdet Ihr feststellen, dass meine Waren gut gearbeitet sind.' WHERE id=5497;
UPDATE npc_text SET Text0_0='Wenn man auf dem Rücken eines Windreiters reitet, sieht man, dass sein Ruf von Kraft und Tempo wohl verdient ist.' WHERE id=5500;
UPDATE npc_text SET Text0_0='Das hier ist ein gefährliches Geschäft!$B$BMiran hat sich gerade mit einer Lieferung auf den Weg gemacht. Er sollte aber in ein paar Minuten zurück sein.' WHERE id=5501;
UPDATE npc_text SET Text0_0='Seht Euch mein Fleisch an. Ihr solltet nicht ohne ausreichend Proviant ins Brachland ziehen.' WHERE id=5515;
UPDATE npc_text SET Text0_0='Man hat erst gelebt, wenn man vom Rücken eines Windreiters auf die Welt hinabgesehen hat.' WHERE id=5516;
UPDATE npc_text SET Text0_0='Ihr könnt an diesem Dock eine Passage nach Theramore buchen. Beherzigt meine Worte: Das Bereisen dieser Länder ist für Menschen der Allianz nicht gerade sicher. Achtet auf Eure Schritte und auf das, was hinter Euch vorgeht.' WHERE id=5517;
UPDATE npc_text SET Text0_0='Ihr habt mehr Ahnung von Leder als ich selbst. Vielleicht solltet Ihr Aayndia Blütenwind in Astranaar im Wald vom Eschental aufsuchen. Sie verfügt über viel Wissen, das sie einem Schüler Eures Talents bieten kann.' WHERE id=5518;
UPDATE npc_text SET Text0_0='Schönen guten Tag.' WHERE id=5519;
UPDATE npc_text SET Text0_0='Ihr seid meinem Kenntnisstand weit überlegen. Vielleicht kann Euch Georgio Bolero, der Meister von Duncans Textilien um die Ecke, beim Verbessern Eurer Fertigkeiten helfen.' WHERE id=5520;
UPDATE npc_text SET Text0_0='Sprecht mit Lawrence Schneider bei Duncans Textilien, links um die Ecke. Er ist bewandert im Umgang mit Anfängern.' WHERE id=5522;
UPDATE npc_text SET Text0_0='Ihr habt Euren Wert wohl gezeigt. Eure Unterweisung kann nun fortgesetzt werden.' WHERE id=5523;
UPDATE npc_text SET Text0_0='Ah, dieses Gewebe aus Darnassus ist das Feinste!' WHERE id=5524;
UPDATE npc_text SET Text0_0='Na, Ihr seid ein kleiner Besserwisser, was? Ihr wisst mehr als ich, das kann ich Euch sagen! Bildet Euch darauf nichts ein, aber wenn Ihr mehr lernen wollt, dann sprecht mit Georgio Bolero in Sturmwind. Man sagt, dass er viel Ahnung von Schneiderei hat.' WHERE id=5525;
UPDATE npc_text SET Text0_1='Hört Ihr den Ruf der Jagd, $C? Ihr müsst genau hinhören, denn der Ruf kommt aus Eurem Inneren. Er kommt tief aus dem Inneren von uns allen.' WHERE id=5529;
UPDATE npc_text SET Text0_0='Ich habe viele der Dinge hergestellt, die Ihr in dem Laden seht. Was meint Ihr? Wählt Eure Antwort sorgfältig, Euer Leben könnte davon abhängen.' WHERE id=5530;
UPDATE npc_text SET Text0_0='Ihr müsst von jemandem lernen, der mehr weiß als ich. Sprecht mit Josef Gregorian in Unterstadt. Na los, was macht Ihr immer noch hier?' WHERE id=5531;
UPDATE npc_text SET Text0_0='Wie? Warum belästigt Ihr mich? He, ich wette, Ihr könnt nicht einmal eine qualitativ hochwertige Robe herstellen! Sprecht mit Snang und kommt zurück, wenn Ihr meines Talents würdig seid!' WHERE id=5533;
UPDATE npc_text SET Text0_0='Wurde auch Zeit, dass Ihr mal ein paar Resultate vorzuweisen habt! Jetzt werde ich Euch mal Resultate zeigen!' WHERE id=5534;
UPDATE npc_text SET Text0_0='Man sagt, ein Stich zur rechten Zeit spart... ähm, elf...? Ah, ich weiß es nicht mehr. Sowieso ein dummes Sprichwort.' WHERE id=5535;
UPDATE npc_text SET Text0_0='Nicht schlecht... für einen Amateur! Trotzdem wisst Ihr schon so viel wie ich. Wenn Ihr mehr lernen wollt, müsst Ihr mit Josef Gregorian in Unterstadt sprechen.' WHERE id=5536;
UPDATE npc_text SET Text0_1='Was für ein toller Tag! Er inspiriert mich zu einem neuen Design.' WHERE id=5540;
UPDATE npc_text SET Text0_1='Eure Talente sind überragend! Ihr seid wahrlich begabt. Um Eure Suche nach Wissen fortzusetzen, solltet Ihr mit Georgio Bolero in Sturmwind sprechen. Er wird hoch geachtet und kann Eure Ausbildung voranbringen.' WHERE id=5541;
UPDATE npc_text SET Text0_1='Ihr habt Eure Sache gut gemacht. Ich bin stolz darauf, Euch meinen Schüler nennen zu dürfen!' WHERE id=5544;
UPDATE npc_text SET Text0_1='Willkommen in unserem Zelt. Kann ich Euch helfen?' WHERE id=5545;
UPDATE npc_text SET Text0_1='Euer Wissen ist groß. Ihr müsst von jemandem lernen, der weiser ist als ich. Es werden viele Geschichten über die Weisheit von Josef Gregorian erzählt. Ihr findet ihn in Unterstadt.' WHERE id=5546;
UPDATE npc_text SET Text0_1='Sind die Muster nicht wunderschön? Jedes spricht mit seiner eigenen Stimme.' WHERE id=5551;
UPDATE npc_text SET Text0_1='Mein Wissen ist begrenzt, aber ich lerne immer noch. Es ist an der Zeit für Euch, weiterzuziehen. Sucht Josef Gregorian in Unterstadt auf und sagt ihm, dass Ihr bereit seid.' WHERE id=5552;
UPDATE npc_text SET Text0_1='Die Schneiderei kann sehr nützlich sein. Ich musste mal meinen Arm wieder annähen.' WHERE id=5556;
UPDATE npc_text SET Text0_1='Ihr habt Euch hochgearbeitet und es ist an der Zeit, dass Ihr den Chef seht. Geht zu Josef und sagt ihm, dass Ihr bereit seid.' WHERE id=5557;
UPDATE npc_text SET Text0_1='Wie kann ich meine Geheimnisse mit einem Anfänger teilen? Sprecht zuerst mit Victor Ward.' WHERE id=5559;
UPDATE npc_text SET Text0_1='Gut, Ihr zeigt Entschlossenheit! Das gefällt mir. Also, dann fangen wir mal an!' WHERE id=5560;
UPDATE npc_text SET Text0_0='Ja, ich führe diesen Laden jetzt schon eine ganze Zeit. Die Geschäfte gingen in letzter Zeit ziemlich gut. Es kommen viele Abenteurer hier durch...' WHERE id=5561;
UPDATE npc_text SET Text0_0='Ich kenne nur eine einzige Person, die mehr über die Kunst der Schneiderei weiß als ich. Das Letzte, was ich gehört habe, war, dass man diese Person auf der Insel Theramore finden kann. Diese Person solltet Ihr aufsuchen.' WHERE id=5562;
UPDATE npc_text SET Text0_0='Ihr braucht mehr Ausbildung, bevor ich mein Wissen mit Euch teilen kann, fürchte ich.' WHERE id=5564;
UPDATE npc_text SET Text0_0='Gut! Ich sehe, dass meine Bemühungen nicht vergebens sein werden!' WHERE id=5565;
UPDATE npc_text SET Text0_0='Stoff ist ganz in Ordnung. Persönlich arbeite ich aber lieber mit menschlicher Haut.' WHERE id=5566;
UPDATE npc_text SET Text0_0='Ich werde Euch anweisen, wenn die Zeit gekommen ist... und diese Zeit ist nicht jetzt. Ihr müsst zuerst noch mehr lernen.' WHERE id=5569;
UPDATE npc_text SET Text0_0='Gut! Ich sehe, dass meine Bemühungen nicht vergebens sein werden!' WHERE id=5570;
UPDATE npc_text SET Text0_0='Ich habe Könige eingekleidet. Mit der richtigen Ausbildung könnt Ihr das auch.' WHERE id=5571;
UPDATE npc_text SET Text0_0='Ihr seid weit gereist. Es gibt noch eine weitere Aufgabe, die Ihr erfüllen müsst...' WHERE id=5573;
UPDATE npc_text SET Text0_0='Ich kann Euch erst helfen, wenn Ihr mehr Grundlagen gelernt habt. Kommt zu mir zurück, wenn Ihr bereit seid.' WHERE id=5574;
UPDATE npc_text SET Text0_0='Der Ort hier ist ein wenig zugig... aber was kümmert es mich? Ich bin tot.' WHERE id=5575;
UPDATE npc_text SET Text0_0='Ihr macht Euch sehr gut in der Kunst der Schneiderei, muss ich sagen. Tja, dann lasst uns mal sehen, wie gut Ihr WIRKLICH seid...' WHERE id=5577;
UPDATE npc_text SET Text0_0='Wer hat Euch hergeschickt? Ihr seid noch nicht bereit für meinen Unterricht! Man wird Euch sagen, wann Ihr bereit seid.' WHERE id=5578;
UPDATE npc_text SET Text0_1='Ich genieße es ungemein, hier draußen zu sitzen und den Geräuschen des großen Meeres zu lauschen. Oh, was? Ja, es gibt ein Schiff, das Euch zum Hafen von Menethil bringen wird. Es legt von Zeit zu Zeit hier an.' WHERE id=5579;
UPDATE npc_text SET Text0_0='Denkt an meine Worte! Ihr werdet nirgendwo in den östlichen Königreichen Greife finden, die schneller sind als die hier in Thelsamar!' WHERE id=5580;
UPDATE npc_text SET Text0_0='Wenn Ihr mich fragt, gibt es keinen besseren Beruf als Minenarbeiter. Und wenn Ihr mich fragt, gibt es keinen besseren Minenarbeiter als einen Zwerg!' WHERE id=5581;
UPDATE npc_text SET Text0_0='Die Zwerge der Wildhämmer haben vielleicht schnelle Rösser, aber können diese Nistgipfelgreife die Hitze der großen Schmiede aushalten? Das glaube ich nicht!' WHERE id=5582;
UPDATE npc_text SET Text0_0='$GDer beste Minenarbeiter:Die beste Minenarbeiterin; hat $Gseine:ihre; Hacke fest im Griff, ein derbes Herz in $Gseiner:ihrer; Brust und dicken, schwarzen Staub in $Gseinem:ihrem; Haar.' WHERE id=5583;
UPDATE npc_text SET Text0_0='Die stabile Kiste steht in der Mitte des Sägewerks, umgeben von Trümmern und äußerst eifrigen Anhängern des Scharlachroten Kreuzzugs.' WHERE id=5584;
UPDATE npc_text SET Text0_1='Der Bär wandert einen Pfad entlang, der nahe dem Steinkrallenpfad liegt, westlich vom Mystralsee und dem Außenposten der Allianz, der Silberwindzuflucht. Er ist zwar die einfachste der drei Kreaturen, die Euch auf der Jagd im Eschental begegnen werden, aber man sollte ihn nicht unterschätzen. Seine Anwesenheit ist eine beständige Bedrohung für Reisende, die sich zwischen dem Eschental und dem Steinkrallengebirge bewegen.' WHERE id=5593;
UPDATE npc_text SET Text0_1='Die stärkste aller Bestien, die Teil der Jagd im Eschental ist, ist Scharfkralle, dem die selbsternannten Wächter dieses Landes, die Nachtelfen, erlauben frei umherzuziehen. Scharfkralle sucht südlich von hier nach Beute und erfreut sich daran, ahnungslose und unwissende Reisende zu zerreißen.$B$BSolch ein Biest zur Strecke zu bringen, ist das Höchste, das ein Jäger tun kann, $N. Ich wünsche Euch Glück bei der Jagd nach dieser Kreatur!' WHERE id=5594;
UPDATE npc_text SET Text0_1='Dieser Nachtsäbler verhält sich angeblich in der Anwesenheit von Nachtelfen und ihren Verbündeten friedlich, was ihn zu einem noch würdigeren Gegner macht. Shattumbras Jagdroute verläuft vom Rajenbaum im Westen bis hin zu Ländern, die weiter westlich von hier liegen. Ihr Fell hat die Farbe von Pech und ihre Geschwindigkeit darf nicht unterschätzt werden.' WHERE id=5595;
UPDATE npc_text SET Text0_0='Willkommen $C... willkommen an der Frontlinie bei unserer Eroberung des Eschentals! Wenn Ihr etwas zu tun haben wollt, dann seid Ihr am richtigen Ort. Ich suche immer nach tauglichen Individuen, die dabei helfen, der Horde Ruhm zu bringen!' WHERE id=5613;
UPDATE npc_text SET Text0_0='Wenn Ihr etwas über das Kommen und Gehen der Karawanen und Händler hier im Splitterholzposten wissen wollt, dann bin ich zu Eurem Glück der Orc, nach dem Ihr sucht. Aber zu Eurem Unglück mag ich keine Menschen.$B$BWenn Ihr also etwas wollt, dann macht es schnell.' WHERE id=5653;
UPDATE npc_text SET Text0_0='Die Elemente sprechen mit uns, $C. Sie teilen mit uns Wissen, das wir lernen müssen, und sie teilen mit uns Geheimnisse, die geheim gehalten werden müssen.' WHERE id=5654;
UPDATE npc_text SET Text0_0='Durch rigoroses Abtrainieren musste ich vielen Schülern das abgewöhnen, was sie bisher gelernt hatten, damit ich sie von neuem unterrichten konnte. Der Dienst, den ich anbiete, ist jedes Mal schwieriger durchzuführen. Aus diesem Grunde wird er mit jeder Umschulung teurer. Wünscht Ihr, dass ich Euch die Talente nehme, die Ihr erlernt habt?', Text0_1='Durch rigoroses Abtrainieren musste ich vielen Schülern das abgewöhnen, was sie bisher gelernt hatten, damit ich sie von neuem unterrichten konnte. Der Dienst, den ich anbiete, ist jedes Mal schwieriger durchzuführen. Aus diesem Grunde wird er mit jeder Umschulung teurer. Wünscht Ihr, dass ich Euch die Talente nehme, die Ihr erlernt habt?' WHERE id=5673;
UPDATE npc_text SET Text0_0='Wenn Ihr es wünscht, dann liegt es neben der Ausbildung in meiner Macht, das Wissen über Eure Talente aus Eurem Gehirn zu löschen. Doch ich warne Euch: Diese Prozedur wird mit jeder Durchführung schwieriger und daher auch teurer.', Text0_1='Wenn Ihr es wünscht, dann liegt es neben der Ausbildung in meiner Macht, das Wissen über Eure Talente aus Eurem Gehirn zu löschen. Doch ich warne Euch: Diese Prozedur wird mit jeder Durchführung schwieriger und daher auch teurer.' WHERE id=5674;
UPDATE npc_text SET Text0_0='Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.', Text0_1='Das Arkane verdirbt nur die Schwachen. Darum übt unbedingt weiter, sonst ereilt Euch ein ähnliches Schicksal.' WHERE id=5693;
UPDATE npc_text SET Text0_0='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.', Text0_1='Willkommen, $G mein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BSchon bald wird die Zeit kommen, da sich der Smaragdgrüne Traum Eurer bemächtigen wird. Dann werdet Ihr viele Jahre lang eine Art Winterschlaf halten und unter den reinsten aller Formen wandeln. Dann und dort werdet Ihr Eure endgültige Ausbildung erhalten.' WHERE id=5694;
UPDATE npc_text SET Text0_0='Was kann ich für Euch tun?', Text0_1='Was kann ich für Euch tun?' WHERE id=5695;
UPDATE npc_text SET Text0_1='Ich will Euch nichts Böses. Ich spähe die Stacheleber aus und mache mir Notizen über ihre Aktivitäten. Meine Leute haben etwas Merkwürdiges bemerkt - eine Ausstrahlung oder Dunkelheit -, das aus diesem Bereich kommt. Ich bin gekommen, um das zu untersuchen, und was ich gefunden habe, ist recht verstörend.$B$BEs scheint einen untoten Diplomaten zu geben, der die Kunst des Arkanen äußerst gut beherrscht. Ich glaube nicht, dass er einer der Verlassenen ist... Ich glaube, es ist schlimmer.' WHERE id=5713;
UPDATE npc_text SET Text0_0='Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.', Text0_1='Ihr wisst genug, um gefährlich zu sein, aber für mich sieht es immer noch so aus, als würdet Ihr Ausbildung benötigen, $N.' WHERE id=5714;
UPDATE npc_text SET Text0_0='Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.', Text0_1='Euer Wille ist nicht stark genug, um die Kräfte zu manipulieren, die wir kontrollieren, $C.' WHERE id=5715;
UPDATE npc_text SET Text0_0='$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?', Text0_1='$N, wie kann ich Eure Ausbildung in den Augen der Erdenmutter voranbringen?' WHERE id=5716;
UPDATE npc_text SET Text0_0='Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.', Text0_1='Vielfältig sind die Wege der Erdenmutter. Mögen Eure Vorfahren über Euch wachen, $C.' WHERE id=5717;
UPDATE npc_text SET Text0_0='Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.', Text0_1='Es ist weise, dass Ihr Euch in Eurem Wunsch nach mehr Hexenmeisterwissen vor mir erniedrigt, $N.' WHERE id=5719;
UPDATE npc_text SET Text0_0='Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.', Text0_1='Ich kann es mir nicht leisten, mit Euresgleichen meine Zeit zu verschwenden, $C.' WHERE id=5720;
UPDATE npc_text SET Text0_0='Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.', Text0_1='Ich kann schon allein aus der Art, wie Ihr dort steht, erkennen, dass Ihr $Gkein Krieger:keine Kriegerin; seid. $B$BWarum macht Ihr nicht etwas, das sicher ist, wie zum Beispiel ein Buch lesen.' WHERE id=5721;
UPDATE npc_text SET Text0_0='Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!', Text0_1='Die Dunkelheit umgibt Euch nicht, $C. Hört mit dem Geschwätz auf und geht mir aus den Augen! Hinfort mit Euch!' WHERE id=5722;
UPDATE npc_text SET Text0_0='Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.', Text0_1='Ich fürchte, Euer Dunkelheit-Quotient ist nicht ausreichend, als dass sich noch länger mit Euch zu reden lohnt. Geht weiter bitte.' WHERE id=5723;
UPDATE npc_text SET Text0_0='Ihr seid $Gkein Krieger:keine Kriegerin;... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen? $B$B$GEin:Eine:c; $C, $Gder sich für einen Krieger hält:die sich für eine Kriegerin hält:c;. Ha!', Text0_1='Ihr seid $Gkein Krieger:keine Kriegerin;... Wie wollt Ihr mehr als einen Tag unter meiner Ausbildung bestehen? $B$B$GEin:Eine:c; $C, $Gder sich für einen Krieger hält:die sich für eine Kriegerin hält:c;. Ha!' WHERE id=5724;
UPDATE npc_text SET Text0_0='Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.', Text0_1='Ich sehe in Euch nicht den Geist des Kriegers. Ich kann Euch nichts beibringen, was Euch helfen würde, $C.' WHERE id=5725;
UPDATE npc_text SET Text0_0='Die Zerschmetterte Hand konnte Spione überall in die obere Schwarzfelszitadelle einschleusen. Obwohl die Informationen nur langsam kamen, haben wir Entdeckungen gemacht, die nicht ignoriert werden können.' WHERE id=5733;
UPDATE npc_text SET Text0_0='Es scheint, als wäre der Einfluss des schwarzen Drachenschwarms gewachsen und über die Grenzen der brennenden Steppe hinaus bis in das Königreich Sturmwind gelangt! Der Thronräuber wurde inzwischen demaskiert und vertrieben.' WHERE id=5734;
UPDATE npc_text SET Text0_0='So ist es. Die Brutmutter des schwarzen Drachenschwarms hat Illusion und Betrügerei angewendet, um sich als Mitglied des königlichen Hofes zu verkleiden. Und was noch schlimmer ist, sie übernahm die Kontrolle über den derzeitigen Hochlord von Sturmwind, Bolvar Fordragon.$B$BKein Wunder, dass solche närrischen militärischen Entscheidungen gefällt wurden. Die in letzter Zeit erfolgten Bewegungen der Streitkräfte der Allianz hatten uns sehr verwundert. Wir wissen jetzt, dass der schwarze Drachenschwarm versucht hatte, das Reich zu teilen - es von Innen zu zerstören.' WHERE id=5735;
UPDATE npc_text SET Text0_0='<Thrall lacht.>$B$BIhr müsst noch so viel lernen, $R.$B$BIhr seid zu jung, um Euch an die Kriege und das Blutvergießen zu erinnern, das weit in der Vergangenheit liegt. Zu jung, um Euch an die Bürde des Fluchs zu erinnern, die von unseren Leuten getragen wird.$B$BUnsere Position gegenüber der Allianz ist im Moment recht angreifbar. Scharmützel und kleinere Kämpfe finden statt, aber es herrscht Frieden - etwas, das unsere Leute seit Jahrtausenden nicht mehr kannten. Würdet Ihr das aufgeben und uns wieder in die Klauen des Chaos stürzen, nur diesmal gegen einen neuen Feind? Einer, der erst ruhen wird, wenn wir alle vernichtet sind?' WHERE id=5736;
UPDATE npc_text SET Text0_0='Nein, $N, das habt Ihr nicht. Ich gebe Euch aber keine Schuld, denn Eure Gedanken gleichen den meinen, als ich noch jung war. Ungestüm, rebellisch und häufig ohne einen zusammenhängenden Gedanken! Eigenschaften, die ich mir nicht mehr leisten kann.$B$BAber genug gejammert. $R, ich habe eine Aufgabe für Euch, eine, die Ihr vielleicht nicht überleben werdet.' WHERE id=5737;
UPDATE npc_text SET Text0_0='<Thrall lacht.>$B$BDas merke ich mir... Und jetzt passt auf, $N. Die Brutmutter des schwarzen Drachenschwarms, Onyxia, wurde von ihrer Machtposition in Sturmwind verjagt. Sie ist entkommen und zu ihrem Unterschlupf in den Düstermarschen geflüchtet.$B$BWir dürfen nicht zulassen, dass sie so nahe an unseren großen Städten lebt. Wir müssen zuschlagen bevor wir geschlagen werden!$B$BIch will, dass sie vernichtet wird, $N.' WHERE id=5738;
UPDATE npc_text SET Text0_0='Willkommen, $Gmein Bruder:meine Schwester;. Wenn Ihr hier seid um zu lernen, dann bin ich nur zu gern bereit, Euch auszubilden.$B$BWir sind die Bewahrer des Gleichgewichts, jetzt und immerdar, während Malfurion in Träumen liegt. Vergesst das niemals.' WHERE id=5739;
UPDATE npc_text SET Text0_0='Ich sehe hier am Hafen einige interessante Leute kommen und gehen. Passt auf Euch auf, $N. Man weiß nie genau, wem man vertrauen kann, bei den ganzen zwielichtigen Aktivitäten, die hier ablaufen...' WHERE id=5753;
UPDATE npc_text SET Text0_0='In dieser Region des Steinkrallengebirges gibt es viele gefährliche Höhlen. Die Erforschung dieser Höhlen hält viele Abenteuer für die bereit, die ihr Leben für Ruhm und Ehre riskieren wollen.$B$BVielleicht sucht Ihr nach Reichtümern, die Ihr nur erreichen könnt, wenn Ihr Eurem Herz und Eurer Seele folgt, $C.' WHERE id=5773;
UPDATE npc_text SET Text0_0='Fisch sehr lecker. Ihr kommen lernen angeln? Fisch essen? Mmmmm...' WHERE id=5793;
UPDATE npc_text SET Text0_0='Ja, ich kann Euch beibringen zu angeln.' WHERE id=5794;
UPDATE npc_text SET Text0_1='Bitte, verlasst diesen Ort, $R. Ihr wisst nichts von dem, was uns Sorgen bereitet...' WHERE id=5795;
UPDATE npc_text SET Text0_0='Wir leiden vielleicht unter einer Dürre, aber an Fleisch herrscht kein Mangel! Mögt Ihr ein wenig in meinen Waren stöbern?' WHERE id=5798;
UPDATE npc_text SET Text0_1='Kann Zamja Euch helfen, $R?' WHERE id=5799;
UPDATE npc_text SET Text0_0='Ich sorge für sie. Jawoll, Daryl Riknussun, zu Euren Diensten.' WHERE id=5800;
UPDATE npc_text SET Text0_0='Ich bin ein Orc der Medizin, $R. Wenn Ihr hergekommen seid, um zu lernen, werde ich mein Bestes geben, um Euch zu unterrichten.' WHERE id=5813;
UPDATE npc_text SET Text0_1='Seid Ihr ein Schüler der Medizin?' WHERE id=5815;
UPDATE npc_text SET Text0_0='Seid gegrüßt, $C. Kann ich Euch irgendwie helfen?' WHERE id=5819;
UPDATE npc_text SET Text0_0='Meine Frage an Euch, $C, ist diese:$BAus welchem der Aspekte, die von den Titanen geschaffen wurden, wurde die schändliche Kreatur erschaffen, die als Todesschwinge bekannt ist?' WHERE id=5820;
UPDATE npc_text SET Text0_0='Es ist immer nett, wenn jemand mit mir sprechen möchte. Wie laufen die Dinge bei Euch, $C?' WHERE id=5821;
UPDATE npc_text SET Text0_0='Beantwortet mir folgende Frage, $N:$BWas für eine Kreatur war der Lichkönig, bevor er die Seuche über Lordaeron brachte?' WHERE id=5822;
UPDATE npc_text SET Text0_0='Die Thoriumbruderschaft besitzt das Wissen, das erforderlich ist, um die hochheiligen Waffen und Rüstungsteile des Feuerlords herzustellen, aber uns fehlen die Leute, um die Materialien zu beschaffen, die für die Herstellung notwendig sind.$B$BIch, Lokhtos Düsterfeilsch, wurde von der Bruderschaft ermächtigt, die erhabenen Anleitungen für Materialien einzutauschen, die vom geschmolzenen Kern gesammelt wurden.$B$BPräsentiert Euer Angebot, $R.' WHERE id=5834;
UPDATE npc_text SET Text0_0='Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.', Text0_1='Ihr möchtet Eure Dämonen mächtiger machen? Das wird Euch einiges kosten, aber Ihr seid hier richtig.' WHERE id=5835;
UPDATE npc_text SET Text0_0='Ihr seid kein Hexenmeister und ich habe keine Zeit für Euch. Hebt Euch hinweg!', Text0_1='Ihr seid kein Hexenmeister und ich habe keine Zeit für Euch. Hebt Euch hinweg!' WHERE id=5836;
UPDATE npc_text SET Text0_0='Ihr seid zur Ausbildung hergekommen, um sie an Eure Tiere weiterzugeben?', Text0_1='Ihr seid zur Ausbildung hergekommen, um sie an Eure Tiere weiterzugeben?' WHERE id=5838;
UPDATE npc_text SET Text0_0='Ah, ich helfe nur Jägern und ihren Tieren.', Text0_1='Ah, ich helfe nur Jägern und ihren Tieren.' WHERE id=5839;
UPDATE npc_text SET Text0_0='Die Königliche Apothekervereinigung hat nur wenig Geduld für Narren, ebenso wie ich. Wenn wir das nächste Mal über meine Rösser sprechen, begegnen Euch die Einwohner von Unterstadt vielleicht mit Ehrfurcht. Bis dahin...', Text0_1='Die Königliche Apothekervereinigung hat nur wenig Geduld für Narren, ebenso wie ich. Wenn wir das nächste Mal über meine Rösser sprechen, begegnen Euch die Einwohner von Unterstadt vielleicht mit Ehrfurcht. Bis dahin...' WHERE id=5840;
UPDATE npc_text SET Text0_0='Meine Reittiere stelle ich nur denjenigen zur Verfügung, die von den Einwohnern Orgrimmars mit Ehrfurcht behandelt werden. Wenn Ihr Euch uns beweist, dann lasse ich Euch meine mächtigen Wölfe begutachten.', Text0_1='Meine Reittiere stelle ich nur denjenigen zur Verfügung, die von den Einwohnern Orgrimmars mit Ehrfurcht behandelt werden. Wenn Ihr Euch uns beweist, dann lasse ich Euch meine mächtigen Wölfe begutachten.' WHERE id=5841;
UPDATE npc_text SET Text0_0='Tut mir leid, $GMann:Mädel;, aber meine Raptoren verkauf ich keinem, der sich nich\' bewiesen hat. Erst wenn Ihr klar Stellung bezieht und der Dunkelspeerstamm Euch mit Ehrfurcht begegnet, dürft Ihr einen von meinen Raptoren ha\'m.', Text0_1='Tut mir leid, $GMann:Mädel;, aber meine Raptoren verkauf ich keinem, der sich nich\' bewiesen hat. Erst wenn Ihr klar Stellung bezieht und der Dunkelspeerstamm Euch mit Ehrfurcht begegnet, dürft Ihr einen von meinen Raptoren ha\'m.' WHERE id=5842;
UPDATE npc_text SET Text0_0='Ihr seid meinem Volk nicht bekannt. Kodos sind mächtige Reittiere, die von meinem Volk mit viel Liebe und Pflege groß gezogen werden, und solche Tiere überlässt man nicht einfach so jedem beliebigen Fremden. Solange die Ältesten von Donnerfels Euch nicht mit Ehrfurcht begegnen, kann ich Euch leider keinen Kodo verkaufen.', Text0_1='Ihr seid meinem Volk nicht bekannt. Kodos sind mächtige Reittiere, die von meinem Volk mit viel Liebe und Pflege groß gezogen werden, und solche Tiere überlässt man nicht einfach so jedem beliebigen Fremden. Solange die Ältesten von Donnerfels Euch nicht mit Ehrfurcht begegnen, kann ich Euch leider keinen Kodo verkaufen.' WHERE id=5843;
UPDATE npc_text SET Text0_0='Leider begegnen Euch die Nachtelfen von Darnassus und Teldrassil nicht mit Ehrfurcht, $C. Erst wenn Ihr Euch uns gegenüber durch Taten bewiesen habt - und nicht nur durch Worte -, werde ich Euch erlauben, einen Blick auf meine Auswahl an Reittieren zu werfen.', Text0_1='Leider begegnen Euch die Nachtelfen von Darnassus und Teldrassil nicht mit Ehrfurcht, $C. Erst wenn Ihr Euch uns gegenüber durch Taten bewiesen habt - und nicht nur durch Worte -, werde ich Euch erlauben, einen Blick auf meine Auswahl an Reittieren zu werfen.' WHERE id=5844;
UPDATE npc_text SET Text0_0='Es tut mir leid, aber ich bin nicht bereit, Pferde an jemanden zu verkaufen, der vom Königreich Sturmwind nicht wenigstens mit Ehrfurcht bedacht wird. Diese Rösser sind die besten, die sich überhaupt irgendwo finden lassen, und wir wollen doch nicht, dass sie in die Hand unserer Feinde geraten, oder?$B$BDas soll natürlich nicht heißen, dass Ihr ein Feind seid... Verdient Euch einfach das Vertrauen von Sturmwind, dann können wir miteinander ins Geschäft kommen.', Text0_1='Es tut mir leid, aber ich bin nicht bereit, Pferde an jemanden zu verkaufen, der vom Königreich Sturmwind nicht wenigstens mit Ehrfurcht bedacht wird. Diese Rösser sind die besten, die sich überhaupt irgendwo finden lassen, und wir wollen doch nicht, dass sie in die Hand unserer Feinde geraten, oder?$B$BDas soll natürlich nicht heißen, dass Ihr ein Feind seid... Verdient Euch einfach das Vertrauen von Sturmwind, dann können wir miteinander ins Geschäft kommen.' WHERE id=5855;
UPDATE npc_text SET Text0_0='Nicht so schnell, $GJunge:Mädel;... Ich gebe die Zügel eines mächtigen Widders doch nicht jemandem in die Hand, der sich nicht als Freund der Zwerge von Eisenschmiede erwiesen hat. Sobald Euch die Einwohner von Eisenschmiede mit Ehrfurcht begegnen, zeige ich Euch gern alle Widder, die bei mir zum Verkauf stehen.', Text0_1='Nicht so schnell, $GJunge:Mädel;... Ich gebe die Zügel eines mächtigen Widders doch nicht jemandem in die Hand, der sich nicht als Freund der Zwerge von Eisenschmiede erwiesen hat. Sobald Euch die Einwohner von Eisenschmiede mit Ehrfurcht begegnen, zeige ich Euch gern alle Widder, die bei mir zum Verkauf stehen.' WHERE id=5856;
UPDATE npc_text SET Text0_0='Ähm... So gut kenne ich Euch leider nicht, tut mir leid. Will sagen, ich bin offizieller Vertragshändler für Roboschreiter, und da kann ich meine Reittiere wirklich nur an Leute verkaufen, die von den Gnomen mit Ehrfurcht bedacht werden. Wenn die Gnome von Gnomeregan Euch mit Ehrfurcht begegnen, zeige ich Euch gern, was ich auf Lager habe.', Text0_1='Ähm... So gut kenne ich Euch leider nicht, tut mir leid. Will sagen, ich bin offizieller Vertragshändler für Roboschreiter, und da kann ich meine Reittiere wirklich nur an Leute verkaufen, die von den Gnomen mit Ehrfurcht bedacht werden. Wenn die Gnome von Gnomeregan Euch mit Ehrfurcht begegnen, zeige ich Euch gern, was ich auf Lager habe.' WHERE id=5857;
UPDATE npc_text SET Text0_0='Die Zwerge von Eisenschmiede müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Zwerge von Eisenschmiede müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5858;
UPDATE npc_text SET Text0_0='Die Verlassenen von Unterstadt müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Verlassenen von Unterstadt müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5859;
UPDATE npc_text SET Text0_0='Die Verbannten von Gnomeregan müssen Euch mit Ehrfurcht begegnen, bevor ich Euch lehre, wie ein Roboschreiter gesteuert wird, $C.', Text0_1='Die Verbannten von Gnomeregan müssen Euch mit Ehrfurcht begegnen, bevor ich Euch lehre, wie ein Roboschreiter gesteuert wird, $C.' WHERE id=5860;
UPDATE npc_text SET Text0_0='Die Menschen von Sturmwind müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Menschen von Sturmwind müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5861;
UPDATE npc_text SET Text0_0='Die Nachtelfen von Darnassus müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Nachtelfen von Darnassus müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5862;
