UPDATE npc_text SET Text0_0='Die Tauren von Donnerfels müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Tauren von Donnerfels müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5864;
UPDATE npc_text SET Text0_0='Die Trolle des Dunkelspeerstamms müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.', Text0_1='Die Trolle des Dunkelspeerstamms müssen Euch mit Ehrfurcht begegnen, bevor ich Euch eine Reitfertigkeit lehre, $C.' WHERE id=5865;
UPDATE npc_text SET Text0_1='Ich glaube nicht, dass Ihr die geistige Schärfe besitzt, um die Natur der Portalmagie zu verstehen.' WHERE id=5873;
UPDATE npc_text SET Text0_1='Als Magier gehört Ihr zu einer auserwählten Gruppe, die ein Portal herbeizaubern kann, das Euch zurück zu diesem Ort transportiert. Ich kann Euch unterrichten, wenn Ihr bereit seid.' WHERE id=5874;
